INSERT INTO `broadcast_text_locale` VALUES (136736, 'enUS', '|TInterface\\Icons\\spell_shadow_summonfelhunter.BLP:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_Socrethar.blp:24|t', '|TInterface\\Icons\\spell_shadow_summonfelhunter.BLP:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_Socrethar.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136737, 'enUS', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136743, 'enUS', '', 'It is time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136748, 'enUS', 'Purple Team gain the Gift of the Emperor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136749, 'enUS', 'Gold Team gains the Gift of the Emperor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136781, 'enUS', '', 'There is much prey to be had here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136782, 'enUS', '', 'Even here, I can hear it calling to me. Its hunger...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136783, 'enUS', '', 'Welcome to the heart of Argus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136784, 'enUS', '', 'They are truly endless. We must find a way to bring a final end to them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136785, 'enUS', 'What information have you been able to gather, Alleria?', 'What information have you been able to gather, Alleria?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136786, 'enUS', 'Turalyon wants to know the situation down here.', 'Turalyon wants to know the situation down here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136787, 'enUS', '', 'The outlook is grim.$b$bMounting a direct assault from this location would prove far too costly. What is more, those cannons atop the wall may well be powerful enough to bring down the Vindicaar.$b$bI suggest using this foothold to launch guerilla strikes. Assassinate key members of their leadership and wreak havoc within their ranks.$b$bI know this is not what you or Turalyon were hoping to hear, but a patient hunter must know when to strike.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136790, 'enUS', 'Here at last. It has been so long...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136791, 'enUS', 'They are in full force here. They truly are Legion.$b$bPerfect.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136792, 'enUS', 'My glaives hunger for the blood of demons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136793, 'enUS', 'I shall put an end to the Legion in this accursed place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136794, 'enUS', 'You ask the obvious.$b$bA direct assault on Antorus would be folly. The Legion\'s forces vastly outnumber us, and they are replacing fallen soldiers at unnatural speed. Some of these brutes are even strong enough to slow me down... slightly.$b$bBreaching their stronghold will require a different plan. In the meantime, there is a sea of demons to slay.$b$bGo back to your High Exarch and let him strategize. I have an enemy to destroy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136817, 'enUS', 'The Marsh Shambler becomes Fel Enraged and breaks free from the corrupters!', 'The Marsh Shambler becomes Fel Enraged and breaks free from the corrupters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136826, 'enUS', '', 'We must continue to fight until the Legion is no more.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136833, 'enUS', '', 'The power of the Netherlight Crucible is yours, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136834, 'enUS', '', 'The sacrifices made to empower the Netherlight Crucible must never be forgotten.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136839, 'enUS', 'Argus will never belong to the Legion so long as we stand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136847, 'enUS', '|TINTERFACE\\ICONS\\SPELL_FIRE_FELFLAMESTRIKE.BLP:20|t This area is targeted for |cFFF00000|Hspell:250249|h[Obliteration]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136873, 'enUS', 'Kor\'vas, what is the status of the Sargerite Keystone?', 'Kor\'vas, what is the status of the Sargerite Keystone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136875, 'enUS', '', 'Lord Illidan has taken it. His plan is in motion, Slayer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136890, 'enUS', '<The warframe hisses with energy, ready to be taken into battle.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136891, 'enUS', 'Begin attack run on Antorus.', 'Begin attack run on Antorus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136897, 'enUS', '|cFFFF0000|Hspell:252508|h[The Paraxis]|h|r is arriving soon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136898, 'enUS', '|cFFFF0000|Hspell:252508|h[The Paraxis]|h|r has arrived! Run for cover!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136924, 'enUS', 'I want to go to the Seat of the Triumvirate (Dungeon).', 'I want to go to the Seat of the Triumvirate (Dungeon).', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136926, 'enUS', 'Purple Team gains Gift of the Emperor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136930, 'enUS', '', 'The Paraxis is returning soon! Army of the Light, set up your shields!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136932, 'enUS', '', 'The Paraxis is here! Take cover under the shields!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136933, 'enUS', 'The Gift of the Emperor is placed within the arena!', '', 26124);
