INSERT INTO `broadcast_text_locale` VALUES (136971, 'enUS', '|TInterface\\Icons\\SPELL_SHADOW_SUMMONFELGUARD.BLP:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t', '|TInterface\\Icons\\SPELL_SHADOW_SUMMONFELGUARD.BLP:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (137042, 'enUS', 'I have not had to create something like this in a millennium; bear with me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (137043, 'enUS', 'Ah, yes, it is coming together nicely.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (137044, 'enUS', 'It is once again whole!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (137081, 'enUS', 'We have acquired many goods and curios over the course of this war.', '', 26124);
