INSERT INTO `creature_text` VALUES (24321, 0, 0, '%s motions for silence.', 16, 0, 100, 397, 0, 0, 23130, 0, 0, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 1, 0, 'Vrykul, your king implores you to listen!', 14, 0, 100, 22, 0, 0, 23134, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 2, 0, 'The crowd gasps in horror.', 16, 0, 100, 53, 0, 0, 23136, 0, 0, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 3, 0, 'Even now, in our darkest hour, they mock us!', 14, 0, 100, 22, 0, 0, 23137, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 4, 0, 'Where are the titans in our time of greatest need? Our women birth abberations - disfigured runts unable to even stand on their own! Weak and ugly... Useless...', 14, 0, 100, 22, 0, 0, 23138, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 5, 0, 'Ymiron has toiled. Long have I sat upon my throne and thought hard of our plight. There is only one answer... one reason...', 14, 0, 100, 22, 0, 0, 23139, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 6, 0, 'For who but the titans themselves could bestow such a curse? What could have such power?', 14, 0, 100, 1, 0, 0, 23140, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 7, 0, 'And the answer is nothing... For it is the titans that have cursed us!', 14, 0, 100, 53, 0, 0, 23141, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 8, 0, 'The crowd cheers.', 16, 0, 100, 0, 0, 0, 23144, 0, 0, 0, 'King Ymiron');
INSERT INTO `creature_text` VALUES (24321, 9, 0, 'On this day all Vrykul will shed their old beliefs! We denounce our old gods! All Vrykul will pledge their allegiance to Ymiron! Ymiron will protect our noble race!', 14, 0, 100, 53, 0, 0, 23143, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 10, 0, 'And now my first decree upon the Vrykul! All malformed infants born of Vrykul mother and father are to be destroyed upon birth! Our blood must remain pure always! Those found in violation of Ymiron\'s decree will be taken to Gjalerbron for execution!', 14, 0, 100, 1, 0, 0, 23145, 0, 0, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24322, 0, 0, 'Silence!', 14, 0, 100, 1, 0, 0, 83588, 0, 0, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 1, 0, 'Show them mercy, my king! They are of our flesh and blood!', 14, 0, 100, 53, 0, 0, 23128, 0, 0, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 2, 0, 'All hail our glorious king, Ymiron!', 14, 0, 100, 53, 0, 0, 23123, 0, 0, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 0, 0, 'Show the abberations no mercy, Ymiron!', 14, 0, 100, 1, 0, 0, 23125, 0, 0, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 1, 0, 'They weaken us! Our strength is dilluted by their very existence! Destroy them all!', 14, 0, 100, 113, 0, 0, 23129, 0, 0, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24327, 0, 0, 'Shall we prepare it for you, my lord?', 12, 0, 100, 0, 0, 0, 23152, 0, 0, 0, 'Val\'kyr Soulclaimer');
INSERT INTO `creature_text` VALUES (24363, 0, 0, 'Сегодня духи попируют на славу! Начинайте церемонию, принесите пленников в жертву... освободите место для наших гостей!', 14, 0, 100, 0, 0, 12053, 23253, 0, 0, 0, 'Повелитель проклятий Малакрасс to 187359');
INSERT INTO `creature_text` VALUES (24363, 3, 0, 'Вы потерпите неудачу, чужаки. Многие до вас пытались победить нас. И мы становились сильнее, убивая их.', 14, 0, 100, 0, 0, 12056, 23318, 168293, 168293, 0, 'Повелитель проклятий Малакрасс to 187359');
INSERT INTO `creature_text` VALUES (24366, 0, 0, 'Guards!', 14, 7, 100, 0, 0, 0, 4583, 0, 0, 0, 'Nizzle');
INSERT INTO `creature_text` VALUES (24396, 3, 0, 'Приветствую тебя, $gдобрый незнакомец:добрая незнакомка;, и благодарю за твою самоотверженность.', 12, 0, 100, 0, 0, 6378, 23346, 1356327, 1356327, 0, 'Маваго to Player');
INSERT INTO `creature_text` VALUES (24398, 3, 0, 'Ad Waldir thor waldirs ', 14, 0, 100, 0, 0, 0, 0, 1766000, 1766000, 0, 'Землечерп Стальных ворот to Землечерп Стальных ворот');
INSERT INTO `creature_text` VALUES (24492, 0, 0, 'Эй, попробуйте это превосходное пиво!', 12, 0, 100, 0, 0, 33400, 22547, 0, 0, 0, 'Зазывала пивоварни Дрона');
INSERT INTO `creature_text` VALUES (24492, 3, 0, 'Выпей бесплатного пива и запусти в обжору кружкой!', 14, 1, 100, 0, 0, 33400, 22385, 303208, 428704, 0, 'Зазывала пивоварни Дрона to [DND] Brewfest Dark Iron Event Generator');
INSERT INTO `creature_text` VALUES (24492, 3, 0, 'О выпивке Дрона уже слагают легенды! Заходите и сами пробуйте какова легенда на вкус!', 12, 1, 100, 396, 0, 33400, 23514, 42547, 42547, 0, 'Зазывала пивоварни Дрона');
INSERT INTO `creature_text` VALUES (24493, 0, 0, 'Эй, попробуйте это превосходное пиво!', 12, 0, 100, 0, 0, 33400, 22547, 0, 0, 0, 'Зазывала вудуистской пивоварни Т\'чали');
INSERT INTO `creature_text` VALUES (24493, 3, 0, 'Пей до дна! Пей до дна!', 14, 1, 100, 0, 0, 33400, 26023, 983343, 983343, 0, 'Зазывала вудуистской пивоварни Т\'чали to [DND] Brewfest Dark Iron Event Generator');
INSERT INTO `creature_text` VALUES (24493, 3, 0, 'Хочешь почувствовать вкус к жизни – отведай пива с завода Т\'чали Вуду!', 12, 1, 100, 396, 0, 33400, 23518, 335579, 335579, 0, 'Зазывала вудуистской пивоварни Т\'чали');
INSERT INTO `creature_text` VALUES (24495, 3, 0, 'Эй-эй! Красавица!', 12, 1, 100, 396, 0, 33400, 23497, 50954, 50954, 0, 'Бликс Чиништукс to Player');
INSERT INTO `creature_text` VALUES (24528, 0, 0, 'How much\'ll it cost me to get my mules shod?', 12, 7, 100, 6, 0, 0, 23561, 0, 0, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 1, 0, 'You don\'t understand! If I\'m not ready to go soon, all the good homesteading land will be taken!', 12, 7, 100, 1, 0, 0, 23563, 0, 0, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 2, 0, 'That, sir, is a mule!', 12, 7, 100, 25, 0, 0, 23567, 0, 0, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24532, 0, 0, 'And I don\'t want our land to have any of those horrible wolves, or giants, or ugly rams!', 12, 7, 100, 1, 0, 0, 23607, 0, 0, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24532, 1, 0, 'I\'d sooner butcher that mule of yours! Why did you bring that beast in here, anyway?', 12, 7, 100, 5, 0, 0, 23609, 0, 0, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24532, 2, 0, 'Remember, honey, we need to get a plot of land with a nice hot spring.', 12, 7, 100, 1, 0, 0, 23605, 0, 0, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24534, 0, 0, 'Welcome to Fort Wildervar, brave homesteaders! There\'s a whole continent out there just waiting to be claimed!', 12, 7, 100, 1, 0, 0, 23570, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 1, 0, 'True, Northrend is a hard land, but our people are strong, hardy, and equal to the task!', 12, 7, 100, 0, 0, 0, 23571, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 2, 0, 'We will win this land with the sword, and break it with the plow! You are the men and women who will be remembered for taming the wild continent!', 12, 7, 100, 1, 0, 0, 23572, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 3, 0, 'But, you will not be alone out there. My men and I have prepared pack mules carrying the supplies you\'ll need most.', 12, 7, 100, 1, 0, 0, 23573, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 4, 0, 'Axes, picks, seed, nails, food, blankets, water... it\'s all there, waiting for you. I think you\'ll find my prices quite reasonable, too.', 12, 7, 100, 25, 0, 0, 23574, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 5, 0, 'There are more than enough to go around. Should you need other goods, don\'t hesitate to ask!', 12, 7, 100, 1, 0, 0, 23575, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 6, 0, 'Now, my loyal custo... err, friends, go forth and conquer this land for our people!', 12, 7, 100, 274, 0, 0, 23576, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24536, 0, 0, 'Кто-то сказал про бесплатное пиво или мне послышалось?', 14, 0, 100, 0, 0, 33400, 22318, 0, 0, 0, 'Глашатай из клана Черного Железа to [DND] Brewfest Dark Iron Event Generator');
INSERT INTO `creature_text` VALUES (24536, 0, 0, 'ОТСТУПАЕМ!!! Нас теснят, мы потеряли уже $3151W! Нельзя терять еще больше! НАЗАД!!!', 14, 0, 100, 0, 0, 33400, 23489, 0, 0, 0, 'Глашатай из клана Черного Железа to [DND] Brewfest Dark Iron Event Generator');
INSERT INTO `creature_text` VALUES (24539, 0, 0, 'Do not test me, scurvy dog! I\'m trained in the way of the Blood Knights!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (24539, 1, 0, 'I\'ll pay! I\'ll pay! Eeeek! Please don\'t hurt me!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (24560, 0, 0, 'Уничтожьте их.', 14, 0, 100, 0, 0, 12395, 89752, 0, 0, 0, 'Жрица Делрисса to Player');
INSERT INTO `creature_text` VALUES (24560, 1, 0, 'На это... я... не рассчитывала...', 14, 0, 100, 0, 0, 12397, 25431, 0, 0, 0, 'Жрица Делрисса to Жрица Делрисса');
INSERT INTO `creature_text` VALUES (24560, 2, 0, 'Ужасно, просто ужасно!', 14, 0, 100, 0, 0, 0, 25432, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 3, 0, 'Повезло, да?', 14, 0, 100, 0, 0, 0, 25433, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 4, 0, 'Меня это начинает раздражать...', 14, 0, 100, 0, 0, 0, 25434, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 5, 0, 'Да будут прокляты эти прислужники! Я сама покончу с вами!', 14, 0, 100, 0, 0, 0, 25435, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 6, 0, 'Это я называю удачным началом.', 14, 0, 100, 0, 0, 0, 25436, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 7, 0, 'Могу поклясться, вас было больше...', 14, 0, 100, 0, 0, 0, 25437, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 8, 0, 'Ну что, на группу вы уже не похожи?', 14, 0, 100, 0, 0, 0, 25438, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 9, 0, 'Один в поле не воин, да?', 14, 0, 100, 0, 0, 0, 25439, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24560, 10, 0, 'Неплохо повеселились, правда?', 14, 0, 100, 0, 0, 0, 25440, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24639, 0, 0, 'Keep quiet, will you? If anyone catches on, we\'re both dead.', 15, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24639, 1, 0, 'All right, fellas! Who ordered the spiced rum? Was it you, Jackie boy?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24639, 2, 0, 'It\'s okay, sweetheart. This one\'s on the house.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24664, 0, 0, 'Нечего надувать щеки! Мое поражение в Крепости Бурь – всего лишь мелкая помеха. Неужели вам и правда пришло в голову, что я могу доверить будущее какому-то слепому полукровке-ночному эльфу?', 14, 0, 100, 0, 0, 12413, 25386, 0, 0, 0, 'Кель\'тас Солнечный Скиталец to Магистр Солнечного Клинка');
INSERT INTO `creature_text` VALUES (24664, 1, 0, 'Месть жжет, как пламя!', 14, 0, 100, 0, 0, 0, 25388, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24664, 2, 0, 'Феломин Ашал!', 14, 0, 100, 0, 0, 0, 25389, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24664, 3, 0, 'I will turn your world ... upside down.', 14, 0, 100, 0, 0, 12418, 25390, 0, 0, 0, 'Кель\'тас Солнечный Скиталец to 0');
INSERT INTO `creature_text` VALUES (24664, 4, 0, 'Владыка, дай мне сил!', 14, 0, 100, 0, 0, 0, 25391, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24664, 5, 0, 'Не слишком-то... расслабляйтесь.', 14, 0, 100, 0, 0, 0, 25392, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24664, 6, 0, 'Моя смерть ничего не изменит! Владыка доберется до вас! Вы захлебнетесь собственной кровью! Весь мир поглотит пламя! Аххх...', 14, 0, 100, 0, 0, 12421, 25393, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24664, 7, 0, 'О нет, он был лишь орудием, лишь камнем в основании грандиозного замысла! Долгий путь вел к этому мгновению... И на этот раз ты ничему не сможешь помешать!', 14, 0, 100, 0, 0, 0, 25387, 0, 0, 0, 'Кель\'тас Солнечный Скиталец to Магистр Солнечного Клинка');
INSERT INTO `creature_text` VALUES (24688, 0, 0, 'ЭТО МОЕ!!!', 12, 0, 100, 0, 0, 12378, 23845, 0, 0, 0, 'Тихий презренный to Player');
INSERT INTO `creature_text` VALUES (24689, 0, 0, 'ЭТО МОЕ!!!', 12, 0, 100, 0, 0, 12378, 23845, 0, 0, 0, 'Презренный-костолом to Player');
INSERT INTO `creature_text` VALUES (24690, 0, 0, 'Бурлит и пылает...', 12, 0, 100, 0, 0, 0, 23841, 0, 0, 0, 'Презренный-полутруп to 0');
INSERT INTO `creature_text` VALUES (24690, 1, 0, 'Ты хочешь украсть силу! Смерть тебе!', 12, 0, 100, 0, 0, 12378, 23846, 0, 0, 0, 'Презренный-полутруп to Player');
INSERT INTO `creature_text` VALUES (24710, 0, 0, 'Right! Let\'s try this so-called dire brew...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 1, 0, 'Bronzebeard\'s beard! What a drink!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 2, 0, '$N, this beer... well, it might be the best of Brewfest!', 12, 0, 100, 15, 0, 0, 27421, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 3, 0, 'It is a shame Coren\'s Dire Brew can\'t quench more thirsting throats! It\'s blasphemy, I know, but I envy those Dark Iron dwarves!', 12, 0, 100, 274, 0, 0, 0, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 0, 'Ho there, and welcome to Brewfest!', 12, 7, 100, 3, 0, 0, 23691, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 1, 'Hello, friend! Welcome to the Brewfest Grounds!', 12, 7, 100, 3, 0, 0, 23692, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 2, 'Hail, good $c! Welcome to Ironforge\'s annual Brewfest celebration!', 12, 7, 100, 3, 0, 0, 23693, 0, 0, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24711, 0, 0, 'Well time\'s money, and time\'s wasting! Let\'s have that drink...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 1, 0, 'Wow! This really greases the steamplugs!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 2, 0, 'If I were an honest goblin, I\'d say this was the best beer of the festival...', 12, 0, 100, 1, 0, 0, 27437, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 3, 0, 'Too bad Coren\'s dead. If I could buy that brew from him and resell it - watered down and rebottled under my own label, of course - I\'d be rich!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 0, 'Hey there, and welcome to Brewfest. Come in and have a drink or three!', 12, 1, 100, 3, 0, 0, 23698, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 1, 'What\'s up, friend? Welcome to the Brewfest Grounds! Now go buy some drinks!', 12, 1, 100, 3, 0, 0, 23700, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 2, 'Hey hey, it\'s another $r! Welcome to my annual Brewfest celebration... now go feast and drink some!', 12, 1, 100, 3, 0, 0, 0, 0, 0, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24718, 1, 0, 'What do you think you\'re doing, man? Lebronski does NOT appreciate you dragging your loose metal parts all over his rug.', 12, 7, 100, 0, 0, 0, 23890, 0, 0, 0, 'Lebronski');
INSERT INTO `creature_text` VALUES (24718, 2, 0, 'Far out, man. This bucket of bolts might make it after all...', 12, 7, 100, 0, 0, 0, 23891, 0, 0, 0, 'Lebronski');
INSERT INTO `creature_text` VALUES (24723, 0, 0, 'I am wasting my time with you!', 14, 0, 100, 0, 0, 12378, 25423, 0, 0, 0, 'Селин Огненное Сердце to Player');
INSERT INTO `creature_text` VALUES (24723, 1, 0, 'Мой голод неутолим!', 14, 0, 100, 0, 0, 12381, 25424, 0, 0, 0, 'Селин Огненное Сердце to 0');
INSERT INTO `creature_text` VALUES (24723, 2, 0, 'Да! Я бог!', 14, 0, 100, 0, 0, 12382, 25425, 0, 0, 0, 'Селин Огненное Сердце to 0');
INSERT INTO `creature_text` VALUES (24723, 3, 0, 'I\'m invincible.', 14, 0, 100, 0, 0, 12385, 25427, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24723, 4, 0, 'Хватит размениваться на мелочи.', 14, 0, 100, 0, 0, 12388, 25428, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24723, 5, 0, 'Нет... Еще, мне нужно еще!', 14, 0, 100, 0, 0, 12383, 25426, 0, 0, 0, 'Селин Огненное Сердце to 0');
INSERT INTO `creature_text` VALUES (24723, 6, 0, '%s начинает вытягивать энергию из ближайшего кристалла Скверны… Уничтожьте его!', 41, 0, 100, 0, 0, 0, 23753, 0, 0, 0, 'Селин Огненное Сердце to 0');
INSERT INTO `creature_text` VALUES (24744, 0, 0, 'Пить... жизнь!', 14, 0, 100, 0, 0, 12389, 25623, 0, 0, 0, 'Вексалиус to Player');
INSERT INTO `creature_text` VALUES (24744, 1, 0, 'Не... удержать.', 14, 0, 100, 0, 0, 12392, 25626, 0, 0, 0, 'Вексалиус to 0');
INSERT INTO `creature_text` VALUES (24744, 2, 0, 'За... пускать.', 14, 0, 100, 0, 0, 0, 25625, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24744, 3, 0, 'Пог... лотить.', 14, 0, 100, 0, 0, 0, 25627, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24744, 4, 0, 'Вексалиус выпускает заряд чистой энергии!', 41, 0, 100, 0, 0, 0, 23752, 0, 0, 0, 'Вексалиус to 0');
INSERT INTO `creature_text` VALUES (24779, 0, 0, 'Tonight we feast on you!', 12, 0, 100, 0, 0, 0, 23806, 0, 0, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24779, 0, 1, 'Such easy prey...', 12, 0, 100, 0, 0, 0, 23805, 0, 0, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24779, 0, 2, 'Who\'s hunting who?', 12, 0, 100, 0, 0, 0, 23804, 0, 0, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24788, 0, 0, 'Sure thing, love. Put it on Harry\'s tab, will ya? He owes me a drink!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (24788, 1, 0, 'Free rum? Why... that\'s me favorite kind!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (24788, 2, 0, 'Sweet Neptulon! That was... one drink... too many!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (24806, 0, 0, 'Launching...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (24823, 0, 0, 'Model U-9207 Iron Rune Construct does not appreciate your tone of voice. Commencing total annihilation of your rug, Lebronski.', 12, 0, 100, 0, 0, 0, 23892, 0, 0, 0, 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES (24825, 0, 0, 'Alert! Alert! Construct senses that enemies have breeched our defenses. Increase pace.', 12, 9, 100, 0, 0, 0, 23895, 0, 0, 0, 'GOLEM');
INSERT INTO `creature_text` VALUES (24825, 0, 1, 'Construct class carrier reporting for duty. Primary function: Overseeing.', 12, 9, 100, 0, 0, 0, 23896, 0, 0, 0, 'GOLEM');
