INSERT INTO `creature_text` VALUES (26203, 0, 0, 'Да, это удивительно, Тассариан. Я не получал известий от Могрейна и других рыцарей смерти уже несколько месяцев. Как я понимаю, ты пришел воссоединиться с Плетью?', 12, 0, 100, 0, 0, 14757, 25363, 0, 0, 0, 'Проекция Короля-лича to Player');
INSERT INTO `creature_text` VALUES (26203, 1, 0, 'Не подведи меня, Сан\'лейн. Либо вернись в Ледяную Корону с головой этого глупца, либо не возвращайся вовсе.', 12, 0, 100, 1, 0, 14758, 25364, 0, 0, 0, 'Проекция Короля-лича to Player');
