INSERT INTO `creature_text` VALUES (26246, 3, 0, 'Со временем свет и надежда помогут возродить не только этот исток силы... Быть может, они возродят и самою душу народа.', 12, 0, 100, 1, 0, 12521, 25412, 135141, 135141, 0, 'Пророк Велен to Хранитель портала из армии Расколотого Солнца');
INSERT INTO `creature_text` VALUES (26247, 3, 0, 'Благословенные предки!.. Я чувствую... столько любви... милосердия... Нет слов, чтобы описать это...', 12, 0, 100, 5, 0, 12526, 25413, 149735, 149735, 0, 'Леди Лиадрин to Хранитель портала из армии Расколотого Солнца');
INSERT INTO `creature_text` VALUES (26266, 3, 0, 'Деритесь, квалдиры! Мы сровняем Каскалу с землей!', 14, 0, 100, 0, 0, 0, 25454, 156, 156, 0, 'Хейгарр Душегуб');
INSERT INTO `creature_text` VALUES (26293, 0, 0, 'The Hulking Jormungar falters for a moment, opening its mouth wide.', 41, 0, 100, 0, 0, 0, 25961, 0, 0, 0, 'Hulking Jormungar');
INSERT INTO `creature_text` VALUES (26310, 3, 0, 'Keristrasza! You\'ve bested my consort... and now YOU shall take her place!', 14, 0, 100, 0, 0, 0, 25485, 137406, 137406, 0, 'Malygos to Player');
INSERT INTO `creature_text` VALUES (26321, 0, 0, 'The %s gives you its thanks.', 16, 0, 100, 0, 0, 1525, 26039, 0, 0, 0, 'Lothalor Ancient');
INSERT INTO `creature_text` VALUES (26334, 0, 0, 'В битву, мои дети! Никакой пощады нашим врагам!', 14, 0, 100, 0, 0, 0, 25484, 0, 0, 0, 'Начальник кузни Дамрат');
INSERT INTO `creature_text` VALUES (26334, 0, 0, 'Големы, защитите столицу любой ценой!', 14, 0, 100, 0, 0, 0, 25483, 0, 0, 0, 'Начальник кузни Дамрат');
INSERT INTO `creature_text` VALUES (26334, 3, 0, 'За тана! За Локена!', 14, 0, 100, 0, 0, 0, 25486, 1717000, 3390000, 0, 'Начальник кузни Дамрат');
INSERT INTO `creature_text` VALUES (26334, 3, 0, 'Сокрушите детей камня, мои железные собратья!', 14, 0, 100, 0, 0, 0, 25487, 11794000, 11794000, 0, 'Начальник кузни Дамрат');
INSERT INTO `creature_text` VALUES (26403, 3, 0, 'Thank the spirits!  The blizzard will slow down the kvadir ships!', 14, 0, 100, 0, 0, 0, 25614, 88781, 88781, 0, 'Kaskala Lookout to Player');
INSERT INTO `creature_text` VALUES (26403, 3, 0, 'Слава духам! Этот буран замедлит передвижение кораблей квалдиров!', 14, 0, 100, 0, 0, 0, 25614, 1000, 65000, 0, 'Наблюдатель клана Каскала to Player');
INSERT INTO `creature_text` VALUES (26417, 0, 0, 'Children of iron, lend me your blades!', 12, 0, 100, 0, 0, 0, 25981, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26417, 0, 1, 'Iron masters, aid me in this battle!', 12, 0, 100, 0, 0, 0, 25967, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26417, 0, 2, 'The enemy grows stronger! Aid me!', 12, 0, 100, 0, 0, 0, 25982, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26420, 0, 0, 'Ah, yes. Loken is well known to me.', 12, 0, 100, 0, 0, 0, 26267, 0, 0, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 1, 0, 'It is he who commands the sons of iron in their war against us.', 12, 0, 100, 0, 0, 0, 26268, 0, 0, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 2, 0, 'From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants!', 12, 0, 100, 0, 0, 0, 26269, 0, 0, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26421, 0, 0, 'Breaking off a piece of its bark, the %s hands it to you before departing.', 16, 0, 100, 0, 0, 0, 26041, 0, 0, 0, 'Woodlands Walker');
INSERT INTO `creature_text` VALUES (26421, 1, 0, 'The %s is angered by your request and attacks!', 16, 0, 100, 0, 0, 0, 26042, 0, 0, 0, 'Woodlands Walker');
INSERT INTO `creature_text` VALUES (26450, 3, 0, 'The fallen of Pal\'ea will not rest, son.  Find the naga priestess!', 12, 0, 100, 1, 0, 0, 25684, 2609, 2609, 0, 'Ariut to Player');
INSERT INTO `creature_text` VALUES (26484, 0, 0, 'He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind!', 12, 0, 100, 0, 0, 0, 26266, 0, 0, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 1, 0, 'That\'s not something Limpy Joe would ask! But yeah, I know Loken.', 12, 0, 100, 0, 0, 0, 26265, 0, 0, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26497, 3, 0, 'Прости, Артас. Я не могу на это смотреть.', 12, 0, 100, 396, 0, 14949, 27330, 115947, 115947, 0, 'Леди Джайна Праудмур to Player');
INSERT INTO `creature_text` VALUES (26499, 0, 0, 'Glad you could make it, Uther.', 12, 0, 100, 0, 0, 12828, 27314, 0, 0, 0, 'culling SAY_PHASE101');
INSERT INTO `creature_text` VALUES (26499, 1, 0, 'As if I could forget. Listen, Uther, there\'s something about the plague you should know...', 12, 0, 100, 0, 0, 12829, 27316, 0, 0, 0, 'culling SAY_PHASE103');
INSERT INTO `creature_text` VALUES (26499, 2, 0, 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', 12, 0, 100, 0, 0, 12830, 27317, 0, 0, 0, 'culling SAY_PHASE104');
INSERT INTO `creature_text` VALUES (26499, 3, 0, 'This entire city must be purged.', 12, 0, 100, 0, 0, 12831, 27319, 0, 0, 0, 'culling SAY_PHASE106');
INSERT INTO `creature_text` VALUES (26499, 4, 0, 'Damn it, Uther! As your future king, I order you to purge this city!', 12, 0, 100, 0, 0, 12832, 27321, 0, 0, 0, 'culling SAY_PHASE108');
INSERT INTO `creature_text` VALUES (26499, 5, 0, 'Then I must consider this an act of treason.', 12, 0, 100, 0, 0, 12833, 27323, 0, 0, 0, 'culling SAY_PHASE110');
INSERT INTO `creature_text` VALUES (26499, 6, 0, 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', 12, 0, 100, 0, 0, 12834, 27325, 0, 0, 0, 'culling SAY_PHASE112');
INSERT INTO `creature_text` VALUES (26499, 7, 0, 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', 12, 0, 100, 0, 0, 12835, 27327, 0, 0, 0, 'culling SAY_PHASE114');
INSERT INTO `creature_text` VALUES (26499, 8, 0, 'Jaina?', 12, 0, 100, 0, 0, 12836, 27329, 0, 0, 0, 'culling SAY_PHASE116');
INSERT INTO `creature_text` VALUES (26499, 9, 0, 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron!', 14, 0, 100, 0, 0, 14327, 27466, 0, 0, 0, 'culling SAY_PHASE118');
INSERT INTO `creature_text` VALUES (26499, 10, 0, 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', 12, 0, 100, 0, 0, 14293, 27535, 0, 0, 0, 'culling SAY_PHASE201');
INSERT INTO `creature_text` VALUES (26499, 11, 0, 'I can only help you with a clean death.', 12, 0, 100, 0, 0, 14294, 27548, 0, 0, 0, 'culling SAY_PHASE203');
INSERT INTO `creature_text` VALUES (26499, 12, 0, 'That was just the beginning.', 12, 0, 100, 0, 0, 14295, 27553, 0, 0, 0, 'culling SAY_PHASE205');
INSERT INTO `creature_text` VALUES (26499, 13, 0, 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', 14, 0, 100, 0, 0, 14296, 27577, 0, 0, 0, 'culling SAY_PHASE208');
INSERT INTO `creature_text` VALUES (26499, 14, 0, 'Mal\'Ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', 14, 0, 100, 0, 0, 14885, 27584, 0, 0, 0, 'culling SAY_PHASE209');
INSERT INTO `creature_text` VALUES (26499, 15, 0, 'Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis.', 14, 0, 100, 0, 0, 14297, 28890, 0, 0, 0, 'culling SAY_PHASE210');
INSERT INTO `creature_text` VALUES (26499, 16, 0, 'Follow me, I know the way through.', 12, 0, 100, 0, 0, 14298, 27800, 0, 0, 0, 'culling SAY_PHASE301');
INSERT INTO `creature_text` VALUES (26499, 17, 0, 'Yes, I\'m glad I could get to you before the plague.', 12, 0, 100, 0, 0, 14299, 27812, 0, 0, 0, 'culling SAY_PHASE303');
INSERT INTO `creature_text` VALUES (26499, 18, 0, 'What is this sorcery?', 12, 0, 100, 0, 0, 14300, 27814, 0, 0, 0, 'culling SAY_PHASE304');
INSERT INTO `creature_text` VALUES (26499, 19, 0, 'Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste.', 12, 0, 100, 0, 0, 14301, 27840, 0, 0, 0, 'culling SAY_PHASE306. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 20, 0, 'More vile sorcery! Be ready for anything!', 12, 0, 100, 0, 0, 14302, 27882, 0, 0, 0, 'culling SAY_PHASE307');
INSERT INTO `creature_text` VALUES (26499, 21, 0, 'Let\'s move on.', 12, 0, 100, 0, 0, 14303, 36010, 0, 0, 0, 'culling SAY_PHASE308');
INSERT INTO `creature_text` VALUES (26499, 22, 0, 'Watch your backs: they have us surrounded in this hall.', 12, 0, 100, 0, 0, 14304, 27901, 0, 0, 0, 'culling SAY_PHASE309');
INSERT INTO `creature_text` VALUES (26499, 23, 0, 'One less obstacle to deal with.', 12, 0, 100, 0, 0, 0, 31356, 0, 0, 0, 'culling SAY_PHASE310. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 24, 0, 'Mal\'Ganis is not making this easy.', 12, 0, 100, 0, 0, 14305, 27902, 0, 0, 0, 'culling SAY_PHASE311');
INSERT INTO `creature_text` VALUES (26499, 25, 0, 'They\'re very persistent.', 12, 0, 100, 0, 0, 14306, 27904, 0, 0, 0, 'culling SAY_PHASE312');
INSERT INTO `creature_text` VALUES (26499, 26, 0, 'What else can he put in my way?', 12, 0, 100, 0, 0, 14307, 27905, 0, 0, 0, 'culling SAY_PHASE313');
INSERT INTO `creature_text` VALUES (26499, 27, 0, 'I do what I must for Lordaeron, and neither your words nor your actions will stop me.', 12, 0, 100, 0, 0, 14309, 27916, 0, 0, 0, 'culling SAY_PHASE315');
INSERT INTO `creature_text` VALUES (26499, 28, 0, 'The quickest path to Mal\'Ganis lies behind that bookshelf ahead.', 12, 0, 100, 0, 0, 14308, 27906, 0, 0, 0, 'culling SAY_PHASE401');
INSERT INTO `creature_text` VALUES (26499, 29, 0, 'This will only take a moment.', 12, 0, 100, 0, 0, 14310, 28040, 0, 0, 0, 'culling SAY_PHASE402');
INSERT INTO `creature_text` VALUES (26499, 30, 0, 'I\'m relieved this secret passage still works.', 12, 0, 100, 0, 0, 14311, 28041, 0, 0, 0, 'culling SAY_PHASE403');
INSERT INTO `creature_text` VALUES (26499, 31, 0, 'Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might.', 12, 0, 100, 0, 0, 14312, 28562, 0, 0, 0, 'culling SAY_PHASE404');
INSERT INTO `creature_text` VALUES (26499, 32, 0, 'Rest a moment and clear your lungs, but we must move again soon.', 12, 0, 100, 0, 0, 14313, 28045, 0, 0, 0, 'culling SAY_PHASE405');
INSERT INTO `creature_text` VALUES (26499, 33, 0, 'That\'s enough; we must move again. Mal\'Ganis awaits.', 12, 0, 100, 0, 0, 14314, 28047, 0, 0, 0, 'culling SAY_PHASE406');
INSERT INTO `creature_text` VALUES (26499, 34, 0, 'At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward.', 12, 0, 100, 0, 0, 14315, 28048, 0, 0, 0, 'culling SAY_PHASE407');
INSERT INTO `creature_text` VALUES (26499, 35, 0, 'Justice will be done.', 12, 0, 100, 0, 0, 14316, 28563, 0, 0, 0, 'culling SAY_PHASE501');
INSERT INTO `creature_text` VALUES (26499, 36, 0, 'We\'re going to finish this right now, Mal\'Ganis. Just you... and me.', 12, 0, 100, 0, 0, 14317, 28520, 0, 0, 0, 'culling SAY_PHASE502');
INSERT INTO `creature_text` VALUES (26499, 37, 0, 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', 12, 0, 100, 0, 0, 14318, 28529, 0, 0, 0, 'culling SAY_PHASE503');
INSERT INTO `creature_text` VALUES (26499, 38, 0, 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', 12, 0, 100, 0, 0, 14319, 28530, 0, 0, 0, 'culling SAY_PHASE504');
INSERT INTO `creature_text` VALUES (26520, 0, 0, 'So good! So packed with energy!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26520, 0, 1, 'It has everything a growing scorchling needs!', 12, 0, 100, 0, 0, 0, 25719, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26520, 0, 2, 'I can feel the power SURGING within me!', 12, 0, 100, 0, 0, 0, 25730, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26520, 0, 3, 'Now! Finally! Our plans can take effect!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26520, 0, 4, 'KNEEL, LITTLE MORTAL! KNEEL BEFORE THE MIGHT OF THE HERALD OF RAGNAROS!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26520, 0, 5, 'YOU WILL ALL PERISH IN FLAMES!', 12, 0, 100, 0, 0, 0, 25723, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26528, 3, 0, 'Что?', 12, 0, 100, 5, 0, 12840, 127498, 49559, 49559, 0, 'Утер Светоносный to Player');
INSERT INTO `creature_text` VALUES (26529, 0, 0, 'Play time!', 14, 0, 100, 0, 0, 13428, 27716, 0, 0, 0, 'meathook SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26529, 1, 0, 'Boring...', 14, 0, 100, 0, 0, 13430, 29943, 0, 0, 0, 'meathook SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26529, 1, 1, 'Why you stop moving?', 14, 0, 100, 0, 0, 13431, 29944, 0, 0, 0, 'meathook SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26529, 1, 2, 'Get up! Me not done!', 14, 0, 100, 0, 0, 13432, 29945, 0, 0, 0, 'meathook SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26529, 2, 0, 'New toys!', 14, 0, 100, 0, 0, 13429, 29942, 0, 0, 0, 'meathook SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26529, 3, 0, 'This not fun...', 14, 0, 100, 0, 0, 13433, 29941, 0, 0, 0, 'meathook SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 0, 0, 'Ah, the entertainment has arrived!', 14, 0, 100, 0, 0, 13472, 29947, 0, 0, 0, 'salramm SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26530, 1, 0, 'You are too late, champion of Lordaeron. The dead shall have their day.', 14, 0, 100, 0, 0, 13471, 27717, 0, 0, 0, 'salramm SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26530, 2, 0, 'The fun is just beginning!', 14, 0, 100, 0, 0, 13473, 29948, 0, 0, 0, 'salramm SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26530, 2, 1, 'Ah! Quality materials.', 14, 0, 100, 0, 0, 13474, 29949, 0, 0, 0, 'salramm SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26530, 2, 2, 'Don\'t worry; I\'ll make good use of you!', 14, 0, 100, 0, 0, 13475, 29950, 0, 0, 0, 'salramm SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26530, 3, 0, 'You only advance... the master\'s plan...', 14, 0, 100, 0, 0, 13483, 29946, 0, 0, 0, 'salramm SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 4, 0, 'BOOM! Hahahahah...', 14, 0, 100, 0, 0, 13478, 29953, 0, 0, 0, 'salramm SAY_EXPLODE_GHOUL_1');
INSERT INTO `creature_text` VALUES (26530, 4, 1, 'Blood... destruction... EXHILARATING!', 14, 0, 100, 0, 0, 13479, 29954, 0, 0, 0, 'salramm SAY_EXPLODE_GHOUL_2');
INSERT INTO `creature_text` VALUES (26530, 5, 0, 'I want a sample...', 14, 0, 100, 0, 0, 13480, 29955, 0, 0, 0, 'salramm SAY_STEAL_FLESH_1');
INSERT INTO `creature_text` VALUES (26530, 5, 1, 'Such strength... it must be mine!', 14, 0, 100, 0, 0, 13481, 29956, 0, 0, 0, 'salramm SAY_STEAL_FLESH_2');
INSERT INTO `creature_text` VALUES (26530, 5, 2, 'Your flesh betrays you.', 14, 0, 100, 0, 0, 13482, 29957, 0, 0, 0, 'salramm SAY_STEAL_FLESH_3');
INSERT INTO `creature_text` VALUES (26530, 6, 0, 'Say hello to some friends of mine.', 14, 0, 100, 0, 0, 13476, 29951, 0, 0, 0, 'salramm SAY_SUMMON_GHOULS_1');
INSERT INTO `creature_text` VALUES (26530, 6, 1, 'Come, citizens of Stratholme! Meet your \"saviors.\"', 14, 0, 100, 0, 0, 13477, 29952, 0, 0, 0, 'salramm SAY_SUMMON_GHOULS_2');
INSERT INTO `creature_text` VALUES (26532, 0, 0, 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', 14, 0, 100, 0, 0, 13408, 27911, 0, 0, 0, 'epoch SAY_INTRO | culling SAY_PHASE314');
INSERT INTO `creature_text` VALUES (26532, 1, 0, 'We\'ll see about that, young prince.', 14, 0, 100, 0, 0, 13409, 27920, 0, 0, 0, 'epoch SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26532, 2, 0, 'Tick tock, tick tock...', 14, 0, 100, 0, 0, 13410, 30451, 0, 0, 0, 'epoch SAY_TIME_WARP_1');
INSERT INTO `creature_text` VALUES (26532, 2, 1, 'Not quick enough!', 14, 0, 100, 0, 0, 13411, 30452, 0, 0, 0, 'epoch SAY_TIME_WARP_2');
INSERT INTO `creature_text` VALUES (26532, 2, 2, 'Let\'s get this over with.', 14, 0, 100, 0, 0, 13412, 30453, 0, 0, 0, 'epoch SAY_TIME_WARP_3');
INSERT INTO `creature_text` VALUES (26532, 3, 0, 'There is no future for you.', 14, 0, 100, 0, 0, 13413, 30454, 0, 0, 0, 'epoch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26532, 3, 1, 'This is the hour of our greatest triumph!', 14, 0, 100, 0, 0, 13414, 30455, 0, 0, 0, 'epoch SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26532, 3, 2, 'You were destined to fail.', 14, 0, 100, 0, 0, 13415, 30456, 0, 0, 0, 'epoch SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26533, 0, 0, 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', 14, 0, 100, 0, 0, 14410, 27574, 0, 0, 0, 'malganis SAY_INTRO_1 | culling SAY_PHASE206');
INSERT INTO `creature_text` VALUES (26533, 1, 0, 'As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever.', 14, 0, 100, 0, 0, 14411, 27575, 0, 0, 0, 'malganis SAY_INTRO_2 | culling SAY_PHASE207');
INSERT INTO `creature_text` VALUES (26533, 2, 0, 'This will be a fine test, Prince Arthas.', 14, 0, 100, 0, 0, 14413, 31344, 0, 0, 0, 'malganis SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26533, 3, 0, 'All too easy.', 14, 0, 100, 0, 0, 14416, 31347, 0, 0, 0, 'malganis SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26533, 3, 1, 'The Dark Lord is displeased with your interference.', 14, 0, 100, 0, 0, 14417, 31348, 0, 0, 0, 'malganis SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26533, 3, 2, 'It is Prince Arthas I want, not you!', 14, 0, 100, 0, 0, 14418, 31349, 0, 0, 0, 'malganis SAY_KILL_3');
INSERT INTO `creature_text` VALUES (26533, 4, 0, 'Anakh kyree!', 14, 0, 100, 0, 0, 14422, 30412, 0, 0, 0, 'malganis SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26533, 4, 1, 'My Onslaught will wash over the Lich King\'s forces!', 14, 0, 100, 0, 0, 14423, 30413, 0, 0, 0, 'malganis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26533, 4, 2, 'Your death is in vain, tiny mortal.', 14, 0, 100, 0, 0, 14424, 30414, 0, 0, 0, 'malganis SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26533, 4, 3, 'Your time has come to an end.', 14, 0, 100, 0, 0, 14425, 30415, 0, 0, 0, 'malganis SAY_SLAY_4');
INSERT INTO `creature_text` VALUES (26533, 5, 0, 'Time out.', 14, 0, 100, 0, 0, 14414, 31345, 0, 0, 0, 'malganis SAY_SLEEP_1');
INSERT INTO `creature_text` VALUES (26533, 5, 1, 'You seem tired.', 14, 0, 100, 0, 0, 14415, 31346, 0, 0, 0, 'malganis SAY_SLEEP_2');
INSERT INTO `creature_text` VALUES (26533, 6, 0, 'Gah! I spent too much time in that weak little shell.', 14, 0, 100, 0, 0, 14426, 30275, 0, 0, 0, 'malganis SAY_30HEALTH');
INSERT INTO `creature_text` VALUES (26533, 7, 0, 'Kirel narak! I am Mal\'Ganis. I AM ETERNAL!', 14, 0, 100, 0, 0, 14427, 30276, 0, 0, 0, 'malganis SAY_15HEALTH');
INSERT INTO `creature_text` VALUES (26533, 8, 0, 'ENOUGH! I waste my time here. I must gather my strength on the homeworld.', 14, 0, 100, 0, 0, 14428, 30277, 0, 0, 0, 'malganis SAY_ESCAPE_SPEECH_1');
INSERT INTO `creature_text` VALUES (26533, 9, 0, 'You\'ll never defeat the Lich King without my forces. I\'ll have my revenge... on him AND you!', 14, 0, 100, 0, 0, 14429, 30278, 0, 0, 0, 'malganis SAY_ESCAPE_SPEECH_2');
INSERT INTO `creature_text` VALUES (26533, 10, 0, 'Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold.', 14, 0, 100, 0, 0, 14412, 28528, 0, 0, 0, 'malganis SAY_OUTRO');
INSERT INTO `creature_text` VALUES (26537, 3, 0, 'Только что прилетел дирижабль, который отправится в крепость Песни Войны! Все на борт! Мы летим в Борейскую тундру!', 14, 0, 100, 0, 0, 0, 29549, 0, 0, 0, 'Гриб Ракетокрыл');
INSERT INTO `creature_text` VALUES (26538, 3, 0, 'Только что прилетел дирижабль, который отправится в Оргриммар! Все на борт! Мы летим в Дуротар!', 14, 0, 100, 22, 0, 11804, 29551, 283596, 283596, 0, 'Нарго Вертокрут');
INSERT INTO `creature_text` VALUES (26539, 3, 0, 'Только что прилетел дирижабль, который отправится в Лагерь Возмездия! Все на борт! Мы летим к Ревущему фьорду!', 14, 0, 100, 22, 0, 11804, 29540, 37695, 467019, 0, 'Мифи \"Поддай газу\"');
INSERT INTO `creature_text` VALUES (26540, 0, 0, 'Дирижабль до Подгорода прибыл! Кому в Тирисфальские леса – садитесь!', 14, 0, 100, 22, 0, 11804, 29545, 0, 0, 0, 'Дренк Шестериск');
INSERT INTO `creature_text` VALUES (26566, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 4583, 0, 0, 0, 'Narzun Skybreaker');
INSERT INTO `creature_text` VALUES (26602, 0, 0, 'Guards!', 14, 7, 100, 0, 0, 0, 4583, 0, 0, 0, 'Kara Thricestar');
INSERT INTO `creature_text` VALUES (26625, 4, 0, '%s убегает в свое логово...', 16, 0, 100, 0, 0, 0, 26536, 9000, 9000, 0, 'Темнопутый паук-отшельник');
INSERT INTO `creature_text` VALUES (26626, 3, 0, 'Восстаньте, мои воины, и сражайтесь за вашего нового повелителя!', 14, 0, 100, 0, 0, 0, 26464, 9000, 9000, 48597, 'Воскреситель из Плети to Player');
INSERT INTO `creature_text` VALUES (26626, 3, 0, 'Не пытайся так быстро сбежать! У меня есть прощальный подарок...', 14, 0, 100, 0, 0, 0, 26474, 4000, 4000, 0, 'Воскреситель из Плети to Player');
INSERT INTO `creature_text` VALUES (26626, 3, 0, 'Пожалуйста, наслаждайтесь их обществом, Король-лич передает привет!', 14, 0, 100, 11, 0, 0, 26473, 16000, 16000, 0, 'Воскреситель из Плети to Player');
INSERT INTO `creature_text` VALUES (26630, 0, 0, 'More grunts, more glands, more FOOD!', 14, 0, 100, 0, 0, 13181, 0, 0, 0, 0, 'trollgore SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26630, 1, 0, 'You have gone, me gonna eat you!', 14, 0, 100, 0, 0, 13185, 0, 0, 0, 0, 'trollgore SAY_KILL');
INSERT INTO `creature_text` VALUES (26630, 2, 0, 'So hungry! Must feed!', 14, 0, 100, 0, 0, 13182, 30043, 0, 0, 0, 'trollgore SAY_CONSUME');
INSERT INTO `creature_text` VALUES (26630, 3, 0, 'Corpse go boom!', 14, 0, 100, 0, 0, 13184, 30044, 0, 0, 0, 'trollgore SAY_EXPLODE');
INSERT INTO `creature_text` VALUES (26630, 4, 0, 'Aaaargh...', 14, 0, 100, 0, 0, 13183, 25522, 0, 0, 0, 'trollgore SAY_DEATH');
INSERT INTO `creature_text` VALUES (26631, 0, 0, 'The chill that you feel is the herald of your doom!', 14, 0, 100, 0, 0, 13173, 0, 0, 0, 0, 'novos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26631, 1, 0, 'Such is the fate of all who oppose the Lich King.', 14, 0, 100, 0, 0, 13175, 0, 0, 0, 0, 'novos SAY_KILL');
INSERT INTO `creature_text` VALUES (26631, 2, 0, 'Your efforts... are in vain.', 14, 0, 100, 0, 0, 13174, 28034, 0, 0, 0, 'novos SAY_DEATH');
INSERT INTO `creature_text` VALUES (26631, 3, 0, 'Bolster my defenses! Hurry, curse you!', 14, 0, 100, 0, 0, 13176, 26933, 0, 0, 0, 'novos SAY_NECRO_ADD');
INSERT INTO `creature_text` VALUES (26631, 4, 0, 'Surely you can see the futility of it all!', 14, 0, 100, 0, 0, 13177, 27777, 0, 0, 0, 'novos SAY_REUBBLE_1');
INSERT INTO `creature_text` VALUES (26631, 4, 1, 'Just give up and die already!', 14, 0, 100, 0, 0, 13178, 30036, 0, 0, 0, 'novos SAY_REUBBLE_2');
INSERT INTO `creature_text` VALUES (26631, 5, 0, '%s calls for assistance!', 41, 0, 100, 0, 0, 0, 29588, 0, 0, 0, 'Novos the Summoner - EMOTE_SUMMONING_ADDS');
INSERT INTO `creature_text` VALUES (26632, 0, 0, 'Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty!', 14, 0, 100, 0, 0, 13862, 29606, 0, 0, 0, 'tharon ja SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26632, 1, 0, 'As Tharon\'ja predicted.', 14, 0, 100, 0, 0, 13863, 0, 0, 0, 0, 'tharon ja SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26632, 1, 1, 'As it was written.', 14, 0, 100, 0, 0, 13864, 0, 0, 0, 0, 'tharon ja SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26632, 2, 0, 'Your flesh serves Tharon\'ja now!', 14, 0, 100, 0, 0, 13865, 29613, 0, 0, 0, 'tharon ja SAY_FLESH_1');
INSERT INTO `creature_text` VALUES (26632, 2, 1, 'Tharon\'ja has a use for your mortal shell!', 14, 0, 100, 0, 0, 13866, 29614, 0, 0, 0, 'tharon ja SAY_FLESH_2');
INSERT INTO `creature_text` VALUES (26632, 3, 0, 'No! A taste... all too brief!', 14, 0, 100, 0, 0, 13867, 29615, 0, 0, 0, 'tharon ja SAY_SKELETON_1');
INSERT INTO `creature_text` VALUES (26632, 3, 1, 'Tharon\'ja will have more!', 14, 0, 100, 0, 0, 13868, 0, 0, 0, 0, 'tharon ja SAY_SKELETON_2');
INSERT INTO `creature_text` VALUES (26632, 4, 0, 'I\'m... impossible! Tharon\'ja is eternal! Tharon\'ja... is...', 14, 0, 100, 0, 0, 13869, 0, 0, 0, 0, 'tharon ja SAY_DEATH');
INSERT INTO `creature_text` VALUES (26635, 3, 0, 'Поддержка! Нам нужна поддержка!', 14, 0, 100, 0, 0, 0, 26537, 90000, 90000, 0, 'Восставший воин Драккари to Player');
INSERT INTO `creature_text` VALUES (26635, 3, 0, 'Помоги, $gбратишка:сестренка;! Их слишком много!', 14, 0, 100, 0, 0, 0, 26538, 90000, 90000, 0, 'Восставший воин Драккари to Player');
INSERT INTO `creature_text` VALUES (26648, 1, 1, 'Little $n, why do you call me forth? Are you working with the trolls of this land? Have you come to kill me and take my power as your own?', 14, 0, 100, 0, 0, 0, 25854, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26648, 2, 1, 'I sense uncertainty in you, and I do not trust it whether you are with them, or not. If you wish my augury for the Kalu`ak, you will have to prove yourself first.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26648, 3, 1, 'I will lay a mild compulsion upon you. Jump into the depths before me so that you put yourself into my element and thereby display your submission.', 14, 0, 100, 0, 0, 0, 25864, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26648, 4, 1, 'Well done, $n. Your display of respect is duly noted. Now, I have information for you that you must convey to the Kalu`ak.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26648, 5, 1, 'Simply put, you must tell the tuskarr that they cannot run. If they do so, their spirits will be destroyed by the evil rising within Northrend.', 15, 0, 100, 0, 0, 0, 25868, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (26648, 6, 1, 'Tell the mystic that his people are to stand and fight alongside the Horde and Alliance against the forces of Malygos and the Lich King.', 15, 0, 100, 0, 0, 0, 25869, 0, 0, 0, '');
