INSERT INTO `creature_text` VALUES (27209, 1, 0, 'We know... that you don\'t... know why we\'re immune... to your so-called blight. Grand Admiral Westwind somehow gave the high abbot that prayer. I beg you... no more... please?!', 15, 0, 0, 1, 0, 0, 26483, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27209, 2, 0, 'AHHHHHHHHH! Please... we know... that you... have a spy... disguised as... one of us! There... that\'s all that I know. Please... mercy... STOP!', 15, 0, 0, 1, 0, 0, 26484, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27209, 3, 0, 'Come to play?', 12, 0, 0, 1, 0, 0, 26472, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27212, 0, 0, 'You`re late, overseer.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 1, 0, 'Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar. ', 12, 0, 100, 0, 0, 0, 26233, 0, 0, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 2, 0, 'Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed. ', 12, 0, 100, 0, 0, 0, 26234, 0, 0, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 3, 0, 'If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants.', 12, 0, 100, 0, 0, 0, 26235, 0, 0, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 4, 0, 'Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks.', 12, 0, 100, 0, 0, 0, 26236, 0, 0, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27234, 0, 0, 'You are being misled! The Onslaught is all lies! The Scourge and the Forsaken are not our enemies! Wake up!', 1, 0, 100, 5, 0, 0, 26596, 0, 0, 0, 'Blacksmith Goodman');
INSERT INTO `creature_text` VALUES (27235, 0, 0, 'Renounce the Scarlet Onslaught! Don\'t listen to the lies of the high general and the grand admiral any longer!', 1, 0, 100, 5, 0, 0, 26598, 0, 0, 0, 'Lead Cannoneer Zierhut');
INSERT INTO `creature_text` VALUES (27236, 0, 0, 'Abbendis is nothing but a harlot and Grand Admiral Westwind is selling her cheap like he sold us out!', 1, 0, 100, 5, 0, 0, 26599, 0, 0, 0, 'Stable Master Mercer');
INSERT INTO `creature_text` VALUES (27237, 0, 0, 'High general Abbendis personally told me that it was a mistake to come north and that we\'re doomed! I urge you all to lay down your weapons and leave before it\'s too late!', 1, 0, 100, 5, 0, 0, 26597, 0, 0, 0, 'Commander Jordan');
INSERT INTO `creature_text` VALUES (27299, 3, 0, 'Было достаточно сильно?', 12, 0, 100, 0, 0, 0, 26302, 538000, 538000, 0, 'Посвященная Грир');
INSERT INTO `creature_text` VALUES (27299, 3, 0, 'Попробуем вот это...', 12, 0, 100, 0, 0, 0, 26298, 527000, 527000, 0, 'Посвященная Грир');
INSERT INTO `creature_text` VALUES (27315, 0, 0, 'HURRAY!', 12, 0, 100, 0, 0, 0, 26382, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27315, 0, 1, 'Are you sure you know how to fly this thing? Feels a little wobbly.', 12, 0, 100, 0, 0, 0, 26359, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27315, 1, 0, 'Safe at last! Thank you, stranger!', 12, 0, 100, 0, 0, 0, 26381, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27315, 1, 1, 'Kindness is not lost with this one, Urik. Thank you, hero!', 12, 0, 100, 0, 0, 0, 26383, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27316, 0, 0, 'Stand back, $N, the beast might lash out and harm you.', 12, 7, 100, 396, 0, 0, 26800, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 1, 0, 'GODFREY! Hear me, fiend! Hear me, for I am the Light, here to deliver you from evil!', 12, 7, 100, 5, 0, 0, 26801, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 2, 0, 'Good. I have your attention, then, Godfrey?', 12, 7, 100, 396, 0, 0, 26803, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 3, 0, 'We can do this in one of two ways, Godfrey. First, I will simply ask you to tell me what the unholy markings etched upon the pages of this tome mean. What say you?', 12, 7, 100, 1, 0, 0, 26804, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 4, 0, 'Then it is option two.', 12, 7, 100, 1, 0, 0, 26809, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 5, 0, 'I say a prayer for you now, lost soul. May the Light take you gracefully.', 12, 7, 100, 68, 4000, 0, 26810, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 6, 0, 'Let the sermon begin.', 12, 7, 100, 1, 0, 0, 26811, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 7, 0, 'LIGHT TAKE YOU, BEAST!', 12, 7, 100, 0, 0, 0, 26812, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 8, 0, 'BURN IN HOLY FIRE!', 12, 7, 100, 0, 0, 0, 26816, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 9, 0, 'LET THE LIGHT IN, GODFREY GOODARD!', 12, 7, 100, 0, 0, 0, 26814, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 10, 0, 'May the power of Light compel you!', 12, 7, 100, 0, 0, 0, 26813, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 11, 0, 'I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?', 12, 7, 100, 396, 0, 0, 26824, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 12, 0, 'RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!', 12, 7, 100, 6, 0, 0, 26826, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 13, 0, 'What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.', 12, 7, 100, 6, 0, 0, 26829, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 14, 0, 'You let us worry about how we kill the monster, Godfrey.', 12, 7, 100, 396, 0, 0, 26830, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 15, 0, 'Return to Halford with the information that the good mayor was kind enough to submit, $N. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"', 12, 7, 100, 1, 0, 0, 26833, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 16, 0, 'Let\'s you and I have a chat about some things, Godfrey.', 12, 7, 100, 1, 0, 0, 26834, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27328, 0, 0, 'Stay behind me, $r.  I will do my best to protect you.', 12, 0, 100, 0, 0, 0, 26373, 0, 0, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 1, 0, 'My freedom means nothing if we fail to save Ursoc.  Make haste, $N.', 12, 0, 100, 0, 0, 0, 26374, 0, 0, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 2, 0, 'I will watch over you and your allies, $r.', 12, 0, 100, 0, 0, 0, 26375, 0, 0, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 3, 0, 'Ursoc, brother of Ursol and guardian of the furbolg tribes... show yourself!', 12, 0, 100, 0, 0, 0, 26427, 0, 0, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27336, 0, 0, 'HURRAY!', 12, 0, 100, 0, 0, 0, 26382, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27336, 0, 1, 'Are you sure you know how to fly this thing? Feels a little wobbly.', 12, 0, 100, 0, 0, 0, 26359, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27336, 1, 0, 'Safe at last! Thank you, stranger!', 12, 0, 100, 0, 0, 0, 26381, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27336, 1, 1, 'Kindness is not lost with this one, Urik. Thank you, hero!', 12, 0, 100, 0, 0, 0, 26383, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27344, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 4583, 0, 0, 0, 'Bat Handler Adeline');
INSERT INTO `creature_text` VALUES (27355, 0, 0, 'No... NO! What have you done?! So many ancient wyrms wasted... what magic could do this?', 14, 0, 100, 1, 0, 0, 26555, 0, 0, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27355, 1, 0, 'Foolish errand $g boy:girl; ... you will die for interrupting my work!', 14, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27355, 2, 0, 'This is not the end... death only... strengthens...', 14, 0, 100, 1, 0, 0, 26669, 0, 0, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27359, 0, 0, 'Finally! A hero has come!', 12, 0, 100, 1, 0, 0, 26469, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27359, 1, 0, 'Good of you to come, champion! The beast almost had me!', 12, 0, 100, 1, 0, 0, 26470, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27359, 2, 0, 'I\'ll never forget you, friend.', 12, 0, 100, 1, 0, 0, 26468, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27359, 3, 0, 'Thank you, kind soul.', 12, 0, 100, 1, 0, 0, 26467, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'C\'mon boy, let\'s get you away from all this noise.', 12, 7, 100, 0, 0, 0, 26431, 6910781, 7643172, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'Hey Celidh!  Do you have any pets you want me to take over to the stable?', 12, 7, 100, 0, 0, 0, 26428, 2066469, 7629797, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'I bet with all these new people here, Celidh, you\'ve got to have some pets you want me to move to the stables.', 12, 7, 100, 0, 0, 0, 26430, 7306734, 8047937, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'Let\'s go boy.', 12, 7, 100, 0, 0, 0, 26432, 7320047, 7320047, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'Sure is busy here Celidh.  Do you have any pets you want me to move to the stables?', 12, 7, 100, 0, 0, 0, 26429, 6897437, 6897437, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'This way, boy.  I\'ll take you to our nice stables in the back.', 12, 7, 100, 0, 0, 0, 26433, 6591469, 8061266, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'Сюда, мальчик. Я отведу тебя на наши милые конюшни на задний двор.', 12, 7, 100, 0, 0, 0, 26433, 379000, 379000, 0, 'Конюх Джимми');
INSERT INTO `creature_text` VALUES (27373, 0, 0, 'I thank you all. The druids of old were wise to tear down Vordrassil, for its roots seep deep into the dwelling of an ancient evil.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Spirit of Ursoc');
INSERT INTO `creature_text` VALUES (27373, 1, 0, 'You know their kind as old gods. Beware Yogg-Saron, the beast with a thousand maws. His evil extends beyond Vordrassil\'s roots.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Spirit of Ursoc');
INSERT INTO `creature_text` VALUES (27376, 0, 0, 'What do you mean my time has come?! I\'ll kill you where you stand!', 12, 0, 100, 1, 0, 0, 26449, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (27378, 0, 0, 'You can\'t possibly mean to.... I\'ll write you up for this, $c!', 12, 0, 100, 1, 0, 0, 26450, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (27379, 0, 0, 'No! I beg you! Please don\'t kill me!', 12, 0, 100, 1, 0, 0, 26454, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (27381, 0, 0, 'What is the meaning of this?! Stop! I\'ll double whatever they\'re paying you!', 12, 0, 100, 1, 0, 0, 26458, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (27411, 0, 0, 'Were you bitten?', 12, 0, 100, 6, 0, 0, 26640, 0, 0, 0, 'Freed Alliance Scout Say1');
INSERT INTO `creature_text` VALUES (27411, 1, 1, 'Answer me! Were you bloody bitten?', 14, 0, 100, 22, 0, 0, 0, 0, 0, 0, 'Freed Alliance Scout Yell');
INSERT INTO `creature_text` VALUES (27411, 2, 2, 'You don\'t even know, do you? The trappers? They\'re not human... we have to get out of here! Come, quick!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Freed Alliance Scout Say2');
INSERT INTO `creature_text` VALUES (27411, 3, 3, 'Take Ducal\'s horse, he\'s one of them now!', 12, 0, 100, 0, 0, 0, 26540, 0, 0, 0, 'Freed Alliance Scout Say3');
INSERT INTO `creature_text` VALUES (27411, 4, 4, 'There\'s lamp oil in the back - make use of it. The fiends hate fire!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Freed Alliance Scout Say4');
INSERT INTO `creature_text` VALUES (27430, 0, 0, 'The Destructive Ward gains in power.', 41, 0, 100, 0, 0, 0, 26601, 0, 0, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27430, 1, 0, 'The Destructive Ward is fully charged!', 41, 0, 100, 0, 0, 0, 26602, 0, 0, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27439, 0, 0, ' I know a place nearby where we can speak in private, my child. Follow me.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27439, 1, 0, ' Did you think that I could not see through your flimsy disguise, $n?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27439, 2, 0, ' There is much that you do not understand, $r. The Master sees all.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27439, 3, 0, ' He told me that you would come for me. I won\'t die by your hand, though. I have seen what you have done to my compatriots.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27439, 4, 0, ' No. I will leave this world in a manner of my own choosing. And I will return, the grand admiral\'s will permitting!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27439, 5, 0, ' AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27447, 0, 0, 'There will be no mercy!', 14, 0, 0, 0, 0, 13649, 31813, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27447, 1, 1, 'Blast them! Destroy them!', 14, 0, 0, 0, 0, 13650, 31814, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27447, 2, 2, '%s calls an Azure Ring Captain!', 41, 0, 0, 0, 0, 0, 33077, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27447, 3, 3, 'They are... too strong! Underestimated their... fortitude.', 14, 0, 0, 0, 0, 13655, 31819, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27455, 0, 0, 'Captain, why are the guards not at their posts?', 12, 0, 100, 6, 0, 12725, 26714, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 1, 0, 'Uther had my troops recalled? Damn it! If my warriors abandon me, I\'ll never defeat Mal\'Ganis. The ships must be burned before the men reach the shore!', 12, 0, 100, 5, 0, 12726, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 2, 0, 'Burned down to their frames! No one goes home until our job here is done!', 12, 0, 100, 15, 0, 12727, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 3, 0, 'Spare me, Muradin. You weren\'t there to see what Mal\'Ganis did to my homeland.', 12, 0, 100, 1, 0, 12728, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 4, 0, 'Behold, Muradin, our salvation, Frostmourne.', 12, 0, 100, 1, 0, 12729, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 5, 0, 'I would gladly bear any curse to save my homeland.', 12, 0, 100, 1, 0, 12730, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 6, 0, 'Damn the men! Nothing shall prevent me from having my revenge, old friend. Not even you.', 12, 0, 100, 5, 0, 12731, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 7, 0, 'Now, I call out to the spirits of this place. I will give anything or pay any price, if only you will help me save my people.', 12, 0, 100, 396, 0, 12732, 0, 0, 0, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27476, 0, 0, 'I apologize, emissary, but the prince is away on an errand. Wha brings you to this desolate place?', 12, 0, 100, 1, 3000, 12719, 0, 0, 0, 0, 'Valonforth 0  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 1, 0, 'We\'re to just pick up and leave?', 12, 0, 100, 6, 1500, 12720, 26711, 0, 0, 0, 'Valonforth 1  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 2, 0, 'To hell with the undead! We\'ll cut our way through the woods, men!', 12, 0, 100, 22, 3000, 12721, 0, 0, 0, 0, 'Valonforth 2  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 3, 0, 'Well, milord, your father had our troops recalled at Lord Uther\'s request.', 12, 0, 100, 1, 3000, 12722, 0, 0, 0, 0, 'Valonforth 3 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27480, 0, 0, 'Isn\'t that a bit much, lad?', 12, 0, 100, 6, 0, 12733, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 1, 0, 'You lied to your men and betrayed the mercenaries who fought for you. What\'s happening to you, Arthas? Is vengeance all that\'s important to you?', 12, 0, 100, 1, 0, 12734, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 2, 0, 'Hold, lad. There\'s an inscription on the dais. It\'s a warning. It says, \"Whomsoever takes up this blade shall wield power eternal. Just as the blade rends flesh, so must power scar the spirit.\" Oh, I should\'ve known. The blade is cursed! Let\'s get the hell out of here!', 12, 0, 100, 0, 0, 12735, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 3, 0, 'Leave it be, Arthas. Forget this business and lead your men home.', 12, 0, 100, 396, 0, 12736, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 4, 0, 'O\' my head... Wh... Where am I?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 5, 0, 'Who... Who am I?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27482, 0, 0, 'I\'d nearly given up.You\'ve given me new life!', 12, 0, 50, 0, 0, 0, 0, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 1, 'Bless you, friend.I nearly expired....', 12, 0, 50, 0, 0, 0, 0, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 2, 'Without your help, I surely would have died....', 12, 0, 50, 0, 0, 0, 26685, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 3, 'Thank you $r.', 12, 0, 50, 0, 0, 0, 0, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27483, 0, 0, '%s угрожающе поднимает когти!', 41, 0, 100, 0, 0, 0, 26745, 0, 0, 0, 'Король Дред to 0');
INSERT INTO `creature_text` VALUES (27492, 0, 0, 'By royal edict, you men are to return to Lordaeron immediately. Lord Uther has convinced the king to recall this expedition.', 12, 0, 100, 25, 3500, 12723, 0, 0, 0, 0, 'Alliance Emissary 0 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27492, 1, 0, 'That\'s correct. My men report that the roads from here to the shore are held by the undead. You\'ll need to find an alternate route back to your ships.', 12, 0, 100, 378, 4000, 12724, 0, 0, 0, 0, 'Alliance Emissary 1 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27619, 0, 0, 'Rise, Arugal! The power of the Lich King commands you!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Prince Valanar - Silverpine');
INSERT INTO `creature_text` VALUES (27624, 0, 0, 'Yes... he shall suit our needs perfectly. Have him sent to Solstice Village when you\'re ready. Arthas will not be pleased if we fall behind schedule.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Prince Theraldis - Silverpine');
INSERT INTO `creature_text` VALUES (27654, 0, 0, 'The prisoners shall not go free! The word of Malygos is law!', 14, 0, 100, 0, 0, 13594, 31793, 0, 0, 0, 'drakos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27654, 1, 0, 'A fitting punishment!', 14, 0, 100, 0, 0, 13602, 0, 0, 0, 0, 'drakos SAY_KILL_1');
INSERT INTO `creature_text` VALUES (27654, 1, 1, 'Sentence: executed!', 14, 0, 100, 0, 0, 13603, 0, 0, 0, 0, 'drakos SAY_KILL_2');
INSERT INTO `creature_text` VALUES (27654, 1, 2, 'Another casualty of war!', 14, 0, 100, 0, 0, 13604, 31803, 0, 0, 0, 'drakos SAY_KILL_3');
INSERT INTO `creature_text` VALUES (27654, 2, 0, 'The war... goes on.', 14, 0, 100, 0, 0, 13605, 31804, 0, 0, 0, 'drakos SAY_DEATH');
INSERT INTO `creature_text` VALUES (27654, 3, 0, 'It is too late to run!', 14, 0, 100, 0, 0, 13598, 31797, 0, 0, 0, 'drakos SAY_PULL_1');
INSERT INTO `creature_text` VALUES (27654, 3, 1, 'Gather \'round....', 14, 0, 100, 0, 0, 13599, 31798, 0, 0, 0, 'drakos SAY_PULL_2');
INSERT INTO `creature_text` VALUES (27654, 3, 2, 'None shall escape!', 14, 0, 100, 0, 0, 13600, 31799, 0, 0, 0, 'drakos SAY_PULL_3');
INSERT INTO `creature_text` VALUES (27654, 3, 3, 'I condemn you to death!', 14, 0, 100, 0, 0, 13601, 31800, 0, 0, 0, 'drakos SAY_PULL_4');
INSERT INTO `creature_text` VALUES (27654, 4, 0, 'Tremble, worms!', 14, 0, 100, 0, 0, 13595, 31794, 0, 0, 0, 'drakos SAY_STOMP_1');
INSERT INTO `creature_text` VALUES (27654, 4, 1, 'I will crush you!', 14, 0, 100, 0, 0, 13596, 104829, 0, 0, 0, 'drakos SAY_STOMP_2');
INSERT INTO `creature_text` VALUES (27654, 4, 2, 'Can you fly?', 14, 0, 100, 0, 0, 13597, 31796, 0, 0, 0, 'drakos SAY_STOMP_3');
INSERT INTO `creature_text` VALUES (27655, 0, 0, 'What do we have here... those that would defy the Spell-Weaver? Those without foresight or understanding. How can I make you see? Malygos is saving the world from itself! Bah! You are hardly worth my time!', 14, 0, 100, 1, 0, 13635, 27411, 0, 0, 0, 'Mage-Lord Urom - SAY_SUMMON_1');
INSERT INTO `creature_text` VALUES (27655, 1, 0, 'Clearly my pets failed. Perhaps another demonstration is in order.', 14, 0, 100, 1, 0, 13636, 27413, 0, 0, 0, 'Mage-Lord Urom - SAY_SUMMON_2');
INSERT INTO `creature_text` VALUES (27655, 2, 0, 'Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring.', 14, 0, 100, 1, 0, 13637, 27414, 0, 0, 0, 'Mage-Lord Urom - SAY_SUMMON_3');
INSERT INTO `creature_text` VALUES (27655, 3, 0, 'Poor blind fools!', 14, 0, 100, 0, 0, 13638, 27412, 0, 0, 0, 'Mage-Lord Urom - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27655, 4, 0, '%s begins to cast Empowered Arcane Explosion!', 41, 0, 100, 0, 0, 0, 27903, 0, 0, 0, 'Mage-Lord Urom - EMOTE_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 5, 0, 'A taste... just a small taste... of the Spell-Weaver\'s power!', 14, 0, 100, 0, 0, 13639, 31829, 0, 0, 0, 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 5, 1, 'So much unstable energy... but worth the risk to destroy you!', 14, 0, 100, 0, 0, 13640, 31830, 0, 0, 0, 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 6, 0, 'Everything I have done... has been for Azeroth....', 14, 0, 100, 0, 0, 13644, 31834, 0, 0, 0, 'Mage-Lord Urom - SAY_DEATH');
INSERT INTO `creature_text` VALUES (27655, 7, 0, 'If only you understood!', 14, 0, 100, 0, 0, 13641, 31831, 0, 0, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27655, 7, 1, 'Now do you see? Do you?!', 14, 0, 100, 0, 0, 13642, 31832, 0, 0, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27655, 7, 2, 'Unfortunate, but necessary.', 14, 0, 100, 0, 0, 13643, 31833, 0, 0, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27656, 0, 0, 'Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute!', 14, 0, 100, 0, 0, 13622, 31841, 0, 0, 0, 'Ley-Guardian Eregos - SAY_SPAWN');
INSERT INTO `creature_text` VALUES (27656, 1, 0, 'You brash interlopers are out of your element! I will ground you!', 14, 0, 100, 0, 0, 13623, 31873, 0, 0, 0, 'Ley-Guardian Eregos - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27656, 2, 0, 'Such insolence... such arrogance... must be PUNISHED!', 14, 0, 100, 0, 0, 13624, 31874, 0, 0, 0, 'Ley-Guardian Eregos - SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (27656, 3, 0, 'It\'s a long way down...', 14, 0, 100, 0, 0, 13628, 0, 0, 0, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 3, 1, 'Back to the earth with you!', 14, 0, 100, 0, 0, 13629, 49574, 0, 0, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 3, 2, 'Enjoy the fall!', 14, 0, 100, 0, 0, 13630, 31880, 0, 0, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 4, 0, 'Savor this small victory, foolish little creatures. You and your dragon allies have won this battle, but we will win... the Nexus War.', 14, 0, 100, 0, 0, 13631, 31881, 0, 0, 0, 'Ley-Guardian Eregos - SAY_DEATH');
INSERT INTO `creature_text` VALUES (27656, 5, 0, 'We command the arcane! It shall not be used against us.', 14, 0, 100, 0, 0, 13626, 0, 0, 0, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27656, 5, 1, 'It is trivial to extinguish your fire!', 14, 0, 100, 0, 0, 13627, 31877, 0, 0, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27656, 5, 2, 'No magic of nature will help you now!', 14, 0, 100, 0, 0, 13625, 31875, 0, 0, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27658, 0, 0, 'Thank you for freeing us, mortals. Beware, the blue flight is alerted to your presence. Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus. You will need our help to stand a chance.', 12, 0, 100, 1, 3500, 0, 0, 0, 0, 0, 'Belgaristrasz - On freed');
INSERT INTO `creature_text` VALUES (27692, 0, 0, 'Emerald Drake flies away.', 16, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Emerald - On take off');
INSERT INTO `creature_text` VALUES (27692, 1, 0, 'Welcome Friend. Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Emerald - On welcome');
INSERT INTO `creature_text` VALUES (27692, 2, 0, 'Use Leeching Poison to damage enemies and keep me healed. Touch the Nightmare is very powerful, but it hurts me, so only use it when I have a lot of health!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Emerald - On explaining abilities');
INSERT INTO `creature_text` VALUES (27692, 3, 0, 'Now that I am at my full power I can perform Dream Funnel. You can use it to heal other drakes, but it drains my health, so make sure you\'re using Leeching Poison too!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Emerald - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27692, 4, 0, 'I\'m badly injured! I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Emerald - On below 40%');
INSERT INTO `creature_text` VALUES (27745, 3, 0, 'Лорд Утер...', 12, 0, 100, 0, 0, 0, 28626, 95705, 95705, 0, 'Лордеронский пехотинец to Player');
INSERT INTO `creature_text` VALUES (27755, 0, 0, 'Amber Drake flies away.', 16, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Amber - On take off');
INSERT INTO `creature_text` VALUES (27755, 1, 0, 'Welcome Friend. Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Amber - On welcome');
INSERT INTO `creature_text` VALUES (27755, 2, 0, 'Use Shock Lance to damage enemies. If we get in trouble, Stop Time to freeze all enemies in place, then hit them with Shock Lance for massive damage!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Amber - On explaining abilities');
INSERT INTO `creature_text` VALUES (27755, 3, 0, 'Now that I am at my full power I can perform Temporal Rift. You can use it to make enemies take extra damage and to get Shock Charges. Save up Shock Charges and then Shock Lance for huge damage!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Amber - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27755, 4, 0, 'I\'m badly injured! I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Amber - On below 40%');
INSERT INTO `creature_text` VALUES (27756, 0, 0, 'Ruby Drake flies away.', 16, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Ruby - On take off');
INSERT INTO `creature_text` VALUES (27756, 1, 0, 'Welcome Friend. Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Ruby - On welcome');
INSERT INTO `creature_text` VALUES (27756, 2, 0, 'Use Searing Wrath to damage enemies and Evasive Maneuvers if I start taking damage. Remember I need to build up Evasive Charges by taking damage to perform Evasive Maneuvers!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Ruby - On explaining abilities');
INSERT INTO `creature_text` VALUES (27756, 3, 0, 'Now that I am at my full power I can perform Martyr. You can use it to protect other drakes, but I will take lots of damage, so make sure you\'re using Evasive Maneuvers too!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Ruby - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27756, 4, 0, 'I\'m badly injured! I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Ruby - On below 40%');
INSERT INTO `creature_text` VALUES (27791, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (27842, 0, 0, 'Bat gizzards again for the gnomes tonight...', 12, 0, 100, 1, 0, 0, 27129, 0, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 1, 'What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get.', 12, 0, 100, 1, 0, 0, 27127, 0, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 2, 'We like trees, Fenrick. They provide cover. They won\'t let me chop them down, either.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 3, 'I wonder how many reinforcements need to suffer injury before they allows us to chop down these idiotic trees. They\'re costing us a fortune in bats. Maybe I\'ll rig a harness or two...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 1, 0, 'Arrrhhh...Guards!', 14, 7, 100, 0, 0, 0, 0, 0, 0, 0, 'Common Horde Flight Master');
INSERT INTO `creature_text` VALUES (27851, 0, 0, 'The walls of Frostmourne Cavern shudder and shake.', 41, 0, 100, 0, 0, 0, 27196, 0, 0, 0, 'Thel\'zan Spell Dummy');
INSERT INTO `creature_text` VALUES (27877, 3, 0, 'Вы же не возражаете, чтобы я проверил ваши товары?', 12, 0, 100, 6, 0, 0, 27205, 95783, 95783, 0, 'Сержант Мориган to Player');
INSERT INTO `creature_text` VALUES (27888, 3, 0, 'For Dalaran! Attack!', 14, 0, 100, 0, 0, 0, 24890, 3497453, 3497453, 45913, 'Warmage Archus to Player');
INSERT INTO `creature_text` VALUES (27888, 3, 0, 'За Даларан! В атаку!', 14, 0, 100, 0, 0, 0, 24890, 118000, 118000, 45913, 'Боевой маг Аркус to Player');
INSERT INTO `creature_text` VALUES (27899, 1, 0, 'Hey there, $N, don\'t be alarmed. It\'s me... you... from the future. I\'m here to help.', 15, 0, 100, 396, 0, 0, 0, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 2, 0, 'Head\'s up... here they come. I\'ll help as much as I can. Let\'s just keep them off the hourglass!', 15, 0, 100, 396, 0, 0, 0, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 3, 0, 'No matter what, you can\'t die, because that would mean that I would cease to exist, right? I was here before when i was you. I\'m so confused!', 15, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 4, 0, 'I can\'t believe that I used to wear that.', 15, 0, 100, 0, 0, 0, 27301, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 5, 0, 'Sorry, but Chromie said that I couldn\'t reveal anything about your future to you. She said that if I did, I would cease to exist.', 15, 0, 100, 0, 0, 0, 27311, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 6, 0, 'Wow, I\'d forgotten how inexperienced I used to be.', 15, 0, 100, 0, 0, 0, 27307, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 7, 0, 'Look at you fight; no wonder I turned to drinking.', 15, 0, 100, 0, 0, 0, 27304, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 8, 0, 'What? Am I here alone. We both have a stake at this, you know!', 15, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 9, 0, 'Listen. I\'m not supposed to tell you this, but there\'s going to be this party that you\'re invited to. Whatever you do, DO NOT DRINK THE PUNCH!', 15, 0, 100, 0, 0, 0, 27313, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 10, 0, 'Wish I could remember how many of the Infinite Dragonflight were going to try to stop you. This fight was so long ago.', 15, 0, 100, 0, 0, 0, 27312, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 11, 0, 'Look, $N, the hourglass has revealed Nozdormu!', 15, 0, 100, 25, 0, 0, 27275, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27899, 12, 0, 'Farewell, $N, Keep us alive and get some better equipment!', 15, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Future You');
INSERT INTO `creature_text` VALUES (27907, 3, 0, 'Ну, наверное, мне нужно сложить все обратно в телегу.', 12, 0, 100, 0, 0, 0, 27257, 100225, 100225, 0, 'Бартлби Баттсон to Player');
INSERT INTO `creature_text` VALUES (27913, 0, 0, 'Силы Плети были замечены возле Врат Торгового ряда!', 14, 0, 100, 0, 0, 0, 27643, 0, 0, 0, 'Лордеронский глашатай to Player');
INSERT INTO `creature_text` VALUES (27913, 0, 0, 'У городской ратуши замечены солдаты Плети!', 14, 0, 100, 0, 0, 0, 27646, 0, 0, 0, 'Лордеронский глашатай to Player');
INSERT INTO `creature_text` VALUES (27913, 3, 0, 'У фонтана на Королевской площади замечены солдаты Плети!', 14, 0, 100, 0, 0, 13471, 27642, 94000, 94000, 0, 'Лордеронский глашатай to Player');
INSERT INTO `creature_text` VALUES (27914, 3, 0, 'Ну, вот и я. Приступим к работе?', 12, 0, 100, 1, 0, 0, 27295, 4204, 4204, 0, 'Читающий to Player');
INSERT INTO `creature_text` VALUES (27915, 3, 0, 'До встречи, $gпутешественник:путешественница; во времени!', 15, 0, 100, 70, 0, 0, 29168, 21146, 21146, 0, 'Хроми to Player');
INSERT INTO `creature_text` VALUES (27959, 0, 0, 'You think that you can get rid of me through meditation?', 15, 0, 100, 0, 0, 0, 27368, 0, 0, 0, 'Your Inner Turmoil text 0');
INSERT INTO `creature_text` VALUES (27959, 1, 0, 'Fool! I will destroy you and finally become that which has been building inside of you all of these years!', 15, 0, 100, 0, 0, 0, 27369, 0, 0, 0, 'Your Inner Turmoil text 1');
INSERT INTO `creature_text` VALUES (27959, 2, 0, 'You cannot defeat me. I\'m an inseparable part of you!', 15, 0, 100, 0, 0, 0, 27370, 0, 0, 0, 'Your Inner Turmoil text 50%');
INSERT INTO `creature_text` VALUES (27959, 3, 0, 'NOOOOOOOoooooooooo!', 15, 0, 100, 0, 0, 0, 27371, 0, 0, 0, 'Your Inner Turmoil text death');
INSERT INTO `creature_text` VALUES (27975, 0, 0, 'Вам не надо было приходить. Теперь вы умрете!', 14, 0, 100, 0, 0, 13487, 29563, 0, 0, 0, 'Дева Скорби to Player');
INSERT INTO `creature_text` VALUES (27975, 1, 0, 'Потеряно так много времени... и вам его никогда не вернуть!', 14, 0, 100, 0, 0, 13492, 29570, 0, 0, 0, 'Дева Скорби to 0');
INSERT INTO `creature_text` VALUES (27975, 2, 0, 'Надеюсь, вы все подохнете! Я этого... не хотела...', 14, 0, 100, 0, 0, 13493, 29574, 0, 0, 0, 'Дева Скорби to Player');
INSERT INTO `creature_text` VALUES (27977, 0, 0, 'Крушить...', 14, 0, 100, 0, 0, 14176, 94308, 0, 0, 0, 'Кристаллус to Player');
INSERT INTO `creature_text` VALUES (27977, 1, 0, 'Сломать... всех...', 14, 0, 100, 0, 0, 14178, 29559, 0, 0, 0, 'Кристаллус to 0');
INSERT INTO `creature_text` VALUES (27977, 2, 0, 'Ха... ха... ха...', 14, 0, 100, 0, 0, 14178, 29558, 0, 0, 0, 'Кристаллус to Player');
INSERT INTO `creature_text` VALUES (27977, 3, 0, 'Ааааааагххх...', 14, 0, 100, 0, 0, 14179, 29560, 0, 0, 0, 'Кристаллус to Player');
INSERT INTO `creature_text` VALUES (27978, 0, 0, 'Мягкие, ненадежные оболочки. Мимолетные, хрупкие жизни. Вы не можете избавиться от проклятия плоти.', 14, 0, 100, 0, 0, 14180, 29582, 0, 0, 0, 'Сьоннир Литейщик to Player');
INSERT INTO `creature_text` VALUES (27978, 1, 0, 'Локен не отступится, пока Кузница снова не будет захвачена! Вы ничего... не измените.', 14, 0, 100, 0, 0, 14184, 29586, 0, 0, 0, 'Сьоннир Литейщик to Player');
INSERT INTO `creature_text` VALUES (27986, 1, 0, 'Well, lad, you can start by readin\' The Green Hills of Stranglethorn. Then, maybe you can join me on one of my safaris. What do you say?', 12, 0, 0, 0, 0, 0, 34687, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (27990, 0, 0, 'You\'re too late, $n. Another visitor from Dalaran came asking after information about the same prismatic dragon blades.', 12, 0, 100, 1, 0, 0, 37769, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 1, 0, 'From your description, I\'m certain the book I loaned our visitor could allow you to easily identify the weapon.', 12, 0, 100, 25, 0, 0, 0, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 2, 0, 'I\'m afraid you\'ll have to ask the -- Well, perhaps Kalecgos can help.', 12, 0, 100, 1, 0, 0, 37772, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 3, 0, '$n may have found the remains of a prismatic blade, Kalecgos. Will you offer your help to our visitor?', 12, 0, 100, 1, 0, 0, 37773, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 4, 0, 'You believe our allies will not be able to control the power of the swords?', 12, 0, 100, 1, 0, 0, 37778, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 5, 0, 'As will we all.', 12, 0, 100, 25, 0, 0, 37785, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 6, 0, 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', 12, 0, 100, 1, 0, 0, 37873, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 7, 0, 'Please, mortal, seek out Magister Hathorel in Dalaran. He might be able to negotiate with the Silver Covenant for access to the book.', 12, 0, 100, 1, 0, 0, 37874, 0, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 8, 0, 'Rest assured, Kekek, she is the queen of the dragons. We often take the forms of smaller beings when we work with them.', 12, 0, 0, 0, 0, 0, 34665, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28006, 0, 0, 'Слушайте меня, вассалы! Слушайте своего господина, Антиока! Удвойте свои усилия, а не то вы сполна заплатите за неудачу!', 14, 0, 100, 0, 0, 0, 27407, 0, 0, 0, 'Верховный некролорд Антиок to Тиасси Призыватель Молний');
INSERT INTO `creature_text` VALUES (28012, 0, 0, 'The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearences! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28012, 1, 1, 'Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'А-ах, наконец-то я снова в Драк\'Тароне.', 12, 0, 100, 71, 0, 14006, 27933, 7000, 7000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Господин, твое задание выполнено!', 12, 0, 100, 0, 0, 14012, 28125, 73000, 73000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Дракуру смотрит вниз, на крепость Драк\'Тарон.', 16, 0, 100, 0, 0, 0, 27934, 20000, 20000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'За ваши старания вас ждет величайшая из наград...', 12, 0, 100, 0, 0, 14009, 28118, 44000, 44000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Замрите, и перед вами откроется истина!', 12, 0, 100, 0, 0, 14011, 28120, 61000, 61000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Как пожелаешь, господин.', 12, 0, 100, 396, 0, 14014, 28419, 130000, 130000, 0, 'Дракуру to Король-лич');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Мне будут нужны покорные слуги в Зул\'Драке. Слуги, готовые управлять могущественными силами.', 12, 0, 100, 396, 0, 14017, 28422, 176000, 176000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Мне надо было убедиться, что вам можно доверять.', 12, 0, 100, 396, 0, 14016, 28421, 169000, 169000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Молодцы!', 12, 0, 100, 0, 0, 14007, 27935, 28000, 28000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Откровение!', 12, 0, 100, 5, 0, 14010, 28119, 48000, 48000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Простите мне этот обман, друзья, он был ради вашего же блага.', 12, 0, 100, 396, 0, 14015, 28420, 162000, 162000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'С помощью этих смертных мы очистили местность от всех, кто сопротивлялся нам.', 12, 0, 100, 0, 0, 14013, 28127, 79000, 79000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Служите ему, и скоро вы вкусите бессмертие!', 12, 0, 100, 396, 0, 14019, 28424, 195000, 195000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Теперь вы видите, Король-лич щедро награждает тех, кто послушен ему.', 12, 0, 100, 396, 0, 14018, 28423, 187000, 187000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Я даже не ожидал от вас такого.', 12, 0, 100, 0, 0, 14008, 27954, 33000, 33000, 0, 'Дракуру to Player');
INSERT INTO `creature_text` VALUES (28028, 4, 0, 'Свет, спаси меня!', 12, 0, 100, 0, 0, 9410, 27794, 7000, 7000, 11977, 'Щитоносец Серебряного Авангарда to Ужас небесный');
INSERT INTO `creature_text` VALUES (28029, 3, 0, 'Помогите!', 12, 0, 100, 0, 0, 0, 140174, 5000, 5000, 0, 'Рыцарь Серебряного Авангарда to Ужас небесный');
INSERT INTO `creature_text` VALUES (28033, 3, 0, 'И вуаля – Дух Гномрегана!', 12, 0, 100, 25, 0, 0, 29880, 567000, 567000, 0, 'Веслекс Вездеключ to Player');
INSERT INTO `creature_text` VALUES (28033, 3, 0, 'Можешь пользоваться, но только не жалуйся: после ремонта из сидений могут торчать зубья пилы или что-нибудь в этом роде. Что у меня было – все пошло в работу.', 12, 0, 100, 1, 0, 0, 29881, 571000, 571000, 0, 'Веслекс Вездеключ to Player');
INSERT INTO `creature_text` VALUES (28033, 3, 0, 'Наконец-то у нас есть запчасти, чтобы починить Дух Гномрегана.', 12, 0, 100, 1, 0, 0, 29878, 557000, 557000, 0, 'Веслекс Вездеключ to Player');
INSERT INTO `creature_text` VALUES (28033, 3, 0, 'Ну-ка, слегка подправить здесь, слегка настроить тут...', 12, 0, 100, 432, 0, 0, 29879, 562000, 562000, 0, 'Веслекс Вездеключ to Player');
INSERT INTO `creature_text` VALUES (28041, 0, 0, 'Careful here, $C. These trolls killed their own snake god!', 15, 0, 50, 0, 0, 0, 27449, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 1, 'Watch your back. These Drakkari are a nasty lot.', 15, 0, 50, 0, 0, 0, 27448, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 2, 'These Drakkari are just bad news. We need to leave and head back to Justice Keep!', 15, 0, 50, 0, 0, 0, 0, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 3, 'See you around.', 15, 0, 50, 0, 0, 0, 67956, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 4, 'I wonder where we\'re headed to. And who\'s going to deal with these guys?', 15, 0, 50, 0, 0, 0, 27451, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 5, 'Right. I\'d better get back to the sergeant then.', 15, 0, 50, 0, 0, 0, 27446, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28043, 3, 0, 'Рыцарь! Возьми эти дрова и разведи огонь для наших воинов.', 12, 0, 100, 0, 0, 0, 27733, 132000, 132000, 51233, 'Капитан Грондель to Player');
INSERT INTO `creature_text` VALUES (28048, 0, 0, 'Ар-р! Это Старый Зубастень, ужас бескрайних морей! За ним, парни, это наш шанс!', 14, 0, 100, 0, 0, 0, 114420, 0, 0, 0, 'Жуткий Капитан де Меза to Старый Зубастень');
INSERT INTO `creature_text` VALUES (28068, 0, 0, 'Mmm, more mojo.', 14, 0, 100, 1, 0, 0, 27531, 0, 0, 0, 'JustSummoned Text');
INSERT INTO `creature_text` VALUES (28068, 1, 0, 'NOOOOOOOOooooooo.........!', 14, 0, 100, 1, 0, 0, 27532, 0, 0, 0, 'JustSummoned Text');
INSERT INTO `creature_text` VALUES (28070, 0, 0, 'Пора узнать кое-какие ответы! Начнем веселье!', 14, 0, 100, 0, 0, 14259, 30524, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 1, 0, 'Насладитесь этим моментом вместе со мной! Скоро... мне все станет известно. Ну, поехали!', 14, 0, 100, 4, 0, 14247, 30523, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 2, 0, 'Теперь будьте внимательны! Не успеете и глазом моргнуть, как...', 14, 0, 100, 432, 0, 14248, 30552, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 3, 0, 'Ой, это нехорошо. У нас могут возникнуть небольшие затруднения...', 14, 0, 100, 1, 0, 14249, 30553, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 4, 0, 'Ага, значит, играем по-крупному, а? Обожаю такие игры!', 14, 0, 100, 0, 0, 14250, 30554, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 5, 0, 'Еще пара минут и я... ой.', 14, 0, 100, 0, 0, 14251, 30555, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 6, 0, 'Повышение? А хорошие новости есть?', 14, 0, 100, 0, 0, 14252, 30556, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 7, 0, 'Так вот в чем было дело! Теперь я двигаюсь в нужном направлении...', 14, 0, 100, 0, 0, 14253, 30557, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 8, 0, 'Стойте! Никакого \"очищения\" не будет, пока я тут командую!', 14, 0, 100, 0, 0, 14254, 30558, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 9, 0, 'Ха! Мои старые добрые пальцы наконец-то одолели эту преграду! Теперь, перейдем к...', 14, 0, 100, 0, 0, 14255, 30559, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 10, 0, 'Стирание памяти? Нет, нет, нет, нет, нет... куда я... Ага, вот это должно помочь...', 14, 0, 100, 0, 0, 14256, 30560, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 11, 0, 'Запрос? Ты что думаешь, я пришел попить чаю с печеньем? Выкладывай уже!', 14, 0, 100, 0, 0, 14263, 30565, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 12, 0, 'Ты прав, я вернусь к этому позже. Я думаю, пришло время проверить, что там за той дверью неподалеку от входа. Я собираюсь проникнуть туда, тихо и аккуратно. Жди меня у двери, я впущу нас внутрь.', 14, 0, 100, 0, 0, 13773, 33003, 0, 0, 0, 'Бранн Бронзобород to Invis Lightning Stalker');
INSERT INTO `creature_text` VALUES (28070, 13, 0, 'Не волнуйтесь! Старик Бранн прикроет вас! Вы пока займите чем-нибудь то железное чудище, а я попробую уболтать эту машину помочь нам.', 14, 0, 100, 0, 0, 14274, 31555, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 14, 0, 'Это будет посложнее предыдущего... Ох, бес побери... наступают!', 14, 0, 100, 0, 0, 14275, 27589, 0, 0, 0, 'Бранн Бронзобород to Сьоннир Литейщик');
INSERT INTO `creature_text` VALUES (28070, 15, 0, 'Что, во имя Мадорана, ЭТО было? О, погодите. Я почти понял...', 14, 0, 100, 0, 0, 14276, 27590, 0, 0, 0, 'Бранн Бронзобород to Сьоннир Литейщик');
INSERT INTO `creature_text` VALUES (28070, 16, 0, 'Ха, вот так! Помощь уже в пути! Получай, ты, светящийся железный чурбан!', 14, 0, 100, 0, 0, 14277, 27591, 0, 0, 0, 'Бранн Бронзобород to Сьоннир Литейщик');
INSERT INTO `creature_text` VALUES (28070, 17, 0, 'Локен?! Час от часу не легче... Мы справились с железными дворфами, но готов поспорить, что рядом есть другая машина, выплевывающая железных врайкулов пачками.', 14, 0, 100, 1, 0, 14278, 28591, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28070, 18, 0, 'Я воспользуюсь кузней и создам несколько отрядов земельников для охраны... Но наша главная задача еще не решена: надо найти и остановить Локена!', 14, 0, 100, 1, 0, 0, 30584, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (28077, 0, 0, 'Dumb big-tongue lover! You not friend of Frenzyheart no more. Frenzyheart will get you good.', 14, 0, 100, 0, 0, 0, 27798, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28086, 0, 0, '%s becomes frenzy!', 2, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'combat Frenzy');
INSERT INTO `creature_text` VALUES (28090, 0, 0, 'We\'ll cleanse this place! Arthas beware!', 12, 0, 100, 0, 0, 0, 27500, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28090, 0, 1, 'Your\'re right! We can do this!', 12, 0, 100, 0, 0, 0, 27497, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28090, 0, 2, 'Your\'re right! What was I thinking? Bring on the Scourge!', 12, 0, 100, 0, 0, 0, 27496, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28113, 0, 0, 'Please take... my shinies. All done...', 12, 0, 100, 0, 0, 0, 28630, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 1, 'We not do anything... to them... I no understand.', 12, 0, 100, 0, 0, 0, 28903, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 2, 'Use my shinies... make weather good again... make undead things go away.', 12, 0, 100, 0, 0, 0, 28901, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 3, 'We gave shinies to shrine... we not greedy... why this happen?', 12, 0, 100, 0, 0, 0, 28900, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 4, 'I do something bad? I sorry...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 0, 'We saved. You nice, dryskin.', 12, 0, 100, 0, 0, 0, 28631, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 1, 'Maybe you make weather better too?', 12, 0, 100, 0, 0, 0, 28906, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 2, 'You save us! Yay for you!', 12, 0, 100, 0, 0, 0, 28909, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 3, 'Thank you! You good!', 12, 0, 100, 0, 0, 0, 28905, 0, 0, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28118, 3, 0, 'I will connect your watery insides with mine.', 12, 0, 100, 34, 0, 0, 27504, 1000, 1000, 32995, 'Водянистый повелитель to Player');
INSERT INTO `creature_text` VALUES (28118, 3, 0, 'Приготовься утонуть!', 12, 0, 100, 34, 0, 0, 27502, 1000, 1000, 32995, 'Водянистый повелитель to Player');
INSERT INTO `creature_text` VALUES (28118, 3, 0, 'Ты смеешь призывать меня?!', 12, 0, 100, 34, 0, 0, 27501, 1000, 1000, 32995, 'Водянистый повелитель to Player');
INSERT INTO `creature_text` VALUES (28120, 0, 0, 'Ooh! Shinies!', 12, 0, 100, 0, 0, 0, 28261, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28133, 0, 0, 'I am in your debt, friend. Thank you for my life.', 12, 0, 100, 1, 0, 0, 27528, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (28136, 3, 0, 'Спасибо, друг. Если бы не твоя помощь, меня бы больше не было в живых.', 12, 0, 100, 0, 0, 0, 27521, 61000, 61000, 50709, 'Рыцарь Джонатан to Leave No One Behind Bunny');
INSERT INTO `creature_text` VALUES (28141, 0, 0, 'I am in your debt, friend. Thank you for my life.', 12, 0, 100, 1, 0, 0, 27528, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (28142, 3, 0, '<вздыхает>... Что ж, полагаю, могло быть и хуже.', 12, 0, 100, 0, 0, 0, 27523, 74000, 74000, 50723, 'Рыцарь Ламуф to Leave No One Behind Bunny');
INSERT INTO `creature_text` VALUES (28143, 0, 0, 'I am in your debt, friend. Thank you for my life.', 12, 0, 100, 1, 0, 0, 27528, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (28148, 3, 0, 'Я в долгу перед тобой, друг. Спасибо за спасение моей жизни.', 12, 0, 100, 0, 0, 0, 27528, 88000, 88000, 50711, 'Рыцарь Жозефина to Leave No One Behind Bunny');
INSERT INTO `creature_text` VALUES (28167, 2, 0, 'I ... did not deserve it ...', 12, 0, 100, 0, 0, 0, 28868, 0, 0, 52384, 'Горожанин Стратхольма to Лордеронский пехотинец');
INSERT INTO `creature_text` VALUES (28167, 3, 0, 'Видишь?', 12, 0, 100, 5, 0, 0, 40793, 287192, 287192, 0, 'Горожанин Стратхольма to Player');
INSERT INTO `creature_text` VALUES (28169, 3, 0, 'О нет!..', 12, 0, 100, 0, 0, 0, 27552, 286786, 286786, 0, 'Житель Стратхольма to Player');
INSERT INTO `creature_text` VALUES (28213, 0, 0, '%s взвизгивает от ярости и мчится защищать своих детенышей.', 16, 0, 100, 0, 0, 0, 27607, 0, 0, 0, 'Предводительница стаи барабанчей to Player');
INSERT INTO `creature_text` VALUES (28214, 3, 0, 'I hit him right in the face! The bitterness is so good.', 12, 0, 100, 0, 0, 0, 28012, 6000, 6000, 0, 'Горегек Охотник на горилл to Покорный барабанч');
INSERT INTO `creature_text` VALUES (28214, 3, 0, 'Гляди, $gраб:рабыня;, как Горегек расправится с этими барабанчами!', 12, 0, 100, 0, 0, 0, 27992, 2000, 2000, 0, 'Горегек Охотник на горилл to Bristlepine Den Bunny');
INSERT INTO `creature_text` VALUES (28217, 0, 0, 'Let me know when you ready to go, okay?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28217, 1, 0, 'Home time!', 12, 0, 100, 0, 0, 0, 27751, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28217, 2, 0, 'Oh no! Some puppy-men followed!', 12, 0, 100, 0, 0, 0, 27799, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Драккари, это не остановит нас от кражи сил ваших дикарских богов. Мы заберем ее или прямо у них, или у вас, если вы уже убили их – это не имеет значения.', 14, 0, 100, 0, 0, 0, 27672, 6779000, 6779000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Если ты убьешь их, я просто оживлю их трупы. А когда ты умрешь, я то же самое сделаю и с тобой.', 14, 0, 100, 0, 0, 0, 27674, 23021000, 23021000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Многие из Серебряного Рассвета входят в число простаков, павших предо мной. Серебряный Авангард постигнет та же участь.', 14, 0, 100, 0, 0, 0, 27673, 11399000, 11399000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Мой прекрасный Колрамас! Вы все дорого заплатите за это злодеяние!', 14, 0, 100, 0, 0, 0, 27670, 6734000, 6734000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'От смерти не уйти!', 14, 0, 100, 0, 0, 0, 27676, 16265000, 16265000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Придите, глупцы, и встретьте свою судьбу! Я жду вас.', 14, 0, 100, 0, 0, 0, 27671, 3165000, 3165000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28255, 3, 0, 'Уничтожьте Драккари! Уничтожьте Серебряный Авангард! Всех их превратите в Плеть!', 14, 0, 100, 0, 0, 0, 27669, 4977000, 4977000, 0, 'Малас Осквернитель');
INSERT INTO `creature_text` VALUES (28257, 3, 0, 'Наконец-то ты познал свою истинную судьбу.', 12, 0, 100, 396, 0, 0, 27629, 237000, 237000, 0, 'Некромаг Хат\'ар');
INSERT INTO `creature_text` VALUES (28266, 3, 0, 'Когда же у нас будет новый пилот? Мне непременно нужны исследования с воздуха, если я хочу хоть как-то продвинуться в работе.', 12, 0, 100, 396, 0, 0, 27641, 553000, 553000, 0, 'Профессор Кальверт');
INSERT INTO `creature_text` VALUES (28266, 3, 0, 'Нордскол – холоднее, чем сердце ведьмы; но вот здесь... Должно же быть какое-то объяснение! Лучше всего – объяснение, касающееся хрустальных колонн!', 12, 0, 100, 396, 0, 0, 27638, 157000, 157000, 0, 'Профессор Кальверт');
INSERT INTO `creature_text` VALUES (28284, 3, 0, 'АЙ! О, нет, я вся в паутине! Какая же она липкая. МОИ ВОЛОСЫ!', 12, 0, 100, 15, 0, 6906, 27735, 21000, 308000, 51322, 'Специалист Зубоколес to Player');
INSERT INTO `creature_text` VALUES (28284, 3, 0, 'Ой, какие липкие! Интересно, как они стреляют этой паутиной?', 12, 0, 100, 0, 0, 0, 27734, 298000, 298000, 0, 'Специалист Зубоколес to Player');
INSERT INTO `creature_text` VALUES (28340, 3, 0, 'О, вы наконец-то прибыли, принц Артас. Вы едва успели.', 12, 0, 100, 1, 0, 0, 27811, 60000, 60000, 0, 'Горожанин Стратхольма to Player');
INSERT INTO `creature_text` VALUES (28340, 3, 0, 'Тебе и не надо этого понимать, Артас. Все, что от тебя требуется – это умереть.', 12, 0, 100, 1, 0, 0, 0, 76000, 76000, 0, 'Горожанин Стратхольма to Player');
INSERT INTO `creature_text` VALUES (28347, 0, 0, 'Тогда в следующий раз положи двойную порцию !', 12, 0, 100, 0, 0, 0, 27805, 0, 0, 0, 'Майлс Сидни to Райт Вильямс');
INSERT INTO `creature_text` VALUES (28347, 1, 0, 'Well Met!', 12, 7, 100, 3, 0, 0, 53806, 0, 0, 0, 'Майлс Сидни to Player');
INSERT INTO `creature_text` VALUES (28347, 2, 0, 'Запомни, дружок – ты всегда ошибаешься немного больше, чем ты думаешь. Раза в два.', 12, 0, 100, 0, 0, 0, 27807, 0, 0, 0, 'Майлс Сидни to Райт Вильямс');
INSERT INTO `creature_text` VALUES (28355, 3, 0, 'Я над ним тружусь, просто не хочу продавать зелье до тех пор, пока не буду полностью им доволен. Ждать осталось недолго!', 12, 0, 100, 0, 0, 0, 27808, 266169, 266169, 0, 'Райт Вильямс to Райт Вильямс');
INSERT INTO `creature_text` VALUES (28357, 3, 0, 'Я вынес свой вердикт. Ты недостоин стать одним из нас...', 12, 0, 100, 397, 0, 0, 27866, 117670, 306395, 0, 'Инструктор Разувий to Рыцарь смерти-посвященный');
INSERT INTO `creature_text` VALUES (28375, 3, 0, 'Пока, неудачники. Я сваливаю отсюда!', 12, 0, 100, 0, 0, 414, 27829, 150000, 150000, 0, 'Глрглрглр to Player');
INSERT INTO `creature_text` VALUES (28383, 3, 0, 'Да, инструктор.', 12, 0, 100, 1, 0, 0, 27874, 107611, 296692, 0, 'Некромант Акеруса to Инструктор Разувий');
INSERT INTO `creature_text` VALUES (28391, 3, 0, 'Какая боль... я не могу больше терпеть... умоляю, прекратите это!', 12, 0, 100, 0, 0, 0, 27859, 146830, 146830, 0, 'Рыцарь смерти-посвященный to Рыцарь смерти-посвященный');
INSERT INTO `creature_text` VALUES (28393, 3, 0, 'Живых не останется, мой повелитель.', 12, 0, 100, 66, 0, 0, 30180, 2000, 2000, 0, 'Рыцарь смерти-посвященный to Инструктор Разувий');
INSERT INTO `creature_text` VALUES (28401, 0, 0, '$N, please come to me.  I\'m here in Zim\'Torga now.', 15, 0, 100, 0, 0, 0, 28947, 0, 0, 0, 'Har\'koa');
