INSERT INTO `creature_text` VALUES (28659, 1, 0, 'А, герои. Ваши маленькие друзья предупредили, что вы явитесь. Что ж, это избавляет меня от необходимости самому охотиться за вами.', 14, 0, 100, 0, 0, 0, 28646, 0, 0, 0, 'Артруис Бессердечный');
INSERT INTO `creature_text` VALUES (28665, 0, 0, 'Thank you for saving me, $N. This is the least that I could do to return the favor. Hold on tight. Here we go.', 15, 0, 100, 0, 0, 0, 28652, 0, 0, 0, 'Har\'koa\'s Kitten');
INSERT INTO `creature_text` VALUES (28665, 1, 0, 'This doesn\'t look good. Whatever you do, don\'t fall off. There\'s a ton of nasty things in there!', 15, 0, 100, 0, 0, 0, 28654, 0, 0, 0, 'Har\'koa\'s Kitten');
