INSERT INTO `creature_text` VALUES (28670, 3, 0, 'Укрывайтесь от стрел и копий Алого ордена! Вовремя уходите с линии огня!', 42, 0, 100, 0, 0, 0, 29888, 112, 112, 0, 'Ледяной покоритель to Player');
INSERT INTO `creature_text` VALUES (28670, 4, 0, 'Чтобы восполнить энергию, пожирайте крестоносцев Алого ордена!', 42, 0, 100, 0, 0, 0, 29889, 89305, 89305, 0, 'Ледяной покоритель to Player');
