INSERT INTO `creature_text` VALUES (28785, 1, 0, 'I\'ve returned to invite you to my housewarming in the underworld!', 14, 0, 100, 0, 0, 7056, 28842, 0, 0, 0, 'Spirit of Quetz\'lun');
