INSERT INTO `creature_text` VALUES (28785, 3, 0, 'Well done. I\'ll see to it that my prophet enjoys his eternal stay with me.', 14, 0, 100, 0, 0, 0, 28876, 0, 0, 0, 'Spirit of Quetz\'lun');
INSERT INTO `creature_text` VALUES (28788, 0, 0, 'Впечатляет, рыцарь смерти. Возвращайся ко мне в мир живых за своей наградой.', 12, 0, 100, 2, 0, 0, 28835, 0, 0, 0, 'Саланар Всадник to Player');
INSERT INTO `creature_text` VALUES (28801, 3, 0, 'Армия Плети штурмует северную стену!', 14, 0, 100, 0, 0, 0, 30284, 1313000, 1313000, 0, 'Защитник Серебряной заставы');
INSERT INTO `creature_text` VALUES (28834, 0, 0, 'Прогоните их с корабля! Убейте их всех!', 14, 0, 100, 0, 0, 0, 28976, 0, 0, 0, 'Защитник флота Алого ордена to Player');
INSERT INTO `creature_text` VALUES (28834, 1, 0, 'Смерть Плети!', 14, 0, 100, 0, 0, 0, 28979, 0, 0, 0, 'Защитник флота Алого ордена to Пушка Алого ордена');
INSERT INTO `creature_text` VALUES (28841, 0, 0, 'Откуда это? Спущу-ка я эту штуку к кораблям, пока старший не увидел.', 12, 0, 100, 0, 0, 0, 28898, 0, 0, 0, 'Шахтер из Алого ордена to Вагонетка');
INSERT INTO `creature_text` VALUES (28841, 1, 0, 'I\'ll just leave her here - and end up in the water!', 12, 0, 100, 0, 0, 0, 28899, 0, 0, 0, 'Шахтер из Алого ордена to Вагонетка');
