INSERT INTO `creature_text` VALUES (28846, 1, 0, 'Это будет не так-то просто, чудовище!', 12, 0, 100, 0, 0, 0, 28965, 0, 0, 0, 'Привидение из Алого ордена to Player');
INSERT INTO `creature_text` VALUES (28856, 0, 0, 'Отродье Плети! УМРИ!', 12, 0, 100, 0, 0, 0, 29716, 0, 0, 0, 'Страж флота Алого ордена to Хрящелов');
INSERT INTO `creature_text` VALUES (28856, 1, 0, 'СЗАДИ! Они захватили корабли!', 14, 0, 100, 0, 0, 0, 28973, 0, 0, 0, 'Страж флота Алого ордена to Хрящелов');
INSERT INTO `creature_text` VALUES (28859, 0, 0, 'Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity!', 14, 0, 100, 0, 10000, 14512, 31759, 0, 0, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 1, 'None but the blue dragonflight are welcome here. Perhaps this is the work of Alexstrasza? Well, then, she has sent you to your deaths!', 14, 0, 100, 0, 11000, 14513, 31760, 0, 0, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 2, 'What could you hope to accomplish? To storm brazenly into my domain... to employ magic... against ME?', 14, 0, 100, 0, 13000, 14514, 31761, 0, 0, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 3, 'I am without limits here. The rules of your cherished reality do not apply. In this realm, I am in control!', 14, 0, 100, 0, 10000, 14515, 31762, 0, 0, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 4, 'I give you one chance. Pledge fealty to me, and perhaps I will not slaughter you for your insolence.', 14, 0, 100, 0, 7000, 14516, 31763, 0, 0, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 1, 0, 'My patience has reached its limit. I will be rid of you!', 14, 0, 100, 0, 4000, 14517, 31764, 0, 0, 0, 'Malygos - Start phase 1');
INSERT INTO `creature_text` VALUES (28859, 2, 0, 'You will not succeed while I draw breath!', 14, 0, 100, 0, 3000, 14518, 31781, 0, 0, 0, 'Malygos - Begin to cast Deep Breath');
INSERT INTO `creature_text` VALUES (28859, 3, 0, 'Your stupidity has finally caught up with you!', 14, 0, 100, 0, 3250, 14519, 31767, 0, 0, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 1, 'More artifacts to confiscate.', 14, 0, 100, 0, 2800, 14520, 31768, 0, 0, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 2, 'How very naive.', 14, 0, 100, 0, 4800, 14521, 31769, 0, 0, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 4, 0, 'I had hoped to end your lives quickly, but you have proven more... resilient than I anticipated. Nonetheless, your efforts are in vain. It is you reckless, careless mortals who are to blame for this war. I do what I must, and if it means your extinction.... then SO BE IT!!', 14, 0, 100, 0, 22900, 14522, 31772, 0, 0, 0, 'Malygos - End Phase One');
INSERT INTO `creature_text` VALUES (28859, 5, 0, 'Few have experienced the pain I will now inflict upon you!', 14, 0, 100, 0, 5500, 14523, 31765, 0, 0, 0, 'Malygos - Start phase 2');
INSERT INTO `creature_text` VALUES (28859, 6, 0, 'I will teach you ignorant children just how little you know of magic.', 14, 0, 100, 0, 7000, 14524, 31773, 0, 0, 0, 'Malygos - Anti-Magic Shell');
INSERT INTO `creature_text` VALUES (28859, 7, 0, 'Watch helplessly as your hopes are swept away!', 14, 0, 100, 0, 4000, 14525, 31774, 0, 0, 0, 'Malygos - Magic Blast');
INSERT INTO `creature_text` VALUES (28859, 8, 0, 'Your energy will be put to good use.', 14, 0, 100, 0, 2000, 14526, 31775, 0, 0, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 1, 'I am the Spell-Weaver! My power is infinite!', 14, 0, 100, 0, 5200, 14527, 31776, 0, 0, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 2, 'Your spirit will linger here forever!', 14, 0, 100, 0, 3800, 14528, 31777, 0, 0, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 9, 0, 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it!', 14, 0, 100, 0, 7000, 14529, 31778, 0, 0, 0, 'Malygos - End Phase 2');
INSERT INTO `creature_text` VALUES (28859, 10, 0, 'Now your benefactors make their appearance, but they are too late! The powers contained here are sufficient to destroy the world ten times over. What do you think they will do to you?', 14, 0, 100, 0, 13000, 14530, 31779, 0, 0, 0, 'Intro Phase 3');
INSERT INTO `creature_text` VALUES (28859, 11, 0, 'Submit!', 14, 0, 100, 0, 1000, 14531, 72513, 0, 0, 0, 'Malygos - Start phase 3');
INSERT INTO `creature_text` VALUES (28859, 12, 0, '%s takes a deep breath...', 41, 0, 100, 0, 10000, 0, 33657, 0, 0, 0, 'Malygos - Surge of Power warning (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 13, 0, 'The powers at work here exceed anything you could possibly imagine!', 14, 0, 100, 0, 5000, 14532, 31782, 0, 0, 0, 'Malygos - Surge of Power');
INSERT INTO `creature_text` VALUES (28859, 14, 0, 'I am unstoppable!', 14, 0, 100, 0, 2000, 14533, 91067, 0, 0, 0, 'Malygos - Buffed by a spark');
INSERT INTO `creature_text` VALUES (28859, 15, 0, 'Alexstrasza, another of your brood falls!', 14, 0, 100, 0, 3600, 14534, 31784, 0, 0, 0, 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 15, 1, 'Little more than gnats!', 14, 0, 100, 0, 2650, 14535, 31785, 0, 0, 0, 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 15, 2, 'Your red allies will share your fate!', 14, 0, 100, 0, 3000, 14536, 31786, 0, 0, 0, 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 16, 0, 'Still standing? Not for long.', 14, 0, 100, 0, 3600, 14537, 31787, 0, 0, 0, 'Malygos - Spell Casting 1(Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 16, 1, 'Your cause is lost!', 14, 0, 100, 0, 2000, 14538, 31788, 0, 0, 0, 'Malygos - Spell Casting 2 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 16, 2, 'Your fragile mind will be shattered!', 14, 0, 100, 0, 4000, 14539, 31789, 0, 0, 0, 'Malygos - Spell Casting 3 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 17, 0, 'Unthinkable! The mortals will destroy... everything! My sister, what have you...', 14, 0, 100, 0, 8500, 14540, 31790, 0, 0, 0, 'Malygos - Death');
INSERT INTO `creature_text` VALUES (28859, 18, 0, '%s fixes his eyes on you!', 42, 0, 100, 0, 3000, 0, 31321, 0, 0, 0, 'Malygos - Surge of Power warning (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 19, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 10000, 0, 34057, 0, 0, 0, 'Malygos - Hit berserk timer (Any phase)');
INSERT INTO `creature_text` VALUES (28860, 1, 0, 'Моя обязанность – оберегать эти яйца, и вы сгорите, прежде чем хоть пальцем тронете их!', 14, 0, 100, 0, 0, 14093, 31400, 0, 0, 0, 'Сартарион to Player');
INSERT INTO `creature_text` VALUES (28860, 2, 0, 'Такова цена... неудачи...', 14, 0, 100, 0, 0, 14107, 31341, 0, 0, 0, 'Сартарион to Player');
INSERT INTO `creature_text` VALUES (28860, 4, 0, 'Тенеброн! Ты тоже должна защищать яйца!', 14, 0, 100, 0, 0, 14106, 31319, 20688, 20688, 0, 'Сартарион');
INSERT INTO `creature_text` VALUES (28902, 0, 0, '%s becomes enraged.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Warlord Zol\'Maz');
INSERT INTO `creature_text` VALUES (28902, 1, 0, 'You have an enchanted tiki? YOU KILLED MY FAMILY!', 14, 0, 100, 15, 0, 6200, 29040, 0, 0, 0, 'Warlord Zol\'Maz');
INSERT INTO `creature_text` VALUES (28902, 2, 0, 'I WILL KILL YOU, $N!', 14, 0, 100, 0, 0, 0, 29041, 0, 0, 0, 'Warlord Zol\'Maz');
INSERT INTO `creature_text` VALUES (28907, 0, 0, 'ДА ПРОДЛЯТСЯ ВАШИ СТРАДАНИЯ, РЫЦАРИ СМЕРТИ!', 12, 0, 100, 71, 0, 9006, 29061, 0, 0, 0, 'Принц Валанар to Принц Валанар');
INSERT INTO `creature_text` VALUES (28912, 0, 0, 'Проклятый Орден! Кажется, у меня сломаны ребра и началось внутреннее кровотечение.', 12, 0, 100, 1, 0, 0, 29197, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 1, 0, 'Мне понадобится мой рунический клинок и доспехи... Нужно еще немного времени.', 12, 0, 100, 0, 0, 0, 29201, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 2, 0, 'Я все еще слаб, но сил моих должно хватить на поддержание антимагического барьера. Он защитит тебя от вражеских заклинаний.', 12, 0, 100, 0, 0, 0, 29203, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 3, 0, 'Но для поддержания барьера мне нужно максимально сконцентрироваться. Убей их всех!', 12, 0, 100, 0, 0, 0, 29205, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 4, 0, 'Они наступают. Обороняйся! Не выходи за пределы защитного поля! Без него они разорвут тебя на куски!', 12, 0, 100, 0, 0, 0, 29207, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 5, 0, 'I can no longer hold the defense ... Where did this coward go?', 12, 0, 100, 0, 0, 0, 29208, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 6, 0, 'Идет верховный инквизитор! Приготовься, рыцарь смерти! Не дай ему выманить себя за пределы антимагического барьера! Убей его и отруби ему голову!', 12, 0, 100, 0, 0, 0, 29210, 0, 0, 0, 'Кольтира Ткач Смерти to Player');
INSERT INTO `creature_text` VALUES (28912, 7, 0, 'Смерть верховного инквизитора из Нового Авалона не останется незамеченной. Тебе надо спасаться! Беги, пока здесь не появился кто-нибудь еще. За меня не беспокойся.', 12, 0, 100, 1, 0, 0, 29239, 0, 0, 0, 'Кольтира Ткач Смерти to Верховный инквизитор Валрот');
INSERT INTO `creature_text` VALUES (28912, 8, 0, 'I will distract them for myself, and you run.', 12, 0, 100, 1, 0, 0, 29240, 0, 0, 0, 'Кольтира Ткач Смерти to 29001');
INSERT INTO `creature_text` VALUES (28912, 9, 0, 'Рыцари! Ваш верховный инквизитор – просто безвольный кусок мяса! Никто не устоит против сил Плети!', 14, 0, 100, 22, 0, 0, 29241, 0, 0, 0, 'Кольтира Ткач Смерти to 29001');
INSERT INTO `creature_text` VALUES (28913, 3, 0, 'На что ты намекаешь, Орбаз? На то, что он – эльф крови?', 12, 0, 100, 6, 0, 0, 29192, 77345, 77345, 0, 'Тассариан to Player');
INSERT INTO `creature_text` VALUES (28914, 3, 0, 'Он сам виноват, что его схватили.', 12, 0, 100, 1, 0, 0, 29190, 70877, 70877, 0, 'Орбаз Кровопорч to Player');
INSERT INTO `creature_text` VALUES (28919, 3, 0, 'Моя задача выполнена!', 12, 0, 100, 0, 0, 8849, 29070, 1495821, 1495821, 0, 'Нот Чумной to Player');
INSERT INTO `creature_text` VALUES (28921, 0, 0, '%s поднимается по тоннелю!', 41, 0, 100, 0, 0, 13941, 29852, 0, 0, 0, 'Хадронокс to World Trigger (Large AOI, Not Immune PC/NPC)');
INSERT INTO `creature_text` VALUES (28922, 3, 0, 'Враг преодолел ворота! Быстрее перебросьте туда силы и разберитесь с чужаками!', 14, 0, 100, 0, 0, 13941, 29473, 231000, 231000, 0, 'Ануб\'арский сокрушитель to Player');
INSERT INTO `creature_text` VALUES (28922, 4, 0, '%s is furious!', 41, 0, 100, 0, 0, 0, 38630, 13000, 13000, 53801, 'Ануб\'арский сокрушитель');
INSERT INTO `creature_text` VALUES (28923, 0, 0, 'I have witnessed the rise and fall of empires... the birth and extinction of entire species... Over countless millennia the foolishness of mortals has remained the only constant. Your presence here confirms this.', 14, 0, 100, 0, 0, 14160, 30536, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 1, 0, 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', 14, 0, 100, 0, 0, 14161, 30537, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 2, 0, 'What hope is there for you? None!', 14, 0, 100, 0, 0, 14162, 30535, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 0, 'You cannot hide from fate!', 14, 0, 100, 0, 0, 14163, 30538, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 1, 'Come closer. I will make it quick.', 14, 0, 100, 0, 0, 14164, 30539, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 2, 'Your flesh cannot hold out for long.', 14, 0, 100, 0, 0, 14165, 30540, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 0, 'Only mortal...', 14, 0, 100, 0, 0, 14166, 30541, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 1, 'I... am... FOREVER!', 14, 0, 100, 0, 0, 14167, 30542, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 2, 'What little time you had, you wasted!', 14, 0, 100, 0, 0, 14168, 30543, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 5, 0, 'You stare blindly into the abyss!', 14, 0, 100, 0, 0, 14169, 30545, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 6, 0, 'Your ignorance is profound. Can you not see where this path leads?', 14, 0, 100, 0, 0, 14170, 30546, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 7, 0, 'You cross the precipice of oblivion!', 14, 0, 100, 0, 0, 14171, 30547, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 8, 0, 'My death... heralds the end of this world.', 14, 0, 100, 0, 0, 14172, 30544, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 9, 0, '%s begins to cast Lightning Nova!', 41, 0, 100, 0, 0, 0, 29865, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28936, 0, 0, 'Scourge lilth!DIE!', 0, 0, 50, 0, 0, 0, 0, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (28936, 0, 1, 'I will present your head to Abbendis myself!', 0, 0, 50, 0, 0, 0, 29718, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (28936, 0, 2, 'The Crusade will be victorious!', 0, 0, 50, 0, 0, 0, 29717, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (28939, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (28939, 1, 0, 'I\'ll tear the secrets from your soul! Tell me about the \"Crimson Dawn\" and your life may be spared!', 12, 0, 100, 0, 0, 0, 29138, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 2, 0, 'Tell me what you know about \"Crimson Dawn\" or the beatings will continue!', 12, 0, 100, 0, 0, 0, 29134, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 3, 0, 'I\'m through being courteous with your kind, human! What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 29135, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 4, 0, 'Is your life worth so little? Just tell me what I need to know about \"Crimson Dawn\" and I\'ll end your suffering quickly.', 12, 0, 100, 0, 0, 0, 29139, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 5, 0, 'I can keep this up for a very long time, Scarlet dog! Tell me about the \"Crimson Dawn!\"', 12, 0, 100, 0, 0, 0, 29137, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 7, 0, '\"Crimson Dawn!\" What is it! Speak!', 12, 0, 100, 0, 0, 0, 29136, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 8, 0, 'You\'ll be hanging in the gallows shortly, Scourge fiend!', 12, 0, 100, 0, 0, 0, 29160, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 9, 0, 'You\'ll have to kill me, monster! I will tell you NOTHING!', 12, 0, 100, 0, 0, 0, 29142, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 10, 0, 'You hit like a girl. Honestly. Is that the best you can do?', 12, 0, 100, 0, 0, 0, 29162, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 11, 0, 'ARGH! You burned my last good tabard!', 12, 0, 100, 0, 0, 0, 29143, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 12, 0, 'Argh... The pain... The pain is almost as unbearable as the lashings I received in grammar school when I was but a child.', 12, 0, 100, 0, 0, 0, 29143, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 13, 0, 'I used to work for Grand Inquisitor Isillien! Your idea of pain is a normal mid-afternoon for me!', 12, 0, 100, 0, 0, 0, 29161, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 14, 0, 'I\'ll tell you everything! STOP! PLEASE!', 12, 0, 100, 0, 0, 0, 29149, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 15, 0, 'We... We have only been told that the \"Crimson Dawn\" is an awakening. You see, the Light speaks to the High General. It is the Light...', 12, 0, 100, 0, 0, 0, 29150, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 16, 0, 'The Light that guides us. The movement was set in motion before you came... We... We do as we are told. It is what must be done.', 12, 0, 100, 0, 0, 0, 29151, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 17, 0, 'I know very little else... The High General chooses who may go and who must stay behind. There\'s nothing else... You must believe me!', 12, 0, 100, 0, 0, 0, 29152, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 18, 0, 'LIES! The pain you are about to endure will be talked about for years to come!', 12, 0, 100, 0, 0, 0, 29163, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28939, 19, 0, 'NO! PLEASE! There is one more thing that I forgot to mention... A courier comes soon... From Hearthglen. It...', 12, 0, 100, 0, 0, 0, 29153, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28940, 0, 0, 'Бьешь, как девчонка! Честное слово! Это все, на что ты способен?', 12, 0, 100, 0, 0, 0, 29146, 0, 0, 0, 'Крестоносец Алого ордена to Player');
INSERT INTO `creature_text` VALUES (28940, 1, 0, 'I worked for the greatest inquisitor Isillien! What you consider to be pain is an afternoon rest for me!', 12, 0, 100, 0, 0, 0, 29161, 0, 0, 0, 'Крестоносец Алого ордена to Player');
INSERT INTO `creature_text` VALUES (28941, 0, 0, 'Why?', 0, 0, 50, 22, 0, 0, 28869, 0, 0, 0, 'combat Say');
