INSERT INTO `creature_text` VALUES (29152, 3, 0, 'I am not guilty. Innocent, I tell you! The cataclysm is not my work!', 12, 0, 100, 0, 0, 0, 44915, 55000, 55000, 0, 'Штормградский портовый рабочий to Player');
INSERT INTO `creature_text` VALUES (29152, 3, 0, 'I do not know anything about this cult. Let me get back to work ...', 12, 0, 100, 16, 0, 0, 44909, 24000, 24000, 0, 'Штормградский портовый рабочий to Player');
INSERT INTO `creature_text` VALUES (29152, 3, 0, 'Сумеречный Молот? Я ничего не знаю, почему вы меня об этом спрашиваете?', 12, 0, 100, 16, 0, 0, 44908, 16000, 16000, 0, 'Штормградский портовый рабочий to Player');
INSERT INTO `creature_text` VALUES (29152, 3, 0, 'Что? Какой еще культ? Если я что-нибудь узнаю, непременно вам сообщу.', 12, 0, 100, 432, 0, 0, 44911, 14000, 14000, 0, 'Штормградский портовый рабочий to Player');
INSERT INTO `creature_text` VALUES (29173, 0, 0, 'Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord!', 14, 0, 100, 22, 0, 14679, 29699, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 1, 0, 'RISE!', 14, 0, 100, 15, 0, 14680, 29700, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 2, 0, 'The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Onward! Leave only ashes and misery in your destructive wake!', 14, 0, 100, 25, 0, 14682, 29722, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 0, 'Kneel before the Highlord!', 12, 0, 100, 0, 0, 14683, 29727, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 1, 'You stand no chance!', 12, 0, 100, 0, 0, 14684, 29728, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 2, 'The Scourge will destroy this place!', 12, 0, 100, 0, 0, 14685, 29729, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 3, 'Your life is forfeit.', 12, 0, 100, 0, 0, 14686, 46677, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 4, 'Life is meaningless without suffering.', 12, 0, 100, 0, 0, 14687, 29731, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 5, 'How much longer will your forces hold out?', 12, 0, 100, 0, 0, 14688, 29732, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 6, 'The Argent Dawn is finished!\"', 12, 0, 100, 0, 0, 14689, 29733, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 7, 'Spare no one!', 12, 0, 100, 0, 0, 14690, 29734, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 8, 'What is this?! My... I cannot strike...', 12, 0, 100, 0, 0, 14691, 29735, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 9, 'Obey me, blade!', 14, 0, 100, 0, 0, 14692, 29736, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 10, 'You will do as I command! I am in control here!', 12, 0, 100, 0, 0, 14693, 29737, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 11, 'I can not... the blade fights me.', 12, 0, 100, 0, 0, 14694, 29738, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 12, 'What is happening to me?', 12, 0, 100, 0, 0, 14695, 29739, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 13, 'Power...wanes...', 12, 0, 100, 0, 0, 14696, 29740, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 14, 'Ashbringer defies me...', 12, 0, 100, 0, 0, 14697, 29741, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 15, 'Minions, come to my aid!', 12, 0, 100, 0, 0, 14698, 29742, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 4, 0, 'Stand down, death knights. We have lost... The Light... This place... No hope...', 12, 0, 100, 68, 0, 14699, 29760, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 5, 0, 'Save your breath, old man. It might be the last you ever draw.', 12, 0, 100, 25, 0, 14700, 29761, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 6, 0, 'Father!', 12, 0, 100, 5, 0, 14701, 29767, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 7, 0, 'Argh...what...is...', 12, 0, 100, 68, 0, 14702, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 0, 'You have\'ve betrayed me! You betrayed us all you monster! Face the might of Mograine!', 14, 0, 100, 0, 0, 14707, 29781, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 9, 0, 'That day is not today...', 12, 0, 100, 0, 0, 14708, 29824, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 10, 0, 'Tirion!', 14, 0, 100, 0, 0, 14709, 29801, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 11, 0, 'So too do the Knights of the Ebon Blade... While our kind has no place in your world, we will fight to bring an end to the Lich King. This I vow!', 12, 0, 100, 1, 0, 14710, 29851, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 12, 0, '%s kneels in defeat before Tirion Fordring.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 13, 0, '%s becomes a shade of his past, and walks up to his father.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 14, 0, '%s becomes himself again...and is now angry.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 15, 0, '%s throws the Corrupted Ashbringer to Tirion, who catches it. Tirion becomes awash with Light, and the Ashbringer is cleansed.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 16, 0, '%s collapses.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29175, 0, 0, 'You cannot win, Darion!', 14, 0, 100, 0, 0, 14584, 29743, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 1, 0, 'Bring them before the chapel!', 14, 0, 100, 0, 0, 14585, 29756, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 2, 0, 'Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness...the hate... Feeding upon the misery of those you tortured and killed!', 12, 0, 100, 378, 0, 14586, 29744, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 3, 0, 'Your master knows what lies beneath the chapel. It is why he dares not show his face! He\'s sent you and your death knights to meet their doom, Darion.', 12, 0, 100, 25, 0, 14587, 29745, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 4, 0, 'What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!', 12, 0, 100, 1, 0, 14588, 29746, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 5, 0, 'You\'re a damned monster, Arthas!', 12, 0, 100, 25, 0, 14589, 29783, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 6, 0, 'ARTHAS!!!!', 14, 0, 100, 0, 0, 14591, 29803, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 7, 0, 'Your end.', 14, 0, 100, 0, 0, 14592, 29861, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 8, 0, 'Rise, Darion, and listen...', 12, 0, 100, 0, 0, 14593, 29835, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 9, 0, 'We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives - our lives!', 12, 0, 100, 0, 0, 14594, 29843, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 10, 0, 'And while such things can never be forgotten, we must remain vigilant in our cause!', 12, 0, 100, 0, 0, 14595, 29845, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 11, 0, 'The Lich King must answer for what he has done and must not be allowed to cause further destruction to our world.', 12, 0, 100, 0, 0, 14596, 29846, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 12, 0, 'I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union.', 12, 0, 100, 0, 0, 14597, 29847, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 13, 0, 'The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!', 12, 0, 100, 0, 0, 14598, 29848, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 14, 0, 'We will take the fight to Arthas and tear down the walls of Icecrown!', 12, 0, 100, 15, 0, 14599, 29849, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 15, 0, 'The Argent Crusade comes for you, Arthas!', 14, 0, 100, 15, 0, 14600, 29850, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 16, 0, '%s gasps for air.', 16, 0, 100, 0, 0, 0, 29786, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 17, 0, '%s charges towards the Lich King, Ashbringer in hand and strikes the Lich King.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29176, 0, 0, 'Scourge armies approach!', 14, 0, 100, 0, 0, 0, 29724, 0, 0, 0, 'Korfax, Champion of the Light');
INSERT INTO `creature_text` VALUES (29178, 0, 0, 'Stand fast, brothers and sisters! The Light will prevail!', 14, 0, 100, 0, 0, 14487, 29723, 0, 0, 0, 'Lord Maxwell Tyrosus');
INSERT INTO `creature_text` VALUES (29178, 1, 0, 'ATTACK!!!', 14, 0, 100, 0, 0, 14488, 29787, 0, 0, 0, 'Lord Maxwell Tyrosus');
INSERT INTO `creature_text` VALUES (29183, 0, 0, 'Touching...', 14, 0, 100, 0, 0, 14803, 29779, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 1, 0, 'He\'s mine now...', 12, 0, 100, 0, 0, 14805, 29780, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 2, 0, 'Pathetic...', 12, 0, 100, 0, 0, 14804, 29782, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 3, 0, 'You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours...', 12, 0, 100, 1, 0, 14806, 29784, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 4, 0, 'How simple it was to draw the great Tirion Fordring out of hiding. You\'ve left yourself exposed, paladin. Nothing will save you...', 12, 0, 100, 1, 0, 14807, 29785, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 5, 0, 'APOCALYPSE!', 14, 0, 100, 0, 0, 14808, 29788, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 6, 0, 'What is this?', 14, 0, 100, 0, 0, 14809, 29804, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 7, 0, 'Impossible...', 14, 0, 100, 0, 0, 14810, 29825, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 8, 0, 'This... isn\'t... over...', 14, 0, 100, 25, 0, 14811, 29826, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 9, 0, 'When next we meet it won\'t be on holy ground, paladin.', 14, 0, 100, 1, 0, 14812, 29827, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 10, 0, '%s casts a spell on Tirion.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 11, 0, '%s casts a powerful spell, killing the Defenders and knocking back the others.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 12, 0, '%s disappears. Tirion walks over to where Darion lay', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29204, 0, 0, '%s flee', 16, 0, 100, 0, 0, 0, 29758, 0, 0, 0, 'Orbaz');
INSERT INTO `creature_text` VALUES (29227, 0, 0, 'My son! My dear, beautiful boy!', 12, 0, 100, 0, 0, 14493, 29762, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 1, 0, 'Nothing could have kept me away from here, Darion. Not from my home and family.', 12, 0, 100, 1, 0, 14494, 29773, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 2, 0, 'Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I couldn\'t bear losing you. Even the thought...', 12, 0, 100, 1, 0, 14495, 29775, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 3, 0, 'My son, there will come a day when you will command the Ashbringer and, with it, mete justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.', 12, 0, 100, 1, 0, 14496, 29777, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 4, 0, 'Do not forget...', 12, 0, 100, 6, 0, 14497, 29778, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 5, 0, '%s arrives.', 16, 0, 100, 0, 0, 0, 1415, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 6, 0, '%s disappears, and the Lich King appears.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Alexandros');
INSERT INTO `creature_text` VALUES (29228, 0, 0, 'Father, you have returned!', 12, 0, 100, 0, 0, 14703, 29769, 0, 0, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 1, 0, 'You have been gone a long time, father. I thought...', 12, 0, 100, 0, 0, 14704, 29770, 0, 0, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 2, 0, 'Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!', 12, 0, 100, 6, 0, 14705, 29774, 0, 0, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 3, 0, 'If I die, father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, father, I die with you!', 12, 0, 100, 6, 0, 14706, 29776, 0, 0, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 4, 0, '%s hugs his father.', 16, 0, 100, 0, 0, 0, 29771, 0, 0, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29260, 0, 0, 'Компания Искросвета ждет деловых предложений!', 14, 0, 100, 0, 0, 0, 30230, 0, 0, 0, 'Монтажник Искросвет');
