INSERT INTO `creature_text` VALUES (29280, 0, 0, 'Your sacrifice is a testament to your obedience.  Indeed you are worthy of this charge. Arise, and forever be known as Svala Sorrowgrave!', 14, 0, 0, 0, 0, 14732, 29688, 0, 0, 0, 'Image of Arthas SAY_DIALOG_OF_ARTHAS_1');
