INSERT INTO `creature_text` VALUES (29579, 1, 0, 'How did you get my communicator from the Explorers League?', 12, 0, 100, 5, 0, 0, 30625, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (29579, 2, 0, 'If the Explorers League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves.', 12, 0, 100, 0, 0, 0, 30622, 0, 0, 0, 'Player text');
INSERT INTO `creature_text` VALUES (29579, 3, 0, 'You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me?', 12, 0, 100, 6, 0, 0, 30626, 0, 0, 0, 'Brann Bronzebeard');
