INSERT INTO `creature_text` VALUES (29579, 6, 0, 'If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other!', 12, 0, 100, 5, 0, 0, 30628, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (29579, 7, 0, 'Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city.', 12, 0, 100, 5, 0, 0, 30629, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (29579, 8, 0, 'If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance.', 12, 0, 100, 6, 0, 0, 30630, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (29579, 9, 0, 'Speak to the commander at your post, lad, and persuade him to abandon Vor\'takh\'s foolish plan.', 12, 0, 100, 6, 0, 0, 30631, 0, 0, 0, 'Brann Bronzebeard');
