INSERT INTO `creature_text` VALUES (30025, 0, 0, 'I\'m the king of dirt, there is none higher! To my aid, minions - assist your sire!', 1, 0, 100, 0, 0, 0, 30767, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (30035, 0, 0, 'I was certain I was going to die out here.', 12, 0, 100, 1, 0, 0, 30721, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30035, 0, 1, 'Let me fight by your side.', 12, 0, 100, 1, 5, 0, 30722, 0, 0, 0, '');
