INSERT INTO `creature_text` VALUES (30084, 0, 0, 'A Power Spark forms from a nearby rift!', 41, 0, 100, 0, 10000, 0, 32957, 0, 0, 0, 'Power spark to portal');
INSERT INTO `creature_text` VALUES (30107, 0, 0, 'I can\'t thank you enough for all of your help in putting together the keystone. Great things will come of this, I assure you.', 12, 0, 100, 0, 0, 0, 31023, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 1, 0, 'Iron dwarves... everywhere.... they\'re making their way down from the top. They certainly are persistent.', 12, 0, 100, 0, 0, 0, 31024, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 2, 0, 'They\'re trying to take down the plane! I can\'t pull up any steeper... you need to keep them off of us!', 12, 0, 100, 0, 0, 0, 31025, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 3, 0, 'There are more just ahead - keep at it!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 4, 0, 'We\'re almost out... just a little bit further.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 5, 0, 'Barring stray boulders from those giants, we should be clear... it seems a mess from down there though.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 6, 0, 'Their numbers are unbelievable... I\'m glad to hear King Stormheart came to your aid. I only hear good things of him - I think it\'s high time we meet.', 12, 0, 100, 0, 0, 0, 31029, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 7, 0, 'Poor Creteus. He was a good keeper... I\'m glad he at least got to see his task to completion, I imagine that\'s all that really mattered to him.', 12, 0, 100, 0, 0, 0, 31030, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 8, 0, 'We\'re coming up on Frosthold. I would be very appreciative if you would introduce me to King Stormeheart before you go. I believe we are both very much in his debt.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30115, 3, 0, 'Настал великий день для всех, кто сражается с Королем-личом! $GОтважный:Отважная; $n $Gвосстановил:восстановила; древний клинок Кель\'Делар во имя победы Серебряного союза!', 14, 0, 100, 5, 0, 0, 38035, 261000, 261000, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (30124, 0, 0, 'О, привет, $gмаленький:маленькая:r; |3-6($r).', 12, 0, 100, 0, 0, 0, 31148, 0, 0, 0, 'Храппи to Player');
INSERT INTO `creature_text` VALUES (30146, 0, 0, 'Curse you! You will not treat me like a beast!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 0, 1, 'Enough! I will teach you some manners, wench!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 0, 2, 'I\'d rather die fighting than live like a slave!', 12, 0, 100, 1, 0, 0, 30795, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 0, 'Back... to work...', 12, 0, 100, 1, 0, 0, 30796, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 1, 'We will have revenge... some day.', 12, 0, 100, 1, 0, 0, 30798, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 2, 'You treat us worse than animals!', 12, 0, 100, 1, 0, 0, 30797, 0, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30154, 0, 0, 'I\'m not afraid of anything - let\'s get started!', 12, 0, 100, 0, 0, 0, 30807, 0, 0, 0, 'Агнетта Тирсдоттар to Player');
INSERT INTO `creature_text` VALUES (30163, 3, 0, 'Дворфы Закаленных Бурей появляются на месте изломанного мертвого великана!', 16, 0, 100, 0, 0, 0, 30892, 25000, 25000, 56239, 'Мертвый железный великан to Player');
INSERT INTO `creature_text` VALUES (30190, 0, 0, 'Beginning analysis ...', 12, 0, 50, 0, 0, 0, 30848, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 1, 0, 'Specimen identified as an alloy of saronite and iron.', 12, 0, 50, 0, 0, 0, 30849, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 2, 0, 'Metallic specimen of foreign origin detected.', 12, 0, 50, 0, 0, 0, 30847, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 3, 0, 'The ore used in this sample originated deep within the crust of Azeroth and was recently unearthed.', 12, 0, 50, 0, 0, 0, 30850, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 4, 0, 'The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth.', 12, 0, 50, 0, 0, 0, 30851, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 5, 0, 'This ore can only have been created by a powerful, malevolent force. Attempting to access databanks for more information ...', 12, 0, 50, 0, 0, 0, 30854, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 6, 0, 'Access denied?!', 12, 0, 50, 0, 0, 0, 30859, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 7, 0, 'Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken.', 12, 0, 50, 0, 0, 0, 30860, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 8, 0, 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', 12, 0, 50, 0, 0, 0, 30867, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30190, 9, 0, 'Directive completed. Returning to standby mode.', 12, 0, 50, 0, 0, 0, 30861, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30219, 0, 0, 'Ворг начинает искать врагов.', 16, 0, 100, 0, 0, 1108, 30909, 0, 0, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'Ворг останавливается на мгновение – и бросается в другую сторону.', 16, 0, 100, 0, 0, 0, 30911, 18000, 18000, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'Ледяной ворг начинает выть, обнаружив жертву!', 16, 0, 100, 53, 0, 9036, 30915, 51000, 51000, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'На секунду замерев, ворг возобновляет охоту.', 16, 0, 100, 0, 0, 0, 30912, 20000, 20000, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'Почуяв что-то, ворг обнажает клыки и пускается по следу.', 16, 0, 100, 0, 0, 0, 30913, 35000, 35000, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'Призрачный ледяной ворг без колебаний отправляется на поиски.', 16, 0, 100, 0, 0, 1108, 30910, 0, 0, 31748, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30219, 3, 0, 'Рыча, ворг приближается к своей жертве.', 16, 0, 100, 0, 0, 0, 30914, 36000, 36000, 0, 'Призрачный ледяной ворг to Player');
INSERT INTO `creature_text` VALUES (30222, 3, 0, 'Видишь меня, да? А теперь ты умрешь!', 12, 0, 100, 0, 0, 0, 30916, 0, 0, 0, 'Секретный агент клана Закаленных Бурей to Призрачный ледяной ворг');
INSERT INTO `creature_text` VALUES (30222, 3, 0, 'Ты никому об этом не расскажешь, потому что будешь мертв!', 12, 0, 100, 0, 0, 0, 30918, 0, 0, 0, 'Секретный агент клана Закаленных Бурей to Призрачный ледяной ворг');
INSERT INTO `creature_text` VALUES (30222, 3, 0, 'Умри, друг льдов!', 12, 0, 100, 0, 0, 0, 30917, 0, 0, 0, 'Секретный агент клана Закаленных Бурей to Призрачный ледяной ворг');
INSERT INTO `creature_text` VALUES (30224, 0, 0, 'What\'s on yer mind, lad?', 12, 0, 100, 6, 0, 0, 31065, 0, 0, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (30250, 1, 0, 'Хараак фолн!', 12, 0, 100, 0, 0, 13541, 30506, 0, 0, 0, 'Валхальский варгул to Player');
INSERT INTO `creature_text` VALUES (30271, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 4583, 0, 0, 0, 'Galendror Whitewing');
INSERT INTO `creature_text` VALUES (30274, 0, 0, 'Thank you and farewell, friend. I must return to the Argent Vanguard.', 12, 0, 100, 0, 0, 0, 30980, 0, 0, 0, 'Freed Crusader Speach');
INSERT INTO `creature_text` VALUES (30275, 3, 0, '%s наносит вам удар когтями!', 42, 0, 100, 0, 0, 0, 32763, 268000, 273000, 56705, 'Дикий змей to Player');
INSERT INTO `creature_text` VALUES (30275, 3, 0, '%s, завизжав от боли и завертевшись волчком, хватает вас пастью!', 42, 0, 100, 0, 0, 0, 32813, 277000, 277000, 60865, 'Дикий змей to Player');
INSERT INTO `creature_text` VALUES (30275, 3, 0, 'ВЫ ПРОМАХНУЛИСЬ! НУЖНО РАСКРЫТЬ ПАСТЬ ДРАКОНА ШИРЕ!', 42, 0, 100, 0, 0, 0, 32795, 176000, 198000, 60587, 'Дикий змей to Player');
