INSERT INTO `creature_text` VALUES (30299, 0, 0, 'You didn\'t think that I was going to let you walk in here and take the Heart of the Storm, did you?', 12, 0, 100, 1, 0, 0, 30972, 0, 0, 0, 'Smart Text');
