INSERT INTO `creature_text` VALUES (30313, 2, 0, 'Mechanical servants appear to have turned against each other. Several attendants have been piled together in an unorganized manner.', 12, 0, 100, 15, 0, 0, 31500, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 3, 0, 'Remaining mechagnome guardians corrupted by unknown source.', 12, 0, 100, 15, 0, 0, 31501, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 4, 0, 'Watcher Mimir verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 31502, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 5, 0, 'Temple of Winter analysis commencing.', 12, 0, 100, 15, 0, 0, 31504, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 6, 0, 'Temple of Winter verified to be in-tact.', 12, 0, 100, 15, 0, 0, 31506, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 7, 0, 'Temple guardians verified to be deceased. Sulfurous odor suggests that death resulted from a fire-base entity.', 12, 0, 100, 15, 0, 0, 31507, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 8, 0, 'Previously established cold weather patterns at the temple have ceased.', 12, 0, 100, 15, 0, 0, 31508, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 9, 0, 'Watcher Hodir verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 31509, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 10, 0, 'Temple of Life analysis commencing.', 12, 0, 100, 15, 0, 0, 31515, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 11, 0, 'Temple of Life verified to be damaged beyond repair.', 12, 0, 100, 15, 0, 0, 31512, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 12, 0, 'Evidence indicates a significant battle. The opponent of Watcher Freya estimated to be of similar size and strength to Watcher Freya.', 12, 0, 100, 15, 0, 0, 31513, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 13, 0, 'Temple guardians are no longer present. Plant forms associated with temple are deceased.', 12, 0, 100, 15, 0, 0, 31514, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 14, 0, 'Watcher Freya verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 31510, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 15, 0, 'Temple of Order analysis commencing.', 12, 0, 100, 15, 0, 0, 31516, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 16, 0, 'Temple of Order verified to be in-tact.', 12, 0, 100, 15, 0, 0, 31517, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 17, 0, 'No indications of struggle are present. No guardians are present.', 12, 0, 100, 15, 0, 0, 31518, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 18, 0, 'Watcher Tyr verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 31519, 0, 0, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30377, 0, 0, 'You are dealing with a being that holds within it the consciousness of the most cunning, intelligent, and ruthless individuals to ever live.', 12, 0, 100, 1, 0, 0, 31005, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 1, 0, 'The Lich King is unlike any foe that you have ever faced, Highlord. Though you bested him upon the holy ground of Light\'s Hope Chapel, you tread now upon his domain.$B', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 2, 0, 'You cannot win. Not like this...$B', 12, 0, 100, 274, 0, 0, 0, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 3, 0, 'Nothing. There is nothing that you can do while the Light binds you. It controls you wholly, shackling you to the ground with its virtues.', 12, 0, 100, 274, 0, 0, 31009, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 4, 0, '%s shakes his head.', 16, 0, 100, 0, 0, 0, 31011, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 5, 0, 'Look upon the field, Highlord. The Lich King has halted your advance completely and won the upper hand!', 12, 0, 100, 25, 0, 0, 31012, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 6, 0, 'The breach you created was sealed with Nerubian webbing almost as quickly as it was opened.', 12, 0, 100, 1, 0, 0, 31013, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 7, 0, 'Your soldiers are being used as living shields to stave off artillery fire in the Valley of Echoes, allowing the forces of the Lich King to assault your base without impediment.', 12, 0, 100, 1, 0, 0, 31014, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 8, 0, 'The Lich King knows your boundaries, Highlord. He knows that you will not fire on your own men. Do you not understand? He has no boundaries. No rules to abide. $B', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 9, 0, 'Then you have lost, Highlord.$B', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30385, 3, 0, 'I am the chosen one!', 14, 0, 100, 0, 0, 14345, 78566, 25000, 25000, 0, 'Сумеречный доброволец to Джедога Искательница Теней');
INSERT INTO `creature_text` VALUES (30385, 3, 0, 'I am the chosen one!!', 14, 0, 100, 0, 0, 14344, 78566, 21000, 21000, 56868, 'Сумеречный доброволец to Джедога Искательница Теней');
INSERT INTO `creature_text` VALUES (30385, 3, 0, 'I betray myself to the lord!', 14, 0, 100, 0, 0, 14347, 30863, 35000, 35000, 0, 'Сумеречный доброволец to Джедога Искательница Теней');
INSERT INTO `creature_text` VALUES (30395, 0, 0, 'How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here.', 12, 0, 100, 0, 0, 0, 31152, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 1, 0, 'My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest.', 12, 0, 100, 0, 0, 0, 31151, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 2, 0, 'I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next.', 12, 0, 100, 0, 0, 0, 31154, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 3, 0, 'It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger?', 12, 0, 100, 0, 0, 0, 31155, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 4, 0, 'He sought an artifact... a horn, I think. He left the village on a long journey in search of it.', 12, 0, 100, 0, 0, 0, 31153, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 5, 0, 'The disturbances in the tomb, they must be involved. Look around you, $n. Do you see them?', 12, 0, 100, 0, 0, 0, 31156, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30395, 6, 0, 'Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger?', 12, 0, 100, 0, 0, 0, 31157, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 0, 0, 'You seem eager to join your beloved Sif, brother.', 12, 0, 100, 0, 0, 0, 31082, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 1, 0, 'ENOUGH!', 14, 0, 100, 0, 0, 0, 108414, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 2, 0, 'Looks like you can still best me in a fair fight, little brother.', 12, 0, 100, 0, 0, 0, 31110, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 3, 0, 'Unfortunately for you.... this fight is anything but fair!', 12, 0, 100, 0, 0, 0, 31111, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 4, 0, 'Thanks to your little friend, you\'ve left the Temple of Storms, where you were at your strongest. Instead we fight closer to my master\'s power.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 5, 0, 'Minions! Take my brother and his drake to their new dwellings. Make sure the beast makes it alive. I have special plans for the broodmother.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 6, 0, 'As for your life, mortal. I will be generous. After all... why would I destroy my most useful servant? I waited for you for weeks inside that Hyldnir mine.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30396, 7, 0, 'The shape wasn\'t mine, of course. And had you not been so reckless, you would\'ve seen past my illusion. But you came through for me, and for that... I must thank you!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30399, 0, 0, 'It is time I put an end to my brother\'s madness.', 12, 0, 100, 0, 0, 0, 31078, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30399, 1, 0, 'Loken!!', 14, 0, 100, 0, 0, 0, 31079, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30399, 2, 0, 'Stand up and fight me, you worthless traitor!', 14, 0, 100, 0, 0, 0, 31080, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30399, 3, 0, 'Do not dare..', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30399, 4, 0, 'Speak...', 12, 0, 100, 0, 0, 0, 31116, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30399, 5, 0, 'Her name!!', 12, 0, 100, 0, 0, 0, 31117, 0, 0, 0, 'Thorim Quest');
INSERT INTO `creature_text` VALUES (30401, 0, 0, 'King Stormheart is putting you to the test, eh?  He must see something in you to begin with or I doubt he\'d put you through such a sacred ritual.', 12, 0, 100, 0, 0, 0, 30942, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 1, 0, 'I know you\'re new to our kind, so I\'ll catch you up a bit while we\'re on our way over.', 12, 0, 100, 0, 0, 0, 30943, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 2, 0, 'Years back, my father and several other frostborn were returning from a trek across Dragonblight. There was a heavy blizzard... far worse than we\'ve ever seen since', 12, 0, 100, 0, 0, 0, 30944, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 3, 0, 'They crossed a trail of blood-soaked snow and followed it to find a dwarf wandering and speaking in a dialect they couldn\'t make out... and not a dwarf of our kind mind you, but a mountain dwarf - something our kind had never seen before.', 12, 0, 100, 0, 0, 0, 30945, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 4, 0, 'The dwarf seemed lost, having no memory of where he came from, or even of his own name. Not being the kind to leave a dwarven cousin to die in the snow, my father\'s party took him in and continued back towards Frosthold.', 12, 0, 100, 0, 0, 0, 30946, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 5, 0, 'Not long later, out of nowhere, the snow burst before them and a jormungar the size of Veranus herself came down upon their party... one of them was swallowed whole before they even had time to react.', 12, 0, 100, 0, 0, 0, 30947, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 6, 0, 'My father thought they were all doomed... but behind him, a furious roar rumbled across the snow, and he turned to see the mountain dwarf growing in size, his skin taking on a stone-like texture, and his hands sizzling with lightning.', 12, 0, 100, 0, 0, 0, 30948, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 7, 0, 'The dwarf barreled forward with a sound like rolling thunder and hurled a shining metal hammer, lightning coursing over its surface, directly into the jormungar\'s throat.', 12, 0, 100, 0, 0, 0, 30949, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 8, 0, 'The jormungar collapsed instantly, its head barely still attached to its convulsing body.  My father turned to the dwarf in awe and raised a fist in praise...', 12, 0, 100, 0, 0, 0, 30950, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 9, 0, 'The stranger having no name of his own, my father deemed \"Yorg,\" a name reserved for champions of legend. Years later, he now stands before us as Yorg Stormheart, King of the Frostborn.', 12, 0, 100, 0, 0, 0, 30952, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 10, 0, 'King Stormheart has trained us well... turned us into even more fearsome warriors than we could have boasted during the time of our war with the Frost Giants.', 12, 0, 100, 0, 0, 0, 30953, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 11, 0, 'And as one of the fiercest tests put upon a warrior of the frostborn, we are made to face a creature far larger than ourselves--giants, dragons, jormungar--as a testament to the fact that size will never be our weakness', 12, 0, 100, 0, 0, 0, 30955, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 12, 0, 'This is the test put before you this day. Return to us only once The Iron Watcher is dead, and be revered as a warrior of the frostborn.', 14, 0, 100, 0, 0, 0, 30956, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 13, 0, 'He is slow from the rust of the ages... be quick on your feet and he will not best you. You have King Stormheart\'s favor - do not disappoint.', 14, 0, 100, 0, 0, 0, 31343, 0, 0, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30403, 0, 0, 'As you jump into Nergeld, the rotting flesh of the construct comes to life.', 16, 0, 100, 0, 0, 0, 31120, 0, 0, 0, 'Nergeld');
INSERT INTO `creature_text` VALUES (30403, 1, 0, '%s falls to pieces.', 16, 0, 100, 0, 0, 0, 31132, 0, 0, 0, 'Nergeld to Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 0, 0, 'WHO DARES?! I AM GOING TO FLAY THE FLESH FROM YOUR BONES EVEN IF I HAVE TO DESTROY NERGELD TO GET TO YOU!', 14, 0, 100, 0, 0, 0, 31134, 0, 0, 0, 'Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 1, 0, 'Now the nightmare\'s real.Now Dr. Terrible is here.To make you quake with fear.To make the whole world kneel.And I won\'t feel ...a thing', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 2, 0, 'I\'LL RISE AGAIN AND WHEN I DO I WILL HAVE MY REVENGE!.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Dr. Terrible to Nergeld');
INSERT INTO `creature_text` VALUES (30405, 0, 0, 'By all the gods... it can\'t be... Muradin?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 1, 0, 'Come on boy, there\'s no mistak\'n it - it\'s definately you. Don\'t ya recognize your younger brother?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 2, 0, 'I can\'t believe this! You were dead! All accounts said so... what happened, Muradin. How did you get here?', 12, 0, 100, 0, 0, 0, 31034, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 3, 0, 'Indeed! Magni will be so happy to see you too! He\'s gotten nothing but bad news for a long time now, but this changes everything!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 4, 0, 'He\'s here in Northrend, brother, looking for you. A seer in Wintergarde brought word that you were not dead, and he left Ironforge immediately to come find you.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 5, 0, 'Speaking of which...', 12, 0, 100, 0, 0, 0, 31087, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 6, 0, 'That\'s in the past, Muradin. Regrets won\'t change anything.', 12, 0, 100, 0, 0, 0, 31096, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 7, 0, 'Aye, be safe Muradin. I\'d join you, but I\'m on top of the most amazing discovery the world has yet seen. I can\'t abandon it now.', 12, 0, 100, 0, 0, 0, 31102, 0, 0, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30407, 0, 0, 'Let\'s get out of here! Quickly!', 12, 0, 100, 0, 0, 0, 31115, 0, 0, 0, 'Captured Crusader Start');
INSERT INTO `creature_text` VALUES (30407, 1, 0, 'Faster, hero! We must get back to the Argent Vanguard and warn them of the impending attack!', 12, 0, 100, 0, 0, 0, 31122, 0, 0, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 1, 'There are hundreds of them! Maybe thousands! They will attack at any moment! I\'m sure of it!', 12, 0, 100, 0, 0, 0, 31123, 0, 0, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 2, 'Had you not come along I would have died for sure!', 12, 0, 100, 0, 0, 0, 31124, 0, 0, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 3, 'They\'re planning a huge attack! We need to warn Highlord Fordring!', 12, 0, 100, 2, 0, 0, 31121, 0, 0, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 2, 0, 'Thank you, hero.', 12, 0, 100, 2, 0, 0, 57489, 0, 0, 0, 'Captured Crusader End');
INSERT INTO `creature_text` VALUES (30407, 2, 1, 'Thank you, friend, but we\'re not out of trouble yet!', 12, 0, 100, 2, 0, 0, 31114, 0, 0, 0, 'Captured Crusader End');
INSERT INTO `creature_text` VALUES (30408, 0, 0, 'What\'s that? You talkin\' to me, lad?', 12, 0, 100, 0, 0, 0, 31037, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 1, 0, 'My brother... yes... I do have brothers...', 12, 0, 100, 0, 0, 0, 31038, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 2, 0, 'Muradin clutches his head and reels for a moment as the memories rush back to him.', 16, 0, 100, 0, 0, 0, 31039, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 3, 0, '...Brann?', 12, 0, 100, 0, 0, 0, 31040, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 4, 0, 'I... I dunno, Brann. I\'ve been \'ere a long time... all I \'ave of me life before this place are flashes and nightmares.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 5, 0, 'It\'s good te see you though, brother. More than words can say.', 12, 0, 100, 0, 0, 0, 31043, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 6, 0, 'Magn! Forgive me, the memories are comin\' back slowly, brother.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 7, 0, 'The frostborn have been very good to me. They\'re strong people.', 12, 0, 100, 0, 0, 0, 31048, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 8, 0, 'Not much, Magni. I\'ve had nightmares of a human... tall... light hair... death black armor. His name rests on the tip of me tongue, but...', 12, 0, 100, 0, 0, 0, 31089, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 9, 0, '...Arthas.', 12, 0, 100, 0, 0, 0, 31091, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 10, 0, 'Aye, I know. I watched him turn... I watched him give up all that was right and I didn\'t lift a hand... I didn\'t even consider it until it was too late.', 12, 0, 100, 0, 0, 0, 31094, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 11, 0, 'No... no they won\'t. But I can make this right. I have te. I\'m goin\' after the boy. I\'ll make\'m answer for everything he\'s done.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 12, 0, 'I\'m sure, Magni. I\'ll see this through, don\'t ya worry.', 12, 0, 100, 0, 0, 0, 31100, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 13, 0, 'Go Brann. Bring back an epic tale when we meet again. Farewell for now, brothers...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30409, 0, 0, 'You\'ve come for the doctor\'s plans! You\'ll only find death!', 12, 0, 100, 0, 0, 0, 31119, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30409, 1, 0, 'The doctor entrusted me with the plans to Nergeld, our flesh giant amalgamation made entirely of vargul! It will be the most powerful creation of its kind and a whole legion of them will be created to destroy your pitiful forces!', 12, 0, 100, 0, 0, 0, 31090, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (30411, 0, 0, 'Look, Lagnus, I consider you a capable man, but my patience is wearing thin. I know that Muradin is here, can you point me to him or not?', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 1, 0, 'Brother! There you are! I can barely believe my eyes... you\'re alive!', 14, 0, 100, 0, 0, 0, 31085, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 2, 0, 'It\'s so good to see you again, Muradin. And what\'s this I heard about you being a King in your own right now? The Bronzebeards were always destined to greatness.', 12, 0, 100, 0, 0, 0, 31086, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 3, 0, 'So it seems! And you haven\'t lost any muscle yourself. Do you remember anything of what happened, Muradin? Fate as turned ill in your absence.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 4, 0, 'Magni nods.', 16, 0, 100, 0, 0, 0, 31092, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 5, 0, 'He\'s not the boy of your memories anymore, Muradin. He\'s become something else entirely.', 12, 0, 100, 0, 0, 0, 31093, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 6, 0, 'Are you sure Muradin? I just got you back after years of thinking you were dead. I do not want to lose you again.', 12, 0, 100, 0, 0, 0, 31099, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 7, 0, 'So be it then. I have to return to my people, brothers. Come back to me in one piece.', 12, 0, 100, 0, 0, 0, 31101, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 8, 0, '...farewell brother.', 12, 0, 100, 0, 0, 0, 31129, 0, 0, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30449, 1, 0, 'Вы безобидны, ничтожные создания. Покажите, на что вы способны!', 14, 0, 100, 0, 0, 14133, 52120, 0, 0, 0, 'Весперон to Player');
INSERT INTO `creature_text` VALUES (30449, 2, 0, 'I ... can still ... fight ...', 14, 0, 100, 0, 0, 14140, 32009, 0, 0, 0, 'Весперон to Player');
INSERT INTO `creature_text` VALUES (30449, 4, 0, 'В Сумраке появляется ученик Весперона!', 41, 0, 100, 0, 0, 0, 32960, 18266, 18266, 0, 'Весперон');
INSERT INTO `creature_text` VALUES (30451, 1, 0, 'I\'m not afraid of anything! Especially - you!', 14, 0, 100, 0, 0, 14111, 31985, 0, 0, 0, 'Шадрон to Player');
INSERT INTO `creature_text` VALUES (30451, 2, 0, 'Мы... выше всех! Как это... возможно?..', 14, 0, 100, 0, 0, 14118, 31984, 0, 0, 0, 'Шадрон to Player');
INSERT INTO `creature_text` VALUES (30451, 3, 0, 'I will regret you, Sartarion, but only this time.', 14, 0, 100, 0, 0, 14117, 31991, 85250, 85250, 0, 'Шадрон to Сартарион');
INSERT INTO `creature_text` VALUES (30451, 4, 0, '%s начинает открывать сумеречный портал!', 41, 0, 100, 0, 0, 0, 32554, 11156, 11156, 0, 'Шадрон');
INSERT INTO `creature_text` VALUES (30452, 1, 0, 'Вам здесь не место! Ваше место среди усопших!', 14, 0, 100, 0, 0, 14122, 31993, 0, 0, 0, 'Тенеброн to Player');
INSERT INTO `creature_text` VALUES (30452, 2, 0, 'Не надо... было... вмешиваться.', 14, 0, 100, 0, 0, 14129, 32000, 0, 0, 0, 'Тенеброн to Player');
INSERT INTO `creature_text` VALUES (30452, 3, 0, 'Занятно наблюдать, как ты сражаешься. Хорошо, смотри, как надо это делать.', 14, 0, 100, 0, 0, 14128, 31999, 39000, 39000, 0, 'Тенеброн to Сартарион');
INSERT INTO `creature_text` VALUES (30452, 4, 0, 'Тенеброн начинает высиживать кладку в Зоне сумерек!', 41, 0, 100, 0, 0, 0, 32959, 23313, 23313, 0, 'Тенеброн');
INSERT INTO `creature_text` VALUES (30461, 0, 0, '%s lets out a bellowing roar as she descends upon the nest.', 16, 0, 100, 15, 0, 0, 31158, 0, 0, 0, 'Veranus');
INSERT INTO `creature_text` VALUES (30462, 0, 0, 'Look out below!', 14, 0, 100, 0, 0, 0, 31159, 0, 0, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 1, 0, 'Easy there, girl!  Don\'t you recognize your old master?', 12, 0, 100, 0, 0, 0, 31160, 0, 0, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 2, 0, 'I will see you at the Temple of Storms. Looks like I\'m going to have to break her in again.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30475, 0, 0, 'Pray to your gods for a quick death.', 14, 0, 100, 1, 0, 0, 31213, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (30549, 0, 0, 'Хватит болтать. Готовьтесь к бою. Мы сотрем их в порошок.', 14, 0, 100, 0, 0, 14577, 13058, 0, 0, 0, 'Барон Ривендер to 78116');
INSERT INTO `creature_text` VALUES (30549, 1, 0, 'Не расточайте свой гнев попусту. Взращивайте ярость. Скоро вы сможете дать им выход.', 14, 0, 100, 0, 0, 14578, 13059, 0, 0, 0, 'Барон Ривендер to 78116');
INSERT INTO `creature_text` VALUES (30549, 2, 0, 'Смерть... меня не остановит.', 12, 0, 100, 0, 0, 8900, 33116, 0, 0, 0, 'Барон Ривендер to Player');
INSERT INTO `creature_text` VALUES (30551, 3, 0, 'По-моему, все это сильно смахивает на паранойю, вы не находите?', 12, 0, 100, 6, 0, 0, 31324, 48417, 48417, 0, 'Певчий Форрестен to Player');
INSERT INTO `creature_text` VALUES (30571, 3, 0, 'И что вы делаете в моей кладовой? И без того припасы на исходе, не хватало еще, чтобы какие-то чужаки рылись в моем добре! Кыш! Кыш отсюда!', 12, 0, 100, 5, 0, 0, 31323, 34112, 34112, 0, 'Майкл Белфаст to Player');
INSERT INTO `creature_text` VALUES (30658, 0, 0, 'Prison guards, we are leaving! These adventurers are taking over! Go go go!', 14, 0, 100, 0, 0, 0, 31474, 0, 0, 0, 'sinclari SAY_SINCLARI_1');
INSERT INTO `creature_text` VALUES (30660, 0, 0, 'Портал стабилизировался! В атаку!', 14, 0, 100, 0, 0, 0, 31524, 0, 0, 0, 'Страж портала to Портал');
INSERT INTO `creature_text` VALUES (30660, 0, 0, 'Путь в Даларан открыт!', 14, 0, 100, 0, 0, 0, 31657, 0, 0, 0, 'Страж портала to Портал');
INSERT INTO `creature_text` VALUES (30660, 0, 0, 'Разрушение Даларана неизбежно!', 14, 0, 100, 0, 0, 0, 31658, 0, 0, 0, 'Страж портала to Портал');
INSERT INTO `creature_text` VALUES (30660, 1, 0, 'Это твоя последняя схватка!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30660, 1, 0, 'Этот портал я буду защищать даже ценой своей жизни!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30660, 3, 0, 'Тебе не разрушить этот портал!', 14, 0, 100, 0, 0, 0, 0, 4000, 4000, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30660, 4, 0, 'Моя смерть не остановит это вторжение!', 14, 0, 100, 0, 0, 0, 0, 18000, 18000, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30660, 4, 0, 'На месте этого возникнут другие порталы!', 14, 0, 100, 0, 0, 0, 0, 13000, 13000, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30660, 4, 0, 'Почему ты защищаешь Кирин-Тор...', 14, 0, 100, 0, 0, 0, 0, 12000, 12000, 58508, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (30661, 2, 0, 'Магия должна... быть ограничена...', 12, 0, 100, 0, 0, 0, 31649, 0, 0, 0, 'Лазурный захватчик to Player');
INSERT INTO `creature_text` VALUES (30661, 4, 0, 'Всех нас не остановить!', 12, 0, 100, 0, 0, 0, 31650, 5000, 5000, 0, 'Лазурный захватчик to Player');
INSERT INTO `creature_text` VALUES (30665, 4, 0, 'Войну Нексуса не остановить!', 12, 0, 100, 0, 0, 0, 31536, 3000, 3000, 0, 'Опытный охотник на магов to Player');
INSERT INTO `creature_text` VALUES (30666, 0, 0, 'Кирин-Тор нужно остановить!', 12, 0, 100, 0, 0, 0, 31648, 0, 0, 0, 'Лазурный капитан to Player');
INSERT INTO `creature_text` VALUES (30666, 1, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Лазурный капитан to Player');
INSERT INTO `creature_text` VALUES (30666, 2, 0, 'Магия должна... быть ограничена...', 12, 0, 100, 0, 0, 0, 31649, 0, 0, 0, 'Лазурный капитан to Player');
INSERT INTO `creature_text` VALUES (30667, 0, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Лазурная колдунья to Player');
INSERT INTO `creature_text` VALUES (30667, 1, 0, 'За Хранителя Магии!', 12, 0, 100, 0, 0, 0, 31535, 0, 0, 0, 'Лазурная колдунья to Player');
INSERT INTO `creature_text` VALUES (30667, 2, 0, 'Кирин-Тор нужно остановить!', 12, 0, 100, 0, 0, 0, 31648, 0, 0, 0, 'Лазурная колдунья to Player');
INSERT INTO `creature_text` VALUES (30667, 3, 0, 'Смерть и уничтожение всем, кто против нас!', 12, 0, 100, 0, 0, 0, 31647, 0, 0, 0, 'Лазурная колдунья to Player');
INSERT INTO `creature_text` VALUES (30668, 0, 0, 'За Хранителя Магии!', 12, 0, 100, 0, 0, 0, 31535, 0, 0, 0, 'Лазурный налетчик to Player');
INSERT INTO `creature_text` VALUES (30668, 1, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Лазурный налетчик to Player');
INSERT INTO `creature_text` VALUES (30695, 0, 0, 'Портал стабилизировался! В атаку!', 14, 0, 100, 0, 0, 0, 31524, 0, 0, 0, 'Хранитель портала to Портал');
INSERT INTO `creature_text` VALUES (30695, 0, 0, 'Путь в Даларан открыт!', 14, 0, 100, 0, 0, 0, 31657, 0, 0, 0, 'Хранитель портала to Портал');
INSERT INTO `creature_text` VALUES (30695, 0, 0, 'Разрушение Даларана неизбежно!', 14, 0, 100, 0, 0, 0, 31658, 0, 0, 0, 'Хранитель портала to Портал');
INSERT INTO `creature_text` VALUES (30695, 3, 0, 'Тебе не разрушить этот портал!', 14, 0, 100, 0, 0, 0, 0, 1000, 1000, 0, 'Хранитель портала to Player');
INSERT INTO `creature_text` VALUES (30695, 3, 0, 'Это твоя последняя схватка!', 14, 0, 100, 0, 0, 0, 0, 1000, 1000, 0, 'Хранитель портала to Player');
INSERT INTO `creature_text` VALUES (30695, 3, 0, 'Этот портал я буду защищать даже ценой своей жизни!', 14, 0, 100, 0, 0, 0, 0, 1000, 1000, 0, 'Хранитель портала to Player');
INSERT INTO `creature_text` VALUES (30695, 4, 0, 'Моя смерть не остановит это вторжение!', 14, 0, 100, 0, 0, 0, 0, 12000, 12000, 0, 'Хранитель портала to Player');
INSERT INTO `creature_text` VALUES (30695, 4, 0, 'На месте этого возникнут другие порталы!', 14, 0, 100, 0, 0, 0, 0, 17000, 17000, 0, 'Хранитель портала to Player');
INSERT INTO `creature_text` VALUES (30695, 4, 0, 'Почему ты защищаешь Кирин-Тор...', 14, 0, 100, 0, 0, 0, 0, 14000, 14000, 0, 'Хранитель портала to Player');
