INSERT INTO `creature_text` VALUES (31135, 4, 4, '$N has defeated the fallen heroes of Valhalas battles past. This is only a beginning, but it will suffice.', 14, 0, 0, 0, 0, 14998, 31755, 0, 0, 0, 'Greiff quest 1 say 5');
