INSERT INTO `creature_text` VALUES (31397, 0, 5, 'NO! You\'re wrong! The voices in my head are beautiful!', 14, 0, 0, 1, 0, 0, 32470, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (31426, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 4583, 0, 0, 0, 'Doras');
INSERT INTO `creature_text` VALUES (31810, 0, 0, 'Right. Let\'\'s see if we can finish this keystone.', 12, 0, 100, 1, 0, 0, 32385, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (31810, 1, 0, 'The keystone is completed! We\'ve done it, $n!', 12, 0, 100, 5, 0, 0, 32391, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (31810, 2, 0, 'Who knows what secrets await within Ulduar\'s archives? I\'m off to discover them. Thank you again for your help, $n. Without it, I\'d still be searching for that key.', 12, 0, 100, 6, 0, 0, 32393, 0, 0, 0, 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES (31814, 0, 0, 'Welcome, Brann Bronzebeard. I am all that remains of this temple\'s guardian.', 12, 0, 100, 0, 0, 0, 32386, 0, 0, 0, 'Distant Voice');
INSERT INTO `creature_text` VALUES (31814, 1, 0, 'You have proven yourself as an explorer and seeker of knowledge.', 12, 0, 100, 0, 0, 0, 32387, 0, 0, 0, 'Distant Voice');
INSERT INTO `creature_text` VALUES (31814, 2, 0, 'You have been a steadfast ally to the Earthen.', 12, 0, 100, 0, 0, 0, 32388, 0, 0, 0, 'Distant Voice');
INSERT INTO `creature_text` VALUES (31814, 3, 0, 'You and your companion have proven yourselves in combat and in the pursuit of the keystone.', 12, 0, 100, 0, 0, 0, 32389, 0, 0, 0, 'Distant Voice');
INSERT INTO `creature_text` VALUES (31814, 4, 0, 'Your motives are pure. The keystone\'s parts shall be reunited. Go to Ulduar and learn the answers to your questions, Brann Bronzebeard.', 12, 0, 100, 0, 0, 0, 32390, 0, 0, 0, 'Distant Voice');
INSERT INTO `creature_text` VALUES (31832, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (31833, 0, 0, 'On your feet, men! Move, move move!', 12, 0, 100, 0, 0, 0, 32411, 0, 0, 0, 'Kor\'kron Squad Leader');
INSERT INTO `creature_text` VALUES (31833, 1, 0, 'Quickly, catch your breaths before we press to the gate!', 12, 0, 100, 0, 0, 0, 32410, 0, 0, 0, 'Kor\'kron Squad Leader');
INSERT INTO `creature_text` VALUES (31833, 2, 0, 'For the horde!', 14, 0, 100, 0, 0, 0, 143151, 0, 0, 0, 'Kor\'kron Squad Leader');
INSERT INTO `creature_text` VALUES (31833, 2, 1, 'You\'ve got some fight in you! Blood and honor, friend!', 12, 0, 100, 0, 0, 0, 32412, 0, 0, 0, 'Kor\'kron Squad Leader');
INSERT INTO `creature_text` VALUES (32149, 0, 0, 'At last... now I can rest.', 12, 0, 0, 1, 0, 0, 35291, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (32149, 0, 1, 'I can\'t hear the screams anymore. Is this the end?', 12, 0, 0, 1, 0, 0, 35294, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (32149, 0, 2, 'I\'m so tired. Just let me rest for a moment.', 12, 0, 0, 1, 0, 0, 35295, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (32149, 0, 3, 'It was awful... I dreamt I was fighting against my friends.', 12, 0, 0, 1, 0, 0, 35293, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (32149, 0, 4, 'My nightmare, is it finally over?', 12, 0, 0, 1, 0, 0, 35292, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (32184, 0, 0, 'Uninvited guests! Did you think you\'d go unnoticed inside my dominion?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 1, 0, 'I must confess... you were not altogether unexpected. I hope you find your final resting place... to your liking.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 2, 0, 'Last time we met, you had the advantage of fighting on holy ground. You\'ll find that our situation has been... reversed.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 3, 0, 'I call your bluff. You\'re a paladin after all. Your obsession with redemption goes beyond the inane.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 4, 0, 'You surely wouldn\'t destroy humanity\'s only chance to redeem its most wayward son. You\'d sooner die!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 5, 0, 'Arrrrggggggggggh!!!!', 14, 0, 100, 0, 0, 0, 32694, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32184, 6, 0, 'You... will pay for that, old man. Slay them all!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32191, 0, 0, 'Войну Нексуса не остановить!', 12, 0, 100, 0, 0, 0, 31536, 0, 0, 0, 'Лазурный ловец to Player');
INSERT INTO `creature_text` VALUES (32191, 1, 0, 'Кирин-Тор нужно остановить!', 12, 0, 100, 0, 0, 0, 31648, 0, 0, 0, 'Лазурный ловец to Player');
INSERT INTO `creature_text` VALUES (32191, 3, 0, 'Всех нас не остановить!', 12, 0, 100, 0, 0, 0, 31650, 0, 0, 0, 'Лазурный ловец to Player');
INSERT INTO `creature_text` VALUES (32204, 3, 0, 'Из портала появляется элитный отряд Легиона!', 41, 0, 100, 0, 0, 0, 104686, 75207, 604659, 0, 'Лейтенант Синклари to Лорд Малгат');
INSERT INTO `creature_text` VALUES (32204, 3, 0, 'Страж портала защищает новый портал!', 41, 0, 100, 0, 0, 0, 32995, 388452, 612916, 0, 'Лейтенант Синклари to Портал');
INSERT INTO `creature_text` VALUES (32204, 3, 0, 'Хранитель портала появляется из портала!', 41, 0, 100, 0, 0, 0, 32996, 50951, 705026, 0, 'Лейтенант Синклари to Портал');
INSERT INTO `creature_text` VALUES (32236, 0, 0, 'I feel funny all of a sudden. Er?!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 1, 'Wait a minute, I didn\'t activate my...?', 12, 0, 100, 1, 0, 0, 32631, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 2, 'Okay, who\'s the joker that threw an orb at me?', 12, 0, 100, 1, 0, 0, 32633, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 3, 'NOOOOOO', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 4, 'Not a sentry!', 12, 0, 100, 1, 0, 0, 32635, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 5, 'You\'ve got it all wrong. I\'m a subjugator!', 12, 0, 100, 1, 0, 0, 32636, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32236, 0, 6, 'It was an accident. I was framed. Don\'t drop me!', 12, 0, 100, 1, 0, 0, 32637, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32239, 0, 0, 'It is time. May the Light give us strength.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 1, 0, 'Keep your heads down and follow my lead.', 12, 0, 100, 0, 0, 0, 32616, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 2, 0, 'Here it comes. Stand ready.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 3, 0, 'Something\'s wrong... I sense a dark presence.', 12, 0, 100, 0, 0, 0, 32620, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 4, 0, 'The Lich King is here. May the Light guide our blades.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 5, 0, 'You sound a little too confident. Especially considering the way our last encounter ended.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 6, 0, 'That might be, but I don\'t need to stand on holy ground to run that disembodied heart of yours through with the Ashbringer.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 7, 0, 'The heart... the last remaining vestige of your humanity. I had to stop it from being destroyed. I had to see for myself. And at last I\'m sure...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32239, 8, 0, 'Only shadows from the past remain. There\'s nothing left to redeem!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32273, 0, 0, 'How dare you interfere with our work!', 14, 0, 100, 0, 0, 0, 32639, 0, 0, 0, 'Infinite Corruptor - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (32273, 1, 0, 'My time... has run out...', 14, 0, 100, 0, 0, 0, 32642, 0, 0, 0, 'Infinite Corruptor - SAY_DEATH');
INSERT INTO `creature_text` VALUES (32273, 2, 0, 'The process is finally complete! My work here is done.', 14, 0, 100, 0, 0, 0, 32666, 0, 0, 0, 'Infinite Corruptor - SAY_FAIL');
INSERT INTO `creature_text` VALUES (32295, 0, 0, 'I did what I had to, brother. You gave me no alternative.', 14, 0, 100, 0, 4000, 14406, 32626, 0, 0, 0, 'Alexstrasza - Yell text 1');
INSERT INTO `creature_text` VALUES (32295, 1, 0, 'And so ends the Nexus War. ', 14, 0, 100, 0, 4000, 14407, 32627, 0, 0, 0, 'Alexstrasza - Yell text 2');
INSERT INTO `creature_text` VALUES (32295, 2, 0, 'This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen.', 14, 0, 100, 0, 24000, 14408, 32628, 0, 0, 0, 'Alexstrasza - Yell text 3');
INSERT INTO `creature_text` VALUES (32295, 3, 0, 'The red dragonflight will take on the burden of mending the devastation wrought on Azeroth. Return home to your people and rest. Tomorrow will bring you new challenges, and you must be ready to face them. Life... goes on.', 14, 0, 100, 0, 22000, 14409, 32629, 0, 0, 0, 'Alexstrasza - Yell text 4');
INSERT INTO `creature_text` VALUES (32300, 0, 0, 'Too soon... too sooon....', 12, 0, 100, 1, 0, 0, 32792, 0, 0, 0, 'On Death Text');
INSERT INTO `creature_text` VALUES (32310, 0, 0, 'I hope you fellows don\'t mind if we crash this party. I brought some old friends with me!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32310, 1, 0, 'Looks like whatever Tirion did put some hurt on the Lich King. It\'s too bad we can\'t finish him off....', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32311, 0, 0, 'Take courage, crusaders. You do not fight alone!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32311, 1, 0, 'The Lich King is badly hurt. We ought to stay behind and finish him.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32312, 0, 0, 'Quick, through the portal! He won\'t stay down for long.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32312, 1, 0, 'Patience... we will get our chance soon enough. Be content that for once, it is Tirion who is in our debt.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (32451, 3, 0, 'Череп, украшающий этот топор, выглядит так, словно он голоден.', 12, 0, 100, 0, 0, 82821, 106402, 5371, 5371, 0, 'Житель Даларана to Player');
INSERT INTO `creature_text` VALUES (32638, 3, 0, 'А я-то думал, что мы друзья! Неужто ты так и бросишь Хакмуда?', 12, 0, 100, 6, 0, 0, 33018, 4399, 4399, 0, 'Хакмуд из Аргуса to Player');
INSERT INTO `creature_text` VALUES (32638, 3, 0, 'Ну, старина, и что теперь делать Хакмуду?', 12, 0, 100, 6, 0, 0, 33016, 9562, 9562, 0, 'Хакмуд из Аргуса to Player');
INSERT INTO `creature_text` VALUES (32638, 3, 0, 'Ну, старина, спасибочки!', 12, 0, 100, 6, 0, 0, 33014, 7066, 7066, 0, 'Хакмуд из Аргуса to Player');
INSERT INTO `creature_text` VALUES (32638, 3, 0, 'Слышь, старина, почему ты так обращаешься с Хакмудом?', 12, 0, 100, 6, 0, 0, 33015, 7051, 7051, 0, 'Хакмуд из Аргуса to Player');
INSERT INTO `creature_text` VALUES (32638, 3, 0, 'Хакмуд в таких условиях работать не может! Пока!', 12, 0, 100, 6, 0, 0, 33017, 45094, 45094, 0, 'Хакмуд из Аргуса to Player');
INSERT INTO `creature_text` VALUES (32639, 3, 0, 'Никто Гнимо не любит...', 12, 0, 100, 3, 0, 0, 33019, 4375, 4375, 0, 'Гнимо to Player');
INSERT INTO `creature_text` VALUES (32639, 3, 0, 'Пора Гнимо заткнуться...', 12, 0, 100, 3, 0, 0, 33021, 9890, 9890, 0, 'Гнимо to Player');
INSERT INTO `creature_text` VALUES (32639, 3, 0, 'Прощай! У Гнимо никогда не было такого друга, как ты!', 12, 0, 100, 3, 0, 0, 33020, 35125, 35125, 0, 'Гнимо to Player');
INSERT INTO `creature_text` VALUES (32641, 0, 0, 'Ага, понятно... Значит, ты и со мной обойдешься так же, а?', 12, 0, 100, 6, 0, 0, 33023, 0, 0, 0, 'Дрикс Злобноверт to Player');
INSERT INTO `creature_text` VALUES (32641, 3, 0, 'Нашей дружбе конец!!!', 12, 0, 100, 6, 0, 0, 33025, 15699, 15699, 0, 'Дрикс Злобноверт to Player');
INSERT INTO `creature_text` VALUES (32641, 3, 0, 'Ну да, конечно, всегда мечтал попасть на какой-нибудь необитаемый остров!', 12, 0, 100, 6, 0, 0, 33024, 104276, 104276, 0, 'Дрикс Злобноверт to Player');
INSERT INTO `creature_text` VALUES (32641, 3, 0, 'Что, ты так меня и бросишь? А как же я домой вернусь?', 12, 0, 100, 6, 0, 0, 33026, 25286, 25286, 0, 'Дрикс Злобноверт to Player');
INSERT INTO `creature_text` VALUES (32642, 3, 0, 'Плохое у тебя колдунство! Не говори потом, что Моджодишу не предупреждала!', 12, 0, 100, 1, 0, 0, 33028, 130844, 130844, 0, 'Моджодишу to Player');
INSERT INTO `creature_text` VALUES (32642, 3, 0, 'Слышь, ты че, так и бросишь Моджодишу?', 12, 0, 100, 1, 0, 0, 33027, 9810, 9810, 0, 'Моджодишу to Player');
INSERT INTO `creature_text` VALUES (32642, 3, 0, 'Так и будет!', 12, 0, 100, 1, 0, 0, 33029, 22168, 22168, 0, 'Моджодишу to Player');
INSERT INTO `creature_text` VALUES (32663, 3, 0, 'Действуй, я его оглушил!', 14, 0, 100, 0, 0, 0, 25668, 11110, 11110, 0, 'Бадд to Бадд');
INSERT INTO `creature_text` VALUES (32736, 0, 0, 'The view up here is amazing!', 12, 0, 0, 5, 0, 0, 33031, 0, 0, 0, 'Scribe Whitman');
INSERT INTO `creature_text` VALUES (32736, 0, 1, 'Too bad I left my light feathers at home... Slow Fall would work perfect here!', 12, 0, 0, 1, 0, 0, 33032, 0, 0, 0, 'Scribe Whitman');
INSERT INTO `creature_text` VALUES (32739, 0, 0, 'The view up here is amazing!', 12, 0, 0, 5, 0, 0, 33031, 0, 0, 0, 'Baroness Zildjia');
INSERT INTO `creature_text` VALUES (32739, 0, 1, 'Too bad I left my light feathers at home... Slow Fall would work perfect here!', 12, 0, 0, 1, 0, 0, 33032, 0, 0, 0, 'Baroness Zildjia');
INSERT INTO `creature_text` VALUES (32741, 0, 0, 'The view up here is amazing!', 12, 0, 0, 5, 0, 0, 33031, 0, 0, 0, 'Conjurer Weinhaus');
INSERT INTO `creature_text` VALUES (32741, 0, 1, 'Too bad I left my light feathers at home... Slow Fall would work perfect here!', 12, 0, 0, 1, 0, 0, 33032, 0, 0, 0, 'Conjurer Weinhaus');
INSERT INTO `creature_text` VALUES (32800, 0, 0, 'I\'ve had my fill of this place. Let us depart.', 12, 0, 100, 1, 0, 0, 31942, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32800, 1, 0, 'You must tell my brothers that I live.', 12, 0, 100, 1, 0, 0, 31944, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32800, 2, 0, 'I have you to thank for my life. I will return to my comrades and spread word of your bravery. Fight the Scourge with all the strength you can muster, and we will be by your side.', 12, 0, 100, 1, 0, 0, 31943, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (32845, 0, 0, 'Вы будете наказаны за это вторжение!', 14, 0, 100, 0, 0, 15552, 34336, 0, 0, 0, 'Ходир to Player');
INSERT INTO `creature_text` VALUES (32845, 1, 0, 'Да поглотят вас северные ветра!', 14, 0, 100, 0, 0, 15555, 117467, 0, 0, 0, 'Ходир to 0');
INSERT INTO `creature_text` VALUES (32845, 2, 0, '%s начинает читать заклинание \"Ледяная вспышка\"!', 41, 0, 100, 0, 0, 15555, 33314, 0, 0, 0, 'Ходир to 0');
INSERT INTO `creature_text` VALUES (32845, 3, 0, '%s получает эффект \"Ледяные дуновения\"!', 41, 0, 100, 0, 0, 15556, 33313, 0, 0, 0, 'Ходир to 0');
INSERT INTO `creature_text` VALUES (32845, 4, 0, 'Наконец-то я... свободен от его оков...', 14, 0, 100, 0, 0, 15557, 33484, 0, 0, 0, 'Ходир to 0');
INSERT INTO `creature_text` VALUES (32857, 0, 0, 'Кто бы вы ни были – жалкие бродяги или великие герои... Вы всего лишь смертные!', 14, 0, 100, 0, 0, 15684, 34314, 0, 0, 0, 'Буревестник Брундир to World Trigger');
INSERT INTO `creature_text` VALUES (32865, 0, 0, 'Interlopers! You mortals who dare to interfere with my sport will pay.... Wait--you...', 14, 0, 100, 0, 0, 15733, 33145, 0, 0, 0, 'Thorim SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (32865, 0, 1, 'I remember you... in the mountains.... But you... what is this? Where am--', 14, 0, 100, 0, 0, 15734, 33270, 0, 0, 0, 'Thorim SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (32865, 1, 0, 'Behold the power of the storms and despair!', 14, 0, 100, 0, 0, 15735, 0, 0, 0, 0, 'Thorim SAY_SPECIAL_1');
INSERT INTO `creature_text` VALUES (32865, 2, 0, 'Do not hold back! Destroy them!', 14, 0, 100, 0, 0, 15736, 34241, 0, 0, 0, 'Thorim SAY_SPECIAL_2');
INSERT INTO `creature_text` VALUES (32865, 3, 0, 'Have you begun to regret your intrusion?', 14, 0, 100, 0, 0, 15737, 0, 0, 0, 0, 'Thorim SAY_SPECIAL_3');
INSERT INTO `creature_text` VALUES (32865, 4, 0, 'Impertinent whelps, you dare challenge me atop my pedestal? I will crush you myself!', 14, 0, 100, 0, 0, 15738, 33148, 0, 0, 0, 'Thorim SAY_JUMPDOWN');
INSERT INTO `creature_text` VALUES (32865, 5, 0, 'Can\'t you at least put up a fight?', 14, 0, 100, 0, 0, 15739, 34239, 0, 0, 0, 'Thorim SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (32865, 5, 1, 'Pathetic.', 14, 0, 100, 0, 0, 15740, 35768, 0, 0, 0, 'Thorim SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (32865, 6, 0, 'My patience has reached its limit!', 14, 0, 100, 0, 0, 15741, 33365, 0, 0, 0, 'Thorim SAY_BERSERK');
INSERT INTO `creature_text` VALUES (32865, 7, 0, 'Failures!  Weaklings!', 14, 0, 100, 0, 0, 15742, 33274, 0, 0, 0, 'Thorim SAY_WIPE');
INSERT INTO `creature_text` VALUES (32865, 8, 0, 'Stay your arms! I yield!', 14, 0, 100, 0, 0, 15743, 33948, 0, 0, 0, 'Thorim SAY_DEATH');
INSERT INTO `creature_text` VALUES (32865, 9, 0, 'I feel as though I am awakening from a nightmare, but the shadows in this place yet linger.', 14, 0, 100, 0, 0, 15744, 33949, 0, 0, 0, 'Thorim SAY_END_NORMAL_1');
INSERT INTO `creature_text` VALUES (32865, 10, 0, 'Sif... was Sif here? Impossible--she died at my brother\'s hand. A dark nightmare indeed....', 14, 0, 100, 0, 0, 15745, 33950, 0, 0, 0, 'Thorim SAY_END_NORMAL_2');
INSERT INTO `creature_text` VALUES (32865, 11, 0, 'I need time to reflect.... I will aid your cause if you should require it. I owe you at least that much. Farewell.', 14, 0, 100, 0, 0, 15746, 33951, 0, 0, 0, 'Thorim SAY_END_NORMAL_3');
INSERT INTO `creature_text` VALUES (32865, 12, 0, 'You! Fiend, you are not my beloved! Begone!', 14, 0, 100, 0, 0, 15747, 33952, 0, 0, 0, 'Thorim SAY_END_HARD_1');
INSERT INTO `creature_text` VALUES (32865, 13, 0, 'Behold the hand behind all the evil that has befallen Ulduar, left my kingdom in ruins, corrupted my brother, and slain my wife.', 14, 0, 100, 0, 0, 15748, 33953, 0, 0, 0, 'Thorim SAY_END_HARD_2');
INSERT INTO `creature_text` VALUES (32865, 14, 0, 'And now it falls to you, champions, to avenge us all. The task before you is great, but I will lend you my aid as I am able. You must prevail.', 14, 0, 100, 0, 0, 15749, 33954, 0, 0, 0, 'Thorim SAY_END_HARD_3');
INSERT INTO `creature_text` VALUES (32867, 0, 0, 'Мое поражение лишь приблизит вашу погибель.', 14, 0, 100, 0, 0, 15678, 34325, 0, 0, 0, 'Сталелом to Сталелом');
INSERT INTO `creature_text` VALUES (32871, 0, 0, 'Translocation complete. Commencing planetary analysis of Azeroth.', 12, 0, 0, 0, 0, 15405, 34026, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES (32871, 1, 0, 'Stand back, mortals. I\'m not here to fight you.', 12, 0, 0, 0, 0, 15406, 34027, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES (32871, 2, 0, 'It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere.', 12, 0, 0, 0, 0, 15407, 34028, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_INTRO_3');
INSERT INTO `creature_text` VALUES (32871, 3, 0, 'Your actions are illogical. All possible results for this encounter have been calculated. The Pantheon will receive the Observer\'s message regardless of outcome.', 14, 0, 0, 0, 0, 15386, 33995, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_START_TIMER');
INSERT INTO `creature_text` VALUES (32871, 4, 0, 'See your world through my eyes: A universe so vast as to be immeasurable - incomprehensible even to your greatest minds.', 14, 0, 0, 1, 0, 15390, 33998, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_AGGRO');
INSERT INTO `creature_text` VALUES (32871, 5, 0, 'The stars come to my aid!', 14, 0, 0, 0, 0, 15392, 33245, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 6, 0, '%s begins to Summon Collapsing Stars!', 41, 0, 0, 0, 0, 0, 33244, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 7, 0, 'Witness the fury of the cosmos!', 14, 0, 0, 0, 0, 15396, 33269, 0, 0, 0, 'Algalon the Observer - SAY_ALGALONG_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 8, 0, '%s begins to cast Big Bang!', 41, 0, 0, 0, 0, 0, 34465, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 9, 0, 'You are out of time.', 14, 0, 0, 0, 0, 15394, 73002, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_ASCEND');
INSERT INTO `creature_text` VALUES (32871, 10, 0, '%s begins to cast Cosmic Smash!', 41, 0, 0, 0, 0, 0, 34500, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_COSMIC_SMASH');
INSERT INTO `creature_text` VALUES (32871, 11, 0, 'Behold the tools of creation!', 14, 0, 0, 0, 0, 15397, 33994, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_PHASE_TWO');
INSERT INTO `creature_text` VALUES (32871, 12, 0, 'I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do?', 14, 0, 0, 1, 0, 15393, 34000, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_OUTRO_1');
INSERT INTO `creature_text` VALUES (32871, 13, 0, 'Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed.', 14, 0, 0, 1, 0, 15401, 34012, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_OUTRO_2');
INSERT INTO `creature_text` VALUES (32871, 14, 0, 'I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore.', 14, 0, 0, 1, 0, 15402, 34013, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_OUTRO_3');
INSERT INTO `creature_text` VALUES (32871, 15, 0, 'I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies.', 14, 0, 0, 1, 0, 15403, 34016, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_OUTRO_4');
INSERT INTO `creature_text` VALUES (32871, 16, 0, 'Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world...', 14, 0, 0, 1, 0, 15404, 34018, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_OUTRO_5');
INSERT INTO `creature_text` VALUES (32871, 17, 0, 'Analysis complete. There is partial corruption in the planet\'s life-support systems as well as complete corruption in most of the planet\'s defense mechanisms.', 12, 0, 0, 0, 0, 15398, 34006, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_DESPAWN_1');
INSERT INTO `creature_text` VALUES (32871, 18, 0, 'Begin uplink: Reply Code: \'Omega\'. Planetary re-origination requested.', 12, 0, 0, 0, 0, 15399, 0, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_DESPAWN_2');
INSERT INTO `creature_text` VALUES (32871, 19, 0, 'Farewell, mortals. Your bravery is admirable, for such flawed creatures.', 12, 0, 0, 0, 0, 15400, 34015, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_DESPAWN_3');
INSERT INTO `creature_text` VALUES (32871, 20, 0, 'Loss of life unavoidable.', 14, 0, 0, 0, 0, 15387, 33996, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES (32871, 20, 1, 'I do what I must.', 14, 0, 0, 0, 0, 15388, 33997, 0, 0, 0, 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES (32873, 0, 0, '%s наделяет ближайших союзников руническим могуществом!', 41, 0, 100, 0, 0, 0, 33523, 0, 0, 0, 'Древний рунический великан to Player');
INSERT INTO `creature_text` VALUES (32906, 0, 0, 'Древни, дайте мне силы!', 14, 0, 100, 0, 0, 15527, 33520, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32906, 1, 0, 'Он больше не властен надо мной. Мой взор снова ясен. Благодарю вас, герои.', 14, 0, 100, 0, 0, 15531, 33524, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32906, 2, 0, 'Появились союзники природы!', 41, 0, 100, 0, 0, 15533, 33427, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32906, 3, 0, '|cFF00FFFFGarden of Life|r begins to glow!', 41, 0, 100, 0, 0, 0, 33370, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32906, 5, 0, 'Эонар, твоей прислужнице нужна помощь!', 14, 0, 100, 0, 0, 15528, 33517, 0, 0, 0, 'Фрейя to Фрейя');
INSERT INTO `creature_text` VALUES (32906, 13, 0, 'Фрейя начинает читать заклинание |cFFFF0000Дрожание земли!|r', 41, 0, 100, 0, 0, 0, 33312, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32906, 14, 0, 'Фрейя читает заклинание |cFF00FF00Крепкие железные корни!|r', 41, 0, 100, 0, 0, 0, 33991, 0, 0, 0, 'Фрейя to 0');
INSERT INTO `creature_text` VALUES (32916, 0, 0, '%s падает на землю и начинает восстанавливаться.', 16, 0, 100, 0, 0, 0, 33463, 0, 0, 0, 'Хватоплет to 0');
INSERT INTO `creature_text` VALUES (32919, 0, 0, '%s падает на землю и начинает восстанавливаться.', 16, 0, 100, 0, 0, 0, 33463, 0, 0, 0, 'Грозовой плеточник to 0');
INSERT INTO `creature_text` VALUES (32919, 2, 0, 'С помощью союзников %s полностью восстанавливается.', 16, 0, 100, 0, 0, 0, 33464, 0, 0, 0, 'Грозовой плеточник to Грозовой плеточник');
INSERT INTO `creature_text` VALUES (32927, 0, 0, 'И что вам дало мое поражение? Вы все так же обречены, смертные.', 14, 0, 100, 0, 0, 15663, 34334, 0, 0, 0, 'Мастер рун Молгейм to Player');
INSERT INTO `creature_text` VALUES (32930, 0, 0, 'None shall pass!', 14, 0, 100, 0, 0, 15586, 34133, 0, 0, 0, 'Kologarn SAY_AGGRO');
INSERT INTO `creature_text` VALUES (32930, 1, 0, 'KOL-THARISH!', 14, 0, 100, 0, 0, 15587, 34135, 0, 0, 0, 'Kologarn SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (32930, 1, 1, 'YOU FAIL!', 14, 0, 100, 0, 0, 15588, 34137, 0, 0, 0, 'Kologarn SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (32930, 2, 0, 'Just a scratch!', 14, 0, 100, 0, 0, 15589, 34143, 0, 0, 0, 'Kologarn SAY_LEFT_ARM_GONE');
INSERT INTO `creature_text` VALUES (32930, 3, 0, 'Only a flesh wound!', 14, 0, 100, 0, 0, 15590, 34144, 0, 0, 0, 'Kologarn SAY_RIGHT_ARM_GONE');
INSERT INTO `creature_text` VALUES (32930, 4, 0, 'OBLIVION!', 14, 0, 100, 0, 0, 15591, 34140, 0, 0, 0, 'Kologarn SAY_SHOCKWAVE');
INSERT INTO `creature_text` VALUES (32930, 5, 0, 'I will squeeze the life from you!', 14, 0, 100, 0, 0, 15592, 34138, 0, 0, 0, 'Kologarn SAY_GRAB_PLAYER');
INSERT INTO `creature_text` VALUES (32930, 6, 0, 'Master, they come....', 14, 0, 100, 0, 0, 15593, 34141, 0, 0, 0, 'Kologarn SAY_DEATH');
INSERT INTO `creature_text` VALUES (32930, 7, 0, 'I am invincible!', 14, 0, 100, 0, 0, 15594, 34142, 0, 0, 0, 'Kologarn SAY_BERSERK');
INSERT INTO `creature_text` VALUES (32930, 8, 0, 'Kologarn casts Stone Grip!', 41, 0, 100, 0, 0, 15592, 33864, 0, 0, 0, 'Kologarn EMOTE_STONE_GRIP');
INSERT INTO `creature_text` VALUES (32959, 0, 0, 'Анайя?.. Или мои глаза обманывают меня? Неужели это и вправду ты?', 12, 0, 100, 6, 0, 0, 1278, 0, 0, 0, 'Сerellean');
INSERT INTO `creature_text` VALUES (32959, 1, 0, 'Что судьба в жестокости своей позволит нам лишь это после тысячи лет разлуки..', 12, 0, 100, 1, 0, 0, 1280, 0, 0, 0, 'Сerellean');
INSERT INTO `creature_text` VALUES (32959, 2, 0, 'Нет! Анайя... Анайя! Не оставляй меня! Прошу тебя...', 12, 0, 100, 0, 0, 0, 1286, 0, 0, 0, 'Сerellean');
INSERT INTO `creature_text` VALUES (32959, 3, 0, 'Как же, любовь моя? Где взять силы прожить вечность без тебя?', 12, 0, 100, 6, 0, 0, 1287, 0, 0, 0, 'Сerellean');
INSERT INTO `creature_text` VALUES (32964, 0, 0, 'Thanks for the rescue. Let\'s get out of here!', 12, 0, 100, 1, 0, 0, 33456, 0, 0, 0, 'Sentinel Aynasha');
INSERT INTO `creature_text` VALUES (32964, 1, 0, 'Thanks for getting me out of there. I should be able to swim back from here, but before I go, one quick thing...', 12, 0, 100, 1, 0, 0, 33457, 0, 0, 0, 'Sentinel Aynasha');
INSERT INTO `creature_text` VALUES (32964, 2, 0, 'I was afforded a brief chance to attack their leader, and he was protected by some kind of magic. Make sure Morra hears of this before leading any kind of attack.', 12, 0, 100, 1, 0, 0, 33459, 0, 0, 0, 'Sentinel Aynasha');
INSERT INTO `creature_text` VALUES (32964, 3, 0, 'Farewell newcomer. May Elune\'s blessing be with you.', 12, 0, 100, 1, 0, 0, 33460, 0, 0, 0, 'Sentinel Aynasha');
INSERT INTO `creature_text` VALUES (32969, 0, 0, 'Гершал... Дентария будет счастлива видеть тебя.', 12, 0, 100, 0, 0, 0, 33258, 0, 0, 0, 'Lordanel Sentinel');
INSERT INTO `creature_text` VALUES (32969, 1, 0, 'Кириллиан! Ты жив!', 12, 0, 100, 0, 0, 0, 33248, 0, 0, 0, 'Lordanel Sentinel');
INSERT INTO `creature_text` VALUES (32973, 0, 0, 'Все ясно, часовой. Возвращайся на побережье и помоги доставить сюда всех, кто еще способен ходить.', 12, 0, 100, 0, 0, 0, 33861, 0, 0, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (33008, 0, 0, 'It\'s an angry male!', 42, 0, 100, 0, 0, 0, 33191, 0, 0, 0, 'Male Icepaw Bear');
INSERT INTO `creature_text` VALUES (33041, 0, 0, 'EVEN IN THIS WEAKENED FORM, I WILL DESTROY YOU!!!', 14, 0, 100, 1, 0, 0, 33237, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (33043, 4, 0, 'Благодарю за спасение.', 12, 0, 100, 1, 0, 0, 131568, 31000, 31000, 0, 'Обезумевший фурболг Чернолесья to Дух осквернения');
INSERT INTO `creature_text` VALUES (33044, 0, 0, 'Thank you for saving me.', 12, 0, 100, 1, 0, 0, 131568, 31000, 31000, 0, 'Зараженный фурболг Чернолесья');
INSERT INTO `creature_text` VALUES (33044, 0, 0, 'Благодарю за спасение.', 12, 0, 100, 1, 0, 0, 131568, 31000, 31000, 0, 'Зараженный фурболг Чернолесья');
INSERT INTO `creature_text` VALUES (33107, 0, 0, 'Goddess watch over you.', 12, 0, 100, 0, 0, 0, 33257, 0, 0, 0, 'Priestess Alinya');
INSERT INTO `creature_text` VALUES (33113, 0, 0, 'Общий системный сбой. Отказ защитных протоколов. Боевая единица \"Левиафан\" завершает свою работу.', 14, 0, 100, 0, 0, 15520, 33506, 0, 0, 0, 'Огненный Левиафан to Трофейный разрушитель');
INSERT INTO `creature_text` VALUES (33113, 1, 0, '%s наводится на |3-3($n).', 41, 0, 100, 0, 0, 0, 33502, 0, 0, 0, 'Огненный Левиафан to Player');
INSERT INTO `creature_text` VALUES (33113, 2, 0, 'Тревога. Сбой стационарных средств защиты. Система орбитального подавления отключена.', 14, 0, 100, 0, 0, 15511, 33492, 0, 0, 0, 'Огненный Левиафан to 0');
INSERT INTO `creature_text` VALUES (33113, 3, 0, 'Изменения в протоколе оценки угрозы. Уровень угрозы текущей цели нулевой. Поиск новой цели.', 14, 0, 100, 0, 0, 15521, 33507, 0, 0, 0, 'Огненный Левиафан to Трофейный чоппер');
INSERT INTO `creature_text` VALUES (33113, 4, 0, 'Обнаружены противники. Запуск протокола оценки угрозы. Главная цель выявлена. Повторный анализ через 30 секунд.', 14, 0, 100, 0, 0, 15506, 33487, 0, 0, 0, 'Огненный Левиафан to Трофейный разрушитель');
INSERT INTO `creature_text` VALUES (33115, 0, 0, 'Шальдин!', 12, 0, 100, 0, 0, 0, 33261, 0, 0, 0, 'Lordanel Sentinel');
INSERT INTO `creature_text` VALUES (33115, 1, 0, 'Жрицы, я только что получила весть, что к нам движется еще одна группа беженцев. Они делают все возможное, чтобы расчистить путь для выживших, но им срочно нужна помощь!', 12, 0, 100, 0, 0, 0, 33860, 0, 0, 0, 'Lordanel Sentinel');
INSERT INTO `creature_text` VALUES (33115, 2, 0, 'Конечно, жрица.', 12, 0, 100, 0, 0, 0, 33863, 0, 0, 0, 'Lordanel Sentinel');
INSERT INTO `creature_text` VALUES (33118, 0, 0, 'Дерзкие глупцы! Ваша кровь закалит оружие, которым был завоеван этот мир!', 14, 0, 100, 0, 0, 15564, 34306, 0, 0, 0, 'Повелитель Горнов Игнис to Player');
INSERT INTO `creature_text` VALUES (33118, 1, 0, 'Вперед, воины железного тигеля! Да свершится воля Творца!', 14, 0, 100, 0, 0, 15565, 34307, 0, 0, 0, 'Повелитель Горнов Игнис to 0');
INSERT INTO `creature_text` VALUES (33118, 2, 0, 'ГОРИТЕ! Горите в огне Творца!', 14, 0, 100, 0, 0, 15568, 34310, 0, 0, 0, 'Повелитель Горнов Игнис to Player');
INSERT INTO `creature_text` VALUES (33118, 3, 0, '%s читает заклинание \"Огненный поток\"!', 41, 0, 100, 0, 0, 15566, 33465, 0, 0, 0, 'Повелитель Горнов Игнис to 0');
INSERT INTO `creature_text` VALUES (33118, 4, 0, 'Пройдите очищение огнем!', 14, 0, 100, 0, 0, 15566, 34308, 0, 0, 0, 'Повелитель Горнов Игнис to 0');
INSERT INTO `creature_text` VALUES (33118, 5, 0, 'I ... lost.', 14, 0, 100, 0, 0, 15572, 34313, 0, 0, 0, 'Повелитель Горнов Игнис to Player');
INSERT INTO `creature_text` VALUES (33134, 0, 0, 'Help me! Please get them off me!', 14, 0, 100, 0, 0, 15771, 0, 0, 0, 0, 'Sara YELL_PREFIGHT');
INSERT INTO `creature_text` VALUES (33134, 0, 1, 'What do you want from me? Leave me alone!', 14, 0, 100, 0, 0, 15772, 34343, 0, 0, 0, 'Sara YELL_PREFIGHT');
INSERT INTO `creature_text` VALUES (33134, 1, 0, 'Yes! YES! Show them no mercy! Give no pause to your attacks!', 14, 0, 100, 0, 0, 15773, 34344, 0, 0, 0, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 1, 1, 'Let hatred and rage guide your blows!', 14, 0, 100, 0, 0, 15774, 34345, 0, 0, 0, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 1, 2, 'The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions!', 14, 0, 100, 457, 0, 15775, 34346, 0, 0, 0, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 2, 0, 'Suffocate upon your own hate!', 14, 0, 100, 0, 0, 15776, 34347, 0, 0, 0, 'Sara YELL_COMBAT_PHASE_2');
INSERT INTO `creature_text` VALUES (33134, 2, 1, 'Tremble, mortals, before the coming of the end!', 14, 0, 100, 0, 0, 15777, 34348, 0, 0, 0, 'Sara YELL_COMBAT_PHASE_2');
INSERT INTO `creature_text` VALUES (33134, 3, 0, 'Powerless to act...', 14, 0, 100, 0, 0, 15778, 0, 0, 0, 0, 'Sara YELL_SLAY');
INSERT INTO `creature_text` VALUES (33134, 3, 1, 'Could they have been saved?', 14, 0, 100, 0, 0, 15779, 34350, 0, 0, 0, 'YELL SAY_SLAY');
INSERT INTO `creature_text` VALUES (33178, 1, 0, 'Let me know when you are ready to begin the attack.', 12, 0, 100, 0, 0, 0, 34223, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33178, 2, 0, 'Let us put an end this Shatterspear threat for good. Sentinels, attack now!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33178, 3, 0, 'I\'ve shattered the shield protecting their leader! Quickly, kill him!', 14, 0, 100, 0, 0, 0, 34219, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33181, 0, 0, 'Наконец-то моя душа найдет покой... О, дорогой мой Кириллиан...', 12, 0, 100, 0, 0, 0, 1224, 0, 0, 0, 'Anaya Dawnrunner');
INSERT INTO `creature_text` VALUES (33186, 0, 0, '%s обессилела и больше не может летать!', 41, 0, 100, 0, 0, 0, 33931, 0, 0, 0, 'Острокрылая to 0');
INSERT INTO `creature_text` VALUES (33196, 0, 0, 'Торим, мой повелитель! Зачем еще явились сюда эти чужаки, если не для того, чтобы убить тебя? Их нужно остановить!', 14, 0, 100, 0, 0, 15668, 33325, 0, 0, 0, 'Сиф to Player');
INSERT INTO `creature_text` VALUES (33196, 1, 0, 'Это невозможно! Торим, не сомневайся – твоих врагов ждет ледяная смерть!', 14, 0, 100, 0, 0, 15670, 33369, 0, 0, 0, 'Сиф to Player');
INSERT INTO `creature_text` VALUES (33202, 0, 0, '%s падает на землю и начинает восстанавливаться.', 16, 0, 100, 0, 0, 0, 33463, 0, 0, 0, 'Древний дух воды to 0');
INSERT INTO `creature_text` VALUES (33202, 2, 0, 'С помощью союзников %s полностью восстанавливается.', 16, 0, 100, 0, 0, 0, 33464, 0, 0, 0, 'Древний дух воды to Древний дух воды');
INSERT INTO `creature_text` VALUES (33207, 0, 0, 'Queen Azshara will return and your land will be forfeit.', 12, 0, 100, 0, 0, 0, 34489, 0, 0, 0, 'Lady Janira');
INSERT INTO `creature_text` VALUES (33207, 0, 1, 'We will suffer no interruptions!', 12, 0, 100, 0, 0, 0, 34488, 0, 0, 0, 'Lady Janira');
INSERT INTO `creature_text` VALUES (33210, 0, 0, 'Не спать! Скоро покажутся буровые машины этих отвратительных железных дворфов!', 14, 0, 100, 0, 0, 0, 33607, 0, 0, 0, 'Начальник экспедиции to Player');
INSERT INTO `creature_text` VALUES (33210, 1, 0, 'Быстрее! Сейчас она снова взлетит!', 14, 0, 100, 0, 0, 15648, 33606, 0, 0, 0, 'Начальник экспедиции to Razorscale Controller');
INSERT INTO `creature_text` VALUES (33213, 3, 0, 'Герои, вас защитит покров зимы!', 14, 0, 100, 0, 0, 15559, 34366, 1013000, 1013000, 0, 'Ходир to Player');
INSERT INTO `creature_text` VALUES (33213, 3, 0, 'Как пожелаешь, $n.', 12, 0, 100, 0, 0, 15559, 34418, 1013000, 1013000, 0, 'Ходир to Player');
INSERT INTO `creature_text` VALUES (33220, 0, 0, 'Can it really be? Free after all these years?', 12, 0, 100, 1, 0, 0, 33366, 0, 0, 0, 'Maiden of Ashwood Lake');
INSERT INTO `creature_text` VALUES (33220, 1, 0, 'And now, I must return to the waters of the lake', 12, 0, 100, 2, 0, 0, 0, 0, 0, 0, 'Maiden of Ashwood Lake');
INSERT INTO `creature_text` VALUES (33228, 0, 0, 'Фрейя впитывает сущность Дара Эонара, исцеляя ближайших к ней союзников!', 41, 0, 100, 0, 0, 0, 34752, 0, 0, 0, 'Дар Эонара to 0');
INSERT INTO `creature_text` VALUES (33231, 0, 0, 'Hollee!', 12, 0, 100, 5, 0, 0, 33382, 0, 0, 0, 'Elisa Steelhand');
INSERT INTO `creature_text` VALUES (33231, 1, 0, 'I know, lass, but you\'re safe now and with friends. I thought you were dead!', 12, 0, 100, 1, 0, 0, 33384, 0, 0, 0, 'Elisa Steelhand');
INSERT INTO `creature_text` VALUES (33231, 2, 0, 'Of course. Don\'t think anything of it. And I won\'t hear of you trying to pay me back, either!', 12, 0, 100, 1, 0, 0, 33387, 0, 0, 0, 'Elisa Steelhand');
INSERT INTO `creature_text` VALUES (33231, 3, 0, 'Anytime, lass. Anytime.', 12, 0, 100, 1, 0, 0, 33389, 0, 0, 0, 'Elisa Steelhand');
INSERT INTO `creature_text` VALUES (33232, 0, 0, 'Great! Let\'s get out of here. I don\'t have a weapon, so I\'d really appreciate it if you could... you know... protect me?', 12, 0, 100, 4, 0, 0, 33378, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 1, 0, 'Let\'s go.', 12, 0, 100, 25, 0, 0, 33379, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 2, 0, 'Taldan!', 12, 0, 100, 5, 0, 0, 33402, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 3, 0, 'This isn\'t happening. Oh no... my friends!', 12, 0, 100, 16, 0, 0, 33403, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 4, 0, 'Hurry! We have to check on them!', 12, 0, 100, 5, 0, 0, 33404, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 5, 0, 'It\'s trashed, but it doesn\'t look like there are any bodies.', 12, 0, 100, 1, 0, 0, 33405, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 6, 0, 'Sorry, but I had to check. Okay, let\'s get out of here.', 12, 0, 100, 1, 0, 0, 33406, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 7, 0, 'There\'s the refugee camp!', 12, 0, 100, 25, 0, 0, 33380, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 8, 0, 'I\'ll race you!', 12, 0, 100, 25, 0, 0, 33381, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 9, 0, 'Elisa! Oh, it\'s so good to get out of there! Auberdine is completely overrun with cultists and elementals', 12, 0, 100, 5, 0, 0, 33383, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 10, 0, 'I have a favor to ask. Could you reward my friend here? I haven\'t any coin. I can pay you when we get back to Ironforge.', 12, 0, 100, 1, 0, 0, 33386, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33232, 11, 0, 'Oh, thank you, Elisa! Thank you!', 12, 0, 100, 4, 0, 0, 33388, 0, 0, 0, 'Archaeologist Hollee');
INSERT INTO `creature_text` VALUES (33233, 0, 0, 'Гарпунная пушка готова!', 41, 0, 100, 0, 0, 0, 33857, 0, 0, 0, 'Razorscale Controller to Player');
INSERT INTO `creature_text` VALUES (33235, 3, 0, 'Вы разгромили Железное Собрание и открыли Архив! Молодцы, ребятки!', 14, 0, 100, 5, 0, 15827, 33833, 9000, 9000, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (33241, 3, 0, 'Как пожелаешь, $n.', 12, 0, 100, 0, 0, 15535, 34418, 573000, 573000, 0, 'Фрейя to Player');
INSERT INTO `creature_text` VALUES (33241, 3, 0, 'Эонар, прошу тебя, даруй свое благословение!', 14, 0, 100, 0, 0, 15535, 34367, 573000, 573000, 0, 'Фрейя to Player');
INSERT INTO `creature_text` VALUES (33242, 3, 0, 'Голганнет, даруй мне силу! Надели моих смертных союзников мощью грома!', 14, 0, 100, 0, 0, 15750, 34369, 53000, 53000, 0, 'Торим to Player');
INSERT INTO `creature_text` VALUES (33242, 3, 0, 'Как пожелаешь, $n.', 12, 0, 100, 0, 0, 15750, 34418, 53000, 53000, 0, 'Торим to Player');
INSERT INTO `creature_text` VALUES (33244, 0, 0, 'Боевая матрица расширена. Какая невероятная скорость!', 14, 0, 100, 0, 0, 15630, 34368, 0, 0, 0, 'Мимирон to Player');
INSERT INTO `creature_text` VALUES (33244, 0, 0, 'Как пожелаешь, $n.', 12, 0, 100, 0, 0, 15630, 34418, 0, 0, 0, 'Мимирон to Player');
INSERT INTO `creature_text` VALUES (33266, 0, 0, 'Blood and thunder, $r!', 12, 0, 100, 0, 0, 0, 33419, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33266, 0, 1, 'Death to the Alliance!', 12, 0, 100, 0, 0, 0, 73018, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33266, 0, 2, 'For the Horde!', 12, 0, 100, 0, 0, 0, 143151, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33266, 0, 3, 'Many thanks, $C!', 12, 0, 100, 0, 0, 0, 33422, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33271, 0, 0, 'Ваша смерть возвестит новую эру страданий!', 14, 0, 100, 0, 0, 15542, 34053, 0, 0, 0, 'Генерал Везакс to Player');
INSERT INTO `creature_text` VALUES (33271, 1, 0, 'Какие ужасы вас ожидают...', 14, 0, 100, 0, 0, 15546, 34054, 0, 0, 0, 'Генерал Везакс to 78116');
INSERT INTO `creature_text` VALUES (33273, 0, 0, 'Are those winter hyacinths? For me?', 12, 0, 100, 6, 0, 0, 33423, 0, 0, 0, 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES (33273, 1, 0, 'It\'s been so long since a traveler has come here bearing flowers.', 12, 0, 100, 1, 0, 0, 33424, 0, 0, 0, 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES (33273, 2, 0, 'The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters.', 12, 0, 100, 1, 0, 0, 33425, 0, 0, 0, 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES (33273, 3, 0, 'Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it.', 12, 0, 100, 1, 0, 0, 33426, 0, 0, 0, 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES (33280, 4, 0, 'Слуга, убей их! Исполни приказ хозяина!', 15, 0, 100, 0, 0, 15760, 34364, 268000, 268000, 63120, 'Голос Йогг-Сарона to Player');
INSERT INTO `creature_text` VALUES (33287, 3, 0, 'Готовы выходить, охраняйте тылы от дворфов!', 14, 0, 100, 0, 0, 0, 0, 835000, 835000, 0, 'Инженер экспедиции to Player');
INSERT INTO `creature_text` VALUES (33287, 3, 0, 'Дайте время подготовить пушки.', 14, 0, 100, 0, 0, 0, 33820, 804000, 804000, 0, 'Инженер экспедиции to Player');
INSERT INTO `creature_text` VALUES (33288, 0, 0, 'ПАДИТЕ НИЦ ПЕРЕД БОГОМ СМЕРТИ!', 14, 0, 100, 0, 0, 0, 34357, 0, 0, 0, 'Йогг-Сарон to Сара');
INSERT INTO `creature_text` VALUES (33288, 1, 0, 'В сознание |3-1(%s) открываются порталы!', 41, 0, 100, 0, 0, 15756, 34037, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33288, 2, 0, 'БЕЗУМИЕ ПОГЛОТИТ ВАС!', 14, 0, 100, 0, 0, 15756, 34361, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33288, 8, 0, 'Взгляните в истинное лицо смерти и знайте, что ваш конец близок!', 14, 0, 100, 0, 0, 15755, 128857, 0, 0, 0, 'Йогг-Сарон to Мозг Йогг-Сарона');
INSERT INTO `creature_text` VALUES (33288, 9, 0, '%s широко открывает рот!', 41, 0, 100, 0, 0, 15757, 34022, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33288, 10, 0, 'Впереди вечные страдания!', 14, 0, 100, 0, 0, 15757, 34362, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33288, 11, 0, '%s готовится вызвать Сгущение тьмы!', 41, 0, 100, 0, 0, 0, 34170, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33288, 12, 0, 'Вы обречены. Ни вы, никто другой НЕ В СИЛАХ избежать близящегося конца света. Уульви ифис халас гаг эрх\'онг ушшш!', 14, 0, 100, 0, 0, 15761, 34365, 0, 0, 0, 'Йогг-Сарон to 0');
INSERT INTO `creature_text` VALUES (33292, 0, 0, '%s начинает закипать, едва коснувшись |3-1($n)!', 16, 0, 100, 0, 0, 0, 35163, 0, 0, 0, 'Зловещее облако to Player');
INSERT INTO `creature_text` VALUES (33293, 0, 0, 'Новые игрушки? Для меня? Обещаю, в этот раз я их не поломаю!', 14, 0, 100, 0, 0, 15724, 33508, 0, 0, 0, 'Разрушитель XT-002 to 78116');
INSERT INTO `creature_text` VALUES (33293, 1, 0, 'I\'m so tired. I’ll rest a bit ...', 14, 0, 100, 0, 0, 15725, 33446, 0, 0, 0, 'Разрушитель XT-002 to 0');
INSERT INTO `creature_text` VALUES (33293, 2, 0, 'Сердце |3-1(%s) обнажено и истекает энергией.', 41, 0, 100, 0, 0, 0, 33447, 0, 0, 0, 'Разрушитель XT-002 to 0');
INSERT INTO `creature_text` VALUES (33293, 3, 0, 'Продолжаем игру!', 14, 0, 100, 0, 0, 15726, 33448, 0, 0, 0, 'Разрушитель XT-002 to Сердце Разрушителя');
INSERT INTO `creature_text` VALUES (33293, 4, 0, 'Сердце |3-1(%s) вырвано из тела.', 41, 0, 100, 0, 0, 15726, 34023, 0, 0, 0, 'Разрушитель XT-002 to Сердце Разрушителя');
INSERT INTO `creature_text` VALUES (33293, 5, 0, 'НЕТ! НЕТ! НЕТ! НЕТ! НЕТ!', 14, 0, 100, 0, 0, 15727, 33453, 0, 0, 0, 'Разрушитель XT-002 to 0');
INSERT INTO `creature_text` VALUES (33293, 6, 0, '%s вызывает землетрясение.', 41, 0, 100, 0, 0, 15727, 33454, 0, 0, 0, 'Разрушитель XT-002 to 0');
INSERT INTO `creature_text` VALUES (33293, 7, 0, '%s поглощает хламобота, чтобы починить себя!', 41, 0, 100, 0, 0, 0, 33856, 0, 0, 0, 'Разрушитель XT-002 to Хламобот XS-013');
INSERT INTO `creature_text` VALUES (33293, 8, 0, 'Плохие... игрушки... очень... плохиеееее.', 14, 0, 100, 0, 0, 15731, 33511, 0, 0, 0, 'Разрушитель XT-002 to Player');
INSERT INTO `creature_text` VALUES (33302, 0, 0, 'How many times must I have you killed, orc?!', 14, 0, 100, 0, 0, 0, 33455, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33302, 1, 0, 'Curse you, $r vermin! This forest belongs to the Kaldorei...', 12, 0, 100, 0, 0, 0, 33462, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33303, 0, 0, 'Thank you, mortal, for freeing me from this curse. I beg you, take this blade.', 12, 0, 100, 1, 0, 0, 33436, 0, 0, 0, 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES (33303, 1, 0, 'It has brought me naught but ill. Mayhap you can find someone who will contain its power.', 12, 0, 100, 1, 0, 0, 33437, 0, 0, 0, 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES (33304, 0, 0, 'Thank you, friend. I could not rest knowing that my mission had not been completed.', 12, 0, 100, 0, 0, 0, 33440, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 1, 0, '...and that my mens\' sacrifice had not been avenged.', 12, 0, 100, 0, 0, 0, 33441, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 2, 0, 'We must hurry, now. I sense that my time is short.', 12, 0, 100, 0, 0, 0, 33442, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 3, 0, 'Follow me closely, and prepare for battle!', 12, 0, 100, 0, 0, 0, 33443, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 4, 0, 'Captain Elendilad conceals himself on the island ahead, cowardly issuing orders to his underlings.', 12, 0, 100, 0, 0, 0, 33444, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 5, 0, 'He must fall!', 12, 0, 100, 0, 0, 0, 33445, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33304, 6, 0, 'Show yourself, elf-coward! Face your death with honor!', 14, 0, 100, 0, 0, 0, 33449, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33350, 0, 0, 'У нас мало времени, друзья! Вы поможете испытать новейшее и величайшее из моих изобретений. И учтите: после того, что вы натворили с XT-002, отказываться просто некрасиво.', 14, 0, 100, 0, 0, 15612, 33636, 0, 0, 0, 'Мимирон to Player');
INSERT INTO `creature_text` VALUES (33350, 1, 0, 'ПРЕВОСХОДНО! Просто восхитительный результат! Целостность обшивки – 98,9 процента! Почти что ни царапинки! Продолжаем!', 14, 0, 100, 0, 0, 15615, 33638, 0, 0, 0, 'Мимирон to Левиафан II');
INSERT INTO `creature_text` VALUES (33350, 2, 0, 'Представляю вам противопехотную штурмовую пушку VX-001! Советую поискать укрытие.', 14, 0, 100, 0, 0, 15616, 33639, 0, 0, 0, 'Мимирон to Левиафан II');
INSERT INTO `creature_text` VALUES (33350, 3, 0, 'Спасибо, друзья! Благодаря вам я получил ценнейшие сведения! Так, а куда же я дел... – ах, вот куда.', 14, 0, 100, 0, 0, 15619, 33668, 0, 0, 0, 'Мимирон to VX-001');
INSERT INTO `creature_text` VALUES (33350, 4, 0, 'Красота, правда? Я называю это непревзойденной воздушной боевой единицей!', 14, 0, 100, 0, 0, 15620, 33669, 0, 0, 0, 'Мимирон to VX-001');
INSERT INTO `creature_text` VALUES (33350, 5, 0, 'Фаза предварительной проверки завершена. Пора начать главный тест!', 14, 0, 100, 0, 0, 15623, 33966, 0, 0, 0, 'Мимирон to Воздушное судно');
INSERT INTO `creature_text` VALUES (33350, 6, 0, 'Насладитесь его великолепием! Грейтесь в его сияющем... эм... сиянии! Перед вами потрясающий... В-0-7-ТРОН!', 14, 0, 100, 0, 0, 15624, 34033, 0, 0, 0, 'Мимирон to VX-001');
INSERT INTO `creature_text` VALUES (33350, 7, 0, 'Очевидно, я совершил небольшую ошибку в расчетах. Пленный злодей затуманил мой разум и заставил меня отклониться от инструкций. Сейчас все системы в норме. Конец связи.', 14, 0, 100, 1, 0, 15627, 34086, 0, 0, 0, 'Мимирон to Воздушное судно');
INSERT INTO `creature_text` VALUES (33440, 0, 0, 'Here I go! Keep an eye out...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33440, 0, 1, 'Hmm. You don\'t look very tough... but okay.', 12, 0, 100, 1, 0, 0, 33549, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33440, 1, 0, 'I have bad feeling about this...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33440, 1, 1, 'I\'m trusting you... Don\'t let me die!', 12, 0, 100, 1, 0, 0, 33570, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33441, 0, 0, 'I will break you ... just like him.', 12, 0, 100, 0, 0, 15599, 33609, 0, 0, 0, 'Король-лич to Йогг-Сарон');
INSERT INTO `creature_text` VALUES (33442, 0, 0, 'I\'m not afraid of you!', 12, 0, 100, 0, 0, 15471, 34739, 0, 0, 0, 'Сгоревший защитник to Йогг-Сарон');
INSERT INTO `creature_text` VALUES (33444, 3, 0, 'Давай поговорим на досуге. Такие вопросы надо решать цивилизованно.', 14, 0, 100, 25, 0, 0, 33561, 1664000, 1664000, 0, 'Предвестник Афотик');
INSERT INTO `creature_text` VALUES (33444, 3, 0, 'Мы повсюду, мы сопровождаем каждый твой шаг под землей.', 14, 0, 100, 25, 0, 0, 33556, 29000, 29000, 0, 'Предвестник Афотик');
INSERT INTO `creature_text` VALUES (33444, 3, 0, 'Смертный, ты вирус, который должен быть истреблен.', 14, 0, 100, 1, 0, 0, 33560, 7956000, 7956000, 0, 'Предвестник Афотик');
INSERT INTO `creature_text` VALUES (33458, 0, 0, 'Ashenvale belongs to the Kaldorei!', 12, 0, 100, 1, 0, 0, 34912, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33458, 1, 0, 'Death to the Horde!', 12, 0, 100, 1, 0, 0, 73023, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (33515, 0, 0, 'Some things are better left alone!', 14, 0, 100, 0, 0, 15473, 34341, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 1, 0, 'The secret dies with you!', 14, 0, 100, 0, 0, 15474, 34354, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 1, 1, 'There is no escape!', 14, 0, 100, 0, 0, 15475, 34355, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 2, 0, 'Auriaya screams in agony.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 3, 0, 'You waste my time!', 14, 0, 100, 0, 0, 15477, 34358, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 4, 0, 'Auriaya begins to cast Terrifying Screech.', 16, 0, 100, 0, 0, 0, 34450, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33515, 5, 0, 'Auriaya begins to activate the Feral Defender!', 16, 0, 100, 0, 0, 0, 34162, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33522, 0, 0, '$N challenges the Black Knight to trial by combat!', 14, 0, 100, 0, 0, 0, 33803, 0, 0, 0, 'yell');
INSERT INTO `creature_text` VALUES (33522, 1, 0, 'Good luck, $N.', 12, 0, 100, 0, 0, 0, 83720, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (33523, 0, 0, 'Да свершится предначертанное.', 12, 0, 100, 273, 0, 15632, 33659, 0, 0, 0, 'Нелтарион to Йогг-Сарон');
INSERT INTO `creature_text` VALUES (33532, 1, 0, 'Look!', 12, 0, 0, 0, 0, 0, 83873, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 2, 0, 'Got you back!', 12, 0, 0, 0, 0, 0, 34622, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 3, 0, 'Good snowball fight!', 12, 0, 0, 0, 0, 0, 34623, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 4, 0, 'Look! That must be Kekek as a mighty warrior! See? What Kekek tell you?', 12, 0, 0, 0, 0, 0, 34610, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 5, 0, 'Hmm... that Kekek very old. Must be elder, leader of Frenzyheart. This good. Kekek have good future as warrior and leader. Kekek very happy you bring him here. Orphan-lady never do that for Kekek.', 12, 0, 0, 0, 0, 0, 34636, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 6, 0, 'Wow. At last, Kekek get to meet Hemet Nesingwary!', 12, 0, 0, 0, 0, 0, 34685, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 7, 0, 'Even Frenzyheart know all about you after you come to Sholazar. Maybe you teach Kekek your tricks?', 12, 0, 0, 0, 0, 0, 34686, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 8, 0, 'Not sure how reading helps. Kekek can\'t read anyway, but if Hemet Nesingwary say, Kekek try.', 12, 0, 0, 0, 0, 0, 34688, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 9, 0, 'Maybe give up and throw book at animals, but try.', 12, 0, 0, 0, 0, 0, 34689, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 10, 0, 'You think maybe one day, Kekek lead Frenzyheart home, like bear-men fight to get tree-city back? Kekek want own home, not city with purple men, not jungle with big-tongues.', 12, 0, 0, 0, 0, 0, 34603, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 11, 0, 'Kekek thought we go see dragon queen. This just some elf-lady with funny horns.', 12, 0, 0, 0, 0, 0, 34655, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 12, 0, 'But why? Dragons big and powerful. Elf-lady weak. Kekek want to see dragon queen\'s real form.', 12, 0, 0, 0, 0, 0, 34664, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33532, 13, 0, 'Oh! Now Kekek understand. Not want to be crushed by big dragon-lady. Maybe come back sometime when not so crowded. Nice to meet you, dragon-lady.', 12, 0, 0, 0, 0, 0, 34666, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 1, 0, 'Look!', 12, 0, 0, 0, 0, 0, 83873, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 2, 0, 'We can dance too!', 12, 0, 0, 0, 0, 0, 34618, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 3, 0, 'We made a new friend!', 12, 0, 0, 0, 0, 0, 34615, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 4, 0, 'We here! Only, if this is world tree, how come it broke when they tried to put whole world in?', 12, 0, 0, 0, 0, 0, 34576, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 5, 0, 'Look! Is that us? We think it\'s us. A bit older, maybe?', 12, 0, 0, 0, 0, 0, 34606, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 6, 0, 'Looks like we blessed by Great Ones! Shrines give magic, make us high-oracle someday? Maybe?', 12, 0, 0, 0, 0, 0, 34607, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 7, 0, 'Wow! A real Great One? We don\'t even think Soo-say has met a real Great One!', 12, 0, 0, 0, 0, 0, 34672, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 8, 0, 'Um, hello, Mr. Great One. We are honored to meet you.', 12, 0, 0, 0, 0, 0, 34673, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 9, 0, 'Maybe you\'re asleep, Mr. Great One? We stand. Must be hard working being Great One, collecting all the shinies we leave for you.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 10, 0, 'We brought you a gift, Great One. Maybe you see it when you wake up. Maybe you remember we came to see you, oki?', 12, 0, 0, 0, 0, 0, 34675, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 11, 0, 'So that\'s the queen of the dragons? Hmm... we thought she\'d be bigger. Yes, we did.', 12, 0, 0, 0, 0, 0, 34631, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 12, 0, 'How come she doesn\'t look like the other dragons? could she turn into a big ol\' dragon if she wanted to?', 12, 0, 0, 0, 0, 0, 34632, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 13, 0, 'Um... Your Majesty, would you turn into a dragon for us?', 12, 0, 0, 0, 0, 0, 34633, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33533, 14, 0, 'Oki... How about now?', 12, 0, 0, 0, 0, 0, 34634, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33552, 3, 0, 'Его род усвоил все уроки давным-давно. Вам это еще только предстоит!', 12, 0, 100, 0, 0, 15765, 33663, 32000, 32000, 0, 'Йогг-Сарон to Йогг-Сарон');
INSERT INTO `creature_text` VALUES (33552, 3, 0, 'Ирр н\'лайет... шуул анаг!', 12, 0, 100, 0, 0, 15766, 33628, 33000, 33000, 0, 'Йогг-Сарон to Йогг-Сарон');
INSERT INTO `creature_text` VALUES (33579, 0, 0, 'Пентарус, ты слышал. Прикажи магам снять щит и пропусти этих смельчаков внутрь!', 14, 0, 100, 0, 0, 0, 33671, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (33579, 1, 0, 'Ну что же! Выдвигаемся. По машинам! Будем держать связь по радио!', 12, 0, 100, 0, 0, 15807, 33684, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (33579, 2, 0, 'Союзники готовы. Снимайте щит и пропускайте их!', 14, 0, 100, 0, 0, 0, 33687, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (33579, 3, 0, 'Железные дворфы появляются из бункеров у основания колонн прямо перед вами! Уничтожьте бункеры, и враг будет вынужден отступить!', 12, 0, 100, 0, 0, 15794, 34145, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (33624, 0, 0, 'Конечно, Бранн. Сейчас мы уберем щит.', 14, 0, 100, 0, 0, 0, 33673, 0, 0, 0, 'Верховный маг Пентарус to Player');
INSERT INTO `creature_text` VALUES (33624, 0, 0, 'Маги Кирин-Тора, Бранн просит вас снять щит! Защищайте платформу и наших союзников, хоть бы и ценой собственных жизней! За Даларан!', 14, 0, 100, 0, 0, 0, 33677, 0, 0, 0, 'Верховный маг Пентарус to Player');
INSERT INTO `creature_text` VALUES (33626, 3, 0, 'Да ничего. Убейте меня, но я не понимаю, как эта штука работает.', 12, 0, 100, 0, 0, 0, 34183, 17000, 17000, 0, 'Наемный инженер to Player');
INSERT INTO `creature_text` VALUES (33626, 3, 0, 'Заработал, чтоб ему пусто было! Он доставит вас прямо на ту сторону Двора, где вы сражались с Огненным Левиафаном.', 12, 0, 100, 1, 0, 0, 0, 25000, 25000, 0, 'Наемный инженер to Player');
INSERT INTO `creature_text` VALUES (33627, 0, 0, 'Проверим устойчивость с новым пассажиром на борту! Думаю, вы скоро будете как родные.', 12, 0, 100, 71, 0, 0, 33725, 0, 0, 0, 'Наемный подрывник to Трофейный разрушитель');
INSERT INTO `creature_text` VALUES (33662, 3, 0, 'Есть успехи?', 12, 0, 100, 0, 0, 0, 34184, 25000, 25000, 0, 'Боевой маг Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (33662, 3, 0, 'Продолжайте. Если наши предположения верны и это действительно транспортер, то он может оказаться очень полезным в битве.', 12, 0, 100, 0, 0, 0, 34186, 41000, 41000, 0, 'Боевой маг Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (33695, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (33696, 0, 0, 'Кирин-Тор не может выделить дополнительные ресурсы для уничтожения чего-либо столь огромного. Впрочем, возможно, нам и не придется.', 14, 0, 100, 0, 0, 0, 34230, 0, 0, 0, 'Верховный маг Ридиан to Бранн Бронзобород');
INSERT INTO `creature_text` VALUES (33696, 0, 0, 'Наши друзья отважно сражались, Бранн, но дело еще не закончено.', 14, 0, 100, 0, 0, 0, 34227, 0, 0, 0, 'Верховный маг Ридиан to Бранн Бронзобород');
INSERT INTO `creature_text` VALUES (33696, 0, 0, 'Никаких успехов. Я подозреваю, что проблемы связаны с огромным механическим големом перед воротами, которого заметили наши разведчики.', 14, 0, 100, 0, 0, 0, 34228, 0, 0, 0, 'Верховный маг Ридиан to Бранн Бронзобород');
INSERT INTO `creature_text` VALUES (33696, 3, 0, 'Мы можем проскользнуть мимо них. Если нам удастся одолеть голема, охраняющего ворота, мы проникнем внутрь.', 14, 0, 100, 0, 0, 0, 34232, 108000, 108000, 0, 'Верховный маг Ридиан to Бранн Бронзобород');
INSERT INTO `creature_text` VALUES (33696, 3, 0, 'Мы преследуем древнего бога, Бранн.', 14, 0, 100, 0, 0, 0, 34234, 125000, 125000, 0, 'Верховный маг Ридиан to Бранн Бронзобород');
