INSERT INTO `creature_text` VALUES (33785, 0, 0, 'Get off that horse and fight me man-to-man!', 14, 0, 100, 0, 0, 0, 34169, 0, 0, 0, 'yell');
INSERT INTO `creature_text` VALUES (33785, 1, 0, 'I will not fail you, master!', 14, 0, 100, 0, 0, 0, 34185, 0, 0, 0, 'yell');
INSERT INTO `creature_text` VALUES (33788, 0, 0, 'Help shield us from these cutting winds, little sapling.', 12, 7, 100, 2, 0, 0, 33814, 0, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33788, 0, 1, 'There you are', 12, 7, 100, 273, 0, 0, 0, 0, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33788, 0, 2, 'Grow, little one.', 12, 7, 100, 273, 0, 0, 33812, 0, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33889, 1, 0, 'When this is over, you may want to brush up on your secrety skills...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (33890, 0, 0, 'Иллюзия разрушена и путь в центральную комнату открыт!', 41, 0, 100, 0, 0, 15760, 34294, 0, 0, 0, 'Мозг Йогг-Сарона to Лазурная супруга');
