INSERT INTO `creature_text` VALUES (33993, 4, 0, 'Служитель бури поднимается на защиту Эмалона!', 41, 0, 100, 0, 0, 0, 34043, 39840, 115535, 0, 'Эмалон Страж Бури');
INSERT INTO `creature_text` VALUES (34015, 4, 0, '%s искрится и лучится чистой энергией!', 41, 0, 100, 0, 0, 0, 34085, 37818, 37818, 64379, 'Хранитель бури');
INSERT INTO `creature_text` VALUES (34033, 3, 0, 'I said - search the bodies BEFORE you gobble them up!', 12, 0, 100, 5, 0, 0, 34167, 6677, 6677, 0, 'Тиган Холловей');
INSERT INTO `creature_text` VALUES (34054, 0, 0, 'Быстрее! Начинаем маневрирование! Маневрирование!', 12, 0, 100, 0, 0, 15806, 34156, 0, 0, 0, 'Рация Бронзоборода to Ульдуарский великан');
INSERT INTO `creature_text` VALUES (34054, 0, 0, 'У вас получилось! Оборона Ульдуара прорвана. Еще чуть-чуть, и мы высадимся у...', 12, 0, 100, 0, 0, 15804, 34154, 0, 0, 0, 'Рация Бронзоборода to Ульдуарский великан');
INSERT INTO `creature_text` VALUES (34054, 0, 0, 'Что это? Берегитесь! К вам что-то приближается!', 12, 0, 100, 0, 0, 15805, 34155, 0, 0, 0, 'Рация Бронзоборода to Ульдуарский великан');
INSERT INTO `creature_text` VALUES (34064, 0, 0, 'We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis--', 14, 0, 0, 0, 0, 15824, 34161, 0, 0, 0, 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES (34064, 1, 0, 'I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him.', 12, 0, 0, 0, 0, 15825, 34029, 0, 0, 0, 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES (34064, 2, 0, 'I know just the place. Will you be all right?', 14, 0, 0, 6, 0, 15823, 34017, 0, 0, 0, 'Brann Bronzebeard - SAY_BRANN_ALGALON_OUTRO');
INSERT INTO `creature_text` VALUES (34071, 0, 0, '%s применяет \"Взрыв плазмы\"!', 41, 0, 100, 0, 0, 0, 34217, 0, 0, 0, 'Левиафан II to Player');
INSERT INTO `creature_text` VALUES (34119, 0, 0, 'Ладно. Если уж наши друзья берут на себя всю грязную работу, предоставим им самим решать, что делать дальше.', 14, 0, 100, 0, 0, 0, 34235, 0, 0, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34119, 0, 0, 'Ох. Значит нам все-таки придется обезвредить эту штуку.', 14, 0, 100, 0, 0, 0, 34229, 0, 0, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34119, 0, 0, 'Что за битва! Ты видела, Ридиан?', 14, 0, 100, 0, 0, 0, 34225, 0, 0, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34119, 3, 0, 'А что насчет бронированного протодракона и огненного великана, которых мы обнаружили поблизости? Твои маги смогут с ними справиться?', 14, 0, 100, 0, 0, 0, 34231, 97000, 97000, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34119, 3, 0, 'Может и так, но рано или поздно мы все равно пробьемся в Ульдуар. Вам не удалось найти способ переместиться внутрь?', 14, 0, 100, 0, 0, 0, 34226, 72000, 72000, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34119, 3, 0, 'Проскользнуть?! Ты что – за крыс нас держишь?!', 14, 0, 100, 0, 0, 0, 34233, 123000, 123000, 0, 'Бранн Бронзобород to Ветролет Бранна');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Отсчет времени до самоуничтожения начат.', 14, 0, 100, 0, 0, 15413, 34268, 31000, 31000, 0, 'Компьютер to Player');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Отсчет времени до самоуничтожения прерван. Код отмены: A-9-0-5.', 14, 0, 100, 0, 0, 15414, 34284, 257000, 257000, 0, 'Компьютер to Воздушное судно');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Самоуничтожение области через восемь минут.', 14, 0, 100, 0, 0, 15417, 34281, 156000, 156000, 0, 'Компьютер to Player');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Самоуничтожение области через девять минут.', 14, 0, 100, 0, 0, 15416, 34282, 95000, 95000, 0, 'Компьютер to Player');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Самоуничтожение области через десять минут.', 14, 0, 100, 0, 0, 15629, 34283, 35000, 35000, 0, 'Компьютер to Player');
INSERT INTO `creature_text` VALUES (34143, 3, 0, 'Самоуничтожение области через семь минут.', 14, 0, 100, 0, 0, 15418, 34280, 217000, 217000, 0, 'Компьютер to Player');
INSERT INTO `creature_text` VALUES (34197, 4, 0, '%s готовится исторгнуть ошеломляющий крик!', 16, 0, 100, 0, 0, 0, 34399, 11000, 11000, 0, 'Страж передней');
INSERT INTO `creature_text` VALUES (34206, 0, 0, 'You hear a shuffling noise nearby, but see nothing.', 16, 0, 100, 0, 0, 0, 34413, 0, 0, 0, 'ICText');
INSERT INTO `creature_text` VALUES (34207, 0, 0, 'Murloc pests, I have come to steal your treasure! Attempt to stop me! Quickly!', 12, 0, 100, 0, 0, 0, 34390, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34207, 1, 0, 'Detonation imminent.', 12, 0, 100, 0, 0, 0, 34391, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34258, 0, 0, 'Mount up, $N! Do not fail me.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Balgor Whipshank ');
INSERT INTO `creature_text` VALUES (34258, 1, 0, 'Excellent work! I\'ll unload the goods. You should check in with Kranal Fiss.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Balgor Whipshank ');
INSERT INTO `creature_text` VALUES (34295, 0, 0, 'ASHENVALE WILL BURN!', 14, 0, 100, 0, 0, 0, 34508, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 1, 'Death rises from below and erupts from above! There is no escape!', 14, 0, 100, 0, 0, 0, 34503, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 2, 'Destroy them, my minions!', 14, 0, 100, 0, 0, 0, 34506, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 3, 'I am free of the bonds of the earth never to return again!', 14, 0, 100, 0, 0, 0, 34507, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 4, 'Like moths to the flame!', 14, 0, 100, 0, 0, 0, 34509, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 5, 'Scorch the ground, boil the water, and ignite the skies!', 14, 0, 100, 0, 0, 0, 34504, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 6, 'The Earthen Ring cannot withstand my fury!', 14, 0, 100, 0, 0, 0, 34505, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34295, 0, 7, 'There is no escape! There is only the fiery embrace of my lava!', 14, 0, 100, 0, 0, 0, 34502, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34326, 0, 0, 'Our men are almost finished unearthing great Soggoth. Is the ritual ready?', 12, 0, 100, 6, 0, 0, 34599, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34326, 1, 0, 'Do not worry. Like I said, our men are almost done unearthing the Slithering One. They will make for a suitable -- and even voluntary -- sacrifice. Soggoth will rise!', 12, 0, 100, 1, 0, 0, 34601, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (34341, 0, 0, 'А вы, головорезы, подтянитесь! Скоро мы найдем применение вашим мечам.', 12, 0, 100, 0, 0, 0, 41574, 0, 0, 0, 'Саурбоз to Кром\'гарская караванная упряжь');
INSERT INTO `creature_text` VALUES (34341, 0, 0, 'Больше пушечного мяса для боевой машины Орды!', 12, 0, 100, 0, 0, 0, 41572, 0, 0, 0, 'Саурбоз to Кром\'гарская караванная упряжь');
INSERT INTO `creature_text` VALUES (34341, 0, 0, 'Похоже, у нас тут есть парочка солдат Рокрейна. Донтраг, Утвоч, вы будете нужны нам в крепости Кром\'гар.', 12, 0, 100, 0, 0, 0, 41573, 0, 0, 0, 'Саурбоз to Кром\'гарская караванная упряжь');
INSERT INTO `creature_text` VALUES (34353, 0, 0, '$GНИЧТОЖНЫЙ:НИЧТОЖНАЯ:r;, |3-6($r)! УМРИ!', 14, 1, 100, 0, 0, 0, 40893, 0, 0, 0, 'Кром\'гарский поджигатель to Player');
INSERT INTO `creature_text` VALUES (34366, 0, 0, 'Убей их всех и сожги башни!', 14, 1, 100, 0, 0, 0, 34663, 0, 0, 0, 'Передовой боец Песни Войны');
INSERT INTO `creature_text` VALUES (34366, 3, 0, 'Уничтожьте эльфов!', 14, 0, 100, 0, 0, 0, 34658, 0, 0, 0, 'Передовой боец Песни Войны');
INSERT INTO `creature_text` VALUES (34367, 0, 0, 'The ritual of soothing has begun. Defend the totem at all costs!', 42, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (34367, 1, 0, 'The ritual of soothing has completed.', 42, 0, 100, 1, 0, 0, 34553, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (34386, 1, 0, 'We remember this visit, yes we do. Never forget what you learn from your new friend, little Roo. You got great future ahead.', 12, 0, 0, 0, 0, 0, 35492, 0, 0, 0, '');
