INSERT INTO `creature_text` VALUES (36132, 3, 0, 'Будь $gосторожен: осторожна;. Не стоит ожидать, что прочие жители обрадуются, увидев тебя – но, во всяком случае, не пристрелят на месте.', 12, 0, 100, 1, 0, 0, 36617, 46358, 46358, 0, 'Креннан Аранас to Player');
