INSERT INTO `creature_text` VALUES (36288, 3, 0, 'Seid Ihr einer von den guten Worgen, $gFremder:Fremde;? Habt Ihr Cynthia gesehen? Sie versteckt sich bei den Hütten draußen.', 12, 0, 100, 0, 0, 0, 36326, 164094, 164094, 0, 'Ashley to Player');
INSERT INTO `creature_text` VALUES (36288, 3, 0, 'А вы – хороший ворген, $gсэр:мэм;? Может быть, вы найдете Синтию? Она прячется где-то тут!', 12, 0, 100, 0, 0, 0, 36326, 115352, 115352, 0, 'Эшли to Player');
INSERT INTO `creature_text` VALUES (36289, 3, 0, 'Tut mir nicht weh! Ich habe nur nach meinen Schwestern gesucht! Ich glaube, dass Ashley in dem Haus da ist!', 12, 0, 100, 0, 0, 0, 36324, 62265, 62265, 0, 'James to Player');
