INSERT INTO `creature_text` VALUES (36294, 4, 0, 'Ах ты жалкий воришка! Корот найдет тебя! Корот тебе морду расплющит!', 14, 0, 100, 0, 0, 0, 36913, 0, 40251, 0, 'Корот Сметигор to Player');
INSERT INTO `creature_text` VALUES (36296, 0, 0, 'Did they bother to tell you who I am and why I am doing this?', 12, 0, 100, 1, 0, 0, 38586, 0, 0, 0, 'Start text');
INSERT INTO `creature_text` VALUES (36296, 1, 0, '...or are they just using you like they do everybody else?', 12, 0, 100, 1, 0, 0, 38600, 0, 0, 0, 'Start text');
INSERT INTO `creature_text` VALUES (36296, 2, 0, 'But what does it matter. It is time for this to end.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Start text');
