INSERT INTO `creature_text` VALUES (36330, 0, 0, 'Тут не на что надеяться, Креннан. Эту жертву придется уничтожить. Таков устав.', 12, 0, 100, 0, 0, 0, 36336, 0, 0, 0, 'Лорд Годфри to Player');
INSERT INTO `creature_text` VALUES (36330, 3, 0, 'Hört auf, Krennan. Es ist an der Zeit, $gihn:sie; einzuschläfern. So will es das Protokoll.', 12, 0, 100, 274, 0, 19635, 36336, 8984, 8984, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (36331, 3, 0, 'I do not despair, $n. I have not yet found a cure for the curse ... But something can ease the torment. You can control yourself.', 12, 0, 100, 274, 0, 20919, 36344, 4638, 4638, 0, 'Креннан Аранас to Player');
INSERT INTO `creature_text` VALUES (36331, 3, 0, 'Ich werde Euch nicht aufgeben, $n. Noch habe ich kein Heilmittel... aber es gibt Behandlungen. Ihr werdet die Kontrolle zurückbekommen.', 12, 0, 100, 274, 0, 20919, 36344, 6547, 6547, 0, 'Krennan Aranas to Player');
INSERT INTO `creature_text` VALUES (36332, 0, 0, 'Sagt mir, Godfrey, diejenigen, die in der Stadt blieben, damit wir überleben konnten... Folgten sie dem Protokoll?', 12, 0, 100, 1, 0, 19721, 36340, 0, 0, 0, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (36332, 1, 0, 'Das dachte ich mir. Gebt mir jetzt diesen Trank, Krennan... und verdoppelt die Dosierung.', 12, 0, 100, 1, 0, 19722, 36341, 0, 0, 0, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (36332, 2, 0, 'Скажи, Годфри. Те, кто остался в Гилнеасе, чтобы спасти наши жизни, тоже действовали по уставу?', 12, 0, 100, 0, 0, 0, 36340, 0, 0, 0, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (36332, 3, 0, 'Не думаю. А теперь передай мне это зелье, Креннан... И удвой дозу.', 12, 0, 100, 0, 0, 0, 36341, 0, 0, 0, 'Король Генн Седогрив to Player');
