INSERT INTO `creature_text` VALUES (36440, 3, 0, 'Das Land hat einfach unter unseren Füßen nachgegeben... Ich dachte, ich wäre tot. Dafür schulde ich Euch was.', 12, 0, 100, 0, 0, 0, 36419, 206000, 206000, 0, 'Ertrinkender Wachmann to Player');
