INSERT INTO `creature_text` VALUES (36458, 1, 0, 'Mit meinem Kätzchen stellt Ihr keine Schweinereien an - Ihr Sohn einer Promenadenmischung!', 14, 0, 100, 0, 0, 0, 36421, 0, 0, 69300, 'Oma Wahl to Lucius der Grausame');
