INSERT INTO `creature_text` VALUES (36470, 0, 0, 'Быстрее, $N, разворачивай Городок-из-табакерки. Там заперты наши друзья, и им грозит опасность! Ой! Я чуть не забыл о твоем парашюте.', 12, 0, 100, 25, 0, 0, 49886, 0, 0, 0, 'Штейгер Промоках');
