INSERT INTO `creature_text` VALUES (36497, 1, 0, 'Наконец-то гости пожаловали!', 14, 0, 100, 0, 0, 16595, 38486, 0, 0, 68904, 'Броньям to Player');
INSERT INTO `creature_text` VALUES (36497, 4, 0, 'Вихрь погубленных душ взывает к вам!', 14, 0, 100, 0, 0, 16599, 38488, 64540, 64540, 68988, 'Броньям');
INSERT INTO `creature_text` VALUES (36502, 1, 0, 'Вы осмелились взглянуть на вместилище душ? Я СОЖРУ ВАС ЗАЖИВО!', 14, 0, 100, 0, 0, 16884, 38012, 0, 0, 0, 'Пожиратель Душ to Player');
INSERT INTO `creature_text` VALUES (36502, 4, 0, 'СТРАДАНИЕ! МУЧЕНИЕ! ХАОС! ВОССТАНЬТЕ И ПИРУЙТЕ!', 14, 0, 100, 0, 0, 16899, 38019, 20564, 20564, 68977, 'Пожиратель Душ');
INSERT INTO `creature_text` VALUES (36513, 0, 0, 'Кого ты здесь перед нами корчишь? Думаешь, тебе ноги целовать будут за спасение и дружбу с орками?', 14, 0, 100, 25, 0, 19584, 36460, 0, 0, 0, 'Торговый принц Галливикс');
INSERT INTO `creature_text` VALUES (36513, 1, 0, 'I\'ll crush you! I am a trading prince! I! I already know how to get out of this island and become the king of Azshara!', 14, 0, 100, 25, 0, 19585, 36461, 0, 0, 0, 'Торговый принц Галливикс');
INSERT INTO `creature_text` VALUES (36513, 2, 0, 'Попомни мои слова! Я до вас всех доберусь! До всех! С дороги!', 14, 0, 100, 15, 0, 19586, 36462, 0, 0, 0, 'Торговый принц Галливикс');
INSERT INTO `creature_text` VALUES (36552, 0, 0, 'Praise to the Light, brother Gitgudguides! Let\'s take a look here...', 12, 0, 50, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 1, 0, 'Well, there\'s your problem! You have what we call a ghost in the machine.', 12, 0, 50, 0, 0, 0, 36504, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 2, 0, 'This calls for an old-fashioned bulldozer exorcism! Give me a hand if she gets uppity, will ya?', 12, 0, 50, 0, 0, 0, 36505, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 3, 0, 'Whoa! We got a live one here!', 12, 0, 50, 0, 0, 0, 36506, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 4, 0, 'I cast you out! Unclean spirit - Begone!', 12, 0, 50, 0, 0, 0, 36507, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 5, 0, 'Terrific! That went down nice and clean.', 12, 0, 50, 0, 0, 0, 36511, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36552, 6, 0, 'Let Clubnik know that the priesthood will be sending him a bill. For the Light!', 12, 0, 50, 0, 0, 0, 36512, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36565, 0, 0, 'Для меня было большой честью служить тебе, Хаммел.', 12, 0, 100, 0, 0, 0, 38595, 0, 0, 0, 'Аптекарь Бакстер to Player');
INSERT INTO `creature_text` VALUES (36597, 0, 0, 'So the Light\'s vaunted justice has finally arrived? Shall I lay down Frostmourne and throw myself at your mercy, Fordring?', 14, 0, 0, 0, 0, 17349, 38070, 0, 0, 0, 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (36597, 1, 0, 'You\'ll learn of that first hand. When my work is complete, you will beg for mercy -- and I will deny you. Your anguished cries will be testament to my unbridled power...', 14, 0, 0, 22, 0, 17350, 38071, 0, 0, 0, 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (36597, 2, 0, 'I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image.', 14, 0, 0, 0, 0, 17351, 38072, 0, 0, 0, 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (36597, 3, 0, 'Come then champions, feed me your rage!', 14, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'The Lich King - SAY_LK_AGGRO');
INSERT INTO `creature_text` VALUES (36597, 4, 0, 'I will freeze you from within until all that remains is an icy husk!', 14, 0, 0, 0, 0, 17369, 38046, 0, 0, 0, 'The Lich King - SAY_LK_REMORSELESS_WINTER');
INSERT INTO `creature_text` VALUES (36597, 5, 0, 'Watch as the world around you collapses!', 14, 0, 0, 0, 0, 17370, 38047, 0, 0, 0, 'The Lich King - SAY_LK_QUAKE');
INSERT INTO `creature_text` VALUES (36597, 6, 0, 'Val\'kyr, your master calls!', 14, 0, 0, 0, 0, 17373, 38052, 0, 0, 0, 'The Lich King - SAY_LK_SUMMON_VALKYR');
INSERT INTO `creature_text` VALUES (36597, 7, 0, 'Frostmourne hungers...', 14, 0, 0, 0, 0, 17366, 38042, 0, 0, 0, 'The Lich King - SAY_LK_HARVEST_SOUL');
INSERT INTO `creature_text` VALUES (36597, 8, 0, 'Argh... Frostmourne, obey me!', 14, 0, 0, 0, 0, 17367, 0, 0, 0, 0, 'The Lich King - SAY_LK_FROSTMOURNE_ESCAPE');
INSERT INTO `creature_text` VALUES (36597, 9, 0, 'Frostmourne feeds on the soul of your fallen ally!', 14, 0, 0, 0, 0, 17368, 38044, 0, 0, 0, 'The Lich King - SAY_LK_FROSTMOURNE_KILL');
INSERT INTO `creature_text` VALUES (36597, 10, 0, 'Hope wanes!', 14, 0, 0, 0, 0, 17363, 38038, 0, 0, 0, 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES (36597, 10, 1, 'The end has come!', 14, 0, 0, 0, 0, 17364, 38040, 0, 0, 0, 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES (36597, 11, 0, 'Face now your tragic end!', 14, 0, 0, 0, 0, 17365, 38041, 0, 0, 0, 'The Lich King - SAY_LK_BERSERK');
INSERT INTO `creature_text` VALUES (36597, 12, 0, '%s begins to cast Defile!', 41, 0, 0, 0, 0, 0, 39688, 0, 0, 0, 'The Lich King - EMOTE_DEFILE_WARNING');
INSERT INTO `creature_text` VALUES (36597, 13, 0, '|TInterface\\Icons\\ability_creature_disease_02.blp:16|tYou have been infected by |cFFCF00FFNecrotic Plague!|r', 42, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'The Lich King - EMOTE_NECROTIC_PLAGUE_WARNING');
INSERT INTO `creature_text` VALUES (36597, 14, 0, 'No questions remain unanswered. No doubts linger. You ARE Azeroth\'s greatest champions. You overcame every challenge I laid before you. My mightiest servants have fallen before your relentless onslaught... your unbridled fury...', 14, 0, 0, 0, 0, 17353, 38074, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_1');
INSERT INTO `creature_text` VALUES (36597, 15, 0, 'Is it truly the righteousness that drives you? I wonder...', 14, 0, 0, 0, 0, 17354, 38075, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_2');
INSERT INTO `creature_text` VALUES (36597, 16, 0, 'You trained them well, Fordring. You delivered the greatest fighting force this world has ever known... right into my hands -- exactly as I intended. You shall be rewarded for your unwitting sacrifice.', 14, 0, 0, 0, 0, 17355, 38076, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_3');
INSERT INTO `creature_text` VALUES (36597, 17, 0, 'Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die.', 14, 0, 0, 0, 0, 17356, 38077, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_4');
INSERT INTO `creature_text` VALUES (36597, 18, 0, 'I delight in the irony...', 14, 0, 0, 0, 0, 17357, 38078, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_5');
INSERT INTO `creature_text` VALUES (36597, 19, 0, 'Impossible...', 14, 0, 0, 0, 0, 17358, 51720, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_6');
INSERT INTO `creature_text` VALUES (36597, 20, 0, 'Now I stand, the lion before the lambs... and they do not fear.', 14, 0, 0, 0, 0, 17361, 39003, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_7');
INSERT INTO `creature_text` VALUES (36597, 21, 0, 'They cannot fear.', 14, 0, 0, 0, 0, 17362, 39004, 0, 0, 0, 'The Lich King - SAY_LK_OUTRO_8');
INSERT INTO `creature_text` VALUES (36599, 0, 0, 'The Arcane Construct surges with uncontrolled arcane energies.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36599, 1, 0, 'Warriors - the Alliance filth have betrayed us! Slay them!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36600, 0, 0, 'Это $N?! Извини, док, я думал, что $gего:ее; убили!', 12, 0, 100, 0, 0, 0, 36577, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36600, 1, 0, 'Да, многие до сих пор заперты в спасательных капсулах, босс. Хотя, стой. Ты же, кажется, больше не босс? Ну, в любом случае, все они утонут, если ты их оттуда не вытащишь.', 12, 0, 100, 1, 0, 0, 49824, 0, 0, 0, 'Зуботочер Механил');
INSERT INTO `creature_text` VALUES (36602, 3, 0, 'И что, мы должны $gего: ее; пропустить?', 12, 0, 100, 1, 0, 0, 36613, 150993, 150993, 0, 'Страж из Темной Гавани to Player');
INSERT INTO `creature_text` VALUES (36606, 3, 0, 'Мы со всем справимся. Не надо вдаваться в панику.', 12, 0, 100, 1, 0, 0, 38628, 193951, 193951, 0, 'Королева Миа Седогрив');
INSERT INTO `creature_text` VALUES (36606, 3, 0, 'Повозки уже готовы, мы ждем только приказа короля Седогрива.', 12, 0, 100, 1, 0, 0, 38626, 81049, 81049, 0, 'Королева Миа Седогрив');
INSERT INTO `creature_text` VALUES (36606, 3, 0, 'Успокойтесь! Мы обо всем позаботимся.', 12, 0, 100, 1, 0, 0, 38625, 31311, 135678, 0, 'Королева Миа Седогрив');
INSERT INTO `creature_text` VALUES (36607, 3, 0, '%s takes in a deep breath...', 16, 0, 100, 0, 0, 0, 36542, 300094, 300094, 69006, 'Onyxian Whelpling');
INSERT INTO `creature_text` VALUES (36607, 3, 0, '%s делает глубокий вдох...', 16, 0, 100, 0, 0, 0, 36542, 601000, 601000, 0, 'Дракончик Ониксии');
INSERT INTO `creature_text` VALUES (36608, 0, 0, 'Механил, что ты тут расселся? Разве не видишь, кто рядом с тобой лежит?!', 12, 0, 100, 396, 0, 0, 36543, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36608, 1, 0, 'Это же $N! Только благодаря $gему:ей; мы все еще дышим, а не превратились в поджаристые шкварки уже там, на Кезане.', 12, 0, 100, 396, 0, 0, 36544, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36608, 2, 0, 'Отойди, сейчас я $gего:ее; воскрешу. Надеюсь, мой дефибриллятор не испортился от воды!', 12, 0, 100, 396, 0, 0, 36545, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36608, 3, 0, 'Ну же! Разряд!', 12, 0, 100, 396, 0, 0, 36546, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36608, 4, 0, 'I did everything I could. Can you hear me, $N? Wake up! Do not go to the Light!', 12, 0, 100, 396, 0, 0, 36547, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36612, 0, 0, 'This is the beginning AND the end, mortals. None may enter the master\'s sanctum!', 14, 0, 0, 0, 0, 16950, 38508, 0, 0, 0, 'Lord Marrowgar - SAY_ENTER_ZONE');
INSERT INTO `creature_text` VALUES (36612, 1, 0, 'The Scourge will wash over this world as a swarm of death and destruction!', 14, 0, 0, 0, 0, 16941, 37684, 0, 0, 0, 'Lord Marrowgar - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36612, 2, 0, 'BONE STORM!', 14, 0, 0, 0, 0, 16946, 36554, 0, 0, 0, 'Lord Marrowgar - SAY_BONE_STORM');
