INSERT INTO `creature_text` VALUES (36612, 3, 1, 'Stick Around!', 14, 0, 0, 0, 0, 16948, 37696, 0, 0, 0, 'Lord Marrowgar - SAY_BONESPIKE_2');
INSERT INTO `creature_text` VALUES (36612, 3, 2, 'The only escape is death!', 14, 0, 0, 0, 0, 16949, 37693, 0, 0, 0, 'Lord Marrowgar - SAY_BONESPIKE_3');
INSERT INTO `creature_text` VALUES (36612, 4, 0, 'More bones for the offering!', 14, 0, 0, 0, 0, 16942, 37686, 0, 0, 0, 'Lord Marrowgar - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (36612, 4, 1, 'Languish in damnation!', 14, 0, 0, 0, 0, 16943, 37687, 0, 0, 0, 'Lord Marrowgar - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36612, 5, 0, 'I see... only darkness...', 14, 0, 0, 0, 0, 16944, 0, 0, 0, 0, 'Lord Marrowgar - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36612, 6, 0, 'THE MASTER\'S RAGE COURSES THROUGH ME!', 14, 0, 0, 0, 0, 16945, 37690, 0, 0, 0, 'Lord Marrowgar - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36612, 7, 0, 'Lord Marrowgar creates a whirling storm of bone!', 41, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Lord Marrowgar - SAY_BONE_STORM_EMOTE');
