INSERT INTO `creature_text` VALUES (36626, 7, 1, 'Dead, dead, dead!', 14, 0, 0, 0, 0, 16903, 37825, 0, 0, 0, 'Festergut - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36626, 8, 0, 'Fun time over!', 14, 0, 0, 0, 0, 16905, 37827, 0, 0, 0, 'Festergut - SAY_BERSERK');
