INSERT INTO `creature_text` VALUES (36627, 4, 0, '|TInterface\\Icons\\spell_shadow_unstableaffliction_2.blp:16|t%s begins to cast |cFFFF0000Unstable Ooze Explosion!|r', 41, 0, 0, 0, 0, 0, 37064, 0, 0, 0, 'Rotface - EMOTE_UNSTABLE_EXPLOSION');
INSERT INTO `creature_text` VALUES (36627, 5, 0, 'I think I made an angry poo-poo. It gonna blow!', 14, 0, 0, 0, 0, 16992, 37063, 0, 0, 0, 'Rotface - SAY_UNSTABLE_EXPLOSION');
INSERT INTO `creature_text` VALUES (36627, 6, 0, 'Daddy make toys out of you!', 14, 0, 0, 0, 0, 16988, 37833, 0, 0, 0, 'Rotface - SAY_KILL_1');
