INSERT INTO `creature_text` VALUES (36627, 9, 0, '|TInterface\\Icons\\ability_creature_disease_02.blp:16|tYou have |cFF00FF00Mutated Infection!|r', 42, 0, 0, 0, 0, 0, 37872, 0, 0, 0, 'Rotface - EMOTE_MUTATED_INFECTION');
INSERT INTO `creature_text` VALUES (36638, 0, 0, 'You\'ve come seeking your death? Happy to oblige.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Twilight Lord Katrana ');
INSERT INTO `creature_text` VALUES (36649, 0, 0, 'You may have saved Azuregos, $N. but your life ends here.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36658, 0, 0, 'Another shall take his place. You waste your time.', 14, 0, 100, 0, 0, 16752, 36765, 0, 0, 0, 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES (36658, 3, 0, 'Your pursuit shall be in vain, intruders, for the Lich King has placed an army of undead at my command! Behold!', 14, 0, 100, 0, 0, 16755, 36708, 0, 0, 0, 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES (36658, 4, 0, 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', 14, 0, 100, 0, 0, 16756, 36709, 0, 0, 0, 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES (36658, 5, 0, 'Rimefang, trap them within the tunnel! BURY THEM ALIVE!', 14, 0, 100, 0, 0, 16757, 36714, 0, 0, 0, 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES (36658, 6, 0, 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', 14, 0, 100, 0, 0, 16758, 37233, 0, 0, 0, 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES (36658, 7, 0, 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die.', 14, 0, 100, 0, 0, 16759, 37234, 0, 0, 0, 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES (36658, 8, 0, 'I shall not fail the Lich King. Come and meet your end!', 14, 0, 100, 0, 0, 16760, 38718, 0, 0, 0, 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36658, 9, 0, 'Such a shameful display. You are better off dead.', 14, 0, 100, 0, 0, 16761, 38715, 0, 0, 0, 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (36658, 9, 1, 'Perhaps you should have stayed... in the mountains!', 14, 0, 100, 0, 0, 16762, 38716, 0, 0, 0, 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (36658, 10, 0, 'Impossible.... Rimefang.... warn....', 14, 0, 100, 0, 0, 16763, 38714, 0, 0, 0, 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES (36658, 11, 0, 'Rimefang, destroy this fool!', 14, 0, 100, 0, 0, 16764, 36648, 0, 0, 0, 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES (36658, 12, 0, 'The frostwyrm Rimefang gazes at $n and readies an icy attack!', 41, 0, 100, 0, 0, 0, 36649, 0, 0, 0, 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `creature_text` VALUES (36658, 13, 0, 'Power... overwhelming!', 14, 0, 100, 0, 0, 16765, 38717, 0, 0, 0, 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `creature_text` VALUES (36658, 14, 0, '%s roars and swells with dark might!', 41, 0, 100, 0, 0, 0, 36628, 0, 0, 0, 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `creature_text` VALUES (36669, 0, 0, 'It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades.', 12, 0, 100, 1, 0, 0, 37620, 0, 0, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36669, 1, 0, 'Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening.', 12, 0, 100, 1, 0, 0, 37621, 0, 0, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36669, 2, 0, 'Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek.', 12, 0, 100, 1, 0, 0, 37622, 0, 0, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36670, 0, 0, 'You\'re late, courier. No, I don\'t want any excuses; this errand is far too important.', 12, 0, 100, 1, 0, 0, 37580, 0, 0, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36670, 1, 0, 'Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately.', 12, 0, 100, 1, 0, 0, 37581, 0, 0, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36670, 2, 0, 'Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us.', 12, 0, 100, 1, 0, 0, 37582, 0, 0, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36678, 0, 0, 'Just an ordinary gas cloud. But watch out, because that\'s no ordinary gas cloud!', 14, 0, 0, 432, 0, 17119, 37843, 0, 0, 0, 'Professor Putricide - SAY_GASEOUS_BLIGHT');
INSERT INTO `creature_text` VALUES (36678, 1, 0, 'Oh, Festergut. You were always my favorite. Next to Rotface. The good news is you left behind so much gas, I can practically taste it!', 14, 0, 0, 0, 0, 17124, 37848, 0, 0, 0, 'Professor Putricide - SAY_FESTERGUT_DEATH');
INSERT INTO `creature_text` VALUES (36678, 2, 0, 'Great news, everyone! The slime is flowing again!', 14, 0, 0, 1, 0, 17126, 38120, 0, 0, 0, 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD1');
INSERT INTO `creature_text` VALUES (36678, 2, 1, 'Good news, everyone! I\'ve fixed the poison slime pipes!', 14, 0, 0, 1, 0, 17123, 37049, 0, 0, 0, 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD2');
INSERT INTO `creature_text` VALUES (36678, 3, 0, 'Terrible news, everyone, Rotface is dead! But great news everyone, he left behind plenty of ooze for me to use! Whaa...? I\'m a poet, and I didn\'t know it? Astounding!', 14, 0, 0, 0, 0, 17146, 37847, 0, 0, 0, 'Professor Putricide - SAY_ROTFACE_DEATH');
INSERT INTO `creature_text` VALUES (36678, 4, 0, 'Good news, everyone! I think I\'ve perfected a plague that will destroy all life on Azeroth!', 14, 0, 0, 0, 0, 17114, 37838, 0, 0, 0, 'Professor Putricide - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36678, 5, 0, '%s begins to cast Unstable Experiment!', 41, 0, 0, 0, 0, 0, 38500, 0, 0, 0, 'Professor Putricide - EMOTE_UNSTABLE_EXPERIMENT');
INSERT INTO `creature_text` VALUES (36678, 6, 0, 'Two oozes, one room! So many delightful possibilities...', 14, 0, 0, 0, 0, 17122, 37846, 0, 0, 0, 'Professor Putricide - SAY_PHASE_TRANSITION_HEROIC');
INSERT INTO `creature_text` VALUES (36678, 7, 0, 'Hmm. I don\'t feel a thing. Whaa...? Where\'d those come from?', 14, 0, 0, 15, 0, 17120, 37844, 0, 0, 0, 'Professor Putricide - SAY_TRANSFORM_1');
INSERT INTO `creature_text` VALUES (36678, 8, 0, 'Tastes like... Cherry! Oh! Excuse me!', 14, 0, 0, 15, 0, 17121, 37845, 0, 0, 0, 'Professor Putricide - SAY_TRANSFORM_2');
INSERT INTO `creature_text` VALUES (36678, 9, 0, '|TInterface\\Icons\\inv_misc_herb_evergreenmoss.blp:16|t%s cast |cFF00FF00Malleable Goo!|r', 41, 0, 0, 0, 0, 0, 38499, 0, 0, 0, 'Professor Putricide - EMOTE_MALLEABLE_GOO');
INSERT INTO `creature_text` VALUES (36678, 10, 0, '%s cast |cFFFF7F00Choking Gas Bomb!|r', 41, 0, 0, 0, 0, 0, 38498, 0, 0, 0, 'Professor Putricide - EMOTE_CHOKING_GAS_BOMB');
INSERT INTO `creature_text` VALUES (36678, 11, 0, 'Hmm... Interesting...', 14, 0, 0, 0, 0, 17115, 37839, 0, 0, 0, 'Professor Putricide - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (36678, 11, 1, 'That was unexpected!', 14, 0, 0, 0, 0, 17116, 38545, 0, 0, 0, 'Professor Putricide - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36678, 13, 0, 'Bad news, everyone! I don\'t think I\'m going to make it.', 14, 0, 0, 0, 0, 17117, 0, 0, 0, 0, 'Professor Putricide - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36723, 0, 0, 'You are not worthy to face the Lich King!', 14, 0, 100, 0, 0, 16921, 38664, 0, 0, 0, 'general SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36723, 1, 0, 'Master, I have failed...', 14, 0, 100, 0, 0, 16922, 36921, 0, 0, 0, 'general SAY_DEATH');
