INSERT INTO `creature_text` VALUES (36774, 0, 0, 'You Sunreaver lackeys are all the same.', 12, 0, 100, 1, 0, 0, 37768, 0, 0, 0, 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES (36774, 0, 1, 'You won\'t escape from us so easily!', 12, 0, 100, 1, 0, 0, 37879, 0, 0, 0, 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES (36776, 0, 0, 'Did you think you would escape with your life?', 12, 0, 100, 1, 0, 0, 37880, 0, 0, 0, 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES (36776, 0, 1, 'You\'ll never run us out of the city, Silver Covenant thug!', 12, 0, 100, 1, 0, 0, 37767, 0, 0, 0, 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES (36789, 0, 0, 'Heroes, lend me your aid. I... I cannot hold them off much longer. You must heal my wounds!', 14, 0, 0, 0, 0, 17064, 37875, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_ENTER_COMBAT');
INSERT INTO `creature_text` VALUES (36789, 1, 0, 'I have opened a portal into the Dream. Your salvation lies within, heroes...', 14, 0, 0, 0, 0, 17068, 37876, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_DREAM_PORTAL');
INSERT INTO `creature_text` VALUES (36789, 2, 0, 'My strength is returning! Press on, heroes!', 14, 0, 0, 0, 0, 17070, 37878, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_75_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 3, 0, 'I will not last much longer!', 14, 0, 0, 0, 0, 17069, 21784, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_25_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 4, 0, 'Forgive me for what I do! I... cannot... stop... ONLY NIGHTMARES REMAIN!', 14, 0, 0, 0, 0, 17072, 0, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_DEATH');
INSERT INTO `creature_text` VALUES (36789, 5, 0, 'A tragic loss...', 14, 0, 0, 0, 0, 17066, 0, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES (36789, 6, 0, 'FAILURES!', 14, 0, 0, 0, 0, 17067, 0, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_BERSERK');
INSERT INTO `creature_text` VALUES (36789, 7, 0, 'I am renewed! Ysera grants me the favor to lay these foul creatures to rest!', 14, 0, 0, 0, 0, 17071, 37852, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_SUCCESS');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Вот падаль. Вы даже для работы на рудниках не годитесь. Запомните свои последние минуты – скоро вы станете безмозглой нежитью.', 14, 0, 100, 0, 0, 16748, 37094, 29621, 29621, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Все ваши усилия напрасны, чужаки, ибо Король-лич поставил меня во главе целой армии нежити! Берегитесь!', 14, 0, 100, 0, 0, 16755, 36708, 510219, 510219, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Его место займет другой. Вы попусту тратите время.', 14, 0, 100, 0, 0, 16752, 36765, 231977, 231977, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Иней, останови их! ПОХОРОНИ ИХ ЗАЖИВО!', 14, 0, 100, 0, 0, 16757, 36714, 677116, 677116, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Настырные щенки! Вам не пройти в покои моего господина. Солдаты, уничтожить их!', 14, 0, 100, 0, 0, 16756, 36709, 609887, 609887, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Последнее, что вам запомнится, – это агония.', 14, 0, 100, 0, 0, 16749, 37095, 46685, 46685, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Прислужники, убейте этих наглецов!', 14, 0, 100, 0, 0, 16751, 37096, 64029, 64029, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Чужаки проникли во владения господина. Поднять тревогу!', 14, 0, 100, 0, 0, 16747, 37093, 23569, 23569, 0, 'Повелитель Плети Тираний to Player');
INSERT INTO `creature_text` VALUES (36799, 0, 0, 'Для перемещения в Вайш\'ир поговори со мной еще раз. ', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36814, 3, 0, 'Lasst die Sichel das lösen, was nicht verbunden sein sollte! Lasst die Seele die Bestie beherrschen, auf dass die Bestie nicht über die Seele herrsche!', 12, 0, 100, 1, 0, 0, 37730, 384015, 384015, 0, 'Talran aus der Wildnis to Player');
INSERT INTO `creature_text` VALUES (36814, 3, 0, 'Пусть Коса разъединит то, что не должно было быть сплетено! Пусть дух возьмет верх над звериным началом, а зверь закалит душу!', 12, 0, 100, 1, 0, 0, 37730, 743721, 743721, 0, 'Талран Дитя Леса to Player');
INSERT INTO `creature_text` VALUES (36823, 0, 0, 'You have come to bring Arthas to justice? To see the Lich King destroyed?', 14, 0, 0, 0, 0, 17394, 38114, 0, 0, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text` VALUES (36823, 1, 0, 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity.', 14, 0, 0, 0, 0, 17395, 38115, 0, 0, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES (36823, 2, 0, 'Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within!', 14, 0, 0, 0, 0, 17396, 38116, 0, 0, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_3');
INSERT INTO `creature_text` VALUES (36827, 0, 0, 'I suppose I need some more practice.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36827, 0, 1, 'Well fought.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36827, 0, 2, 'HELP!HELP!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36828, 0, 0, 'I suppose I need some more practice.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36828, 0, 1, 'Well fought.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36828, 0, 2, 'You win. This time.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36838, 4, 0, '|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t |cFFBC0000 ПЕРЕГРЕВ |r|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t', 42, 0, 100, 0, 0, 0, 36720, 3000, 3000, 0, 'Корабельное орудие Альянса to Player');
INSERT INTO `creature_text` VALUES (36853, 0, 0, 'You are fools to have come to this place! The icy winds of Northrend will consume your souls!', 14, 0, 0, 0, 0, 17007, 37511, 0, 0, 0, 'Sindragosa - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36853, 1, 0, 'Suffer, mortals, as your pathetic magic betrays you!', 14, 0, 0, 0, 0, 17014, 37138, 0, 0, 0, 'Sindragosa - SAY_UNCHAINED_MAGIC');
INSERT INTO `creature_text` VALUES (36853, 2, 0, '%s prepares to unleash a wave of blistering cold!', 41, 0, 0, 0, 0, 0, 37108, 0, 0, 0, 'Sindragosa - EMOTE_WARN_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 3, 0, 'Can you feel the cold hand of death upon your heart?', 14, 0, 0, 0, 0, 17013, 37137, 0, 0, 0, 'Sindragosa - SAY_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 4, 0, 'Aaah! It burns! What sorcery is this?!', 14, 0, 0, 0, 0, 17015, 38550, 0, 0, 0, 'Sindragosa - SAY_RESPITE_FOR_A_TORMENTED_SOUL');
INSERT INTO `creature_text` VALUES (36853, 5, 0, 'Your incursion ends here! None shall survive!', 14, 0, 0, 0, 0, 17012, 37136, 0, 0, 0, 'Sindragosa - SAY_AIR_PHASE');
INSERT INTO `creature_text` VALUES (36853, 6, 0, 'Now feel my master\'s limitless power and despair!', 14, 0, 0, 0, 0, 17016, 37109, 0, 0, 0, 'Sindragosa - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (36853, 7, 0, '%s fires a frozen orb towards $N!', 41, 0, 0, 0, 0, 0, 38672, 0, 0, 0, 'Sindragosa - EMOTE_WARN_FROZEN_ORB');
INSERT INTO `creature_text` VALUES (36853, 8, 0, 'Perish!', 14, 0, 0, 0, 0, 17008, 47717, 0, 0, 0, 'Sindragosa - SAY_KILL 1');
INSERT INTO `creature_text` VALUES (36853, 8, 1, 'A flaw of mortality...', 14, 0, 0, 0, 0, 17009, 37134, 0, 0, 0, 'Sindragosa - SAY_KILL 2');
INSERT INTO `creature_text` VALUES (36853, 9, 0, 'Enough! I tire of these games!', 14, 0, 0, 0, 0, 17011, 37140, 0, 0, 0, 'Sindragosa - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36853, 10, 0, 'Free...at last...', 14, 0, 0, 0, 0, 17010, 37139, 0, 0, 0, 'Sindragosa - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36855, 0, 0, 'You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness.', 14, 0, 0, 0, 0, 17272, 38139, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (36855, 1, 0, 'You can see through the fog that hangs over this world like a shroud, and grasp where true power lies.', 14, 0, 0, 0, 0, 17273, 38140, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (36855, 2, 0, 'Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within.', 14, 0, 0, 0, 0, 16878, 38134, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (36855, 3, 0, 'It is a weakness; a crippling flaw.... A joke played by the Creators upon their own creations.', 14, 0, 0, 0, 0, 17268, 38135, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (36855, 4, 0, 'The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it.', 14, 0, 0, 0, 0, 17269, 38136, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (36855, 5, 0, 'Through our Master, all things are possible. His power is without limit, and his will unbending.', 14, 0, 0, 0, 0, 17270, 38137, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (36855, 6, 0, 'Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken.', 14, 0, 0, 0, 0, 17271, 38138, 0, 0, 0, 'Lady Deathwhisper - SAY_INTRO_7');
INSERT INTO `creature_text` VALUES (36855, 7, 0, 'What is this disturbance?! You dare trespass upon this hallowed ground? This shall be your final resting place.', 14, 0, 0, 0, 0, 16868, 38122, 0, 0, 0, 'Lady Deathwhisper - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36855, 8, 0, 'Enough! I see I must take matters into my own hands!', 14, 0, 0, 0, 0, 16877, 38126, 0, 0, 0, 'Lady Deathwhisper - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (36855, 9, 0, '%s\'s Mana Barrier shimmers and fades away!', 41, 0, 0, 0, 0, 0, 38121, 0, 0, 0, 'Lady Deathwhisper - SAY_PHASE_2_EMOTE');
INSERT INTO `creature_text` VALUES (36855, 10, 0, 'You are weak, powerless to resist my will!', 14, 0, 0, 0, 0, 16876, 38127, 0, 0, 0, 'Lady Deathwhisper - SAY_DOMINATE_MIND');
