INSERT INTO `creature_text` VALUES (37081, 0, 0, 'Eat dwarven lead!', 12, 0, 100, 0, 0, 0, 37008, 0, 0, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37081, 0, 1, 'For Ironforge!', 12, 0, 100, 0, 0, 0, 37009, 0, 0, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37081, 0, 2, 'Get back, ye filthy beast!', 12, 0, 100, 0, 0, 0, 37007, 0, 0, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37087, 0, 0, 'Let\'s keep this fire going, dwarves!', 0, 0, 1, 0, 0, 0, 36972, 0, 0, 0, 'Jona Ironstock - Text 1');
INSERT INTO `creature_text` VALUES (37087, 0, 1, 'What\'s Joren doin\' out there, anyhow?', 0, 0, 1, 0, 0, 0, 36973, 0, 0, 0, 'Jona Ironstock - Text 2');
INSERT INTO `creature_text` VALUES (37087, 0, 2, 'Don\'t fret, we\'ll be outta here soon.', 0, 0, 1, 0, 0, 0, 36971, 0, 0, 0, 'Jona Ironstock - Text 3');
INSERT INTO `creature_text` VALUES (37087, 0, 3, 'Get ta work, boys!', 0, 0, 1, 0, 0, 0, 36970, 0, 0, 0, 'Jona Ironstock - Text 4');
INSERT INTO `creature_text` VALUES (37092, 4, 0, '%s исчезает в земле!', 16, 0, 100, 0, 0, 0, 36918, 3000, 3000, 0, 'Вырост to Player');
INSERT INTO `creature_text` VALUES (37093, 3, 0, '%s распадается на облако загадочных спор.', 16, 0, 100, 0, 0, 0, 36915, 12000, 12000, 0, 'Хлещущая лоза to Player');
INSERT INTO `creature_text` VALUES (37106, 0, 0, 'Kick the Footbomb between the smokestacks up and behind the opposing goal!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37106, 1, 0, 'You got the juice, kid. That\'s for sure! Too bad that dragon had to come and ruin everything. No matter. You made us proud. Now get back to headquarters.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37108, 0, 0, 'Da spirits be angry with us.', 12, 0, 100, 1, 3, 0, 36962, 0, 0, 0, 'Soothsayer Shi\'kala 1st sentence');
INSERT INTO `creature_text` VALUES (37108, 1, 0, 'I don\' know why the spirits be rejectin\' us so.', 12, 0, 100, 1, 3, 0, 36958, 0, 0, 0, 'Soothsayer Shi\'kala 2nd sentence');
INSERT INTO `creature_text` VALUES (37108, 2, 0, 'Don\' worry, child. Grik\'nir gonna help us get through this.', 12, 0, 100, 1, 3, 0, 0, 0, 0, 0, 'Soothsayer Shi\'kala 3rd sentence');
INSERT INTO `creature_text` VALUES (37119, 0, 0, 'This is our final stand. What happens here will echo through the ages. Regardless of outcome, they will know that we fought with honor. That we fought for the freedom and safety of our people!', 14, 0, 0, 22, 0, 16653, 0, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES (37119, 1, 0, 'Remember, heroes, fear is your greatest enemy in these befouled halls. Steel your heart and your soul will shine brighter than a thousand suns. The enemy will falter at the sight of you. They will fall as the light of righteousness envelops them!', 14, 0, 0, 22, 0, 16654, 37028, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES (37119, 2, 0, 'Our march upon Icecrown Citadel begins now!', 14, 0, 0, 22, 0, 16655, 37029, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_3');
INSERT INTO `creature_text` VALUES (37119, 3, 0, 'ARTHAS! I swore that I would see you dead and the Scourge dismantled! I\'m going to finish what I started at Light\'s Hope!', 14, 0, 0, 22, 0, 16656, 37031, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_4');
INSERT INTO `creature_text` VALUES (37119, 4, 0, 'The power of the Light knows no bounds, Saurfang. His soul is under great strain, but he lives - for now.', 12, 0, 0, 1, 0, 16658, 37056, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_5');
INSERT INTO `creature_text` VALUES (37119, 5, 0, 'By the Light, it must be so!', 12, 0, 0, 5, 0, 16657, 37038, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_A_5');
INSERT INTO `creature_text` VALUES (37122, 0, 0, 'Never... could reach... the top shelf...', 14, 0, 0, 0, 0, 16586, 37074, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_DEATH');
INSERT INTO `creature_text` VALUES (37122, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 37076, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES (37122, 2, 0, 'THAT was for bringing me spoiled spider ichor!', 14, 0, 0, 0, 0, 16588, 0, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES (37122, 3, 0, 'Don\'t... let Finklestein use me... for his potions...', 14, 0, 0, 0, 0, 16589, 37077, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37122, 4, 0, 'The loss of our comrades was unpreventable. They lived and died in the service of the Argent Crusade.', 14, 0, 0, 0, 0, 16590, 37534, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37122, 5, 0, 'Even dying here beats spending another day collecting reagents for that madman, Finklestein.', 14, 0, 0, 0, 0, 16585, 36948, 0, 0, 0, 'Captain Arnath - SAY_ARNATH_INTRO_2');
INSERT INTO `creature_text` VALUES (37123, 0, 0, 'No amount of healing can save me now. Fight on, brothers...', 14, 0, 0, 0, 0, 16810, 37665, 0, 0, 0, 'Captain Brandon - SAY_BRANDON_DEATH');
INSERT INTO `creature_text` VALUES (37123, 1, 0, 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!', 14, 0, 0, 0, 0, 16811, 37664, 0, 0, 0, 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES (37123, 2, 0, 'It doesn\'t hurt anymore, does it?', 14, 0, 0, 0, 0, 16812, 0, 0, 0, 0, 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES (37123, 3, 0, 'I\'m sorry...', 14, 0, 0, 0, 0, 16813, 37659, 0, 0, 0, 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37123, 4, 0, 'You have done much in this war against the Scourge. May the light embrace you.', 14, 0, 0, 0, 0, 16815, 37533, 0, 0, 0, 'Captain Brandon - SAY_BRANDON_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37124, 0, 0, 'Please... burn my remains. Let me live warm in the afterlife...', 14, 0, 0, 0, 0, 16844, 37666, 0, 0, 0, 'Captain Grondel - SAY_GRONDEL_DEATH');
INSERT INTO `creature_text` VALUES (37124, 1, 0, 'No! Why was I denied a death by flame? You must all BURN!', 14, 0, 0, 0, 0, 16845, 37667, 0, 0, 0, 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES (37124, 2, 0, 'Can you feel the burn?', 14, 0, 0, 0, 0, 16846, 37660, 0, 0, 0, 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES (37124, 3, 0, 'What... have I done? No!', 14, 0, 0, 0, 0, 16847, 37661, 0, 0, 0, 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37124, 4, 0, 'What can possibly redeem this unholy place? Thank you...', 14, 0, 0, 0, 0, 16849, 0, 0, 0, 0, 'Captain Grondel - SAY_GRONDEL_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37125, 0, 0, 'It was... a worthy afterlife.', 14, 0, 0, 0, 0, 16998, 37668, 0, 0, 0, 'Captain Rupert - SAY_RUPERT_DEATH');
INSERT INTO `creature_text` VALUES (37125, 1, 0, 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', 14, 0, 0, 0, 0, 16999, 37669, 0, 0, 0, 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES (37125, 2, 0, 'So that\'s what happens when you stand too close to a bomb!', 14, 0, 0, 0, 0, 17000, 0, 0, 0, 0, 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES (37125, 3, 0, 'What an... explosive ending!', 14, 0, 0, 0, 0, 17001, 37663, 0, 0, 0, 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37125, 4, 0, 'Beware the dangers that lie ahead... and do try to remain in one piece.', 14, 0, 0, 0, 0, 17003, 37536, 0, 0, 0, 'Captain Rupert - SAY_RUPERT_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37126, 0, 0, 'You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches!', 14, 0, 0, 0, 0, 17017, 37024, 0, 0, 0, 'Sister Svalna - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (37126, 1, 0, 'Miserable creatures! Die!', 14, 0, 0, 0, 0, 17018, 37065, 0, 0, 0, 'Sister Svalna - SAY_KILL_CAPTAIN');
INSERT INTO `creature_text` VALUES (37126, 2, 0, 'Foolish Crok. You brought my reinforcements with you. Arise, Argent Champions, and serve the Lich King in death!', 14, 0, 0, 0, 0, 17019, 37020, 0, 0, 0, 'Sister Svanlna - SAY_RESURRECT_CAPTAINS');
INSERT INTO `creature_text` VALUES (37126, 3, 0, 'Come, Scourgebane. I\'ll show the master which of us is truly worthy of the title of \"Champion\"!', 14, 0, 0, 0, 0, 17020, 37653, 0, 0, 0, 'Sister Svalna - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (37126, 4, 0, 'What a pitiful choice of an ally, Crok!', 14, 0, 0, 0, 0, 17021, 37654, 0, 0, 0, 'Sister Svalna - SAY_KILL');
INSERT INTO `creature_text` VALUES (37126, 5, 0, 'What? They died so easily? No matter.', 14, 0, 0, 0, 0, 17022, 37163, 0, 0, 0, 'Sister Svalna - SAY_CAPTAIN_DEATH');
INSERT INTO `creature_text` VALUES (37126, 6, 0, 'Perhaps... you were right, Crok.', 14, 0, 0, 0, 0, 17023, 37135, 0, 0, 0, 'Sister Svalna - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37126, 7, 0, '%s has impaled $N!', 41, 0, 0, 0, 0, 0, 37977, 0, 0, 0, 'Sister Svalna - EMOTE_SVALNA_IMPALE');
INSERT INTO `creature_text` VALUES (37126, 8, 0, '%s\'s Aether Shield has been shattered by $N!', 41, 0, 0, 0, 0, 0, 37978, 0, 0, 0, 'Sister Svalna - EMOTE_SVALNA_BROKEN_SHIELD');
INSERT INTO `creature_text` VALUES (37129, 0, 0, 'Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives.', 14, 0, 0, 0, 0, 16819, 36945, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_INTRO_1');
INSERT INTO `creature_text` VALUES (37129, 1, 0, 'Enough idle banter! Our champions have arrived - support them as we push our way through the hall!', 14, 0, 0, 0, 0, 16820, 36946, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_INTRO_3');
INSERT INTO `creature_text` VALUES (37129, 2, 0, 'Draw them back to us, and we\'ll assist you.', 14, 0, 0, 0, 0, 16821, 37013, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_0');
INSERT INTO `creature_text` VALUES (37129, 3, 0, 'Quickly, push on!', 14, 0, 0, 0, 0, 16823, 37015, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_1');
INSERT INTO `creature_text` VALUES (37129, 4, 0, 'Her reinforcements will arrive shortly, we must bring her down quickly!', 14, 0, 0, 0, 0, 16824, 37016, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_FINAL_WP');
INSERT INTO `creature_text` VALUES (37129, 5, 0, 'I\'ll draw her attacks. Return our brothers to their graves, then help me bring her down!', 14, 0, 0, 15, 0, 16825, 37017, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 6, 0, 'I must rest for a moment', 14, 0, 0, 0, 0, 16826, 37164, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_WEAKENING_GAUNTLET');
INSERT INTO `creature_text` VALUES (37129, 7, 0, 'Champions, I cannot hold her back any longer!', 14, 0, 0, 0, 0, 16827, 37165, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_WEAKENING_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 8, 0, 'Vengeance alone... was not enough!', 14, 0, 0, 0, 0, 16828, 0, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_DEATH');
INSERT INTO `creature_text` VALUES (37173, 0, 0, 'What we gon\' do now, you ask ? We wait.', 12, 0, 100, 1, 3, 0, 0, 0, 0, 0, 'Soothsayer Rikkari 1st sentence');
INSERT INTO `creature_text` VALUES (37173, 1, 0, 'Grik\'nir says he gon\' talk to the elemental, get it to fight on our side.', 12, 0, 100, 1, 3, 0, 36960, 0, 0, 0, 'Soothsayer Rikkari 2nd sentence');
INSERT INTO `creature_text` VALUES (37173, 2, 0, 'Soon enough we take over dis valley. Soon enough.', 12, 0, 100, 1, 3, 0, 0, 0, 0, 0, 'Soothsayer Rikkari 3rd sentence');
INSERT INTO `creature_text` VALUES (37174, 0, 0, 'Our land be a land of ice an\' snow', 12, 0, 100, 1, 3, 0, 0, 0, 0, 0, 'Soothsayer Mirim\'koa 1st sentence');
INSERT INTO `creature_text` VALUES (37174, 1, 0, 'But beneath the earth, child, there always be fire.', 12, 0, 100, 1, 3, 0, 36964, 0, 0, 0, 'Soothsayer Mirim\'koa 2nd sentence');
INSERT INTO `creature_text` VALUES (37174, 2, 0, 'De spirit come from deep down to talk with Grik\'nir.', 12, 0, 100, 1, 3, 0, 36965, 0, 0, 0, 'Soothsayer Mirim\'koa 3rd sentence');
INSERT INTO `creature_text` VALUES (37179, 0, 0, 'Бросай футбомбные мячи в \"Акул\"!', 42, 0, 100, 0, 0, 17466, 36985, 0, 0, 0, '\"Буканьер Трюмных Вод\"');
INSERT INTO `creature_text` VALUES (37181, 3, 0, 'Сломить его было очень трудно. Я подверг его душу ужасным мучениям... Он так долго сопротивлялся. Но вскоре он склонится перед своим королем.', 14, 0, 100, 0, 0, 17233, 38057, 130916, 130916, 0, 'Король-лич to Проклятый');
INSERT INTO `creature_text` VALUES (37183, 3, 0, 'НИКОГДА!.. Я никогда... не буду служить тебе...', 14, 0, 100, 0, 0, 17078, 37035, 154019, 154019, 0, 'Верховный лорд Болвар Фордрагон to Проклятый');
INSERT INTO `creature_text` VALUES (37187, 0, 0, 'Kor\'kron, move out! Champions, watch your backs. The Scourge have been...', 14, 0, 0, 22, 0, 17103, 37634, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_1');
INSERT INTO `creature_text` VALUES (37187, 1, 0, 'My boy died at the Wrath Gate. I am here only to collect his body.', 12, 0, 0, 397, 0, 17097, 37638, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_3');
INSERT INTO `creature_text` VALUES (37187, 2, 0, 'We named him Dranosh. It means \"Heart of Draenor\" in orcish. I would not let the warlocks take him. My boy would be safe, hidden away by the elders of Garadar.', 12, 0, 0, 1, 0, 17098, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_5');
INSERT INTO `creature_text` VALUES (37187, 3, 0, 'I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted...', 12, 0, 0, 1, 0, 17099, 37641, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_6');
INSERT INTO `creature_text` VALUES (37187, 4, 0, 'Today, I fulfill that promise.', 12, 0, 0, 397, 0, 17100, 37642, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_7');
INSERT INTO `creature_text` VALUES (37187, 5, 0, 'High Overlord Saurfang charges!', 16, 0, 0, 53, 0, 17104, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_8');
INSERT INTO `creature_text` VALUES (37187, 6, 0, 'Behind you lies the body of my only son. Nothing will keep me from him.', 12, 0, 0, 0, 0, 17094, 37525, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_8');
INSERT INTO `creature_text` VALUES (37187, 7, 0, 'High Overlord Saurfang walks over to his son and kneels before his son\'s body.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_12');
INSERT INTO `creature_text` VALUES (37187, 8, 0, '[Orcish] No\'ku kil zil\'nok ha tar.', 12, 0, 0, 0, 0, 17096, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_13');
INSERT INTO `creature_text` VALUES (37187, 9, 0, 'Higher Overlord Saurfang picks up the body of his son and walks over towards Varian', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_14');
INSERT INTO `creature_text` VALUES (37187, 10, 0, 'I will not forget this... kindness. I thank you, Highness', 12, 0, 0, 0, 0, 17095, 37529, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_15');
INSERT INTO `creature_text` VALUES (37187, 11, 0, '%s coughs.', 16, 0, 0, 0, 0, 17105, 49090, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_1');
INSERT INTO `creature_text` VALUES (37187, 12, 0, '%s weeps over the corpse of his son.', 16, 0, 0, 15, 0, 17106, 37647, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_2');
INSERT INTO `creature_text` VALUES (37187, 13, 0, 'You will have a proper ceremony in Nagrand next to the pyres of your mother and ancestors.', 12, 0, 0, 0, 0, 17101, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_3');
INSERT INTO `creature_text` VALUES (37187, 14, 0, 'Honor, young heroes... no matter how dire the battle... Never forsake it!', 12, 0, 0, 0, 0, 17102, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_4');
INSERT INTO `creature_text` VALUES (37187, 15, 0, 'The paladin still lives? Is it possible, Highlord? Could he have survived?', 12, 0, 0, 6, 0, 17107, 37051, 0, 0, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
INSERT INTO `creature_text` VALUES (37187, 16, 0, 'Then we must save him! If we rescue Bolvar Fordragon, we may quell the unrest between the Alliance and the Horde.', 12, 0, 0, 5, 0, 17108, 37053, 0, 0, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37187, 17, 0, 'Our mission is now clear: The Lich King will answer for his crimes and we will save Highlord Bolvar Fordragon!', 12, 0, 0, 15, 0, 17109, 37054, 0, 0, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37187, 18, 0, 'Kor\'kron, prepare Orgrim\'s Hammer for its final voyage! Champions, our gunship will find a point to dock on the upper reaches of the citadel. Meet us there!', 14, 0, 0, 22, 0, 17110, 37055, 0, 0, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37188, 0, 0, '%s вытирает слезы и улыбается королю Вариану Ринну.', 16, 0, 100, 0, 0, 0, 37544, 0, 0, 0, 'Леди Джайна Праудмур to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37188, 0, 0, '%s плачет.', 16, 0, 100, 0, 0, 0, 42473, 0, 0, 0, 'Леди Джайна Праудмур to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37188, 0, 0, 'Все в порядке... Просто... Я очень горжусь своим королем.', 12, 0, 100, 0, 0, 16652, 37546, 0, 0, 0, 'Леди Джайна Праудмур to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Bringt unsere Männer sofort in Stellung, Tobias. Wir dürfen die Sichel nicht in die Hände der Verlassenen gelangen lassen!', 12, 0, 100, 53, 0, 0, 37819, 235328, 235328, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Lorna!', 14, 0, 100, 0, 0, 19511, 38641, 393203, 393203, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Да, Генн. Нас связывает не закон, а что-то гораздо большее. Мои люди готовы отдать жизнь, сражаясь под твоими знаменами.', 12, 0, 100, 273, 0, 19512, 38642, 803806, 803806, 0, 'Лорд Дарий Краули to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Лорна!', 14, 0, 100, 0, 0, 19511, 38641, 761469, 761469, 0, 'Лорд Дарий Краули to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Немедленно выводи наших людей на позиции, Тобиас. Нельзя допустить, чтобы Коса попала в руки Отрекшихся!', 12, 0, 100, 53, 0, 0, 37819, 256858, 256858, 0, 'Лорд Дарий Краули to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Эта жаба вещает от твоего имени, Генн? Ты пришел к нам, как друг? Или как узурпатор?', 14, 0, 100, 0, 0, 19510, 38640, 786190, 786190, 0, 'Лорд Дарий Краули to Player');
INSERT INTO `creature_text` VALUES (37198, 0, 0, '<Static> Is this thing on? Hello?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 1, 0, 'Ahh, there you are. Hello! I figured that, while you\'re flying to Kharanos, I\'d give you some data about what\'s going on outside of Coldridge Valley.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 2, 0, 'It seems you Coldridge folks are dreadfully under-informed about the events of the last few days.', 12, 0, 100, 0, 0, 0, 37118, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 3, 0, 'If you thought you had problems with trolls in Coldridge, wait \'til you get to Kharanos! That big cataclysm got the trolls all stirred up out here.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 4, 0, 'And if you\'ll look to your left, you\'ll see that the gnomes have retaken Gnomeregan. Hooray for us!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 5, 0, 'There\'s plenty else going on out here, too. We\'ve opened up the path to the airport, there\'s rumors of Dark Irons in the east... oh, and I think something weird is going on with that dwarf king, Magni. Might want to check it out.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37198, 6, 0, 'And here we are! Welcome to Kharanos! Watch your step on the way out.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (37200, 0, 0, 'Let\'s get a move on then! Move ou...', 14, 0, 0, 0, 0, 16974, 37457, 0, 0, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES (37200, 1, 0, 'A lone orc against the might of the Alliance???', 14, 0, 0, 0, 0, 16970, 37460, 0, 0, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES (37200, 2, 0, 'Charge!!!', 14, 0, 0, 0, 0, 16971, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES (37200, 3, 0, '%s gasps for air.', 16, 0, 0, 0, 0, 16975, 41180, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES (37200, 4, 0, 'That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end...', 12, 0, 0, 0, 0, 16976, 37503, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES (37200, 5, 0, 'What in the... There, in the distance!', 12, 0, 0, 0, 0, 16977, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES (37200, 6, 0, 'A Horde Zeppelin flies up to the rise.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES (37200, 7, 0, 'Soldiers, fall in! Looks like the Horde are comin\' to take another shot!', 14, 0, 0, 0, 0, 16978, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES (37200, 8, 0, 'The Zeppelin docks, and High Overlord Saurfang hops out, confronting the Alliance soldiers and Muradin', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES (37200, 9, 0, 'Don\'t force me hand, orc. We can\'t let ye pass.', 12, 0, 0, 0, 0, 16972, 37524, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES (37200, 10, 0, 'I... I can\'t do it. Get back on yer ship and we\'ll spare yer life.', 12, 0, 0, 0, 0, 16973, 37526, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_9');
INSERT INTO `creature_text` VALUES (37200, 11, 0, 'A mage portal from Stormwind appears between the two and Varian Wrynn and Jaina Proudmoore emerge.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_10');
INSERT INTO `creature_text` VALUES (37200, 12, 0, 'Right away, yer majesty!', 12, 0, 0, 0, 0, 16979, 37547, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_21');
INSERT INTO `creature_text` VALUES (37200, 13, 0, 'Could it be, Lord Fordring? If Bolvar lives, mayhap there is hope fer peace between the Alliance and the Horde. We must reach the top o\' this cursed place and free the paladin!', 12, 0, 0, 6, 0, 16980, 37037, 0, 0, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37200, 14, 0, 'Prepare the Skybreaker fer an aerial assault on the citadel!', 14, 0, 0, 5, 0, 16981, 37043, 0, 0, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37200, 15, 0, 'Heroes, ye must fight yer way to a clear extraction point within Icecrown. We\'ll try an\' rendezvous on the ramparts!', 14, 0, 0, 22, 0, 16982, 37046, 0, 0, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37217, 0, 0, '%s cries out with a loud, baying howl!', 41, 0, 0, 0, 0, 0, 37849, 0, 0, 0, 'Precious - EMOTE_PRECIOUS_ZOMBIES');
INSERT INTO `creature_text` VALUES (37221, 0, 0, 'The chill of this place... Brr... I can feel my blood freezing.', 14, 0, 100, 0, 0, 16631, 37591, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_1');
INSERT INTO `creature_text` VALUES (37221, 1, 0, 'What is that? Up ahead! Could it be... ? Heroes at my side!', 14, 0, 100, 0, 0, 16632, 37592, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_2');
INSERT INTO `creature_text` VALUES (37221, 2, 0, 'Frostmourne! The blade that destroyed our kingdom...', 14, 0, 100, 0, 0, 16633, 37593, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_3');
INSERT INTO `creature_text` VALUES (37221, 3, 0, 'Stand back! Touch that blade and your soul will be scarred for all eternity! I must attempt to commune with the spirits locked away within Frostmourne. Give me space, back up please!', 14, 0, 100, 0, 0, 16634, 37594, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_4');
INSERT INTO `creature_text` VALUES (37221, 4, 0, 'Uther! Dear Uther! ... I... I\'m so sorry.', 12, 0, 100, 0, 0, 16635, 37595, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_5');
INSERT INTO `creature_text` VALUES (37221, 5, 0, 'Arthas is here? Maybe I...', 12, 0, 100, 0, 0, 16636, 37596, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_6');
INSERT INTO `creature_text` VALUES (37221, 6, 0, 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', 12, 0, 100, 0, 0, 16637, 37597, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_7');
INSERT INTO `creature_text` VALUES (37221, 7, 0, 'Tell me how, Uther? How do I destroy my prince? My...', 12, 0, 100, 0, 0, 16638, 37598, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_8');
INSERT INTO `creature_text` VALUES (37221, 8, 0, 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', 12, 0, 100, 0, 0, 16639, 37599, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_9');
INSERT INTO `creature_text` VALUES (37221, 9, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 16640, 37600, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_10');
INSERT INTO `creature_text` VALUES (37221, 10, 0, 'Then maybe there is still hope...', 12, 0, 100, 0, 0, 16641, 37601, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_11');
INSERT INTO `creature_text` VALUES (37221, 11, 0, 'You won\'t deny me this Arthas! I must know! I must find out!', 14, 0, 100, 0, 0, 16642, 37602, 0, 0, 0, 'Jaina SAY_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES (37223, 0, 0, 'I... I don\'t believe it! Frostmourne stands before us, unguarded! Just as the Gnome claimed. Come, heroes!', 14, 0, 100, 0, 0, 17049, 37568, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_1');
INSERT INTO `creature_text` VALUES (37223, 1, 0, 'Standing this close to the blade that ended my life... The pain... It is renewed.', 14, 0, 100, 0, 0, 17050, 37569, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_2');
INSERT INTO `creature_text` VALUES (37223, 2, 0, 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', 14, 0, 100, 0, 0, 17051, 37570, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_3');
INSERT INTO `creature_text` VALUES (37223, 3, 0, 'Uther...Uther the Lightbringer. How...', 12, 0, 100, 0, 0, 17052, 37571, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_4');
INSERT INTO `creature_text` VALUES (37223, 4, 0, 'The Lich King is here? Then my destiny shall be fulfilled today!', 12, 0, 100, 0, 0, 17053, 37572, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_5');
INSERT INTO `creature_text` VALUES (37223, 5, 0, 'There must be a way... ', 12, 0, 100, 0, 0, 17054, 37573, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_6');
INSERT INTO `creature_text` VALUES (37223, 6, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 17055, 37600, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_7');
INSERT INTO `creature_text` VALUES (37223, 7, 0, 'The Frozen Throne...', 12, 0, 100, 0, 0, 17056, 37575, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_8');
INSERT INTO `creature_text` VALUES (37223, 8, 0, 'You will not escape me that easily, Arthas! I will have my vengeance!', 14, 0, 100, 0, 0, 17057, 37576, 0, 0, 0, 'Sylvanas SAY_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES (37225, 3, 0, 'Да. А-а-ах... Он... он приближается. Вы... вы должны...', 12, 0, 100, 5, 0, 16665, 37589, 103252, 103252, 0, 'Утер Светоносный to Player');
INSERT INTO `creature_text` VALUES (37226, 3, 0, 'Так ты хочешь поговорить с мертвыми? Нет ничего проще.', 14, 0, 100, 6, 0, 17226, 37614, 43847, 43847, 0, 'Король-лич to Player');
INSERT INTO `creature_text` VALUES (37230, 0, 0, 'A Spire Frostwyrm lands just before Orgrim\'s Hammer.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Frostwyrm - SAY_FROSTWYRM_LAND_H_1');
INSERT INTO `creature_text` VALUES (37230, 1, 0, 'A Spire Frostwyrm lands just before The Skybreaker. ', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Frostwyrm - SAY_FROSTWYRM_LAND_A_2');
INSERT INTO `creature_text` VALUES (37487, 3, 0, 'Бе, меня сейчас стошнит!', 12, 0, 100, 0, 0, 0, 37152, 221000, 221000, 0, 'Беженец из Таурахо to Хваткая лиана');
INSERT INTO `creature_text` VALUES (37487, 3, 0, 'Вот так так! Никогда больше не буду есть спаржу.', 12, 0, 100, 18, 0, 0, 37142, 14000, 14000, 0, 'Беженец из Таурахо to Хваткая лиана');
INSERT INTO `creature_text` VALUES (37487, 3, 0, 'Меня схватили!', 12, 0, 100, 0, 0, 0, 37151, 166000, 166000, 0, 'Беженец из Таурахо to Хваткая лиана');
INSERT INTO `creature_text` VALUES (37487, 3, 0, 'Помоги... Мне... Выбраться отсюда!', 12, 0, 100, 5, 0, 0, 37144, 13000, 13000, 0, 'Беженец из Таурахо to Хваткая лиана');
INSERT INTO `creature_text` VALUES (37503, 3, 0, 'Они не должны прорваться к Синдрагосе! Скорее, остановите их!', 14, 0, 100, 0, 0, 17167, 37105, 6349420, 6349420, 0, 'Оберег Синдрагосы to Player');
INSERT INTO `creature_text` VALUES (37533, 0, 0, '%s издает предсмертный крик, эхом затихающий вдали.', 41, 0, 100, 0, 0, 8470, 37513, 0, 0, 0, 'Иней to Player');
INSERT INTO `creature_text` VALUES (37534, 0, 0, '%s издает предсмертный крик, эхом затихающий вдали.', 41, 0, 100, 0, 0, 8470, 37513, 0, 0, 0, 'Хребтохруст to Player');
INSERT INTO `creature_text` VALUES (37554, 0, 0, 'He\'s too powerful! Heroes, quickly, come to me! We must leave this place immediately! I will do what I can do hold him in place while we flee.', 14, 0, 100, 0, 0, 17058, 0, 0, 0, 0, 'Sylvanas SAY_SYLVANA_AGGRO');
INSERT INTO `creature_text` VALUES (37554, 1, 0, 'No wall can hold the Banshee Queen! Keep the undead at bay, heroes! I will tear this barrier down!', 14, 0, 100, 0, 0, 17029, 38540, 0, 0, 0, 'Sylvanas SAY_SYLVANA_WALL_01');
INSERT INTO `creature_text` VALUES (37554, 2, 0, 'Another barrier? Stand strong, champions! I will bring the wall down!', 14, 0, 100, 0, 0, 17030, 38541, 0, 0, 0, 'Sylvanas SAY_SYLVANA_WALL_02');
INSERT INTO `creature_text` VALUES (37554, 3, 0, 'I grow tired of these games, Arthas! Your walls can\'t stop me!', 14, 0, 100, 0, 0, 17031, 38542, 0, 0, 0, 'Sylvanas SAY_SYLVANA_WALL_03');
INSERT INTO `creature_text` VALUES (37554, 4, 0, 'You won\'t impede our escape, fiend. Keep the undead off me while I bring this barrier down!', 14, 0, 100, 0, 0, 17032, 38543, 0, 0, 0, 'Sylvanas SAY_SYLVANA_WALL_04');
INSERT INTO `creature_text` VALUES (37554, 5, 0, 'There\'s an opening up ahead. GO NOW!', 14, 0, 100, 1, 0, 17059, 38551, 0, 0, 0, 'Sylvanas SAY_SYLVANA_ESCAPE_01');
INSERT INTO `creature_text` VALUES (37554, 6, 0, 'We\'re almost there! Don\'t give up!', 14, 0, 100, 0, 0, 17060, 0, 0, 0, 0, 'Sylvanas SAY_SYLVANA_ESCAPE_02');
INSERT INTO `creature_text` VALUES (37554, 7, 0, 'BLASTED DEAD END! So this is how it ends. Prepare yourselves, heroes, for today we make our final stand!', 14, 0, 100, 5, 0, 17061, 37170, 0, 0, 0, 'Sylvanas SAY_SYLVANA_TRAP');
INSERT INTO `creature_text` VALUES (37554, 8, 0, 'We are safe... for now. His strength has increased tenfold since our last battle. It will take a mighty army to destroy the Lich King. An army greater than even the Horde can rouse.', 14, 0, 100, 1, 0, 17062, 37171, 0, 0, 0, 'Sylvanas SAY_SYLVANA_FINAL_2');
INSERT INTO `creature_text` VALUES (37581, 3, 0, 'Герои! Мы будем сдерживать нежить до последнего вздоха. Сразитесь с Повелителем Плети!', 14, 0, 100, 0, 0, 17150, 37232, 20249, 20249, 0, 'Горкун Железный Череп to Иней');
INSERT INTO `creature_text` VALUES (37581, 3, 0, 'Храбрые защитники, мы обязаны вам своими жизнями, своей свободой... И хотя это слишком малая благодарность за ваше бесстрашие, знайте, что мы повсюду разнесем весть о ваших деяниях и о том Свете, что вы несете сквозь эти мрачные залы.', 14, 0, 100, 5, 0, 17151, 37320, 168558, 168558, 0, 'Горкун Железный Череп to Player');
INSERT INTO `creature_text` VALUES (37585, 4, 0, '%s is calling for help!', 16, 0, 100, 0, 0, 0, 81221, 37000, 37000, 0, 'Разведчик крепости Северной Стражи');
INSERT INTO `creature_text` VALUES (37592, 0, 0, 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', 14, 0, 100, 0, 0, 17150, 37232, 0, 0, 0, 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES (37592, 1, 0, 'Brave champions, we owe you our lives, our freedom.... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel. ', 14, 0, 100, 0, 0, 0, 37320, 0, 0, 0, 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES (37592, 2, 0, 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and---', 14, 0, 100, 0, 0, 0, 37321, 0, 0, 0, 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES (37592, 3, 0, 'Благодарю, герои. Я последую за вами и позабочусь о рабах, которых вы освободите. Если вы убьете начальника кузни к востоку отсюда, мы сможем как следует подготовиться к предстоящей битве.', 12, 0, 100, 0, 0, 0, 37261, 147521, 147521, 0, 'Горкун Железный Череп to Player');
INSERT INTO `creature_text` VALUES (37592, 3, 0, 'Начальник кузни мертв! Созовите вооруженных бойцов, теперь надо убить Повелителя Плети.', 14, 0, 100, 0, 0, 0, 37178, 7269, 7269, 0, 'Горкун Железный Череп to Player');
INSERT INTO `creature_text` VALUES (37596, 3, 0, 'Наши разведчики сообщили, что покои Короля-лича находятся в глубине дворца, неподалеку от Ледяного Трона. Это место называется Залами Отражений. Туда и лежит наш путь.', 12, 0, 100, 1, 0, 17040, 37382, 38637, 38637, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (37661, 4, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 1150, 3359, 3359, 0, 'Razormane Seer');
INSERT INTO `creature_text` VALUES (37669, 3, 0, 'Defend the hill!', 42, 0, 100, 0, 0, 0, 37214, 56406, 56406, 73321, 'Fields of Blood Capture Point Bunny to Player');
INSERT INTO `creature_text` VALUES (37669, 3, 0, 'Success! The hill is yours!', 42, 0, 100, 0, 0, 0, 37215, 151047, 151047, 0, 'Fields of Blood Capture Point Bunny to Player');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Danke!', 12, 0, 100, 0, 0, 0, 112662, 68859, 68859, 0, 'Versklavter Dorfbewohner');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Die Verlassenen werden für das bezahlen, was sie getan haben!', 12, 0, 100, 0, 0, 0, 37239, 99297, 99297, 0, 'Versklavte Dorfbewohnerin');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Es ist also wahr? Sogar diejenigen, die vom Fluch betroffen sind, kämpfen gegen die Verlassenen!', 12, 0, 100, 0, 0, 0, 37238, 233266, 233266, 0, 'Versklavter Dorfbewohner');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Отрекшиеся заплатят за свои преступления!', 12, 0, 100, 0, 0, 0, 37239, 146897, 146897, 0, 'Порабощенный селянин');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Спасибо!', 12, 0, 100, 0, 0, 0, 131553, 164844, 164844, 0, 'Порабощенный селянин');
INSERT INTO `creature_text` VALUES (37694, 3, 0, 'Так это правда? Даже пораженные Проклятием готовы сразиться с Отрекшимися!', 12, 0, 100, 0, 0, 0, 37238, 109312, 109312, 0, 'Порабощенный селянин');
INSERT INTO `creature_text` VALUES (37701, 3, 0, 'Ihr könnt Euch glücklich schätzen, dass Ihr noch am Leben seid, Hunde von Gilneas! Und jetzt zurück an die Arbeit, bevor wir es uns anders überlegen!', 14, 0, 100, 25, 0, 0, 37254, 141000, 141000, 0, 'Sklaventreiber der Verlassenen');
INSERT INTO `creature_text` VALUES (37706, 4, 0, '%s calls for help!', 16, 0, 100, 0, 0, 0, 2541, 15469, 15469, 0, 'Desolation Grunt');
INSERT INTO `creature_text` VALUES (37712, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (37735, 4, 0, '%s falls into a frenzy!', 16, 0, 100, 0, 0, 0, 30726, 1595, 1595, 8599, 'Барон Эшбери');
INSERT INTO `creature_text` VALUES (37735, 4, 0, '%s wird wütend!', 16, 0, 100, 0, 0, 0, 24144, 8203, 8203, 8599, 'Baron Ashbury');
INSERT INTO `creature_text` VALUES (37743, 0, 0, '...Busted!', 12, 0, 100, 1, 0, 0, 39085, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37743, 0, 1, 'Cheese it! It\'s the fuzz!', 12, 0, 100, 1, 0, 0, 39081, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37743, 0, 2, 'Hurry it up. The soldiers have already pulled out of here.', 12, 0, 100, 1, 0, 0, 37309, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37743, 1, 0, 'Grab anything you think we can fence.', 12, 0, 100, 1, 0, 0, 37310, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37743, 1, 1, 'Have we cleaned out the inn yet? Burn the rest of it when ', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37743, 1, 2, 'Hey, did anyone loot the mailbox yet?', 12, 0, 100, 1, 0, 0, 37314, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (37803, 3, 0, 'Verfluchte Verlassene! Sie werden für das bezahlen, was sie getan haben!', 12, 0, 100, 1, 0, 0, 37398, 551094, 551094, 0, 'Marcus');
INSERT INTO `creature_text` VALUES (37803, 3, 0, 'Мы не рабы, мы свободные граждане Гилнеаса!', 12, 0, 100, 1, 0, 0, 37400, 723234, 723234, 0, 'Маркус');
INSERT INTO `creature_text` VALUES (37804, 3, 0, 'Совершенно новая \"Каджа-Кола\"! Теперь идей стало на 100 процентов больше!', 14, 0, 100, 0, 0, 0, 37402, 4522, 4522, 0, 'Воздушный шар \"Каджа-Колы\"');
INSERT INTO `creature_text` VALUES (37813, 0, 0, 'For every Horde soldier that you killed -- for every Alliance dog that fell, the Lich King\'s armies grew. Even now the val\'kyr work to raise your fallen as Scourge.', 14, 0, 0, 0, 0, 16701, 0, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES (37813, 1, 0, 'Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me!', 14, 0, 0, 0, 0, 16702, 37459, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES (37813, 2, 0, 'Dwarves...', 14, 0, 0, 0, 0, 16703, 37471, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES (37813, 3, 0, '%s immobilizes Muradin and his guards.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES (37813, 4, 0, 'Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King!', 14, 0, 0, 0, 0, 16704, 37635, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_2');
INSERT INTO `creature_text` VALUES (37813, 5, 0, 'Stubborn and old. What chance do you have? I am stronger, and more powerful than you ever were.', 14, 0, 0, 5, 0, 16705, 37639, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_4');
INSERT INTO `creature_text` VALUES (37813, 6, 0, 'Pathetic old orc. Come then heroes. Come and face the might of the Scourge!', 14, 0, 0, 15, 0, 16706, 0, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_9');
INSERT INTO `creature_text` VALUES (37813, 7, 0, 'BY THE MIGHT OF THE LICH KING!', 14, 0, 0, 0, 0, 16694, 38464, 0, 0, 0, 'Deathbringer Saurfang - SAY_AGGRO');
