INSERT INTO `creature_text` VALUES (37870, 3, 0, 'So wie Goldrinns Geist einst unsere Druiden segnete, so lasst $n gesegnet sein mit der Weisheit seines Volkes und der Wildheit des Wolfgotts.', 12, 0, 100, 1, 0, 0, 0, 369922, 369922, 0, 'Lyros Flinkwind to Player');
INSERT INTO `creature_text` VALUES (37870, 3, 0, 'Как дух Голдринна снизошел на наших предков-друидов, так и на |3-3($n) снизойдет мудрость воргенской расы и ярость их бога.', 12, 0, 100, 1, 0, 0, 0, 720501, 720501, 0, 'Лирий Быстрый Ветер to Player');
INSERT INTO `creature_text` VALUES (37873, 3, 0, 'So wie Daral\'nir die verfluchten Druiden besänftigt, die der Bestie in ihnen die Macht überlassen und das Geichgewicht vernachlässigen, so lasst Tal\'doren $n besänftigen.', 12, 0, 100, 1, 0, 0, 37729, 375125, 375125, 0, 'Vassandra Sturmklaue to Player');
INSERT INTO `creature_text` VALUES (37873, 3, 0, 'Как Дарал\'нир даровал покой проклятым друидам, нарушившим равновесие, так пусть и $n обретет покой в Тал\'дорене.', 12, 0, 100, 1, 0, 0, 37729, 731643, 731643, 0, 'Вассандра Коготь Бури to Player');
INSERT INTO `creature_text` VALUES (37875, 3, 0, 'Nein... Eher sterbe ich, als einen von Euch als meinen König anzuerkennen!', 12, 0, 100, 0, 0, 19631, 38633, 284781, 284781, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (37875, 3, 0, 'Я лучше умру, чем увижу одну из таких тварей на троне!', 12, 0, 100, 0, 0, 19631, 38633, 386823, 386823, 0, 'Лорд Годфри to Player');
INSERT INTO `creature_text` VALUES (37876, 3, 0, 'Es ist vorbei, Godfrey. Ihr habt keine Unterstützung mehr unter den Fürsten des Ostens.', 12, 0, 100, 1, 0, 0, 38632, 279766, 279766, 0, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (37876, 3, 0, 'Все кончено, Годфри. Ни одно восточное баронство больше не поддержит тебя.', 12, 0, 100, 1, 0, 0, 38632, 379935, 379935, 0, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (37879, 0, 0, 'I ... I was not at the Gates of Wrath, but I learned a lot from the surviving soldiers. Your son fought with dignity. He died the hero’s death ... and deserves to be buried with honors.', 12, 0, 100, 0, 0, 16691, 37530, 0, 0, 0, 'Король Вариан Ринн to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37879, 0, 0, 'Джайна? Почему ты плачешь?', 12, 0, 100, 0, 0, 16692, 37543, 0, 0, 0, 'Король Вариан Ринн to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37879, 0, 0, 'Отступи, Мурадин. Позволь пройти скорбящему отцу.', 12, 0, 100, 0, 0, 16690, 37527, 0, 0, 0, 'Король Вариан Ринн to Верховный правитель Саурфанг');
INSERT INTO `creature_text` VALUES (37879, 0, 0, 'Ох! Мурадин, защищай палубу и приготовь солдат к штурму верхних этажей цитадели. Я вызову из Штормграда подкрепление.', 12, 0, 100, 0, 0, 16693, 37545, 0, 0, 0, 'Король Вариан Ринн to Саурфанг Смертоносный');
INSERT INTO `creature_text` VALUES (37915, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 4583, 0, 0, 0, 'Timothy Cunningham');
INSERT INTO `creature_text` VALUES (37944, 0, 0, 'Traitorous dog!', 12, 7, 100, 0, 0, 0, 31681, 0, 0, 0, 'Stormwind City Patroller to Player');
INSERT INTO `creature_text` VALUES (37951, 3, 0, '$n, Zuni, Euer Trainer ist im Ausbildungsgelände im Osten. Macht den Dunkelspeeren Ehre!', 12, 0, 100, 1, 0, 0, 0, 3360, 3360, 0, 'Jin\'thala to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Benutzt Belysras Talisman, um zu entkommen, bevor der dunkle Späher auf Euch schießt!$B$B|TInterface\\Icons\\inv_jewelry_talisman_10.blp:64|t', 42, 0, 100, 1, 0, 0, 0, 4922, 4922, 0, 'Dunkle Späherin to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Welch leichte Beute. Sylvanas wird sehr erfreut sein!', 12, 0, 100, 0, 0, 0, 37672, 2110, 2110, 0, 'Dunkle Späherin to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Wie habt Ihr...?! Auch egal - ich brauche keine Falle, um Euch zu vernichten.', 12, 0, 100, 0, 0, 0, 37673, 6344, 6344, 0, 'Dunkle Späherin to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Воспользуйтесь талисманом Белрисы и убегите прежде, чем темный разведчик вас подстрелит!$B$B|TInterface\\Icons\\inv_jewelry_talisman_10.blp:64|t', 42, 0, 100, 1, 0, 0, 0, 4670, 4670, 0, 'Темный разведчик to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Как?.. Впрочем, неважно – мне не нужны ловушки, чтобы разделаться с тобой.', 12, 0, 100, 0, 0, 0, 37673, 8124, 8124, 0, 'Темный разведчик to Player');
INSERT INTO `creature_text` VALUES (37953, 3, 0, 'Легкая добыча... Сильвана будет в восторге!', 12, 0, 100, 0, 0, 0, 37672, 2352, 2352, 0, 'Темный разведчик to Player');
INSERT INTO `creature_text` VALUES (37955, 0, 0, 'You have made an... unwise... decision.', 14, 0, 0, 0, 0, 16782, 38048, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_AGGRO');
