INSERT INTO `creature_text` VALUES (37955, 6, 0, 'Can you handle this?', 14, 0, 75, 0, 0, 16787, 38062, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_PACT_OF_THE_DARKFALLEN');
INSERT INTO `creature_text` VALUES (37955, 7, 0, 'Here it comes.', 14, 0, 0, 0, 0, 16788, 0, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_AIR_PHASE');
