INSERT INTO `creature_text` VALUES (37955, 8, 1, 'Such a pity.', 14, 0, 0, 0, 0, 16792, 88068, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_KILL');
INSERT INTO `creature_text` VALUES (37955, 9, 0, 'How... unfortunate...', 14, 0, 0, 0, 0, 16789, 0, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_WIPE');
INSERT INTO `creature_text` VALUES (37955, 10, 0, 'THIS ENDS NOW!', 14, 0, 0, 0, 0, 16793, 101597, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_BERSERK');
