INSERT INTO `creature_text` VALUES (37970, 3, 0, '%s begins casting Empowered Schock Vortex!', 41, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Prince Valanar - EMOTE_VALANAR_SHOCK_VORTEX');
