INSERT INTO `creature_text` VALUES (37988, 3, 0, 'Dein Lehrer sollte hier irgendwo in der Nähe sein. Ich seh dich dann später, Mann.', 12, 0, 100, 0, 0, 21367, 39087, 38672, 38672, 0, 'Zuni to Player');
INSERT INTO `creature_text` VALUES (37988, 3, 0, 'Ja, Mann. Lasst uns ein paar Tikizielschädel knacken!', 12, 0, 100, 5, 0, 21366, 37818, 17532, 17532, 0, 'Zuni to Player');
INSERT INTO `creature_text` VALUES (38002, 3, 0, 'Flinkklaue hält nicht ein! Lenkt ihn zurück zu den Raptorenställen, in der Nähe der Dunkelspeerfeste.', 42, 0, 100, 0, 0, 0, 37757, 30844, 30844, 56685, 'Flinkklaue to Player');
INSERT INTO `creature_text` VALUES (38004, 0, 0, 'Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined!', 14, 0, 0, 1, 0, 16795, 37997, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (38004, 1, 0, 'Rise up, brothers, and destroy our enemies.', 14, 0, 0, 0, 0, 16796, 38079, 0, 0, 0, 'Blood-Queen Lana\'thel - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (38017, 0, 0, 'Are you certain you should be helping these mortals in their quest for the sword?', 12, 0, 100, 1, 0, 0, 37774, 0, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 1, 0, 'These blades, Krasus... They were made long ago, when things were... different.', 12, 0, 100, 1, 0, 0, 37775, 0, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 2, 0, 'Our enemies once turned our strongest weapon against us. What makes you think the prismatic blades will be any different?', 12, 0, 100, 1, 0, 0, 37782, 0, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 3, 0, 'As you wish, my queen. I will not stand in their way, but I will keep a close watch.', 12, 0, 100, 16, 0, 0, 37783, 0, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38029, 3, 0, 'Haltet sie beschäftigt, meine Brüder! Ermöglicht es $n, die Sichel zurückzugewinnen!', 14, 0, 100, 0, 0, 0, 37795, 2125, 2125, 71011, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38029, 3, 0, 'Rückzug! Zieht Euch in die Wälder zurück!', 14, 0, 100, 0, 0, 0, 37796, 27594, 27594, 71018, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38051, 3, 0, 'Darius! Die dunklen Späher haben die Sichel! Sie haben sie vor uns erreicht.', 12, 0, 100, 0, 0, 0, 0, 7328, 7328, 0, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38051, 3, 0, 'Будет сделано!', 12, 0, 100, 0, 0, 0, 119428, 16982, 16982, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38051, 3, 0, 'Дарий! Темные следопыты завладели Косой, а мы не смогли их остановить!', 12, 0, 100, 0, 0, 0, 37810, 3955, 3955, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38112, 0, 0, 'Men, women and children... None were spared the master\'s wrath. Your death will be no different.', 14, 0, 100, 0, 0, 16710, 37931, 0, 0, 0, 'falric SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38112, 1, 0, 'Sniveling maggot!', 14, 0, 100, 0, 0, 16711, 37941, 0, 0, 0, 'falric SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (38112, 1, 1, 'The children of Stratholme fought with more ferocity!', 14, 0, 100, 0, 0, 16712, 37942, 0, 0, 0, 'falric SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (38112, 2, 0, 'Marwyn, finish them...', 14, 0, 100, 0, 0, 16713, 37943, 0, 0, 0, 'falric SAY_DEATH');
INSERT INTO `creature_text` VALUES (38112, 3, 0, 'Despair... so delicious...', 14, 0, 100, 0, 0, 16715, 37945, 0, 0, 0, 'falric SAY_IMPENDING_DESPAIR');
INSERT INTO `creature_text` VALUES (38112, 4, 0, 'Fear... so exhilarating...', 14, 0, 100, 0, 0, 16716, 37946, 0, 0, 0, 'falric SAY_DEFILING_HORROR');
INSERT INTO `creature_text` VALUES (38112, 5, 0, 'As you wish, my lord.', 14, 0, 100, 0, 0, 16717, 37953, 0, 0, 0, 'Falric SAY_FALRIC_INTRO_1');
INSERT INTO `creature_text` VALUES (38112, 6, 0, 'Soldiers of Lordaeron, rise to meet your master\'s call!', 14, 0, 100, 0, 0, 16714, 37944, 0, 0, 0, 'Falric SAY_FALRIC_INTRO_2');
INSERT INTO `creature_text` VALUES (38113, 0, 0, 'Death is all that you will find here!', 14, 0, 100, 0, 0, 16734, 37948, 0, 0, 0, 'marwyn SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38113, 1, 0, 'I saw the same look in his eyes when he died. Terenas could hardly believe it. Hahahaha!', 14, 0, 100, 0, 0, 16735, 37949, 0, 0, 0, 'marwyn SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (38113, 1, 1, 'Choke on your suffering!', 14, 0, 100, 0, 0, 16736, 37950, 0, 0, 0, 'marwyn SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (38113, 2, 0, 'Yes... Run... Run to meet your destiny... Its bitter, cold embrace, awaits you.', 14, 0, 100, 0, 0, 16737, 37951, 0, 0, 0, 'marwyn SAY_DEATH');
INSERT INTO `creature_text` VALUES (38113, 3, 0, 'Your flesh has decayed before your very eyes!', 14, 0, 100, 0, 0, 16739, 37954, 0, 0, 0, 'marwyn SAY_CORRUPTED_FLESH_1');
INSERT INTO `creature_text` VALUES (38113, 3, 1, 'Waste away into nothingness!', 14, 0, 100, 0, 0, 16740, 37955, 0, 0, 0, 'marwyn SAY_CORRUPTED_FLESH_2');
INSERT INTO `creature_text` VALUES (38113, 4, 0, 'As you wish, my lord.', 14, 0, 100, 0, 0, 16741, 37953, 0, 0, 0, 'marwyn SAY_MARWYN_INTRO_1');
INSERT INTO `creature_text` VALUES (38120, 0, 0, 'Это действительно довольно просто, $N. Просто прикрепи эти фейерверки к клушам, а я воспользуюсь дистанционным управлением, чтобы они залетели в клетки.', 12, 0, 100, 5, 0, 0, 49894, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 1, 0, '$N, сюда, пожалуйста.', 12, 0, 100, 3, 0, 0, 41262, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 2, 0, 'Не стоит недооценивать механоцыпа, $N. В конце концов, это изобретение Хобарта Дрека!', 12, 0, 100, 274, 0, 0, 49900, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 3, 0, 'Грили, заряди птицефикатор мегаяйцом. Всем наггетсов!', 14, 0, 100, 5, 0, 0, 38106, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 4, 0, 'Выключай, выключай! Яйца протухли!', 14, 0, 100, 0, 0, 0, 38170, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 5, 0, 'В соответствии с маркетинговым исследованием, которое провела Мегз, наступление наг случится очень скоро!', 12, 0, 100, 5, 0, 0, 49934, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 6, 0, 'Не торопись, $gприятель:девочка;. И во что же мне переделать этот реактивный сапог? Хм-м…', 12, 0, 100, 6, 0, 0, 0, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38120, 7, 0, 'I set up the botzuku so that in one shot it fires several rockets. But I\'m not completely sure that I properly calibrated the guidance system.', 12, 0, 100, 273, 0, 0, 50087, 0, 0, 0, 'Хобарт Дрек');
INSERT INTO `creature_text` VALUES (38122, 0, 0, 'Эй, $N, не подойдешь сюда? У меня к тебе дело.', 12, 0, 100, 70, 0, 0, 41261, 0, 0, 0, 'Бамм Взрыбомм');
INSERT INTO `creature_text` VALUES (38122, 1, 0, 'А теперь достань настоящих яиц.', 12, 0, 100, 273, 0, 0, 49899, 0, 0, 0, 'Бамм Взрыбомм');
INSERT INTO `creature_text` VALUES (38124, 0, 0, 'Отличная идея, доктор. Я не устаю восхищаться вашей гениальностью!', 14, 0, 100, 1, 0, 0, 38119, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 1, 0, 'Какая огромная штука! Поменяй полярность птицефикатора. Придется пропихивать его через выходное отверстие.', 14, 0, 100, 5, 0, 0, 38141, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 2, 0, 'Вот так. Сейчас все начнется...', 14, 0, 100, 1, 0, 0, 38142, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 3, 0, '$N, двигайся к устройству управления паровой мехакулой XD. Избавь нас от Молота!', 12, 0, 100, 1, 0, 0, 49911, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 4, 0, 'Ищи сияющее вещество на земле.', 12, 0, 100, 1, 0, 0, 50082, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 5, 0, 'Грили продолжает свою бессмысленную работу, пуская слюни от напряжения.', 16, 0, 100, 0, 0, 0, 50101, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 6, 0, 'Разберись с этими Акулами Хитрой Шестеренки, пока они нас не разгромили!', 12, 0, 100, 1, 0, 0, 50105, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 7, 0, 'Пора устроить небольшой саботаж!', 12, 0, 100, 11, 0, 0, 50109, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38124, 8, 0, 'Доставь футбомбную униформу в Ямы рабов. Торопись, $N!', 12, 0, 100, 25, 0, 0, 50111, 0, 0, 0, 'Ассистентка Грили');
INSERT INTO `creature_text` VALUES (38142, 3, 0, 'Die Meerhexe wird Euch alle töten!', 12, 0, 100, 0, 0, 0, 39090, 43797, 43797, 0, 'Gefangener Späher der Gallschuppen to Gefängnisaufseher der Dunkelspeere');
INSERT INTO `creature_text` VALUES (38161, 3, 0, 'Вы справились! Мы разобьем лагерь в этих покоях. Вскоре мои маги заставят портал Плети работать. Войдите в него, когда будете готовы к следующему заданию. Я присоединюсь к вам чуть позже.', 14, 0, 100, 1, 0, 17044, 37387, 9502, 9502, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38172, 2, 0, 'Это лишь краткий миг отдыха...', 12, 0, 100, 0, 0, 0, 38528, 0, 0, 72166, 'Призрачный маг to Player');
INSERT INTO `creature_text` VALUES (38175, 2, 0, 'Наши души никогда не обретут свободы.', 12, 0, 100, 0, 0, 0, 38529, 0, 0, 0, 'Призрачный жрец to Player');
INSERT INTO `creature_text` VALUES (38176, 2, 0, 'Здесь мы упокоимся навеки.', 12, 0, 100, 0, 0, 0, 38526, 0, 0, 72208, 'Измученный ружейник to Player');
INSERT INTO `creature_text` VALUES (38189, 3, 0, 'Я думала, он никогда не заткнется. Но Синдрагоса заставила этого болтливого дурня умолкнуть. В Залы Отражений, герои!', 12, 0, 100, 396, 0, 17036, 37541, 63302, 63302, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38218, 1, 0, 'Angriff!', 12, 0, 100, 0, 0, 0, 139449, 0, 24344, 31942, 'Prinz Liam Graumähne to Armbrustschütze der Verlassenen');
INSERT INTO `creature_text` VALUES (38218, 1, 0, 'Drängt sie zurück!', 12, 0, 100, 0, 0, 0, 65756, 6000, 767719, 31942, 'Prinz Liam Graumähne to Armbrustschütze der Verlassenen');
INSERT INTO `creature_text` VALUES (38218, 1, 0, 'Тесни их!', 12, 0, 100, 0, 0, 0, 38496, 0, 0, 6660, 'Принц Лиам Седогрив to Отрекшийся-арбалетчик');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Aber wir werden... NIEMALS AUFGEBEN!!!', 14, 0, 100, 0, 0, 19628, 37896, 176204, 176204, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Crowleys Truppen befinden sich vor uns! Weiter!', 14, 0, 100, 0, 0, 19611, 38360, 490922, 490922, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Die Verlassenen glauben uns schwach. Sie halten uns für ein gebrochenes Volk. Sie glauben, wir würden uns auf den Rücken rollen wie verängstigte Hunde.', 14, 0, 100, 0, 0, 19623, 37888, 124141, 124141, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'FÜR GILNEAS!!!', 14, 0, 100, 0, 0, 19651, 37897, 179907, 179907, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Gilneas wird überleben!', 12, 0, 100, 0, 0, 0, 38493, 27906, 804969, 31942, 'Prinz Liam Graumähne to Armbrustschütze der Verlassenen');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Monstrositäten blockieren den Weg zum Militärviertel! Das wird nicht einfach werden.', 12, 0, 100, 0, 0, 19609, 38362, 363563, 363563, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Und sollten wir uns umzingelt und entwaffnet finden... verletzt und überwältigt... so heben wir unsere Häupter und spucken ihnen ins Gesicht.', 14, 0, 100, 25, 0, 19627, 37895, 165344, 165344, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Welch willkommener Anblick Ihr seid, Lorna. Bemannen wir die Kanonen! Der Rest von Euch: Lockt die Monstrositäten in freies Gelände!', 14, 0, 100, 0, 0, 19610, 38345, 382672, 382672, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Wie falsch sie doch liegen. Wir werden sie im Feld bekämpfen, bis der letzte Schützengraben einbricht und die letzte Kanone schweigt.', 14, 0, 100, 0, 0, 19624, 37893, 132469, 132469, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Wir werden sie in den Gassen bekämpfen, bis unsere Knöchel wund und blutig sind und unsere Rapiere zerstört am Boden liegen.', 14, 0, 100, 0, 0, 19626, 37892, 154329, 154329, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Wir werden sie in den Straßen bekämpfen, bis der letzte Schuss fällt. Und geht die Munition zur Neige, schlagen wir ihre Schädel mit den Steinen unserer Stadt ein.', 14, 0, 100, 0, 0, 19625, 37891, 143657, 143657, 0, 'Prinz Liam Graumähne to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'А если мы будем зажаты в угол, лишимся сил, оружия и надежды... Мы поднимем голову с презрением и плюнем им в лицо.', 14, 0, 100, 25, 0, 19627, 37895, 157655, 157655, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'За Гилнеас!', 12, 0, 100, 0, 0, 0, 106378, 11510, 293859, 31942, 'Принц Лиам Седогрив to Отрекшийся-арбалетчик');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'ЗА ГИЛНЕАС!!!', 14, 0, 100, 0, 0, 19651, 37897, 172335, 172335, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Как же они ошибаются! Мы будем стоять до конца, будем биться, пока у нас есть пушки и солдаты в окопах.', 14, 0, 100, 0, 0, 19624, 37893, 124774, 124774, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Лорна, как я рад тебя видеть! Солдаты – по пушкам! Остальные выманивают поганищ на открытое пространство!', 14, 0, 100, 0, 0, 19610, 38345, 319800, 319800, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Мы будем сражаться в подворотнях, пока с наших костяшек не сойдет кожа, а рапиры не упадут на землю сломанными.', 14, 0, 100, 0, 0, 19626, 37892, 146759, 146759, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Мы будем сражаться на улицах до последнего патрона. А когда нам нечем будет стрелять, мы будем разбивать их черепа булыжниками с мостовой.', 14, 0, 100, 0, 0, 19625, 37891, 135803, 135803, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Но мы... НИКОГДА НЕ СДАДИМСЯ!', 14, 0, 100, 0, 0, 19628, 37896, 168549, 168549, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Отрекшиеся думают, что мы ослабли, опустили руки. Они думают, что мы убежим, поджав хвост.', 14, 0, 100, 0, 0, 19623, 37888, 116353, 116353, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'Поганища перекрывают путь к Военному кварталу! Пробиться будет нелегко.', 12, 0, 100, 0, 0, 19609, 38362, 293342, 293342, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38218, 4, 0, 'Eure Zeit ist um, Abschaum der Verlassenen!', 12, 0, 100, 0, 0, 0, 38494, 4890, 26750, 0, 'Prinz Liam Graumähne to Üble Monstrosität');
INSERT INTO `creature_text` VALUES (38218, 4, 0, 'Впереди войска Краули! Поднажмем!', 14, 0, 100, 0, 0, 0, 38360, 10710, 10710, 0, 'Принц Лиам Седогрив to Player');
INSERT INTO `creature_text` VALUES (38225, 0, 0, 'Ya were foolish to come \'ere, Sea Witch. Ya escaped our vengeance once, but the Darkspear Tribe will not abide ya trespassin\' again.', 14, 0, 100, 0, 0, 0, 38318, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 0, 0, 'Squire! Please stand next to me as I request the blessing of this Light-blessed spirit.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 1, 0, 'Light, give us the courage we need to enter battle with Un\'Goro\'s corrupted dragons.', 12, 0, 100, 0, 0, 0, 38264, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 2, 0, 'I also ask you to sprinkle wildflowers around the feet of my beautiful Doloria, where\'er she may walk.', 12, 0, 100, 0, 0, 0, 38266, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 3, 0, 'Finally, I humbly request that you cease venting your hostility \'pon my good squire.', 12, 0, 100, 0, 0, 0, 38271, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 4, 0, 'I accept your blessing.', 12, 0, 100, 0, 0, 0, 38273, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 5, 0, 'Luck is with us today! A damsel appears before us, stuck in a crag, without hope of escape! Let us go save her!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 6, 0, 'Maiden! Fear not, for Maximillian of Northshire has arrived to deliver you from your rocky tomb!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 7, 0, 'Now, cry not, as you fall not to your death, but into the waiting arms of my squire! Off you go now!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38255, 8, 0, 'FOR DOLORIA!', 14, 0, 100, 0, 0, 0, 51108, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38306, 0, 0, 'You are weak Vol\'jin, like your father was weak. Today I will finish what I started long ago - the Darkspear shall be wiped from existence!', 14, 0, 100, 0, 0, 0, 38294, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38323, 3, 0, 'Adorstaer waldir mandos H azi mandos S waldir ', 12, 7, 100, 0, 0, 0, 0, 324000, 324000, 0, 'Генерал Готорн to Player');
INSERT INTO `creature_text` VALUES (38331, 0, 0, 'Faulblut zerquetscht mickrige Worgen!!', 14, 0, 100, 0, 0, 23530, 38251, 0, 0, 0, 'Faulblut to Player');
INSERT INTO `creature_text` VALUES (38331, 0, 0, 'Гнилокров раздавит хилого воргена!', 14, 0, 100, 0, 0, 23530, 38251, 0, 0, 0, 'Гнилокров to Player');
INSERT INTO `creature_text` VALUES (38334, 0, 0, 'What are you staring at? Haven\'t you ever seen a genius before?', 12, 0, 100, 0, 0, 0, 38276, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38334, 1, 0, 'This\'ll teach you to mind your own business!', 12, 0, 100, 0, 0, 0, 38253, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38335, 0, 0, 'That\'s right, I\'d like to list some of these \'fireworks\'.', 12, 0, 100, 0, 0, 0, 38285, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38335, 1, 0, 'Those\'ll net me a nice profit when I return from the South Seas.\'.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38335, 2, 0, 'You... Don\'t think I don\'t see you. Leave me alone!.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38336, 0, 0, 'Thanks for the great cut and shave, buddy.', 12, 0, 100, 0, 0, 0, 38288, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38336, 1, 0, 'Here\'s a little somethin\' extra since I\'ll be away.', 12, 0, 100, 0, 0, 0, 38289, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38336, 2, 0, 'On second thought, keep the whole bag. I have to, uh, get going.', 12, 0, 100, 0, 0, 0, 38290, 0, 0, 0, 'say text');
INSERT INTO `creature_text` VALUES (38336, 3, 0, 'Did you really think you could corner me this easily?', 12, 0, 100, 0, 0, 0, 38291, 0, 0, 0, 'say text');
