INSERT INTO `creature_text` VALUES (38343, 0, 0, 'ВО ИМЯ ДОЛОРИИ!', 14, 0, 100, 0, 0, 0, 38300, 0, 0, 0, 'Максимилиан Североземский to Максимилиан Североземский');
INSERT INTO `creature_text` VALUES (38343, 0, 0, 'ДРАКОН, МОЙ МЕЧ ПРОНЗИТ ТЕБЯ!', 14, 0, 100, 0, 0, 0, 38299, 0, 0, 0, 'Максимилиан Североземский to Максимилиан Североземский');
INSERT INTO `creature_text` VALUES (38343, 0, 0, 'ДРАКОН!', 14, 0, 100, 0, 0, 0, 38298, 0, 0, 0, 'Максимилиан Североземский to Максимилиан Североземский');
INSERT INTO `creature_text` VALUES (38381, 0, 0, 'О твоей доле мы поговорим позже, когда ты вернешься со шкурами. Я думаю, 90 процентов мне и 10 – тебе.', 12, 0, 100, 6, 0, 0, 49948, 0, 0, 0, 'Бретт \"Сдачи нет\" Маквид');
INSERT INTO `creature_text` VALUES (38382, 3, 0, 'Waldir Nu waldir ', 12, 7, 100, 0, 0, 0, 0, 9033, 9033, 0, 'Лейтенант Уорли to Player');
INSERT INTO `creature_text` VALUES (38387, 0, 0, '$N, становись за один из наших баллистических уничтожителей и бей пигмеев!', 14, 0, 100, 25, 0, 18259, 49973, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 1, 0, 'Тебе придется сразиться с ними, $N. Иззи можно найти сразу за деревней.', 12, 0, 100, 273, 0, 0, 49974, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 2, 0, 'Эти зомби не отличаются быстротой. Бежать необязательно. Смотри, чтобы они не съели твои мозги, $N!', 12, 0, 100, 1, 0, 0, 50013, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 3, 0, 'Гоблинские гранаты против гномьих наземных мин. Покажи им, кто тут самый главный, $N!', 12, 0, 100, 1, 0, 0, 50095, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 4, 0, 'Я забыла... Тебя учили летать на Кезане?', 12, 0, 100, 6, 0, 0, 50097, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 5, 0, 'Не медли, $N. Кажется, Тралл уже знает, где находятся наши.', 12, 0, 100, 273, 0, 0, 50099, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 6, 0, 'Девочка, она увела твоего милого! Ты знаешь, что нужно делать в таких случаях!', 12, 0, 100, 273, 0, 0, 50115, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38387, 7, 0, '$N, бери свою идеальную футбомбную униформу и мчи к причалу. Помоги Траллу справиться с торговым принцем!', 12, 0, 100, 0, 0, 0, 43948, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38409, 0, 0, 'Что скажешь? Лучшее убийство зомби на этой неделе?', 14, 0, 100, 94, 0, 1473, 38646, 0, 0, 0, 'Уцелевший гоблин to Баллистический уничтожитель');
INSERT INTO `creature_text` VALUES (38409, 0, 1, 'Как больно!', 14, 0, 100, 0, 0, 0, 38773, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 2, 'Док! Помоги мне!', 14, 0, 100, 0, 0, 0, 38772, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 3, 'Я истекаю кровью!', 14, 0, 100, 0, 0, 0, 38770, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 4, 'Глобус, который показывает внутренности планеты... на ее поверхности. Чтобы знать, где копать!', 14, 0, 100, 0, 0, 0, 37364, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 5, 'Точилки для ложек!', 14, 0, 100, 0, 0, 0, 37418, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 6, 'Водостойкое мыло! Для использования под водой!', 14, 0, 100, 0, 0, 0, 37421, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 7, 'Дома из грязи! Когда ты проводишь в них уборку... ОНИ ИСЧЕЗАЮТ.', 14, 0, 100, 0, 0, 0, 37415, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 8, 'Мы поместим целый город... в крошечную табакерку!', 14, 0, 100, 0, 0, 0, 37356, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 9, 'Прикрепить два транспортных средства к большому транспорту и заставить пассажиров прыгать с одного транспорта на другой!', 14, 0, 100, 0, 0, 0, 37341, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 10, 'Лицензии на убийство!', 14, 0, 100, 0, 0, 0, 37344, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38409, 0, 11, 'Ботинки с колесиками. И перчатки с колесиками – на тот случай, если ты упал.', 14, 0, 100, 0, 0, 0, 37361, 0, 0, 0, 'Уцелевший гоблин');
INSERT INTO `creature_text` VALUES (38415, 3, 0, 'Wir sollten uns Eurem Vater anschließen, Liam. Er braucht unsere Hilfe gegen Sylvanas.', 14, 0, 100, 0, 0, 19500, 38361, 202890, 202890, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (38415, 3, 0, 'Давай присоединимся к твоему отцу. Ему нужна помощь.', 14, 0, 100, 0, 0, 19500, 38361, 152954, 152954, 0, 'Лорд Дарий Краули to Player');
INSERT INTO `creature_text` VALUES (38415, 4, 0, 'Er ist zu stark! Benutzt die Katapulte!', 14, 0, 100, 0, 0, 19499, 38662, 6079, 6079, 0, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (38415, 4, 0, 'Он слишком силен! Стреляйте из катапульт, и он падет!', 14, 0, 100, 0, 0, 19499, 38662, 1684, 1684, 0, 'Лорд Дарий Краули');
INSERT INTO `creature_text` VALUES (38423, 4, 0, 'Ich kümmer\' mich um die Feuer!', 14, 0, 100, 5, 0, 21388, 39043, 62125, 62125, 0, 'Zuni to Zar\'jira');
INSERT INTO `creature_text` VALUES (38426, 3, 0, 'Als Dank für die Hilfe in Glutstein haben uns die Bewohner eine Kleinigkeit gebracht, die uns im Kampf gegen die Verlassenen helfen wird!', 12, 0, 100, 0, 0, 19684, 38322, 8140, 8140, 0, 'Lorna Crowley to Player');
INSERT INTO `creature_text` VALUES (38426, 3, 0, 'Люди благодарны за возвращение Деревни Тлеющего Угля. Они дали нам кое-что, чтобы помочь против Отрекшихся!', 12, 0, 100, 0, 0, 19684, 38322, 7956, 7956, 0, 'Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (38432, 0, 0, 'Слышали, наги? У |3-1($N) свои способы вести \"переговоры\"!', 14, 0, 100, 15, 0, 0, 49938, 0, 0, 0, 'Мегз Жестекрошшер');
INSERT INTO `creature_text` VALUES (38432, 1, 0, 'Надувной пони нравится всем. Это наш лидер продаж после детских разрывных бомб.', 12, 0, 100, 1, 0, 0, 49951, 0, 0, 0, 'Мегз Жестекрошшер');
INSERT INTO `creature_text` VALUES (38432, 2, 0, 'Этот шлем пигмея введет их в заблуждение – по крайней мере, на какое-то время. Возвращайся в город!', 12, 0, 100, 25, 0, 0, 44571, 0, 0, 0, 'Мегз Жестекрошшер');
INSERT INTO `creature_text` VALUES (38437, 3, 0, 'Ich fürchte, es gibt nichts, was ich für unseren Bruder tun kann... Ihre Macht hat seine Seele fortgerissen.', 12, 0, 100, 1, 0, 21946, 38835, 285453, 285453, 0, 'Vanira to Vol\'jin');
INSERT INTO `creature_text` VALUES (38437, 3, 0, 'Vol\'jin ist voller Ungeduld davongestürzt, doch ich kann uns in Sicherheit bringen. Sagt einfach Bescheid, Mann.', 12, 0, 100, 275, 0, 21948, 38837, 234875, 234875, 0, 'Vanira to Player');
INSERT INTO `creature_text` VALUES (38437, 4, 0, 'ZUNI! NEIN! NEEEIIINN!', 14, 0, 100, 5, 0, 21945, 39044, 80281, 80281, 0, 'Vanira to Zuni');
INSERT INTO `creature_text` VALUES (38448, 0, 0, 'Кто осмеливается?!', 14, 0, 100, 15, 0, 15136, 38338, 0, 0, 0, 'Безликий из глубин');
INSERT INTO `creature_text` VALUES (38448, 1, 0, 'Малыши-гоблины? Я помню те дни, когда вашей расы еще не существовало!', 14, 0, 100, 1, 0, 15139, 38346, 0, 0, 0, 'Безликий из глубин');
INSERT INTO `creature_text` VALUES (38448, 2, 0, 'Меня не страшат ни твои угрозы, ни эти наги. Приготовься навсегда покинуть этот мир.', 14, 0, 100, 25, 0, 15140, 38349, 0, 0, 0, 'Безликий из глубин');
INSERT INTO `creature_text` VALUES (38448, 3, 0, 'А теперь, $N, ты умрешь!', 14, 0, 100, 0, 0, 15140, 38359, 0, 0, 0, 'Безликий из глубин');
INSERT INTO `creature_text` VALUES (38455, 0, 0, '$N, эти проказники у меня на крючке. Наги не нападут, пока у нас их потомство.', 12, 0, 100, 1, 0, 0, 38356, 0, 0, 0, 'Туз');
INSERT INTO `creature_text` VALUES (38455, 1, 0, 'Ты $gготов:готова; принудить к сдаче их вожака? Ну, тогда пошли.', 12, 0, 100, 1, 0, 0, 38357, 0, 0, 0, 'Туз');
INSERT INTO `creature_text` VALUES (38455, 2, 0, 'Когда разберемся с вашей капитуляцией, устроим особую распродажу – только для наг. На большинство товаров – скидка в тридцать процентов. Калдорайские артефакты по особой цене! Предложение действует только сегодня!', 14, 0, 100, 5, 0, 0, 38379, 0, 0, 0, 'Туз');
INSERT INTO `creature_text` VALUES (38455, 3, 0, 'Мы знаем, что вы собирались на нас напасть. Так что сами во всем виноваты.', 14, 0, 100, 5, 0, 0, 38377, 0, 0, 0, 'Туз');
INSERT INTO `creature_text` VALUES (38455, 4, 0, 'Ну, предводитель наг, выходи! $N и картель Трюмных Вод приказывают тебе сдаться!', 14, 0, 100, 5, 0, 0, 38381, 0, 0, 0, 'Туз');
INSERT INTO `creature_text` VALUES (38455, 5, 0, 'Э-э, чувак, что-то мне это не нравится. Я сваливаю!', 12, 0, 100, 5, 0, 0, 38358, 0, 0, 0, 'Туз to Безликий из глубин');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Dann wollen wir doch mal sehen, wie tapfer Gilneas ohne seinen störrischen Anführer ist!', 14, 0, 100, 0, 0, 0, 38368, 55516, 55516, 0, 'Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Genug!', 14, 0, 100, 0, 0, 23517, 129681, 53156, 53156, 0, 'Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Welch Verschwendung. Das Pfeilgift war eigentlich nicht für Euren Welpen bestimmt. Wir sehen uns wieder!', 12, 0, 100, 0, 0, 19727, 38370, 64312, 64312, 0, 'Fürstin Sylvanas Windläufer to Prinz Liam Graumähne');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Посмотрим, что бравые гилнеанцы будут делать, оставшись без предводителя!', 14, 0, 100, 0, 0, 0, 38368, 39767, 39767, 0, 'Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Ужасно жаль. Яд этой стрелы предназначался не для твоего щенка. Но мы еще встретимся!', 12, 0, 100, 0, 0, 19727, 38370, 48625, 48625, 72349, 'Леди Сильвана Ветрокрылая to Принц Лиам Седогрив');
INSERT INTO `creature_text` VALUES (38469, 4, 0, 'Хватит!', 14, 0, 100, 0, 0, 23517, 125689, 37282, 37282, 0, 'Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38470, 3, 0, 'SYLVANAS!!', 14, 0, 100, 0, 0, 19726, 38365, 9859, 9859, 72107, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (38470, 3, 0, 'Versperr ihnen den Rückweg, Liam! Sie sind genau da, wo wir sie haben wollen!', 14, 0, 100, 0, 0, 19725, 38364, 3031, 3031, 0, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (38470, 3, 0, 'Не дай им уйти, Лиам! Они как раз там, где нам нужно!', 14, 0, 100, 0, 0, 19725, 38364, 1399, 1399, 0, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (38470, 3, 0, 'СИЛЬВАНА!!!', 14, 0, 100, 0, 0, 19726, 38365, 8285, 8285, 72107, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (38470, 4, 0, 'LIAM!! NEIN!!!', 14, 0, 100, 0, 0, 19727, 38485, 64516, 64516, 0, 'König Genn Graumähne to Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38470, 4, 0, 'ЛИАМ!!! НЕТ!!!', 14, 0, 100, 0, 0, 19727, 38485, 48626, 48626, 0, 'Король Генн Седогрив to Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38472, 0, 0, 'Die, intruders! None shall interfere with the Cult\'s plans!', 14, 0, 0, 0, 0, 0, 38516, 0, 0, 0, 'Darnavan - SAY_DARNAVAN_AGGRO');
INSERT INTO `creature_text` VALUES (38472, 1, 0, 'Wh- where am I...? What a nightmare I have had... But this is no time to reflect, I have much information to report!', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Darnavan - SAY_DARNAVAN_RESCUED');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'VATER!!!', 14, 0, 100, 0, 0, 19612, 38369, 3563, 3563, 0, 'Prinz Liam Graumähne to Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'Wir haben es geschafft, Vater...', 12, 0, 100, 0, 0, 20562, 38371, 13485, 13485, 0, 'Prinz Liam Graumähne to Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'Wir haben unsere Stadt zurückerobert... Wir haben...', 12, 0, 100, 0, 0, 20563, 38372, 17125, 17125, 0, 'Prinz Liam Graumähne to Fürstin Sylvanas Windläufer');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'Мы вернули свой город... Мы вернули...', 12, 0, 100, 0, 0, 20563, 38372, 16698, 16698, 0, 'Принц Лиам Седогрив to Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'Отец, мы победили...', 12, 0, 100, 0, 0, 20562, 38371, 13095, 13095, 0, 'Принц Лиам Седогрив to Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38474, 3, 0, 'ОТЕЦ!!!', 14, 0, 100, 0, 0, 19612, 38369, 4203, 4203, 0, 'Принц Лиам Седогрив to Леди Сильвана Ветрокрылая');
INSERT INTO `creature_text` VALUES (38485, 0, 0, 'Die, intruders! None shall interfere with the Cult\'s plans!', 14, 0, 0, 0, 0, 0, 38516, 0, 0, 0, 'Darnavan - SAY_DARNAVAN_AGGRO');
INSERT INTO `creature_text` VALUES (38485, 1, 0, 'Wh- where am I...? What a nightmare I have had... But this is no time to reflect, I have much information to report!', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Darnavan - SAY_DARNAVAN_RESCUED');
INSERT INTO `creature_text` VALUES (38490, 0, 0, '$n is infected with the Death Plague!', 41, 0, 100, 25, 0, 0, 38679, 0, 0, 0, 'Rotting Frost Giant - Death Plague');
INSERT INTO `creature_text` VALUES (38494, 0, 0, '$n is infected with the Death Plague!', 41, 0, 100, 25, 0, 0, 38679, 0, 0, 0, 'Rotting Frost Giant - Death Plague');
INSERT INTO `creature_text` VALUES (38507, 0, 0, 'Lasst uns sofort aufbrechen. Wir werden Sylvanas aufspüren. Für Gilneas!', 12, 0, 100, 15, 0, 0, 38399, 0, 0, 0, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38507, 0, 0, 'Идем же, все вместе! Мы выследим, где прячется Сильвана. За Гилнеас!', 12, 0, 100, 15, 0, 0, 38399, 0, 0, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Folgt Tobias Dunstmantel ins Kathedralenviertel.', 42, 0, 100, 0, 0, 0, 38400, 2906, 2906, 0, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Geben wir unser Bestes, um nicht gesehen zu werden, $n.', 12, 0, 100, 0, 0, 0, 38401, 13031, 13031, 0, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Habt Ihr das gehört, $n? Sylvanas ist auf dem Weg zur Kathedrale. Wir sollten uns beeilen!', 12, 0, 100, 0, 0, 0, 38402, 52312, 52312, 0, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Schnell! Versteckt Euch im Wasser!', 12, 0, 100, 0, 0, 0, 38403, 74984, 74984, 72476, 'Tobias Dunstmantel to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Быстрее, прячься в воде!', 12, 0, 100, 0, 0, 0, 38403, 73764, 73764, 72476, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Постарайся, чтобы нас не заметили, $n.', 12, 0, 100, 0, 0, 0, 38401, 11410, 11410, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Следуйте за Тобиасом Мистмантлом в Соборный квартал.', 42, 0, 100, 0, 0, 0, 38400, 3319, 3319, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Ты это $gслышал:слышала;, $n? Сильвана направляется к собору. Быстрее, за ней!', 12, 0, 100, 0, 0, 0, 38402, 50694, 50694, 0, 'Тобиас Мистмантл to Player');
INSERT INTO `creature_text` VALUES (38517, 0, 0, 'Так. Мы двинемся прочь от вулкана и попытаемся объединиться с орками, чтобы в последний раз напасть на торгового принца. Увидимся на той стороне... надеюсь.', 12, 0, 100, 1, 0, 0, 39240, 0, 0, 0, 'Крадли Дротик');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Achtet auf Euren Ton, General. Weder Ihr noch Garrosh müsst Euch Sorgen machen. Die Produktion der Seuche wurde eingestellt, ganz wie befohlen. Wir würden sie nie ohne seine Erlaubnis einsetzen.', 12, 0, 100, 0, 0, 20493, 38423, 53047, 53047, 0, 'Fürstin Sylvanas Windläufer to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Geht mit Ehre, General.', 12, 0, 100, 0, 0, 20494, 38424, 75704, 75704, 0, 'Fürstin Sylvanas Windläufer to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Ihr könnt Garrosh versichern, dass dies nur ein kleiner Rückschlag ist. Unser Sieg in Gilneas wird endgültig sein.', 12, 0, 100, 1, 0, 20492, 38422, 32860, 32860, 0, 'Fürstin Sylvanas Windläufer to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Was für eine Frage! Natürlich werden wir die Seuche wie geplant einsetzen. Lasst die Gilneer ihren kleinen Sieg feiern. Morgen werden nicht mal mehr ihre Knochen übrig sein.', 12, 0, 100, 0, 0, 20495, 38425, 91922, 91922, 0, 'Fürstin Sylvanas Windläufer to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Можете передать Гаррошу, что это лишь небольшая задержка. Нас ждет верная победа.', 12, 0, 100, 1, 0, 20492, 38422, 32879, 32879, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Следите за тоном, генерал. Вам с Гаррошем не о чем беспокоиться. Мы прекратили производство чумы, как и было приказано. Мы никогда бы не стали использовать ее без разрешения.', 12, 0, 100, 0, 0, 20493, 38423, 53113, 53113, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Ступайте с честью, генерал.', 12, 0, 100, 0, 0, 20494, 38424, 75851, 75851, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'Что за вопрос? Мы распространим ее, как и запланировали. Пусть люди Гилнеаса насладятся своей маленькой победой. А завтра от них не останется и костей.', 12, 0, 100, 0, 0, 20495, 38425, 91897, 91897, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Es scheint so, als würdet Ihr die Kontrolle über Gilneas verlieren, Sylvanas. Garrosh fürchtet, dass er die Invasion selbst ausführen muss.', 12, 0, 100, 1, 0, 22620, 38426, 21125, 21125, 0, 'General Kriegsheuler to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Ich werde also den Bericht meinem Anführer überbringen. Ihr gestattet, meine Fürstin?', 12, 0, 100, 66, 0, 22622, 38428, 68422, 68422, 0, 'General Kriegsheuler to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Ihr klingt Euch Eurer Sache sehr sicher, Eure Majestät. Ich hoffe ernsthaft, dass Ihr nicht vorhabt, die Seuche einzusetzen. Garrosh hat es explizit untersagt.', 12, 0, 100, 274, 0, 22621, 38427, 40141, 40141, 0, 'General Kriegsheuler to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Вы говорите очень убедительно, Ваше Величество. Надеюсь, что вы не собираетесь использовать чуму. Гаррош это категорически запретил.', 12, 0, 100, 274, 0, 22621, 38427, 40953, 40953, 0, 'Генерал Ворхол to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Гаррош опасается за ситуацию в Гилнеасе. Вы уверены в своих силах, Сильвана?', 12, 0, 100, 1, 0, 22620, 38426, 21519, 21519, 0, 'Генерал Ворхол to Player');
INSERT INTO `creature_text` VALUES (38533, 3, 0, 'Тогда я доложу обо всем Вождю. С Вашего позволения, моя госпожа.', 12, 0, 100, 66, 0, 22622, 38428, 69311, 69311, 0, 'Генерал Ворхол to Player');
INSERT INTO `creature_text` VALUES (38535, 3, 0, 'О-о, этого я знаю! Это гораздо лучше, чем иметь дело с неудачниками-гоблинами, которые вынуждены упрашивать остальных помочь им.', 12, 0, 100, 4, 0, 0, 40539, 2979, 2979, 0, 'Келси Стализвон to Келси Стализвон');
INSERT INTO `creature_text` VALUES (38537, 3, 0, 'Herrin! Soll ich meinen Männern befehlen, den Einsatz der Seuche einzustellen? Oder fahren wir fort wie geplant?', 12, 0, 100, 66, 0, 20917, 38440, 78844, 78844, 0, 'Hochexekutor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38537, 3, 0, 'Wie Ihr wünscht.', 12, 0, 100, 66, 0, 20918, 120871, 103266, 103266, 0, 'Hochexekutor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38537, 3, 0, 'Как пожелаете.', 12, 0, 100, 66, 0, 20918, 52083, 103324, 103324, 0, 'Верховный палач Креншо to Player');
INSERT INTO `creature_text` VALUES (38537, 3, 0, 'Моя госпожа, мне остановить распространение чумы? Или мы продолжаем действовать по плану?', 12, 0, 100, 66, 0, 20917, 38440, 79020, 79020, 0, 'Верховный палач Креншо to Player');
INSERT INTO `creature_text` VALUES (38553, 0, 0, 'Es ist an der Zeit, sich ins Gefecht zu stürzen, $n! Ihr an unserer Seite werdet das Zünglein an der Waage sein in diesem Kampf!', 14, 0, 100, 0, 0, 0, 38513, 0, 0, 0, 'Krennan Aranas to Player');
INSERT INTO `creature_text` VALUES (38579, 0, 0, 'Free at last! It is over, my son. This is the moment of reckoning.', 14, 0, 0, 1, 0, 17397, 38117, 0, 0, 0, 'Terenas Menethil - SAY_TERENAS_OUTRO_1');
INSERT INTO `creature_text` VALUES (38579, 1, 0, 'Rise up, champions of the Light!', 14, 0, 0, 0, 0, 17398, 38118, 0, 0, 0, 'Terenas Menethil - SAY_TERENAS_OUTRO_2');
INSERT INTO `creature_text` VALUES (38617, 3, 0, 'Stellt sicher, dass alles bereit ist. Der orcische Abgesandte ist bereit, Sylvanas in der Kathedrale zu treffen.', 14, 0, 100, 1, 0, 0, 38514, 2547, 2547, 0, 'General der Verlassenen to Player');
INSERT INTO `creature_text` VALUES (38617, 3, 0, 'Убедитесь, что все приготовления завершены. Гонец орков уже ждет Сильвану в соборе.', 14, 0, 100, 1, 0, 0, 38514, 9124, 9124, 0, 'Отрекшийся-генерал to Player');
INSERT INTO `creature_text` VALUES (38643, 0, 0, 'Остальных они отвели в жерло вулкана. Одних собираются превратить в зомби, других – принести в жертву их черепашьему богу Вулканоту!', 12, 0, 100, 5, 0, 0, 38571, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 1, 'I am free! I owe you my life!', 12, 0, 100, 4, 0, 0, 38565, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 2, 'А я-то думал, что мне крышка!', 12, 0, 100, 1, 0, 0, 38566, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 3, 'Я даже не знаю, как тебя отблагодарить!', 12, 0, 100, 18, 0, 0, 38570, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 4, 'I felt how life was leaving me.', 12, 0, 100, 1, 0, 0, 38567, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 5, 'Нужно убираться отсюда, пока меня снова не поймали!', 12, 0, 100, 5, 0, 0, 38569, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38643, 0, 6, 'Они пытались превратить меня в зомби!', 12, 0, 100, 5, 0, 0, 38568, 0, 0, 0, 'Пленный гоблин to Шаман Умлота');
INSERT INTO `creature_text` VALUES (38647, 0, 0, '$N, давай внутрь и спасай всех!', 12, 0, 100, 5, 0, 0, 50007, 0, 0, 0, 'Иззи');
INSERT INTO `creature_text` VALUES (38647, 1, 0, 'Гоблины-зомби? Надо предупредить жителей Городка-из-табакерки!', 12, 0, 100, 6, 0, 0, 50008, 0, 0, 0, 'Иззи');
INSERT INTO `creature_text` VALUES (38647, 2, 0, 'Док! Помоги мне!', 14, 0, 100, 0, 0, 0, 38772, 0, 0, 0, 'Иззи');
INSERT INTO `creature_text` VALUES (38647, 3, 0, 'Ай!', 14, 0, 100, 0, 0, 0, 68631, 0, 0, 0, 'Иззи');
INSERT INTO `creature_text` VALUES (38647, 4, 0, 'Не позволяй ему так с собой обращаться! Вырви его сердце, девочка!', 12, 0, 100, 274, 0, 0, 50107, 0, 0, 0, 'Иззи');
INSERT INTO `creature_text` VALUES (38666, 3, 0, 'Дракон! Внемли! Я – Максимилиан Североземский, твоя смерть во плоти. Твоя кровь наполнит...', 14, 0, 100, 0, 0, 0, 38687, 26000, 26000, 0, 'Максимилиан Североземский to Пименто');
INSERT INTO `creature_text` VALUES (38666, 3, 0, 'Дух великого Утера, она преследует нас! Оруженосец, сделай же что-нибудь! Закидай ее булыжниками! Я не знаю, что делать!', 14, 0, 100, 0, 0, 0, 38688, 29000, 29000, 0, 'Максимилиан Североземский to Пименто');
INSERT INTO `creature_text` VALUES (38738, 0, 0, 'Иди и покажи этим зомби, из какого теста сделаны \"Буканьеры Трюмных Вод!\"', 12, 0, 100, 25, 0, 0, 50072, 0, 0, 0, 'Тренер Проверяк');
INSERT INTO `creature_text` VALUES (38738, 1, 0, 'Тебе понравится!', 12, 0, 100, 273, 0, 0, 117445, 0, 0, 0, 'Тренер Проверяк');
INSERT INTO `creature_text` VALUES (38738, 2, 0, 'Мы сделаем тебе лучшую футбомбную униформу, $gсынок:дочка;. Но для начала нам нужны запчасти. Много запчастей.', 12, 0, 100, 25, 0, 0, 50106, 0, 0, 0, 'Тренер Проверяк');
INSERT INTO `creature_text` VALUES (38745, 0, 0, 'Док! Помоги мне!', 14, 0, 100, 0, 0, 0, 38772, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38745, 0, 1, 'Скорее доктора!', 14, 0, 100, 0, 0, 0, 38767, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38745, 0, 2, 'Ботинки с колесиками. И перчатки с колесиками – на тот случай, если ты упал.', 14, 0, 100, 0, 0, 0, 37361, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38745, 0, 3, 'Лицензии на убийство!', 14, 0, 100, 0, 0, 0, 37344, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38745, 0, 4, 'Детекторы мам.', 14, 0, 100, 0, 0, 0, 37414, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38745, 0, 5, 'Бесплатное здравоохранение!', 14, 0, 100, 0, 0, 0, 37346, 0, 0, 0, 'Кезанский горожанин');
INSERT INTO `creature_text` VALUES (38766, 3, 0, 'Crowley! Euch und Euren elfischen Verbündeten ergeht hiermit der Befehl, an der Seite der Armee des Königs zu kämpfen. Verflucht oder nicht, Ihr unterliegt noch immer den Gesetzen Gilneas\'!', 12, 0, 100, 25, 0, 19636, 38639, 15125, 15125, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (38766, 3, 0, 'Краули! Тебе и эльфам приказано вступить в королевскую армию. Лежит ли на вас проклятие или нет, вы все еще подчиняетесь закону Гилнеаса!', 12, 0, 100, 25, 0, 19636, 38639, 14712, 14712, 0, 'Лорд Годфри to Player');
INSERT INTO `creature_text` VALUES (38766, 3, 0, 'Не может быть!', 14, 0, 100, 0, 0, 19637, 122994, 42787, 42787, 0, 'Лорд Годфри to Player');
INSERT INTO `creature_text` VALUES (38767, 0, 0, 'Es ist also entschieden. Wir vereinen alle Gilneer und vertreiben die Verlassenen aus unserem Land.', 12, 0, 100, 0, 0, 0, 38638, 0, 0, 0, 'König Genn Graumähne to Player');
INSERT INTO `creature_text` VALUES (38767, 3, 0, 'Значит, решено. Мы соберем все силы Гилнеаса и вышвырнем Отрекшихся из наших земель.', 12, 0, 100, 0, 0, 19724, 38638, 58001, 58001, 0, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (38767, 3, 0, 'Нет, друг мой. Я пришел к тебе как к равному.', 12, 0, 100, 0, 0, 19723, 38637, 36399, 36399, 81908, 'Король Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (38768, 0, 0, 'Отец!', 14, 0, 100, 0, 0, 0, 124735, 0, 0, 0, 'Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (38768, 3, 0, 'Vater!', 14, 0, 100, 0, 0, 19697, 69734, 2594, 2594, 0, 'Lorna Crowley to Player');
INSERT INTO `creature_text` VALUES (38781, 3, 0, 'Ihr habt es geschafft! Wir sind gerettet!', 14, 0, 100, 1, 0, 0, 39625, 4390, 4390, 0, 'Gilnearische Überlebende to Player');
INSERT INTO `creature_text` VALUES (38781, 3, 0, 'У тебя все получилось! Мы спасены!', 14, 0, 100, 1, 0, 0, 39625, 583, 583, 0, 'Уцелевший житель Гилнеаса to Player');
INSERT INTO `creature_text` VALUES (38805, 0, 0, 'Can\'t ... give up...', 12, 0, 100, 1, 0, 0, 38707, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38805, 0, 1, 'I think I can limp back to the fortress...', 12, 0, 100, 1, 0, 0, 38706, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38805, 0, 2, 'Orcs and trolls! Everywhere!', 12, 0, 100, 1, 0, 0, 38708, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38805, 0, 3, 'Thank you - this was turning into a massacre...', 12, 0, 100, 1, 0, 0, 38704, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38805, 0, 4, 'These bandages reek! I guess I shouldn\'t complain.', 12, 0, 100, 1, 0, 0, 38705, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38805, 0, 5, 'Why do these bandages smell like wet dog?', 12, 0, 100, 1, 0, 0, 38703, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (38806, 3, 0, 'ОЧИСТИТЕСЬ ОГНЕМ!!!', 14, 0, 100, 449, 0, 23335, 39839, 1000, 1000, 0, 'Рагнарос to Player');
INSERT INTO `creature_text` VALUES (38872, 0, 0, '$n - help! The ropes ... please...', 12, 0, 100, 0, 0, 0, 38860, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38872, 1, 0, 'Ah, thank you! Suffice it to say... Negotiations did proceed as planned.', 12, 0, 100, 0, 0, 0, 38862, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38872, 2, 0, 'I should be able to get back to camp on my own.', 12, 0, 100, 0, 0, 0, 38863, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38910, 0, 0, 'You don\'t understand... I CAN\'T be undead! Not me, not now...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38926, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (38928, 0, 0, '$N, давай! Иди сюда!', 14, 0, 100, 0, 0, 0, 38908, 0, 0, 0, 'Хамми Кофельнагель');
INSERT INTO `creature_text` VALUES (38930, 3, 0, 'Bist du auch schon mit der Ausbildung durch? Schön, dass du\'s geschafft hast, Mann!', 12, 0, 100, 6, 0, 21370, 39067, 7610, 7610, 0, 'Zuni to Echo Isles Quest Bunny');
INSERT INTO `creature_text` VALUES (38930, 3, 0, 'Ich weiß, dass is\' bestimmt eher \'ne Beschäftigungstherapie, macht aber nix. Babyraptoren sind fiese kleine Teufel.', 12, 0, 100, 1, 0, 21371, 39068, 18141, 18141, 0, 'Zuni to Echo Isles Quest Bunny');
INSERT INTO `creature_text` VALUES (38930, 3, 0, 'Versuch, mich nicht schlecht dastehen zu lassen, ja?', 12, 0, 100, 1, 0, 21373, 39069, 36407, 36407, 0, 'Zuni to Echo Isles Quest Bunny');
INSERT INTO `creature_text` VALUES (38930, 3, 0, 'Warte mal, Mann!', 12, 0, 100, 5, 0, 21368, 39066, 922, 922, 0, 'Zuni to Player');
INSERT INTO `creature_text` VALUES (38932, 3, 0, 'Ha! Ich hab dich eingeholt! Lass uns hier drinnen besser zusammenbleiben, okay?', 12, 0, 100, 1, 0, 21387, 39070, 1343, 1343, 0, 'Zuni to Player');
INSERT INTO `creature_text` VALUES (38932, 3, 0, 'Stirb, Schlangenfisch!', 12, 0, 100, 0, 0, 21376, 38256, 100953, 100953, 0, 'Zuni');
INSERT INTO `creature_text` VALUES (38932, 4, 0, 'Ey, Mann, hier riecht\'s nach verfaultem Fisch.', 12, 0, 100, 0, 0, 21385, 38262, 25985, 25985, 0, 'Zuni');
INSERT INTO `creature_text` VALUES (38949, 0, 0, 'You deserve to lose this land, for what your people did to us! Now back off, or face the wrath of the worgen!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Worgen Infiltrator');
INSERT INTO `creature_text` VALUES (38966, 3, 0, 'Ich muss dir etwas zeigen. Es ist einfacher zu verstehen, wenn du es mit eigenen Augen siehst.', 12, 0, 100, 1, 0, 20097, 40418, 174781, 174781, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` VALUES (38968, 0, 0, 'Joke\'s on you, mon. I found Jang\'thraze long ago. Had both of de halves...\'til some wily one come and steal one away in de night.', 12, 0, 100, 0, 0, 0, 38912, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38968, 1, 0, 'Now dat Sang\'thraze is mine again, de only thing ya be findin\' here...is ya own fresh grave.', 12, 0, 100, 0, 0, 0, 38913, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38968, 2, 0, 'Behold! Sul\'thraze the Lasher be rejoined!', 12, 0, 100, 0, 0, 0, 38914, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38968, 3, 0, 'Enough\'a dis...Chief Sandscalp can\'t reward me if I\'m dead, yah? Farewell, mon...for good.', 12, 0, 100, 0, 0, 0, 38915, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 0, 0, 'What\'s going on here?', 12, 0, 100, 0, 0, 0, 40479, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 1, 0, 'Wait, am I... home?', 12, 0, 100, 0, 0, 0, 39161, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 2, 0, 'You people... what are you doing here? Why are you in my house?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 3, 0, 'Mother... father... Thurman... where are you? Come help me!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 4, 0, 'No... I remember. My family is dead. And so am I.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 5, 0, 'And my soul... my soul belongs to the Scourge!', 12, 0, 100, 0, 0, 0, 39165, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38980, 6, 0, 'DIE, YOU WRETCHES!', 14, 0, 100, 0, 0, 0, 39166, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (38989, 3, 0, 'Gebt mir einfach Bescheid, wenn Ihr soweit seid, Mann.', 12, 0, 100, 1, 0, 0, 39243, 29656, 29656, 0, 'Jornun to Player');
INSERT INTO `creature_text` VALUES (38995, 0, 0, 'We\'ll grant you a swift death, Arthas. More than can be said for the thousands you\'ve tortured and slain.', 14, 0, 0, 0, 0, 17390, 38109, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES (38995, 1, 0, 'So be it. Champions, attack!', 14, 0, 0, 0, 0, 17391, 38110, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES (38995, 2, 0, 'LIGHT, GRANT ME ONE FINAL BLESSING. GIVE ME THE STRENGTH... TO SHATTER THESE BONDS!', 14, 0, 0, 0, 0, 17392, 38111, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_1');
INSERT INTO `creature_text` VALUES (38995, 3, 0, 'No more, Arthas! No more lives will be consumed by your hatred!', 14, 0, 0, 0, 0, 17393, 38112, 0, 0, 0, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_2');
INSERT INTO `creature_text` VALUES (38999, 0, 0, 'Yes, my... wait, be quiet! I hear the lieutenant approaching.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 1, 0, 'Gebler, you came! What did he say?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 2, 0, 'What? NO! This can\'t be! Gebler, you know me... we were friends once!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 3, 0, ' Gebler, father, why would you...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 4, 0, ' The world of the living may have turned its back on me, but I\'m no damned Scourge. Just go.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (39002, 0, 0, 'The time has come, my little captive... word has come back from your father.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Scarlet Lieutenant GeblerÂ ');
INSERT INTO `creature_text` VALUES (39002, 1, 0, 'High Priest Voss denounces you as a daughter. He\'s ordered that you be executed immediately.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Scarlet Lieutenant GeblerÂ ');
INSERT INTO `creature_text` VALUES (39002, 2, 0, 'The High Priest sends his regrets. He head hoped that one day you would be a powerful weapon against our enemies.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Scarlet Lieutenant GeblerÂ ');
INSERT INTO `creature_text` VALUES (39002, 3, 0, 'Unfortunately, you were too dangerous in life, and you\'re far too dangerous in undeath. I will enjoy killing you, you Scourged witch...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Scarlet Lieutenant GeblerÂ ');
INSERT INTO `creature_text` VALUES (39003, 3, 0, 'Come an\' get it!', 12, 7, 100, 22, 0, 0, 39038, 692234, 692234, 0, 'Sam Trawley to Player');
INSERT INTO `creature_text` VALUES (39003, 3, 0, 'Now we\'re cooking, $n.', 12, 7, 100, 1, 0, 0, 39037, 686500, 686500, 0, 'Sam Trawley to Player');
INSERT INTO `creature_text` VALUES (39003, 3, 0, 'Waldir Nu waldir ', 12, 7, 100, 0, 0, 0, 0, 11668000, 11715000, 0, 'Сэм Тралли to Player');
INSERT INTO `creature_text` VALUES (39003, 3, 0, 'Yessir, those stormsnouts pack quite a punch.', 12, 7, 100, 1, 0, 0, 39039, 703219, 703219, 0, 'Sam Trawley to Player');
