INSERT INTO `creature_text` VALUES (39025, 3, 0, 'Waldir Nu waldir ', 12, 7, 100, 0, 0, 0, 0, 11590000, 11672000, 0, 'Шайна to Player');
INSERT INTO `creature_text` VALUES (39034, 0, 0, 'Into the Thunderdrome, $n! There\'s no getting out until someone\'s hit the sand. Let\'s start the show!', 12, 0, 100, 1, 0, 0, 39167, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 1, 0, 'Any Ladies and Gentlemen present should LEAVE NOW...for only REAL MEN AND WOMEN will be able to handle the gory spectacle about to unfold! Introducing the first fighter, the warrior of the Wastewander, the Ayatollah of Kaja-Cola...THE GINORMUS!', 14, 0, 100, 1, 0, 0, 39183, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 2, 0, 'The Ginormus has fallen! $n looks like he\'s ready for more!', 14, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 3, 0, 'Our next fighter was found in the deepest tombs of Zul\'Farrak! Gorged on cursed blood, raised from death countless times, he now knows only fury and violence! Dare you face the mindless wrath of...ZUMONGA!', 14, 0, 100, 1, 0, 0, 39195, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 4, 0, '$n has done it again! Zumonga dies once more!', 14, 0, 100, 1, 0, 0, 39232, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 5, 0, 'The faint of heart may wish to shield their eyes! Being brought into the ring now is a horror, captured from the dark tunnels of the Centipaar Hive! I give you the skittering death: SARINEXX!', 14, 0, 100, 1, 0, 0, 39233, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 6, 0, 'There is no stopping $n! Sarinexx has been massacred!', 14, 0, 100, 1, 0, 0, 39235, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39034, 10, 0, 'And that\'s that, folks! This conflict\'s been settled the proper way, with VIOLENCE! Let\'s hear it for our contestants!', 14, 0, 100, 1, 0, 0, 40673, 0, 0, 0, 'DoctorText');
INSERT INTO `creature_text` VALUES (39062, 0, 0, 'Geht in die Grube und zeigt uns, was Ihr drauf habt, $GJunge:Mädel;.', 12, 0, 100, 1, 0, 0, 37886, 0, 0, 0, 'Gefängnisaufseher der Dunkelspeere to Player');
INSERT INTO `creature_text` VALUES (39065, 0, 0, 'Отомсти за наших, $N.', 12, 0, 100, 5, 0, 0, 50094, 0, 0, 0, 'Аггра');
INSERT INTO `creature_text` VALUES (39065, 1, 0, 'Ты оказываешь нам большую услугу. Благодарю тебя, $N.', 12, 0, 100, 1, 0, 0, 50096, 0, 0, 0, 'Аггра');
INSERT INTO `creature_text` VALUES (39066, 0, 0, 'С нами наш маленький друг. Мне жаль дворфов – ведь у гоблина есть его газонокосилка!', 14, 1, 100, 11, 0, 0, 39100, 0, 0, 0, 'Килаг Жутеклык');
INSERT INTO `creature_text` VALUES (39066, 1, 0, 'Держите строй!', 14, 1, 100, 5, 0, 0, 46083, 0, 0, 0, 'Килаг Жутеклык');
INSERT INTO `creature_text` VALUES (39066, 2, 0, 'Тралл Холл!', 14, 1, 100, 15, 0, 0, 39096, 0, 0, 0, 'Килаг Жутеклык');
INSERT INTO `creature_text` VALUES (39066, 3, 0, 'Вперед, Бастия! Отнеси |3-3($N) к Небесному водопаду и исправительной шахте.', 12, 0, 100, 1, 0, 0, 50100, 0, 0, 0, 'Килаг Жутеклык');
INSERT INTO `creature_text` VALUES (39075, 0, 0, 'There has been too much violence. Too much pain. But I have an honorable compromise. Just walk away.', 14, 0, 100, 1, 0, 0, 39397, 0, 0, 0, 'SummonedCreatureText');
INSERT INTO `creature_text` VALUES (39084, 3, 0, 'Thank you, $n! Now I can begin my final assembly...', 12, 0, 100, 396, 0, 0, 39168, 414937, 414937, 0, 'Mizzy Pistonhammer to Player');
