INSERT INTO `creature_text` VALUES (39088, 3, 0, 'They killed him! General Hawthorne - he was ambushed on the road by Horde assassins.', 12, 0, 100, 5, 0, 0, 39133, 8594, 8594, 0, 'Triumph Messenger to Player');
