INSERT INTO `creature_text` VALUES (39096, 3, 0, 'Забейте пасти, или я скормлю вас волкам!', 12, 0, 100, 0, 0, 0, 40726, 522000, 522000, 0, 'Мастер пыток Тундрак to Упряжь бесконечной агонии');
INSERT INTO `creature_text` VALUES (39097, 0, 0, 'Лилиан... Ты... Я рад снова видеть тебя.', 12, 0, 100, 0, 0, 0, 39139, 0, 0, 0, 'Верховный жрец Бенедикт Восс to Лилиан Восс');
