INSERT INTO `creature_text` VALUES (39364, 0, 0, 'One day, The Wolf was very hungry, so he ran east to look for some food', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'the wolf text');
INSERT INTO `creature_text` VALUES (39364, 1, 0, 'The Wolf had grown tired of eating boar meat.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 2, 0, 'The Wolf did not care for raptor meat.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 3, 0, 'The Wolf found scorpid meat too bitter.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 4, 0, 'He was hungry for kodo meat.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 5, 0, 'The Wolf ran and ran, looking for an easy meal.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 6, 0, 'The more he ran, the more his stomach growled.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 7, 0, 'He scoured the sands of Durotar, hunting for his prey.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39364, 8, 0, 'The Wolf was lazy, and didn\'t want to work for his meal.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39365, 0, 0, 'He happened upon a pair of battling kodos.', 41, 0, 100, 567, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39365, 1, 0, 'Clever as he was, The Wolf decided to wait for one to defeat the other.', 41, 0, 100, 567, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39365, 2, 0, 'When the weaker Kodo died, The Wolf approached and tried to eat it.', 41, 0, 100, 567, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39365, 3, 0, 'The remaining Kodo, angry at The Wolf\'s impetuousness,', 41, 0, 100, 567, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (39365, 4, 0, 'swiftly killed The Wolf before he had a chance to eat.', 41, 0, 100, 567, 0, 0, 0, 0, 0, 0, '');
