INSERT INTO `creature_text` VALUES (39382, 3, 0, 'Join us (Join us!) and be granted strength beyond strength! (Strength! His STRENGTH! His MIGHT!)', 14, 0, 100, 15, 0, 22128, 39749, 31312, 31312, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (39382, 3, 0, 'Join us, and embrace the inevitable end. (The Master\'s power calls!) You will be reborn, stronger than flesh! (Strength beyond form! Power in the infinite!)', 14, 0, 100, 1, 0, 22124, 39790, 82359, 82359, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (39382, 3, 0, 'Prove yourselves, my ogres! Slay this insect! (Blood for the master! Blood and suffering!)', 14, 0, 100, 22, 0, 22119, 39596, 116375, 116375, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (39382, 3, 0, 'Swear your allegiance to me (The master wants! He sees and wants and hungers!) and you will be remade in my image. (Wait! INFIDEL! A heathen among us!) An intruder! YOU! Show yourself...', 14, 0, 100, 22, 0, 22125, 39791, 98141, 98141, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (39382, 3, 0, 'The sun of life will set forever, as light gives way to eternal night! (Oblivion looms! It calls!)', 14, 0, 100, 396, 0, 22126, 39747, 11812, 11812, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (39382, 3, 0, 'The Twilight\'s Hammer is the instrument of this grand destruction, the method of glorious annihilation! (Tear aside the veil! Open your mind to emptiness!) Through us will you find true power.', 14, 0, 100, 396, 0, 22127, 39748, 15547, 15547, 0, 'Cho\'gall to Player');
