INSERT INTO `creature_text` VALUES (40955, 3, 0, 'Узри меня в гневе, червь!', 14, 0, 100, 0, 0, 19638, 40892, 4000, 4000, 0, 'Голдринн to Азралон Привратник');
INSERT INTO `creature_text` VALUES (40955, 3, 0, 'Это послужит уроком тем, кто попытается осквернить Хиджал.', 12, 0, 100, 0, 0, 19639, 40891, 13000, 13000, 0, 'Голдринн');
