INSERT INTO `creature_text` VALUES (42039, 0, 0, 'Маша, ступай к нашей палатке и спрячься.', 12, 0, 100, 1, 0, 0, 41957, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 1, 0, '%s вздыхает.', 16, 0, 100, 0, 0, 0, 41959, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 2, 0, 'О, Маша...', 12, 0, 100, 1, 0, 0, 41960, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 3, 0, 'ГЕНЕРАЛ! Выходи из палатки и сразись со мной!', 14, 0, 100, 22, 0, 0, 41963, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 4, 0, 'Ты это потерял, генерал?', 12, 0, 100, 6, 0, 0, 41970, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 5, 0, '$n $gнашел:нашла; его.', 12, 0, 100, 1, 0, 0, 41972, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 6, 0, 'На мертвом теле моего единственного сына, ты, ничтожный отпрыск свиньи.', 12, 0, 100, 5, 0, 0, 41973, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 7, 0, 'Это за моего сына!', 12, 0, 100, 15, 0, 0, 41978, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 8, 0, 'Маша, надо бежать. Нужно уйти как можно дальше отсюда. Они обязательно придут за нами.', 12, 0, 100, 1, 0, 0, 41979, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 9, 0, 'Ты готовишься переступить страшную черту, Кром\'гар. Пусть Мать-Земля смилостивится над тобой.', 12, 0, 100, 1, 0, 21554, 42030, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 10, 0, 'Подожди, вождь! Это $n $gпервым заметил неладное и попытался:первой заметила неладное и попыталась; остановить Кромгара!', 12, 0, 100, 5, 0, 21555, 42067, 0, 0, 0, 'Нigh chieftain cliffwalker');
INSERT INTO `creature_text` VALUES (42039, 11, 0, 'Сжалься, Вождь.', 12, 0, 100, 20, 0, 21556, 42068, 0, 0, 0, 'Нigh chieftain cliffwalker');
